INSERT INTO `xray_template` VALUES (50, 'Ankle joint', '<p><strong>Ankle joint sin</strong><strong>istra</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Besar, bentuk dan struktur trabekula normal</p>\r\n\r\n<p>Sela dan permukaaan sendi normal</p>\r\n\r\n<p>Tidak tampak lesi litik/sklerotik</p>\r\n\r\n<p>Tidak tampak osteofit</p>\r\n\r\n<p>Tidak tampak fraktur</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan :</strong></p>\r\n\r\n<ul>\r\n	<li><strong>Foto ankle joint</strong><strong> sinistra</strong><strong> </strong><strong>saat ini masih dalam batas normal</strong></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n', 'drurip');
INSERT INTO `xray_template` VALUES (51, 'Antebrachi', '<p><strong><u>Antebrachi :</u></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li>Besar,bentuk dan struktur trabekula normal</li>\r\n	<li>Tidak tampak lesi litik / sklerotik</li>\r\n	<li>Tidak tampak garis fraktur</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan: &nbsp;&nbsp;&nbsp;&nbsp; Foto antebrachi saat ini dalam batas normal</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n', 'afif');
INSERT INTO `xray_template` VALUES (52, 'BNO', '<p><strong>BNO</strong><strong> </strong><strong>&nbsp;:<em> </em></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Pre peritoneal fat dan&nbsp; psoas line &nbsp;jelas, &nbsp;kontur kedua ginjal tidak jelas</p>\r\n\r\n<p>Tidak tampak bayangan konkremen opak di sepanjang traktus urinarius</p>\r\n\r\n<p>Distribusi udara di colon dan rongga pelvis masih dalam batas normal</p>\r\n\r\n<p>Skeletal normal</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan&nbsp; : </strong></p>\r\n\r\n<ul>\r\n	<li>Tidak tampak gambaran urolithiasis opak saat ini</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong></p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (53, 'Cardiomegalia Ringan', '<p><strong><u>Thorax</u></strong> :</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kardiomegali ringan</p>\r\n\r\n<p>Sinuses dan diafragma normal.</p>\r\n\r\n<p>Pulmo&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; Hilli normal.</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Corakan bronkhovaskuler normal.</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Tidak tampak infiltrat</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; </strong><strong>K</strong><strong>ardiomegali </strong><strong>r</strong><strong>ingan</strong><strong> (EKG?)</strong></p>\r\n\r\n<p><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong>Pulmo saat ini dalam batas normal</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (54, 'Cardiomegalia', '<p><strong><u>Thorax</u></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kardiomegali ringan</p>\r\n\r\n<p>Sinuses dan difragma normal</p>\r\n\r\n<p>Pulmo&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; Hilli normal</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Corakan bronkhovaskuler normal</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp; Tidak tampak infiltrat</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n\r\n<p><strong>Kesan&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; </strong><strong>K</strong><strong>ardiomegali ringan (EKG?)</strong></p>\r\n\r\n<p><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>&nbsp;&nbsp; Pulmo tidak tampak kelainan</strong></p>\r\n\r\n<p><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong></p>\r\n\r\n<p><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong></p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (55, 'Cervical', '<p><strong><u>Cervical</u></strong><strong><u> </u></strong><strong><u>AP + lateral&nbsp; kiri :</u></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kurva dan alignment baik</p>\r\n\r\n<p>Besar, bentuk dan struktur trabekula normal</p>\r\n\r\n<p>Curiga foramen intervertebralis C4-5, C5-6 dan C6-7 &nbsp;menyempit</p>\r\n\r\n<p>Pedicle normal.</p>\r\n\r\n<p>Tidak tampak lesi litik dan sklerotik</p>\r\n\r\n<p>Tidak tampak fraktur.</p>\r\n\r\n<p>Tidak tampak osteofit</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan :&nbsp;&nbsp; </strong>Curiga foramen intervertebralis C4-5, C5-6 dan C6-7&nbsp; menyempit</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Saran : Foto obliq</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (56, 'Clavicula Normal', '<p><strong><u>Clavicula sinistra</u></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li>Besar bentuk dan struktur trabecula tulang</li>\r\n	<li>Tidak tampak lesi litik / sklerotik</li>\r\n	<li>Tidak tampak fraktur dan dislokasi</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; : Foto clavicula sinistra saat ini masih dalam batas normal</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (57, 'CT Scan Abdomen  maman', '<p>Dilakukan pemeriksaan MSCT whole abdomen tanpa dan dengan kontras potongan aksial, coronal dan sagital mulai dari diafragma sampai perineum, sbb;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>HEPAR &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;: Bentuk dan ukuran normal, permukaan rata, parenkhim innormal. Prekontras, &nbsp;massa hipoekhoik, bulat, batas tegas, ukuran lk 48,3x52,2x54,7 mm dengan sekeliling massa tampak lebih hipoden di hepar lobus kanan. Sistem billier dan vaskuler normal</p>\r\n\r\n<p>V.FELEA &nbsp;&nbsp;&nbsp;: Bentuk dan ukuran normal, dinding reguler. Tidak dijumpai batu/SOL</p>\r\n\r\n<p>PANKREAS: Bentuk, ukuran dan struktur parenkhim normal, tidak tampak lesi pathologis</p>\r\n\r\n<p>LIEN &nbsp;: Bentuk, ukuran dan struktur parenkhim normal, tidak tampak lesi pathologis</p>\r\n\r\n<p>GINJAL &nbsp;KANAN &nbsp;&nbsp;: Besar dan bentuk normal, fungsi baik, sistem pelviokalises tidak melebar. Tidak dijumpai batu. Tampak massa kistik kecil di pole bawah. Ureter tidak melebar.</p>\r\n\r\n<p>GINJAL KIRI &nbsp;&nbsp;&nbsp;&nbsp;: Besar dan bentuk normal, fungsi baik, sistem pelviokalises tidak melebar. Tidak dijumpai batu/SOL. Ureter tidak melebar.</p>\r\n\r\n<p>VU &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;: Bentuk dan ukuran normal.Tidak dijumpai batu/SOL</p>\r\n\r\n<p>AORTA &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;: Bentuk dan kaliber normal. Tidak tampak pembesaran KGB di paraaorta. Tampak kalsifikasi di aorta abdominalis dan a. iliaka kanan-kiri</p>\r\n\r\n<p>Struktur gastrointestinal normal. Tidak dijumpai polip, &nbsp;divertikel atau kolitis. Intraabdomen tidak tampak massa yang mencurigakan. Tampak dilatasi usus.</p>\r\n\r\n<p>Tampak koleksi cairan di rongga pelvis. Tampak nodul iso-hipodens, multipel di abdomen bawah kanan.</p>\r\n\r\n<p>Pada bagian paru yang tervisualisasi &nbsp;tidak dijumpai kelainan. Tidak dijumpai efusi pleura.</p>\r\n\r\n<p>Skeletal : Tampak skoliosis vertebta lumblis ke kiri</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n\r\n<p><em><strong>KESAN &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;: </strong></em></p>\r\n\r\n<p><em><strong>Massa hipoekhoik, bulat, batas tegasdengan sekeliling massa tampak lebih hipoden di hepar lobus kanan --&gt; sugestif suatu abses (lab?)</strong></em></p>\r\n\r\n<p><em><strong>Simpel cyst di ginjal kanan </strong></em></p>\r\n\r\n<p><em><strong>Multipel lymph node di abdomen bawah kanan dan koleksi cairan di rongga pelvis</strong></em></p>\r\n\r\n<p><em><strong>Dilatasi usus suspek ec. ileus</strong></em></p>\r\n\r\n<p><em><strong>Atherosklerosis aorta abdominalis dan a. iliaka kanan-kiri</strong></em></p>\r\n\r\n<p><em><strong>Skoliosis vertebra lumbalis ke kiri </strong></em></p>\r\n\r\n<p><em><strong>CT-scan vesika &nbsp;felea, lien, pankreas, ginjal kiri dan VU tidak dijumpai kelainan</strong></em></p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (58, 'Cruris - Copy', '<p><strong><u>Cruris</u></strong><strong><u> </u></strong><strong><u>dex</u></strong><strong><u>tra </u></strong><strong><u>&nbsp;</u></strong><strong><u>: </u></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li>Besar bentuk dan struktur trabecula tulang normal</li>\r\n	<li>Tidak tampak lesi litik dan sklerotik</li>\r\n	<li>Tidak tampak fraktur</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; Foto cruris&nbsp; saat ini dalam batas normal.</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (59, 'MRI ankle', '<p style=\"text-align:justify\"><em>Klinis: Hipovitaminosis D</em></p>\r\n\r\n<p style=\"text-align:justify\"><strong><em>Dilakukan MRI daerah (R) Ankle joint potongan sagital-axial T1-TSE, T2-STIR, Sagital T2-FSE Fat Sat, Axial &ndash; Coronal PD, Coronal PD Fat sat.</em></strong></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><em>Tampak disruption pada m. Soleus kiri; yang memberikan </em><em>intensitas signal hiperintens pada T1WI, T2STIR, dan PDFS</em><em>.</em></p>\r\n\r\n<p style=\"text-align:justify\">Tidak tampak perubahan intensitas signal pada soft tissue ankle joint dextra.</p>\r\n\r\n<p style=\"text-align:justify\">Tidak tampak perubahan intensitas signal pada bone marrow tulang-tulang lainnya pembentuk ankle joint dextra.</p>\r\n\r\n<p style=\"text-align:justify\">Posisi dan bentuk tulang2 lainnya serta intensitas signal bagian tulang2 sendi ankle lainnya masih tampak normal.</p>\r\n\r\n<p style=\"text-align:justify\">Kontur ligamen medial kompleks (ligamen tibionavikular, ligamen tibiocalcaneal, ligamen tibiotalar posterior dan anterior) dalam batas normal.</p>\r\n\r\n<p style=\"text-align:justify\">Kontur ligamen lateral kompleks <em>(ligamen calcaneofibular, ligamen talofibular anterior dan posterior)</em></p>\r\n\r\n<p style=\"text-align:justify\">Kontur ligamen ankle syndesmosis (ligamen tibiofibular, ligamen transvers inferior, distal interosseus ligament) dalam batas normal</p>\r\n\r\n<p style=\"text-align:justify\">Kontur ligamen spring kompleks (ligamen calcaneonavikular) dalam batas normal.</p>\r\n\r\n<p style=\"text-align:justify\">Kontur dan intensitas signal Tendon Achiles, Tendon Peroneus longus dan brevis,&nbsp; Tendon Flexor hallucis longus, Flexor digitorum longus, Extensor digitorum longus, Extensor hallucis longus dan Tendon tibialis anterior masih memberikan bentuk dan intensitas signal yang normal.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><strong><em>KESIMPULAN :</em></strong></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><strong><em>Partial tear m. soleus kiri. </em></strong></li>\r\n</ul>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (60, 'Cruris', '<p><strong><u>Cruris</u></strong><strong><u> </u></strong><strong><u>sinistra</u></strong><strong><u> </u></strong><strong><u>&nbsp;</u></strong><strong><u>: </u></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><u>Dibandingkan foto tgl&nbsp; 10/11/215</u></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li>Fraktur komplit 1/3 medial os tibia sqa, belum jelas proses remodeling sqa</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li>Tidak tampak fraktur</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; Foto cruris&nbsp; saat ini dalam batas normal.</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (61, 'MRI ankle1', '<p style=\"text-align:justify\"><strong><em>Klinis : Suspek Flexor Hallucis Longus rupture sinistra</em></strong></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><em>Dilakukan pemeriksaan MRI daerah Ankle Joint sinistra dengan sequence potongan sagital-axial T1-SE dan Coronal &ndash; Sagital T2-FSE, Sagital T2-FSE Fat Sat, Axial &ndash; Coronal PD, Coronal PD Fat sat.</em></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><em>Tampak loss of signal pada sebagian tendon flexor hallucis longus sinistra di level distal metatarsal digiti 1 pedis sinistra, yang sebagian kecil intensitas signal yang meningkat inhomogen pada T2WI dan PD Fat Sat, disertai koleksi cairan di sekitarnya.</em></p>\r\n\r\n<p style=\"text-align:justify\">Tidak tampak perubahan intensitas signal pada soft tissue ankle joint dextra.</p>\r\n\r\n<p style=\"text-align:justify\">Tidak tampak perubahan intensitas signal pada bone marrow tulang-tulang lainnya pembentuk ankle joint dextra.</p>\r\n\r\n<p style=\"text-align:justify\">Posisi dan bentuk tulang2 lainnya serta intensitas signal bagian tulang- tulang sendi ankle lainnya masih tampak normal.</p>\r\n\r\n<p style=\"text-align:justify\">Kontur ligamen medial kompleks (ligamen tibionavikular, ligamen tibiospring, ligamen tibiocalcaneal, ligamen tibiotalar posterior) dalam batas normal.</p>\r\n\r\n<p style=\"text-align:justify\">Kontur ligamen ankle syndesmosis (ligamen tibiofibular, ligamen transvers inferior, distal interosseus ligament) dalam batas normal</p>\r\n\r\n<p style=\"text-align:justify\">Kontur ligamen spring kompleks (ligamen calcaneonavikular) dalam batas normal.</p>\r\n\r\n<p style=\"text-align:justify\">Kontur dan intensitas signal Tendon Achiles, Tendon Peroneus longus dan brevis, Flexor digitorum longus, Extensor digitorum longus, Extensor hallucis longus dan Tendon tibialis anterior masih memberikan bentuk dan intensitas signal yang normal.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><strong><em>KESIMPULAN :</em></strong></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><strong><em>Total Rupture tendon flexor hallucis longus sinistra di level distal metatarsal digiti 1 pedis sinistra, </em></strong><strong><em>disertai koleksi cairan minimal disekitarnya </em></strong></li>\r\n</ul>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (62, 'MRI femur - Infectious myositis (Steven)', '<p style=\"text-align:justify\">Dilakukan MRI femur dextra dengan potongan axial-coronal-sagital T1WI, axial-coronal-sagital T2WI dan T2-STIR. Scanning dilakukan tanpa dan dengan kontras media.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><em>Cutis dan subcutis sebagian besar femur dextra tampak menebal dengan gambaran cobble stone. </em></p>\r\n\r\n<p style=\"text-align:justify\"><em>Tampak m. Adductor magnus, m. Adductor brevis, m. Biceps femoris caput brevis menebal dengan parenkim inhomogen, yang memberikan intensitas signal hipointens pada T1WI dan hiperintens pada T2WI dan T2-STIR dengan gambaran multiple septa di dalamnya, yang tampak meluas serta mengobliterasi sebagian m. Gluteus maximus, m. Semimembranosus, m. Vastus medialis, m. Vastus Intermedius dan m. Vastus lateralis dextra. Pada pemberian kontras, tampak enhancement terutama pada septa dan tepinya. </em></p>\r\n\r\n<p style=\"text-align:justify\">M. sartorius, m. Semitendinosus, m. Gracilis, m. Adductor longus, m. Biceps femoris caput longus, m. Rectus femoris serta a.v femoralis dextra masih tampak baik.</p>\r\n\r\n<p style=\"text-align:justify\">Besar, bentuk, dan intensitas signal os femur&nbsp; dextra yang terscanning masih dalam batas normal.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p><strong><em>Kesimpulan&nbsp;&nbsp;&nbsp; : </em></strong></p>\r\n\r\n<ul>\r\n	<li><strong><em>Penebalan dan inhomogenitas parenkim</em></strong><strong><em> m. Adductor magnus, m. Adductor brevis, m. Biceps femoris caput brevis yang tampak meluas serta mengobliterasi sebagian m. Gluteus maximus, m. Semimembranosus, m. Vastus medialis, m. Vastus Intermedius dan m. Vastus lateralis dextra, disertai gambaran multiple septa di dalamnya </em></strong><strong><em>&agrave;</em></strong><strong><em> Karakteristik suatu infectious myositis dengan multiple loculated focal abses. </em></strong></li>\r\n	<li><strong><em>Gambaran selulitis pada cutis dan subcutis sebagian besar femur dextra. </em></strong></li>\r\n	<li><strong><em>Tidak tampak tanda-tanda osteomyelitis. </em></strong></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (63, 'CT Cardiac', '<p style=\"text-align:justify\"><strong>Left main a. Coronaria</strong></p>\r\n\r\n<p style=\"text-align:justify\"><strong>-Plag&hellip;..</strong></p>\r\n\r\n<p style=\"text-align:justify\"><strong>-Stenosis&hellip;..</strong></p>\r\n\r\n<p style=\"text-align:justify\"><strong>-Stent&hellip;..</strong></p>\r\n\r\n<p style=\"text-align:justify\"><strong>LAD</strong></p>\r\n\r\n<p style=\"text-align:justify\"><strong>-Plag&hellip;..</strong></p>\r\n\r\n<p style=\"text-align:justify\"><strong>-Stenosis&hellip;..</strong></p>\r\n\r\n<p style=\"text-align:justify\"><strong>-Stent&hellip;..</strong></p>\r\n\r\n<p style=\"text-align:justify\"><strong>LCX</strong></p>\r\n\r\n<p style=\"text-align:justify\"><strong>-Plag&hellip;..</strong></p>\r\n\r\n<p style=\"text-align:justify\"><strong>-Stenosis&hellip;..</strong></p>\r\n\r\n<p style=\"text-align:justify\"><strong>-Stent&hellip;..</strong></p>\r\n\r\n<p><strong>RCA</strong></p>\r\n\r\n<p style=\"text-align:justify\"><strong>-Plag&hellip;..</strong></p>\r\n\r\n<p style=\"text-align:justify\"><strong>-Stenosis&hellip;..</strong></p>\r\n\r\n<p style=\"text-align:justify\"><strong>-Stent&hellip;..</strong></p>\r\n\r\n<p style=\"text-align:justify\"><strong>Morfologi Jantung</strong></p>\r\n\r\n<p style=\"text-align:justify\"><strong>-Ventrikel kanan</strong></p>\r\n\r\n<p style=\"text-align:justify\"><strong>-Atrium kanan</strong></p>\r\n\r\n<p style=\"text-align:justify\"><strong>-Ventrikel kiri</strong></p>\r\n\r\n<p style=\"text-align:justify\"><strong>-Defect septal</strong></p>\r\n\r\n<p style=\"text-align:justify\"><strong>-Pericardium&hellip;..</strong></p>\r\n\r\n<p style=\"text-align:justify\"><strong>-Aortic value&hellip;..</strong></p>\r\n\r\n<p style=\"text-align:justify\"><strong>Ekstracardial</strong></p>\r\n\r\n<p style=\"text-align:justify\"><strong>-A. Pulmonalis</strong></p>\r\n\r\n<p style=\"text-align:justify\"><strong>-Aorta thorakalis&hellip;..</strong></p>\r\n\r\n<p style=\"text-align:justify\"><strong>-Paru&hellip;..</strong></p>\r\n\r\n<p style=\"text-align:justify\"><strong>-Upper Abdomen&hellip;..</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ct-scan cardiac :</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Klinis :&nbsp;&nbsp;&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><strong>Left main a. Coronaria</strong></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><strong>Tidak tampak plug dan tidak tampak stenosis. </strong></li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\"><strong>LAD</strong></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><strong>Tampak stenosis sebesar&nbsp; &plusmn; 53 % di bagian medial</strong></li>\r\n	<li style=\"text-align:justify\"><strong>Tampak kalsifikasi </strong></li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\"><strong>LCx</strong></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><strong>Tidak tampak plug dan tidak tampak stenosis</strong></li>\r\n	<li style=\"text-align:justify\"><strong>Tampak kalsifikasi</strong></li>\r\n</ul>\r\n\r\n<p><strong>RCA</strong></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><strong>Tampak stenosis sebesar &plusmn; 38 % di bagian medial</strong></li>\r\n	<li style=\"text-align:justify\"><strong>Tampak kalsifikasi</strong></li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><strong>Morfologi Jantung </strong></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><strong>Ventrikel kanan, Atrium kanan, Ventrikel kiri dan atrium kiri tidak masih dalam batas normal</strong></li>\r\n	<li style=\"text-align:justify\"><strong>Tidak tampak defect septal</strong></li>\r\n	<li style=\"text-align:justify\"><strong>Pericardium masih dalam batas normal</strong></li>\r\n	<li style=\"text-align:justify\"><strong>Aortic value masih dalam baas normal</strong></li>\r\n	<li style=\"text-align:justify\"><strong>Tampakm kalsifiaksi di aorta ascenden</strong></li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><strong>Ekstracardial </strong></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><strong>A. pulmonalis, aorta thorakalis, paru yang tervisualisasi dan upper abdomen yang tervisualisasi masih dalam batas normal</strong></li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><strong>Kesan :</strong></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><strong>Stenosis pada LAD dan RCA</strong></li>\r\n	<li style=\"text-align:justify\"><strong>Kalsifikasi ditemukan di LAD, LCx, RCA dan aorta ascenden</strong></li>\r\n	<li style=\"text-align:justify\"><strong>Right dominan</strong></li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (64, 'MRI Genu massa', '<p style=\"text-align:justify\"><em>Dilakukan MRI genu dekstra / sinistra dengan potongan aksial-koronal-sagital PDWI Fat Sat, koronal-sagital T1WI, koronal-sagital T2WI, sagital PD dixon. Scanning dilakukan tanpa media kontras. </em></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<ul>\r\n	<li>Posisi dan&nbsp; bentuk tulang-tulang &nbsp;pembentuk cruris sinistra (distal femur, proksimal tibia, fibula, dan patella) masih tampak normal.</li>\r\n	<li>Intensitas sinyal <em>bone marrow</em> masih tampak normal.</li>\r\n	<li>Korteks memperlihatkan kontur yang rata tanpa disertai perubahan intensitas sinyal di daerah subkondral.</li>\r\n	<li>Kartilago yang &nbsp;meliputi patella, kondilus femoralis dan tibial plateau memiliki ketebalan yang normal dengan permukaan yang rata serta intensitas sinyal yang normal.</li>\r\n	<li>Meniscus medial dan lateral memperlihatkan konfigurasi triangular yang normal dengan intensitas sinyal yang normal.</li>\r\n	<li>Ligamentum kolateral medial dan lateral masih memberikan bentuk, kontur, dan intensitas sinyal yang normal.</li>\r\n	<li>Ligamentum cruciatum anterior dan posterior masih memberikan bentuk, kontur, dan intensitas sinyal yang normal.</li>\r\n	<li>Ligamentum quadriceps femoris dan ligamentum patellaris masih memberikan bentuk, kontur, dan intensitas sinyal yang normal.</li>\r\n	<li>Tidak tampak fluid collection berlebih di daerah celah sendi.</li>\r\n	<li>Tidak tampak intra-articular <em>foreign body.</em></li>\r\n	<li style=\"text-align:justify\">Tidak tampak <em>fluid collection</em> di daerah subligamentum kolateral medial dan lateral serta subligamentum quadriceps femoris dan subligamentum patella.</li>\r\n	<li>Tampak bayangan lesi iso-hiperintens, batas &nbsp;tegas, tepi regular pada T1WI dan terlihat hiperintens dan bersepta pada T2WI. Pada PDWI fat sat lesi terlihat hiperintens. Lesi terletak di subkutis infrapatellaris dan tampak berhubungan dengan bursa superficialis infrapatellaris. Kutis tampak reguler.</li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><strong><u>Kesan: </u></strong></p>\r\n\r\n<p>Bayangan lesi isointens, batas &nbsp;tegas, tepi regular pada T1WI dan terlihat hiperintens dan bersepta pada T2WI dan &nbsp;PDWI fat sat , lesi terletak di subkutis infrapatellaris dan tampak berhubungan dengan bursa superficialis infrapatellaris &agrave; ec. massa kistik DD/bursitis &nbsp;((lab?)</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Saran : MRI dengan kontras</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (65, 'CT Coronary Angiography', '<p style=\"text-align:justify\">-MYOCARDIUM</p>\r\n\r\n<p style=\"text-align:justify\">TEBAL UNIFORM/ TIDAK SAMA</p>\r\n\r\n<p style=\"text-align:justify\">AREA INFARK</p>\r\n\r\n<p style=\"text-align:justify\">FIBROSIS</p>\r\n\r\n<p style=\"text-align:justify\">-RUANG JANTUNG</p>\r\n\r\n<p style=\"text-align:justify\">VENTRIKEL KIRI</p>\r\n\r\n<p style=\"text-align:justify\">VENTRIKEL KANAN</p>\r\n\r\n<p style=\"text-align:justify\">ATRIUM KIRI</p>\r\n\r\n<p style=\"text-align:justify\">ATRIUM KANAN</p>\r\n\r\n<p style=\"text-align:justify\">PERIKARDIUM</p>\r\n\r\n<p style=\"text-align:justify\">V. PULMONALIS</p>\r\n\r\n<p style=\"text-align:justify\">A. PULMOBNALIS</p>\r\n\r\n<p style=\"text-align:justify\">AORTA</p>\r\n\r\n<p style=\"text-align:justify\">PARU YANG TERVISUALISASI</p>\r\n\r\n<p style=\"text-align:justify\">PLEURA YANG TERVISUALISASI</p>\r\n\r\n<p style=\"text-align:justify\">MEDIASTINUM YANG TERVISUALISASI</p>\r\n\r\n<p style=\"text-align:justify\">DINDING DADA DAN KOSTA YANG TERVISUALISASI</p>\r\n\r\n<p style=\"text-align:justify\">LCX</p>\r\n\r\n<p>-Proximal</p>\r\n\r\n<p>-Mid</p>\r\n\r\n<p>-Distal</p>\r\n\r\n<p>-Obtuse marginal</p>\r\n\r\n<p>RCA</p>\r\n\r\n<p>-Proximal</p>\r\n\r\n<p>-Mid</p>\r\n\r\n<p>-Distal</p>\r\n\r\n<p>-PDA</p>\r\n\r\n<p>-PLB</p>\r\n\r\n<p>CALCIUM SCORE</p>\r\n\r\n<p>JANTUNG / PEMBULUH DARAH BESAR / EKSTRACARDIAC</p>\r\n\r\n<p style=\"text-align:center\">CT-Coronary Angiography with IV Contrast</p>\r\n\r\n<p><u>Technique:</u></p>\r\n\r\n<p>INDIKASI KLINIS :</p>\r\n\r\n<p>-Asimptomatis/ indeterminate/ atpical chest pain/ typical chest pain/ follow up</p>\r\n\r\n<p>OBAT PREMEDIKASI:</p>\r\n\r\n<p>-Oralmeteprolol / bisoprolol / atenolol&hellip;..mg</p>\r\n\r\n<p>-IV meteprolol&hellip;&hellip;mg</p>\r\n\r\n<p>-SL nitroglisen &hellip;..mg</p>\r\n\r\n<p>-&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;&hellip;..mg</p>\r\n\r\n<p>-Volume :&hellip;&hellip;&hellip;&hellip;..ml</p>\r\n\r\n<p>-Kecepatan : &hellip;&hellip;&hellip;m/detik</p>\r\n\r\n<p>TEKNIK</p>\r\n\r\n<p>-Ketebalan potongan 0.065 mm</p>\r\n\r\n<p>-ECG&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; / retrospective</p>\r\n\r\n<p>-Denyut rata-rata saat pemeriksaan</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><u>&hellip;&hellip;&hellip;&hellip;:</u></p>\r\n\r\n<p>DOMINAN&nbsp; Kanan/Kiri / Kodominan</p>\r\n\r\n<p>ORIGIN / JALUR ABNORMAL</p>\r\n\r\n<p>LM</p>\r\n\r\n<p>LAD</p>\r\n\r\n<p>-Proksimal</p>\r\n\r\n<p>-Mid</p>\r\n\r\n<p>-Distal</p>\r\n\r\n<p>-Diagonal</p>\r\n\r\n<p>LCX</p>\r\n\r\n<p>-Proksimal</p>\r\n\r\n<p>-Mid</p>\r\n\r\n<p>-Distal</p>\r\n\r\n<p>-Obtuse marginal</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (66, 'MRI Genu Normal', '<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><em>Dilakukan MRI genu dekstra / sinistra dengan potongan aksial-koronal-sagital PDWI Fat Sat, koronal-sagital T1WI, koronal-sagital T2WI, sagital PD dixon. Scanning dilakukan tanpa media kontras. Aksial-sagital-koronal T1WI Fat Sat post contrast</em></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<ul>\r\n	<li>Posisi dan&nbsp; bentuk tulang-tulang pembentuk cruris sinistra (distal femur, proksimal tibia, fibula, dan patella) masih tampak normal.</li>\r\n	<li>Intensitas sinyal <em>bone marrow</em> masih tampak normal.</li>\r\n	<li>Korteks memperlihatkan kontur yang rata tanpa disertai perubahan intensitas sinyal di daerah subkondral.</li>\r\n	<li>Kartilago yang meliputi patella, kondilus femoralis dan tibial plateau memiliki ketebalan yang normal dengan permukaan yang rata serta intensitas sinyal yang normal.</li>\r\n	<li>Meniscus medial dan lateral memperlihatkan konfigurasi triangular yang normal dengan intensitas sinyal yang normal.</li>\r\n	<li>Ligamentum kolateral medial dan lateral masih memberikan bentuk, kontur, dan intensitas sinyal yang normal.</li>\r\n	<li>Ligamentum cruciatum anterior dan posterior masih memberikan bentuk, kontur, dan intensitas sinyal yang normal.</li>\r\n	<li>Ligamentum quadriceps femoris dan ligamentum patellaris masih memberikan bentuk, kontur, dan intensitas sinyal yang normal.</li>\r\n	<li>Tidak tampak fluid collection berlebih di daerah celah sendi.</li>\r\n	<li>Tidak tampak intra-articular <em>foreign body.</em></li>\r\n	<li style=\"text-align:justify\">Tidak tampak <em>fluid collection</em> di daerah subligamentum kolateral medial dan lateral serta subligamentum quadriceps femoris dan subligamentum patella.</li>\r\n	<li style=\"text-align:justify\">Jaringan lunak di sekitar genu dan struktur vaskular yang terlihat masih tampak normal.</li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><strong><u>Kesan: </u></strong></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><strong><em>MRI genu </em></strong><strong><em>dekstra </em></strong><strong><em>/</em></strong><strong><em> sinistra masih tampak normal.</em></strong></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (67, 'CT knee - tibial plateau fracture (Schatzker klasifikasi)', '<p>Dilakukan CT scan upper end tibia dextra dengan slice thickness 0,9 mm. Dilakukan rekonstruksi 3D. Scanning tanpa memakai kontras media.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tampak fraktur kominutif intraartikular mulai dari epifisis sampai metadiafisis os tibia dextra dengan displacement , depresif pada tibial plateau, bicondylar split fraktur, lateral subluksasi</p>\r\n\r\n<p>Tampak fraktur multipel os fibula dextra bagian proksimal dengan posisi fragment fraktur baik</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan :</strong></p>\r\n\r\n<p><strong>- Fraktur kominutif komplit os tibia --&gt; fraktur tibial plateau tipe IV (klasifikasi Schatzker)</strong></p>\r\n\r\n<p><strong>- Fraktur multipel os fibula dextra bagian proksimal dengan posisi fragment fraktur baik</strong></p>\r\n\r\n<p><strong>&nbsp;</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (68, 'MRI HIP Joint', '<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><strong><em>Klinis :</em></strong><strong><em> &nbsp;</em></strong></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><em>Dilakukan MRI HIP joint &nbsp;dengan potongan sagital T1WI, aksial-koronal-sagital T2 FSE Fat Sat, aksial PDWI Fat Sat.</em></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<ul>\r\n	<li>Bentuk caput femoralis &nbsp;dan posisi caput &nbsp;femoralis terhadap cavum &nbsp;acetabulum dalam batas normal.</li>\r\n	<li>Trcohanter majus dan trochanter minus dalam batas normal</li>\r\n	<li>Permukaan artikular sendi &nbsp;hip joint reguler.</li>\r\n	<li>Celah sendi hip joint dalam batas normal.</li>\r\n	<li>Intensitas sinyal tulang-tulang pembentuk hip joint dalam batas normal dengan sinyal yang ekuivalen dengan lemak.</li>\r\n	<li>Tidak tampak defek pada labrum acetabulum</li>\r\n	<li>Sendi hip joint memiliki konfigurasi normal tanpa hipertrofi. &nbsp;Jaringan lemak &nbsp;sekitar hip joint tampak normal.</li>\r\n	<li>Konfigurasi otot-otot dan tendon &nbsp;hip joint dalam batas&nbsp; normal</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><strong><u>Kesan: </u></strong></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><strong><em>MRI </em></strong><strong><em>Hip Joint sinistra saat ini &nbsp;masih </em></strong><strong><em>terlihat dalam batas normal</em></strong></li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (69, 'CT Scan Abdomen Ny. Komariah ( belum diprint )', '<p><strong>Dilakukan pemeriksaan MSCT</strong><strong>/CT scan </strong><strong>abdomen </strong><strong>sampai pelvis potongan </strong><strong>axial. Dibuat rekonstruksi potongan koronal dan sagittal. Scanning dengan memakai kontras oral,I.V.</strong></p>\r\n\r\n<p><strong><em>CT-scan lama tidak dilampirkan</em></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><em><u>Scanning daerah :</u></em></strong></p>\r\n\r\n<p>Hepar&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;: Ukuran membesar, permukaan rata, densitas inhomogen. Pada pre kontras : tampak bayangan isodens 1 buah batas kurang tegas dengan kalsifikasi di tepinya di hepar lobus kanan segmen VI, pada post kontras tampak enhancemant. Vena porta&nbsp; tidak melebar. Vena hepatica tidak melebar. Vena cava inferior tidak melebar. Duktus biliaris</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; intrahepatal tidak melabar.Tidak tampak koleksi cairan di sekitarnya</p>\r\n\r\n<p>Kantung empedu : Ukuran tidak membesar, dinding tidak menebal, regular. Tidak tampak massa/batu. Duktus</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp; biliaris ekstrahepatal tidak melebar</p>\r\n\r\n<p>Limpa&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp; :Ukuran membesar, densitas homogeny. Vena lienalis tidak melebar. Tidak tampak koleksi&nbsp;</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; cairan di sekitarnya.</p>\r\n\r\n<p>Pankreas&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp; :Ukuran tidak membesar, densitas homogeny, ductus pankreatikus tidak melebar</p>\r\n\r\n<p>Ginjal kanan &nbsp; :Ukuran tidak membesar, struktur parenkhim homogen, system pelvokalises melebar, tidak</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; tampak batu/massa. Ureter kanan melebar.</p>\r\n\r\n<p>Ginjal kiri&nbsp; &nbsp; &nbsp;&nbsp;&nbsp;:Ukuran membesar, struktur parenkhim inhomogen, system pelvokalises tidak terlihat. Pada prekontras tampak massa&nbsp; isodens, batas cukup tegas, berbenjol di parekim ginjal dan pada post kontras massa tampak enhancemant. Massa ginjal tampak mendesak limpa ke</p>\r\n\r\n<p>Tidak tampak bayangan hipodens dengan ring enhancement di paraaorta dan parailiaka. Tidak tampak</p>\r\n\r\n<p>kalsifikasi pada aorta abdominalis</p>\r\n\r\n<p>Vesica urinaria: besar dan bentuk normal. Tidak tampak batu/SOL.</p>\r\n\r\n<p>Rektum&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp; :Posisi normal, dinding tidak menebal, perirectal fat normal</p>\r\n\r\n<p>Uterus + adneksa : Besar dan bentuk normal. Parenkim homogeny. Tidak tampak SOL</p>\r\n\r\n<p>Struktur gastrointestinal normal. Tidak dijumpai polip,&nbsp; divertikel atau kolitis. Intraabdomen tidak tampak massa yang mencurigakan. Tidak terlihat pengumpulan cairan bebas di rongga abdomen.</p>\r\n\r\n<p>Pada bagian paru yang tervisualisasi&nbsp; tidak dijumpai kelainan. Tidak dijumpai efusi pleura.</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n\r\n<p>KESAN&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :</p>\r\n\r\n<ul>\r\n	<li>Bayangan isodens 1 buah batas kurang tegas dengan kalsifikasi di tepinya di hepar lobus kanan</li>\r\n</ul>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; segmen VI, pada post kontras tampak enhancemant &agrave; gambaran metastase intrahepatal disetyai</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; hepatoslpenomegali</p>\r\n\r\n<ul>\r\n	<li>Tidak tampak pembesaran KGB di paraaorta/parailiaka</li>\r\n	<li>Scanning k. empedu, pancreas, ginjal kanan-kiri, vesikaurinaria, uterus dan struktur</li>\r\n</ul>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; gastrointestinal saat ini&nbsp; dalam batas normal</p>\r\n\r\n<p>&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (70, 'MRI hunerus patologi', '<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><em>Dilakukan MRI humerus dextra dengan potongan aksial-koronal-sagital PDWI Fat Sat, koronal-sagital T1WI, koronal-sagital T2WI, sagital PD dixon. Scanning dilakukan tanpa media kontras. </em></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<ul>\r\n	<li>Posisi dan&nbsp; bentuk tulang-tulang pembentuk humerus dextra masih terlihat dalam batas&nbsp; normal</li>\r\n	<li>Intensitas sinyal <em>bone marrow</em> masih tampak normal.</li>\r\n	<li>Korteks memperlihatkan kontur yang rata tanpa disertai perubahan intensitas sinyal di daerah subkondral.</li>\r\n	<li>Tampak bayangan lesi iso-hiperintens, batas todak tegas, tepi irregular pada T1WI dan terlihat lebih hiperintens pada T2WI. Pada PDWI fat sat lesi terlihat hiperintens. Lesi tampak berhubungan dengan musculus bicep caput brevis dan mausculus tricep medial. Tampak juga subkutis di sekitar lesi irregular</li>\r\n	<li>Tulang di sekitar lesi tampak masih baik</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><strong><u>Kesan: </u></strong></p>\r\n\r\n<ul>\r\n	<li>Bayangan lesi iso-hiperintens, batas tidak tegas, tepi irregular pada T1WI dan terlihat lebih hiperintens pada T2WI, pada PDWI fat sat lesi terlihat hiperintents di daerao posteromedial humerus dextra dan lesi tampak berhubungan dengan musculus bicep caput brevis dan mausculus tricep medial suspek massa ec. infeksi (abses), lab?) disertai gambaran sellulitis</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Saran : MRI dengan kontras</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (71, 'MRI Lumbosacral - protruded disc (Cahyana)', '<p>&nbsp;</p>\r\n\r\n<p><strong>Telah dilakukan pemeriksaan MRI lumbosacral potongan axial, sagittal T1-T2 tanpa kontras dan T2-Fat sat dengan hasil sebagai berikut :</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><em>Kurva lurus &nbsp;dan alignment baik</em></strong></p>\r\n\r\n<p><strong><em>Internsitas sigan discus intervertebralis L2-3, L3-4, L4-5 dan L5-S1 tmpak menurun</em></strong></p>\r\n\r\n<p><strong><em>VL2-VL3 : disbulging disc </em></strong></p>\r\n\r\n<p><strong><em>VL3-VL4 : protruded disc disertai herniasi nukleus pulposus </em></strong><strong><em>ke arah posterior yang menyebabkan penekanan ke daerah saccus thecalis bagian paracentral kiri, disertai</em></strong><strong><em> hypertrophy &nbsp;ligamentum flavum dan facet joint.</em></strong></p>\r\n\r\n<p><strong><em>VL4-VL5 : protruded disc disertai herniasi nukleus pulposus </em></strong><strong><em>ke arah posterior yang menyebabkan penekanan ke daerah saccus thecalis bagian central disertai</em></strong><strong><em> hypertrophy &nbsp;ligamentum flavum dan facet joint.</em></strong></p>\r\n\r\n<p><strong><em>VL5-VS1 protruded disc disertai herniasi nukleus pulposus </em></strong><strong><em>ke arah posterior yang menyebabkan penekanan ke daerah saccus thecalis bagian paracentral kanan dan foraminal kanan , disertai</em></strong><strong><em> hypertrophy &nbsp;ligamentum flavum dan facet joint.</em></strong></p>\r\n\r\n<p><strong><em>Tampak bayangan hipointens pada T2WI di facies superiro VL3-4</em></strong></p>\r\n\r\n<p>Conus modularis setinggi VL2 medulla spinalis tampak baik dengan intesitas yang normal</p>\r\n\r\n<p>Tidak tampak myelopathy, SOL maupun syringomyelia</p>\r\n\r\n<p>Tidak tampak lesi di ekstradural maupun epidural</p>\r\n\r\n<p>Jaringan lunak vertebral tampak baik. Tidak tampak infiltrate maupun SOL</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><em>Kesan :</em></strong></p>\r\n\r\n<p><strong><em>VL2-VL3 : disbulging disc </em></strong></p>\r\n\r\n<p><strong><em>VL3-VL4 : protruded disc disertai herniasi nukleus pulposus </em></strong><strong><em>ke arah posterior yang menyebabkan penekanan ke daerah saccus thecalis bagian paracentral kiri, disertai</em></strong><strong><em> hypertrophy &nbsp;ligamentum flavum dan facet joint.</em></strong></p>\r\n\r\n<p><strong><em>VL4-VL5 : protruded disc disertai herniasi nukleus pulposus </em></strong><strong><em>ke arah posterior yang menyebabkan penekanan ke daerah saccus thecalis bagian central disertai</em></strong><strong><em> hypertrophy &nbsp;ligamentum flavum dan facet joint.</em></strong></p>\r\n\r\n<p><strong><em>VL5-VS1 protruded disc disertai herniasi nukleus pulposus </em></strong><strong><em>ke arah posterior yang menyebabkan penekanan ke daerah saccus thecalis bagian paracentral kanan dan foraminal kanan , disertai</em></strong><strong><em> hypertrophy &nbsp;ligamentum flavum dan facet joint.</em></strong></p>\r\n\r\n<p><strong><em>Gambaran schmorl&#39;s node pada level VL3-4</em></strong></p>\r\n\r\n<p><strong><em>Perubahan intensitas signal discus intervertebralis L2-3, L3-4, L4-5 dan L5-1 (Pfirrman grade 1-2) &nbsp;</em></strong></p>\r\n\r\n<p><em>&nbsp;</em></p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (72, 'MRI Lumbosacral (Roswati 1)', '<p>&nbsp;</p>\r\n\r\n<p>Dilakukan MRI lumbosacral potongan axial-sagital T1-WI, coronal-sagital T2-WI dan sagittal T2-Fatsat. Scaning dilakukan tanpa media kontras.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Curve dan<strong><em> </em></strong>&nbsp;alignment yang masih baik. Tidak tampak tanda-tanda listhesis maupun fraktur.</p>\r\n\r\n<p>Bentuk dan intensitas signal corpus vertebra lumbosacral masih dalam batas normal</p>\r\n\r\n<p><strong><em>Pada T2WI, tampak penurunan intensitas signal discus ointervertebralis pada level VL4-5 dan VL5-S1</em></strong></p>\r\n\r\n<p>Facies articularis vertebra lumbal dalam batas normal</p>\r\n\r\n<p style=\"text-align:justify\"><strong><em>L4-5 : Disbulging discus disertai </em></strong><strong><em>hypertrophy ringan ligamentum flavum dan facet join yang tidak menyebabkan </em></strong></p>\r\n\r\n<p style=\"text-align:justify\"><strong><em>stenosis foramen neuralis.</em></strong></p>\r\n\r\n<p><strong><em>L5-S1 : Protruded disc disertai herniasi nukleus pulposus ke arah posterior di daerah centralis yang mengakibatkan penekanan ke daerah&nbsp; saccus thecalis bagian anterior </em></strong><strong><em>&nbsp;disertai hypertrophy ligamentum flavum dan facet joint.</em></strong></p>\r\n\r\n<p>Corpus vertebra lumbal yang masuk area scanning masih memberikan bentuk dan intensitas signal yang normal.</p>\r\n\r\n<p>Canalis spinalis lainnya dan medulla spinalis yang masuk area scanning masih memberikan bentuk dan intensitas signal yang normal.</p>\r\n\r\n<p>Ligamentum longitudinalis anterior, ligamentum longitudinalis posterior, ligamentum interspinosus dan ligamentum supraspinosus tidak tampak kelainan.</p>\r\n\r\n<p>Jaringan lunak di daerah paravertebral kanan dan kiri masih dalam batas normal.</p>\r\n\r\n<p>Tidak tampak adanya SOL/massa pada intramedula, intradural, ekstramedula maupun ekstradural.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesimpulan&nbsp;&nbsp;&nbsp;&nbsp; : </strong></p>\r\n\r\n<ul>\r\n	<li><strong><em>L4-5 : Disbulging discus disertai </em></strong><strong><em>hypertrophy ringan ligamentum flavum dan facet join yang tidak menyebabkan stenosis foramen neuralis.</em></strong></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><strong><em>L5-S1 : Protruded disc disertai herniasi nukleus pulposus ke arah posterior di daerah centralis yang mengakibatkan penekanan ke daerah&nbsp; saccus thecalis bagian anterior </em></strong><strong><em>&nbsp;disertai hypertrophy ligamentum flavum dan facet joint.</em></strong></li>\r\n	<li><strong><em>Penurunan intensitas signal discus ointervertebralis pada level VL4-5 dan VL5-S1 (Pffirmann grade II)</em></strong></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (73, 'MRI Lumbosacral Abnormal (Dede, spondilitis)', '<p>&nbsp;</p>\r\n\r\n<p>Dilakukan MRI thorakolumbal potongan axial-sagital T1WI, axial-sagital T2WI dan sagittal STIR. Scanning dilakukan tanpa media kontras.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Curve kifosis vertebra thorakalis.</p>\r\n\r\n<p>Tampak kolaps vertebra TH7 dengan formasi gibus dan tampak corpus vertebra TH8 mengalami &nbsp;perubahan intensitas signal hipointens pada T1WI dan hiperintens inhomogen pada T2WI-STIR. Lesi tampak menyebaban &nbsp;penekanan fragmen posterior (ligamentum longitudinal posterior, epidural) ke daerah canalis spinalis bagian anterior dan radix spinalis bilateral yang menyebabkan stenosis canalis spinalis dan perubahan intensitas signal medulla spinalis setinggi daerah tersebut. Lesi tampaknya juga meluas ke daerah ligamentum longitudinal anterior setinggi VTH7-8.</p>\r\n\r\n<p>Intensitas signal discus intervertebralis thorakal 7-8 tampak menurun dan menyempit.</p>\r\n\r\n<p>Tampak penebalan jaringan lunak yang memberikan signal hipointens inhomogen pada T1WI dan meningkat pada T2WI &ndash;STIR di daerah paravertebra setinggi thorakal 7-8 kanan dan kiri.</p>\r\n\r\n<p>Corpus vertebra thorakolumbal lainnya yang masuk area scanning masih memberikan bentuk dan intensitas signal yang normal.</p>\r\n\r\n<p>Discus intervertebralis dan Facies articularis vertebra thorakolumbal yang masuk area scanning dalam batas normal.</p>\r\n\r\n<p>Canalis spinalis lainnya yang masuk area scanning masih memberikan bentuk dan intensitas signal yang normal</p>\r\n\r\n<p>Ligamentum interspinosus, ligamentum flavum dan ligamentum supraspinosus tidak tampak kelainan.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan :</strong></p>\r\n\r\n<ul>\r\n	<li><strong>Kolaps VTH7 (fraktur) dan perubahan internsitas signal VTH8 dengan formasi gibus pada level corpus vertebra thorakal 7-8 --&gt; sugestif ec. vertebra body abses dengan curiga disertai &nbsp;epidural abses dan paravertebra abses, &nbsp;lesi tampak menyebabkan stenosis canalis spinalis dan tanda-tanda myelopati di daerah tersebut yang mengakibatkan curve kifosis vertebra thorakalis.</strong></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Saran : MRI Thorakolumbal dengan kontras.</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (74, 'MRI Lumbosacral Abnormal', '<p>Dilakukan MRI lumbosacral potongan axial-sagital T1 WI, axial-soronal-sagital T1 WI dengan kontras, axial-sagital-coronal T2 WI, axial-sagital- T2-Fatsat . Scaning dilakukan tanpa dan dengan media kontras :</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Curve lurus dan malaligment vertebra L4-5 ke posterior</p>\r\n\r\n<p>Bentuk dan intensitas signal corpus vertebra lumbosacral masih dalam batas normal</p>\r\n\r\n<p>Discus intervertebralis L4-5 dan L5-S1 tampak menyempit, facies articularis vertebra lumbal dalam batas normal</p>\r\n\r\n<p>Tampak disbulging discus intervertebralis&nbsp;&nbsp; L1-2, L2-3 dan L3-4</p>\r\n\r\n<p>Tampak protruded dicus intervertebralis L4-5 dan L5-S1 disertai dengan herniasi nukleus pulposus ke arah posterior yang menyebabkan penekakan ke daerah saccus thecalis sisi kanan</p>\r\n\r\n<p>Tampak perubahan intensitas signal VL4-5 yang meberikan signal hiperinterns pada T1 T2</p>\r\n\r\n<p>Canalis spinalis dan medulla spinalis yang masuk area scaning masih memberikan bentuk dan intensitas signal yang normal</p>\r\n\r\n<p>Ligamentum flavum menebal.</p>\r\n\r\n<p>Ligamentum longitudinal anterior , ligamentum longitudinal posterior , ligamentum interspinosus , ligamentum supraspinosus dan tampak kelainan</p>\r\n\r\n<p>Jaringan lunak di daerah paravertebra kanan dan kiri masih dalam batas normal</p>\r\n\r\n<p>Tidak tampak adanya SOL / massa pada intramedula, intradural, ekstramedula maupun ekstradural</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesimpulan&nbsp; : </strong></p>\r\n\r\n<p>Spondilolisthesis VL4-5 ke posterior (grade I-II)&nbsp; disertai penyempitan discus intervertebralis L4-5 dan L5-S1</p>\r\n\r\n<p>Protruded dicus intervertebralis L4-5 dan L5-S1 disertai dengan herniasi nukleus pulposus ke arah posterior yang menyebabkan penekanan ke daerah saccus thecalis sisi kanan dan disbulging discus intervertebralis&nbsp;&nbsp; L1-2, L2-3 dan L3-4</p>\r\n\r\n<p>Perubahan internsitas signal VL4-5 sugestif ec. Degeneratif (Modic type II)</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (75, 'MRI Lumbosacral Normal', '<p>Dilakukan MRI lumbosacral potongan axial-sagital T1 WI, axial-soronal-sagital T1 WI dengan kontras, axial-sagital-coronal T2 WI, axial-sagital- T2-Fatsat . Scaning dilakukan tanpa dan dengan media kontras :</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Curve dan aligment vertebra lumbal dalam batas normal</p>\r\n\r\n<p>Bentuk dan intensitas signal corpus vertebra lumbosacral masih dalam batas normal</p>\r\n\r\n<p>Discus intervertebralis dan facies articularis vertebra lumbal dalam batas normal</p>\r\n\r\n<p>Canalis spinalis dan medulla spinalis yang masuk area scaning masih memberikan bentuk dan intensitas signal yang normal</p>\r\n\r\n<p>Ligamentum longitudinal anterior , ligamentum longitudinal posterior , ligamentum interspinosus , ligamentum supraspinosus dan ligamentum flavum tidak tampak kelainan</p>\r\n\r\n<p>Jaringan lunak di daerah paravertebra kanan dan kiri masih dalam batas normal</p>\r\n\r\n<p>Tidak tampak adanya SOL / massa pada intramedula, intradural, ekstramedula maupun ekstradural</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesimpulan&nbsp; : - MRI Lumbosacral masih dalam batas normal</strong></p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (76, 'MRI meniscus tear - bone contusio', '<p style=\"text-align:justify\"><em>Dilakukan MRI genu dekstra / sinistra dengan potongan aksial-koronal-sagital PDWI Fat Sat, koronal-sagital T1WI, koronal-sagital T2WI, sagital PD dixon. Scanning dilakukan tanpa media kontras. Aksial-sagital-koronal T1WI Fat Sat post contrast</em></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<ul>\r\n	<li>Pada&nbsp; T2WI, tampak discontinuitas dari&nbsp; meniscus&nbsp; medial disertai gambaran intensitas signal yang&nbsp; meningkat di sekitarnya pada T2WI dan T2 fat sat . Tampak juga gambaran intensitas signal yang meningkat di pada T2WI dan T2 fat sat di daerah bursa subpatellaris.</li>\r\n	<li>Tampak pemingkatan intensitas sibgal pada T2 fat sat di daerah condylus&nbsp; medialis os femur</li>\r\n	<li>Posisi dan&nbsp; bentuk tulang-tulang pembentuk cruris sinistra (distal femur, proksimal tibia, fibula, dan patella) masih tampak normal.</li>\r\n	<li>Ligamentum kolateral medial dan lateral masih memberikan bentuk, kontur, dan intensitas sinyal yang normal.</li>\r\n	<li>Ligamentum cruciatum anterior dan posterior masih memberikan bentuk, kontur, dan intensitas sinyal yang normal.</li>\r\n	<li>Ligamentum quadriceps femoris dan ligamentum patellaris masih memberikan bentuk, kontur, dan intensitas sinyal yang normal.</li>\r\n	<li>Tidak tampak intra-articular <em>foreign body.</em></li>\r\n	<li style=\"text-align:justify\">Jaringan lunak di sekitar genu terlihat masih tampak normal.</li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><strong><u>Kesan: </u></strong></p>\r\n\r\n<ul>\r\n	<li><strong><em>Gambaran tears di meniscus medialis disertai fluid collection di sekitarnya dan fluid collection di </em></strong><strong><em>daerah bursa subpatellaris.</em></strong></li>\r\n	<li><strong><em>Gambaran bone contusion di daerah condylus medial os femur</em></strong></li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (77, 'MRI Shoulder Normal', '<p style=\"text-align:justify\"><strong><em>Klinis :</em></strong><strong><em> &nbsp;</em></strong></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><em>Dilakukan MRI shoulder dengan potongan sagital T1WI, aksial-koronal-sagital T2 FSE Fat Sat, aksial PDWI Fat Sat.</em></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<ul>\r\n	<li>Bentuk caput humerus dan posisi caput humerus terhadap cavum glenoid dalam batas normal.</li>\r\n	<li>Permukaan artikular sendi glenohumeral reguler.</li>\r\n	<li>Celah sendi glenohumeral dalam batas normal.</li>\r\n	<li>Intensitas sinyal tulang-tulang pembentuk sendi glenohumeral dalam batas normal dengan sinyal yang ekuivalen dengan lemak.</li>\r\n	<li>Tidak tampak defek pada labrum glenoidalis</li>\r\n	<li>Sendi akromioklavikular memiliki konfigurasi normal tanpa hipertrofi. Jaringan lemak subakromial tampak normal.</li>\r\n	<li>Konfigurasi otot-otot dan tendon pembentuk rotator cuff tampak normal.</li>\r\n	<li>Musculus dan tendon biceps tampak normal dengan posisi tendon long head biceps berada di bicipital groove.</li>\r\n	<li>Musculus lain yang terscanning tampak normal.</li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><strong><u>Kesan: </u></strong></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><strong><em>MRI shoulder dekstra dan sinistra masih tampak normal.</em></strong></li>\r\n</ul>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (78, 'CT Scan Abdomen Pria', '<p>Dilakukan pemeriksaan MSCT/CT scan abdomen sampai pelvis potongan axial dengan <em>slice interval</em>&hellip;.mm dan <em>slice thicknes</em> &hellip;mm. Dibuat rekonstruksi potongan koronal dan sagittal dengan <em>slice interval</em>&hellip;mm</p>\r\n\r\n<p>Scanning dengan memakai kontras oral,I.V., dan per rektal</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><em><u>Scanning daerah :</u></em></strong></p>\r\n\r\n<p>Hepar&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;: Ukuran tidak membesar, permukaan rata, densitas homogeny. Tidak tampak massa. Vena porta&nbsp;</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; tidak melebar. Vena hepatica tidak melebar. Vena cava inferior tidak melebar. Duktus biliaris</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; intrahepatal tidak melabar.Tidak tampak koleksi cairan di sekitarnya</p>\r\n\r\n<p>Kt empedu&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; : Ukuran tidak membesar, dinding tidak menebal, regular. Tidak tampak massa/batu. Duktus</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; biliaris ekstrahepatal tidak melebar</p>\r\n\r\n<p>Limpa&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp; &nbsp; : Ukuran tidak membesar, densitas homogeny. Vena lienalis tidak melebar. Tidak tampak&nbsp;</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; koleksi&nbsp; cairan di sekitarnya</p>\r\n\r\n<p>Pankreas&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp; : Ukuran tidak membesar, densitas homogeny, ductus pankreatikus tidak melebar</p>\r\n\r\n<p>Ginjal kanan &nbsp;&nbsp;&nbsp; : Ukuran tidak membesar, struktur parenkhim homogen, system pelvokalises tidak melebar, tidak</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; tampak batu/massa</p>\r\n\r\n<p>Ginjal kiri&nbsp; &nbsp; &nbsp;&nbsp;&nbsp;&nbsp; : Ukuran tidak membesar, struktur parenkhim homogen, system pelvokalises tidak melebar,</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;tidak tampak batu/massa</p>\r\n\r\n<p>Tidak tampak bayangan hipodens dengan ring enhancement di paraaorta dan parailiaka</p>\r\n\r\n<p>Tidak tampak kalsifikasi pada aorta abdominalis, arteri iliaka komunis, arteri iliaka eksterna dan interna</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Vesica urinaria&nbsp; : Terisis penuh, Tidak tampak batu/massa. Perivesical fat normal</p>\r\n\r\n<p>Prostat&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp; &nbsp;&nbsp; : Ukuran tidak membesar, densitas homogen.</p>\r\n\r\n<p>Rektum&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp; : Posisi normal, dinding tidak menebal, perirectal fat normal</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>KESAN&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; : Hepar, kt. empedu, limpa, pankreas, ginjal kanan-kiri dan VU tidak dijumpai kelainan</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Struktur gastrointestinal tidak tampak kelainan, divertikel, polip atau kolitis</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Tidak terlihat koleksi cairan bebas intrabdomen</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Tidak tampak pembearan KGB paraaorta</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (79, 'MRI Shoulder patologis (yusnah)', '<p style=\"text-align:justify\"><strong><em>Klinis :</em></strong><strong><em> </em></strong><strong><em>&nbsp;Frozen shoulder sinistra</em></strong></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><em>Dilakukan MRI shoulder sinistral dengan potongan sagital T1WI, aksial-koronal-sagital T2 FSE Fat Sat, aksial PDWI Fat Sat.</em></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<ul>\r\n	<li>Bentuk caput humerus dan posisi caput humerus terhadap cavum glenoid dalam batas normal.</li>\r\n	<li>Permukaan artikular sendi glenohumeral reguler.</li>\r\n	<li>Celah sendi glenohumeral dalam batas normal.</li>\r\n	<li>Intensitas sinyal tulang-tulang&nbsp; pembentuk sendi glenohumeral dalam batas normal dengan sinyal yang ekuivalen dengan lemak.</li>\r\n	<li><strong><em>Tampak signal inhomogen pada T2WI dan PDWI Fat Sat di daerah tendon supraspinatus dan tendon subscapularis</em></strong></li>\r\n	<li><strong><em>Tampak bayangan hiperintens pada PDWI Fat Sat di daerah bursa subacromialis, bursa subscapularis dan di sekitar labrum glenoidalis </em></strong>.</li>\r\n	<li>Musculus dan tendon biceps tampak normal dengan posisi tendon long head biceps berada di bicipital groove.</li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><strong><u>Kesan: </u></strong></p>\r\n\r\n<ul>\r\n	<li><strong><em>Gambaran partial tears di daerah tendon </em></strong><strong><em>supraspinatus dan tendon subscapularis sinistra</em></strong></li>\r\n	<li><strong><em>Gambaran bursitis di daerah bursa subacromialis, bursa subscapularis dan di sekitar labrum glenoidalis sinistra. </em></strong></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (80, 'Synovial mass in knee', '<p style=\"text-align:justify\"><em>Dilakukan MRI genu&nbsp; sinistra dengan potongan aksial-koronal-sagital PDWI Fat Sat, koronal-sagital T1WI, koronal-sagital T2WI, sagital PD dixon. Scanning dilakukan tanpa media kontras. </em></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><em>&bull; Posisi dan&nbsp; bentuk tulang-tulang pembentuk cruris sinistra (distal femur, proksimal tibia, fibula, dan patella) masih tampak normal.</em></p>\r\n\r\n<p style=\"text-align:justify\"><em>&bull;Intensitas sinyal bone marrow masih tampak normal.</em></p>\r\n\r\n<p style=\"text-align:justify\"><em>&bull;Korteks memperlihatkan kontur yang rata tanpa disertai perubahan intensitas sinyal di daerah subkondral.</em></p>\r\n\r\n<p style=\"text-align:justify\"><em>&bull;Kartilago yang meliputi patella, kondilus femoralis dan tibial plateau memiliki ketebalan yang normal dengan permukaan yang rata serta intensitas sinyal yang normal. </em></p>\r\n\r\n<p style=\"text-align:justify\"><em>&bull;Meniscus medial dan lateral memperlihatkan konfigurasi triangular yang normal dengan intensitas sinyal yang normal.</em></p>\r\n\r\n<p style=\"text-align:justify\"><em>&bull;Ligamentum kolateral medial dan lateral masih memberikan bentuk, kontur, dan intensitas sinyal yang normal.</em></p>\r\n\r\n<p style=\"text-align:justify\"><em>&bull;Ligamentum cruciatum anterior dan posterior masih memberikan bentuk, kontur, dan intensitas sinyal yang normal.</em></p>\r\n\r\n<p style=\"text-align:justify\"><em>&bull;Ligamentum quadriceps femoris dan ligamentum patellaris masih memberikan bentuk, kontur, dan intensitas sinyal yang normal.</em></p>\r\n\r\n<p style=\"text-align:justify\"><em>&bull;Tidak tampak intra-articular foreign body.</em></p>\r\n\r\n<p style=\"text-align:justify\"><em>&bull;Jaringan lunak di sekitar genu dan struktur vaskular yang terlihat masih tampak normal.</em></p>\r\n\r\n<p style=\"text-align:justify\"><em>&bull;Tampak bayangan slight hipointens pada T1WI, slight hiperintens pada T2WI, iso-hiperintens pada T2Fat Sat, isointens pada PDW dan pada GRE terlihat isontens di daerah deep bursa infrapatellaris yang&nbsp; curiga menginfiltrasi Hoffa&#39;s fat </em></p>\r\n\r\n<p style=\"text-align:justify\"><em>&bull;Tampak bayangan hipointens, bersepta, batas tegas pada T1WI dan terlihat hiperintens pada T2WI san T2Fat Sat di daerah muskulus poplitea.</em></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><em>Kesan: </em></p>\r\n\r\n<p style=\"text-align:justify\"><em>Bayangan slight hipointens pada T1WI, slight hiperintens pada T2WI, iso-hiperintens pada T2Fat Sat, isointens pada PDW dan pada GRE terlihat isontens di daerah deep bursa infrapatellaris yang&nbsp; curiga menginfiltrasi Hoffa&#39;s fat&nbsp; --&gt; suspek synovitis deep infrapatellaris bursitis DD/synovial mass</em></p>\r\n\r\n<p style=\"text-align:justify\"><em>Gambaran multipel cyst di muskulus poplitea</em></p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (81, 'ACL dan meniscus tear, parameniscal cyst', '<p style=\"text-align:justify\"><em>Dilakukan MRI genu dextra dengan potongan Sagital T1WI, Coronal-Sagital T2WI, axial-coronal-sagital T2 Fatsat, axial T2 SPAIR, Coronal-Sagital PDWI, Coronal-sagital T2 mDixon. Scanning dilakukan tanpa media kontras. </em></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\">Posisi dan&nbsp; bentuk tulang proksimal tibia, fibula, dan patella tampak baik dengan intensitas signal masih tampak normal.</li>\r\n	<li style=\"text-align:justify\"><em>Ligamentun cruciatum anterior bentuk irreguler dengan batas kabur, yang memberikan intensitas signal intermediate pada T1WI, T2WI dan T2 FS. Tidak tampak gambaran ligamentum cruciatum anterior pada bagian dalam condylus lateralis os femur sinistra (empty notch sign). </em></li>\r\n	<li style=\"text-align:justify\"><em>Tampak gambaran hipointens pada T1WI dan hiperintens pada T2WI dan T2 FatSat di daerah bursa prepatellaris dan daerah posterior dari epicondylus medialis os femur dextra. </em></li>\r\n	<li style=\"text-align:justify\"><em>Pada&nbsp; T2WI, tampak discontinuitas dari&nbsp; meniscus lateralis disertai gambaran intensitas signal yang&nbsp; meningkat di sekitarnya pada T2WI dan T2 fat sat.</em></li>\r\n	<li style=\"text-align:justify\"><em>Tampak lesi dengan batas tegas, bersepta, berukuran lk 2,45 x 1,38 x 1,03 cm di daerah sekitar anterior horn meniscus lateralis, yang memberikan intensitas signal isointens pada T1WI dan hiperintens pada T2WI dan T2 Fatsat</em></li>\r\n	<li style=\"text-align:justify\">Ligamentum cruciatum posterior, kolateral medial dan lateral masih memberikan bentuk, kontur, dan intensitas sinyal yang normal.</li>\r\n	<li style=\"text-align:justify\">Ligamentum quadriceps femoris dan ligamentum patellaris masih memberikan bentuk, kontur, dan intensitas sinyal yang normal.</li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><strong><u>Kesan: </u></strong></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><strong>Gambaran tear pada Ligamentum Cruciatum Anterior dextra (sugestif high grade injury) </strong></li>\r\n	<li style=\"text-align:justify\"><strong>Gambaran tear pada meniscus lateralis dextra disertai sugestif parameniscal cyst.&nbsp; </strong></li>\r\n	<li style=\"text-align:justify\"><strong>Koleksi cairan di daerah bursa prepatellaris dan posterior dari epicondylus medialis os femur dextra. </strong></li>\r\n</ul>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (82, 'ACL rupture', '<p style=\"text-align:justify\">Dilakukan MRI scanning daerah genu dextra potongan axial-coronal-sagital T1WI dan axial-coronal-sagital T2WI. Scanning tanpa memakai kontras media.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\">Bentuk, struktur dan trabekula os femur, os tibia dan os fibula memberikan intensitas signal yang normal.</li>\r\n	<li style=\"text-align:justify\">Meniscus lateral dan medial dextra masih memberikan intensitas signal yang normal.</li>\r\n	<li style=\"text-align:justify\"><em>Tampak struktur yang irreguler dengan signal hipointense inhomogen pada T1WI dan hiperintense inhomogen pada T2WI di daerah ligamentum cruciatum anterior dan postrior dextra.</em></li>\r\n	<li style=\"text-align:justify\"><em>Tampak koleksi cairan berlebih di daerah bursa suprapatella dan subpatella dextra.</em></li>\r\n	<li style=\"text-align:justify\">Ligamentum infrapatella, ligamentum colateral medial dan lateral dextra masih memberikan intensitas signal yang normal.</li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><strong><em><u>Kesan : </u></em></strong></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><strong><em>Partial tear ligamentum crutiatum anterior dan posterior dextra disertai</em></strong><strong><em> bursitis suprapatella&nbsp; dan subpatella dextra.</em></strong></li>\r\n</ul>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (83, 'CT Scan Abdomen Wanita', '<p><strong>Dilakukan pemeriksaan MSCT</strong><strong>/CT scan </strong><strong>abdomen </strong><strong>sampai pelvis potongan </strong><strong>axial. Dibuat rekonstruksi potongan koronal dan sagittal. Scanning dengan memakai kontras oral,I.V.</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><em><u>Scanning daerah :</u></em></strong></p>\r\n\r\n<p>Hepar&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;: Ukuran tidak membesar, permukaan rata, densitas homogeny. Tidak tampak massa. Vena porta&nbsp;</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; tidak melebar. Vena hepatica tidak melebar. Vena cava inferior tidak melebar. Duktus biliaris</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; intrahepatal tidak melebar.Tidak tampak koleksi cairan di sekitarnya</p>\r\n\r\n<p>Kantung empedu : Ukuran tidak membesar, dinding tidak menebal, regular. Tidak tampak massa/batu. Duktus</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp; biliaris ekstrahepatal tidak melebar</p>\r\n\r\n<p>Limpa&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; : Ukuran tidak membesar, densitas homogen. Vena lienalis tidak melebar. Tidak tampak koleksi&nbsp;</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; cairan di sekitarnya.</p>\r\n\r\n<p>Pankreas&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; : Ukuran tidak membesar, densitas homogen, ductus pankreatikus tidak melebar</p>\r\n\r\n<p>Ginjal&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp; : Ukuran tidak membesar, struktur parenkhim homogen, system pelvokalises tidak melebar, tidak</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; tampak batu/massa. Ureter kanan-kiri tidak melebar.</p>\r\n\r\n<p>Vesica urinaria&nbsp;&nbsp; : Besar dan bentuk normal. Tidak tampak batu/SOL.</p>\r\n\r\n<p>Rektum&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; : Posisi normal, dinding tidak menebal, perirectal fat normal</p>\r\n\r\n<p>Tidak tampak bayangan hipodens dengan ring enhancement di paraaorta. Tidak tampak kalsifikasi pada aorta abdominalis</p>\r\n\r\n<p>Struktur gastrointestinal normal. Tidak dijumpai polip,&nbsp; divertikel atau kolitis. Intraabdomen tidak tampak massa yang mencurigakan. Tidak terlihat pengumpulan cairan bebas di rongga abdomen.</p>\r\n\r\n<p>Pada bagian paru yang tervisualisasi&nbsp; tidak dijumpai kelainan. Tidak dijumpai efusi pleura.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n\r\n<p>KESAN&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :</p>\r\n\r\n<ul>\r\n	<li>CT-scan Hepar, vesika&nbsp; felea, lien, pankreas, ginjal kanan-kiri &nbsp;dan VU tidak dijumpai kelainan</li>\r\n	<li>Struktur gastrointestinal tidak tampak kelainan, divertikel, polip atau apolitis</li>\r\n	<li>Tidak terlihat koleksi cairan bebas intrabdomen</li>\r\n	<li>Tidak tampak pembearan KGB paraaorta</li>\r\n</ul>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (84, 'ACL tear (ghaida)', '<p style=\"text-align:justify\"><em>Dilakukan MRI genu dextra dengan potongan axial T1WI, Axial-coronal-sagital T2WI, axial-coronal-sagital T2 STIR, coronal PDWI, Axial T2 SPAIR dan sagital PD dixon. Scanning dilakukan tanpa media kontras. </em></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\">Tampak perubahan intensitas signal epicondylus lateralis os femur dextra, yang tampak hiperintens pada T2WI dan T2-STIR.</li>\r\n	<li style=\"text-align:justify\">Posisi dan&nbsp; bentuk tulang proksimal tibia-fibula dan patella dextra tampak baik dengan intensitas signal masih tampak normal.</li>\r\n	<li style=\"text-align:justify\"><em>Ligamentun cruciatum anterior bentuk irreguler dengan batas kabur, yang memberikan intensitas signal intermediate pada T1WI, T2WI dan T2 FS disertai gambaran hipointens pada T1WI dan hiperintens pada T2WI dan T2 STIR di sekitarnya. Tidak tampak gambaran ligamentum cruciatum anterior pada bagian dalam condylus lateralis os femur dextra (empty notch sign). </em></li>\r\n	<li style=\"text-align:justify\">Ligamentum cruciatum posterior, kolateral medial dan lateral masih memberikan bentuk, kontur, dan intensitas sinyal yang normal.</li>\r\n	<li style=\"text-align:justify\">Ligamentum quadriceps femoris dan ligamentum patellaris masih memberikan bentuk, kontur, dan intensitas sinyal yang normal.</li>\r\n	<li style=\"text-align:justify\"><em>&nbsp;&nbsp;Tampak koleksi cairan di daerah bursa prepatellaris dan suprapatellaris genu dextra. </em></li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><strong><u>Kesan: </u></strong></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><strong><em>Gambaran tear pada Ligamentum Cruciatum Anterior (sugestif high grade injury) dengan koleksi cairan di sekitarnya dan bone contusio pada epicondylus lateralis os femur dextra. </em></strong></li>\r\n	<li style=\"text-align:justify\"><strong><em>Bursitis </em></strong><strong><em>prepatellaris dan suprapatellaris genu dextra. </em></strong></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (85, 'ACL tear (istri superfisi)', '<p style=\"text-align:justify\"><em>Dilakukan MRI genu dextra dengan potongan axial T1WI, Axial-coronal-sagital T2WI, axial-coronal-sagital T2WI Fat Sat, coronal PDWI, Axial T2 SPAIR dan sagital PD dixon. Scanning dilakukan tanpa media kontras. </em></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\">Tampak lesi hipointens pada T1WI maupun T2WI di daerah distal femur (Bone island).</li>\r\n	<li style=\"text-align:justify\">Posisi dan&nbsp; bentuk tulang proksimal tibia, fibula, dan patella tampak baik dengan intensitas signal masih tampak normal.</li>\r\n	<li style=\"text-align:justify\"><em>Ligamentun cruciatum anterior bentuk irreguler dengan batas kabur, yang memberikan intensitas signal intermediate pada T1WI, T2WI dan T2 FS disertai gambaran hipointens pada T1WI dan hiperintens pada T2WI dan T2 FatSat di sekitarnya. Tidak tampak gambaran ligamentum cruciatum anterior pada bagian dalam condylus lateralis os femur sinistra (empty notch sign). </em></li>\r\n	<li style=\"text-align:justify\">Ligamentum cruciatum posterior, kolateral medial dan lateral masih memberikan bentuk, kontur, dan intensitas sinyal yang normal.</li>\r\n	<li style=\"text-align:justify\">Ligamentum quadriceps femoris dan ligamentum patellaris masih memberikan bentuk, kontur, dan intensitas sinyal yang normal.</li>\r\n	<li style=\"text-align:justify\"><em>&nbsp;&nbsp;Tampak koleksi cairan di daerah bursa prepatellaris dan suprapatellaris genu dextra. </em></li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><strong><u>Kesan: </u></strong></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><strong><em>Gambaran tear pada Ligamentum Cruciatum Anterior (sugestif high grade injury) dengan koleksi cairan di sekitarnya.</em></strong></li>\r\n	<li style=\"text-align:justify\"><strong><em>Bursitis </em></strong><strong><em>prepatellaris dan suprapatellaris genu dextratra.</em></strong></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (86, 'ACL tear', '<p style=\"text-align:justify\"><em>Dilakukan MRI genu sinistra dengan potongan axial T1WI, Axial-coronal-sagital T2WI, axial-coronal-sagital T2WI Fat Sat, coronal PDWI, Axial T2 SPAIR dan sagital PD dixon. Scanning dilakukan tanpa media kontras. </em></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\">Tampak lesi hipointens pada T1WI maupun T2WI di daerah distal femur (Bone island).</li>\r\n	<li style=\"text-align:justify\">Posisi dan&nbsp; bentuk tulang proksimal tibia, fibula, dan patella tampak baik dengan intensitas signal masih tampak normal.</li>\r\n	<li style=\"text-align:justify\"><em>Ligamentun cruciatum anterior bentuk irreguler dengan batas kabur, yang memberikan intensitas signal intermediate pada T1WI, T2WI dan T2 FS disertai gambaran hipointens pada T1WI dan hiperintens pada T2WI dan T2 FatSat di sekitarnya. Tidak tampak gambaran ligamentum cruciatum anterior pada bagian dalam condylus lateralis os femur sinistra (empty notch sign). </em></li>\r\n	<li style=\"text-align:justify\">Ligamentum cruciatum posterior, kolateral medial dan lateral masih memberikan bentuk, kontur, dan intensitas sinyal yang normal.</li>\r\n	<li style=\"text-align:justify\">Ligamentum quadriceps femoris dan ligamentum patellaris masih memberikan bentuk, kontur, dan intensitas sinyal yang normal.</li>\r\n	<li style=\"text-align:justify\"><em>&nbsp;&nbsp;Tampak koleksi cairan di daerah bursa prepatellaris dan suprapatellaris genu sinistra. </em></li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><strong><u>Kesan: </u></strong></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><strong><em>Gambaran tear pada Ligamentum Cruciatum Anterior (sugestif high grade injury) dengan koleksi cairan di sekitarnya.</em></strong></li>\r\n	<li style=\"text-align:justify\"><strong><em>Bursitis </em></strong><strong><em>prepatellaris dan suprapatellaris genu sinistra.</em></strong></li>\r\n</ul>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (87, 'Adim (fraktur kompresi)', '<p style=\"text-align:justify\">Dilakukan MRI spine daerah cervical, thorakal, dan lumbosakral dengan potongan axial-sagital T1WI, axial-coronal-sagital T2WI , sagital T2FSE. Scanning dilakukan tanpa media kontras.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\">Curve scoliosis vertebra lumbalis.</li>\r\n	<li style=\"text-align:justify\">Kompresif fraktur corpus vertebra Th 11, Th 12 dan L 1 yang meliputi colum anterior dengan penekanan fragmen posterior terutama vertebra Th 12 pada canalis spinalis bagian anterior dan radix spinalis bilateral, yang menyebabkan perubahan intensitas signal corpus vertebra Th 11, Th 12 dan L 1 dengan intensitas signal hipointens inhomogen pada T1WI dan sedikit meningkat inhomogen pada T2WI dan T2FS. Tampak perubahan intensitas signal medulla spinalis pada daerah setinggi Th 11-12.</li>\r\n	<li style=\"text-align:justify\">Tampak deformitas corpus vertebra C 5-6 dengan bentuk yang kompresif disertai perubahan intensitas signal pada corpus vertebra C 5 sampai Th 2 dengan intensitas signal hipointens inhomogen pada T1WI dan sedikit meningkat inhomogen pada T2WI dan T2FS.</li>\r\n	<li style=\"text-align:justify\">Osteofit tampak pada &lsquo;end plate&rsquo; corpus vertebra cervicalis dan lumbalis.</li>\r\n	<li style=\"text-align:justify\">Diskus intervertebralis tampak menyempit disertai perubahan intensitas signal pada intervertebra Th10-11, Th 11-12 dan Th 12-L1.</li>\r\n	<li style=\"text-align:justify\">Multiple &lsquo;Disc bulging&rsquo; tampak pada intervertebra C3-4, C5-6, C6-7 dan C7-Th1&nbsp; dengan peregangan ligamentum annulare yang masih utuh.</li>\r\n	<li style=\"text-align:justify\">Corpus vertebra lainnya masih dalam batas normal dengan intesitas signal yang masih baik.</li>\r\n	<li style=\"text-align:justify\">Diskus intervertebra thorakal lainnya masih tampak normal dengan intensitas signal yang masih baik.</li>\r\n	<li style=\"text-align:justify\">Canalis spinalis dan medulla spinalis lainnya yang masuk area scanning masih memberikan bentuk dan intensitas signal yang normal</li>\r\n	<li style=\"text-align:justify\">Ligamentum longitudinal anterior dan posterior, ligamentum interspinosus, ligamentum supraspinosus dan ligamentum flavum tampak masih baik.</li>\r\n	<li style=\"text-align:justify\">Tidak tampak adanya SOL/ massa pada intramedula, intradural, ekstramedula maupun ekstradural.</li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><strong>Kesan : </strong></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><strong>Kompresi fraktur corpus vertebra Th 11, Th 12 dan L 1</strong><strong> dengan penekanan fragmen posterior terutama vertebra Th 12</strong><strong> </strong><strong>&nbsp;pada canalis spinalis dan medula spinalis setinggi daerah tersebut dan perubahan intensitas signal corpus vertebra Th 11, Th 12 dan L 1</strong><strong> sugestif disertai tanda-tanda myelopati.</strong></li>\r\n	<li style=\"text-align:justify\"><strong>Fraktur kompresi vertebra C 5-6 disertai perubahan intensitas signal pada </strong><strong>corpus vertebra C 5 sampai Th 2</strong><strong>.</strong></li>\r\n	<li style=\"text-align:justify\"><strong>Multiple Disc bulging. intervertebra C3-4, C5-6, C6-7 dan C7-Th1</strong><strong>&nbsp; dengan peregangan ligamentum annulare yang masih utuh.</strong></li>\r\n	<li style=\"text-align:justify\"><strong>Spondylosis vertebra cervicalis dan lumbalis. </strong></li>\r\n	<li style=\"text-align:justify\"><strong>Skoliosis vertebra lumbalis. </strong></li>\r\n</ul>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (88, 'Agustinus (cervical)', '<p style=\"text-align:justify\">Dilakukan MRI spine daerah cervical dengan potongan axial-sagital T1WI, axial-coronal-sagital T2WI , axial-sagital T2FFE. Scanning dilakukan tanpa media kontras.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<ul>\r\n	<li>Curve dan alignment dalam batas normal.</li>\r\n	<li>Tampak osteofit pada endplate corpus vertebra C 5-7.</li>\r\n	<li>Bentuk dan intensitas sinyal corpus vertebra cervicalis, pedikel, dan arcus neuralis dalam batas normal.</li>\r\n	<li>Tampak peningkatan intensitas signal corpus vertebra Th 1-3 yang terscanning, memberikan signal hiperintens pada T1WI dan T2WI</li>\r\n	<li>Interspinous distance dalam batas normal.</li>\r\n	<li>Discus intervertebralis tidak menyempit.</li>\r\n	<li>Protruded disc tampak pada intervertebral C3-4 disertai herniasi nukleus pulposus ke arah posterior di daerah central-paracentralis kanan serta pada intervertebral C5-6 disertai herniasi nukleus pulposus ke arah posterior di daerah centralis-paracentralis kiri.</li>\r\n	<li>Disbulging pada &nbsp;intervertebral C6-7 dengan peregangan ligamentum annulare yang masih utuh.</li>\r\n	<li>Intensitas sinyal komponen nucleus pulposus dan annulus fibrosus dari disci intervertebralis masih tampak normal.</li>\r\n	<li>Foramina intervertebralis tidak menyempit.</li>\r\n	<li style=\"text-align:justify\">Medulla spinalis dan lapisan leptomeningen masih memberikan bentuk dan intensitas sinyal yang normal.</li>\r\n	<li>Intensitas sinyal pada sendi faset tampak normal.</li>\r\n	<li>Atlantodental interspace dalam batas normal.</li>\r\n	<li>Retropharyngeal, retrolaryngeal, dan retrotracheal space dalam batas normal.</li>\r\n	<li style=\"text-align:justify\">Ligamentum longitudinal anterior, ligamentum longitudinal posterior, ligamentum flavum, ligamentum interspinosus, dan ligamentum supraspinosus tak tampak kelainan.</li>\r\n	<li style=\"text-align:justify\">Jaringan lunak di daerah paravertebra kanan dan kiri masih dalam batas normal.</li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><strong><u>Kesan : </u></strong></p>\r\n\r\n<ul>\r\n	<li><strong>Protruded disc tampak pada intervertebral C3-4 disertai herniasi nukleus pulposus ke arah posterior di daerah central-paracentralis kanan serta pada intervertebral C5-6 disertai herniasi nukleus pulposus ke arah posterior di daerah centralis-paracentralis kiri. </strong></li>\r\n	<li><strong>Disbulging pada&nbsp; intervertebral C6-7 dengan peregangan ligamentum annulare yang masih utuh. </strong></li>\r\n	<li><strong>Peningkatan intensitas signal corpus vertebra Th 1-3 </strong><strong>&agrave; curiga post radiasi DD/ metastasis. </strong></li>\r\n	<li style=\"text-align:justify\"><strong>Spondylosis vertebra cervicalis. </strong></li>\r\n</ul>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (89, 'CT Scan Abdomen', '<p>Ts.Yth,</p>\r\n\r\n<p>Dilakukan pemeriksaan MSCT whole abdomen tanpa dan dengan kontras potongan aksial, coronal dan sagital</p>\r\n\r\n<p>mulai dari diafragma sampai perineum, sbb;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>HEPAR&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; : Bentuk dan ukuran normal, permukaan rata, parenkhim normal. Tidak dijumpai massa solid atau &nbsp;</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; kistik. Sistem billier dan vaskuler normal</p>\r\n\r\n<p>V.FELEA&nbsp;&nbsp;&nbsp; : Bentuk dan ukuran normal, dinding reguler. Tidak dijumpai batu/SOL</p>\r\n\r\n<p>PANKREAS: Bentuk, ukuran dan struktur parenkhim normal, tidak tampak lesi pathologis</p>\r\n\r\n<p>LIEN&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; : Bentuk, ukuran dan struktur parenkhim normal, tidak tampak lesi pathologis</p>\r\n\r\n<p>GINJAL&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; : Kedua ginjal, besar dan bentuk normal, fungsi baik, sistem pelviokalises tidak</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; melebar. Tidak dijumpai batu/SOL</p>\r\n\r\n<p>VU&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; : Bentuk dan ukuran normal.Tidak dijumpai batu/SOL</p>\r\n\r\n<p>AORTA&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; : Bentuk dan kaliber normal. Tidak tampak pembesaran KGB</p>\r\n\r\n<p>Struktur gastrointestinal normal. Tidak dijumpai polip,&nbsp; divertikel atau kolitis. Intraabdomen tidak tampak massa yang mencurigakan. Tidak terlihat pengumpulan cairan bebas di rongga abdomen.</p>\r\n\r\n<p>Pada bagian paru yang tervisualisasi&nbsp; tidak dijumpai kelainan. Tidak dijumpai efusi pleura.</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n\r\n<p>KESAN&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; : Hepar, vesika&nbsp; felea, lien, pankreas, ginjal kanan-kiri dan VU tidak dijumpai kelainan</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Struktur gastrointestinal tidak tampak kelainan, divertikel, polip atau kolitis</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Tidak terlihat koleksi cairan bebas intrabdomen</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Tidak tampak pembearan KGB paraaorta</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (90, 'CT Scan angiography', '<p>Pemeriksaan CT angiography intracranial di lakukan dengan potongan axial, potongan coronal dan sagittal dan 3D volume rendering di lakukan dengan rekonstruksi :</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><em>Sulci corticalis daerah frontalis kanan-kiri, tenmporalis kanan-kiri dan occipitalis kanan-kiri&nbsp;&nbsp; dan cisterna ambiens tampak perdarahan tipis. Tampak juga perdarahan di ventrikel lateral kanan-kiri kornu posterior disertai pelebaran ventrikel lareralis kanan-kiri dan ventrikel III. </em></strong></p>\r\n\r\n<p><strong><em>Tampak juga lesi hipodens, batas cukup tegas, cukup simetris di periventrikuler kanan-kiri </em></strong><strong><em>&agrave; suegstif leukoencephalopathy DD/infark cerebri</em></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>CT-angiography : <strong><em>daerah a. carotis interna kanan segmen petrosus-segment clinoid tampak aneurysme dengan ukuran, 3,9x5,5 mm dan 0,3x17,4 mm,&nbsp; daerah a. carotis interna kiri segmen petrosus-segment communicating&nbsp; tampak aneurysme 1,13x8,22 mm</em></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Arteri basillaris, arteris cerebri media kanan-kiri, a. cerebri anterior, arteri cerebri posterior kanan-kiri dan a. communicant posterior tidak tampak gambaran aneurysme</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (91, 'CT Scan AVM', '<p><u>CT Angiografi</u></p>\r\n\r\n<p>Dilakukan MSCT angigrafi cerebal, di lakukan rekonstruksi pembuluh darah kepala :</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li>Tampak nidus di daerah occipitalis kir yang mendapatkan feeding dari arteri carotis eksterna kiri dan arterivertebralis kiri dan dengan drainase ke vena jugularis interna kiri</li>\r\n	<li>Besar, bentuk dan posisi a. carois interna kanan-kiri, a. cereberi media kanan-kiri, a. cerebri anterior kanan-kiri masih dalam batas normal</li>\r\n	<li>Besar, bentuk dan posisi a. cerebri posterior bilateral masih dalam batas normal.</li>\r\n	<li>Besar, bentuk dan posisi a. vertebralis kanan-kiri, a. basilaris masih dalam batas normal</li>\r\n	<li>Besar, bentuk dan posisi a. communicating anterior dan posterior, a. perocallosa masih dalam batas normal</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kesimpulan :</p>\r\n\r\n<ul>\r\n	<li>Gambaran AVM dengan pembentukannidul di daerah &hellip;.. yangmendapatkan feeding dari a. &hellip;&hellip; dan drainase ke vena &hellip;&hellip;.</li>\r\n</ul>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (92, 'Anita trisnawati, MRI lumbosakral - spondylitis+abses', '<p>Dilakukan MRI lumbosakral potongan axial-sagital T1WI, axial-sagital T2WI dan sagittal STIR. Scanning dilakukan tanpa media kontras.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">Curve kifoscoliosis vertebra lumbalis.</p>\r\n\r\n<p style=\"text-align:justify\">Kompresif fraktur dengan formasi gibbus tampak pada corpus vertebra Lumbal 1-2 disertai perubahan intensitas signal hipointens inhomogen pada T1WI dan hiperintens inhomogen pada T2WI-STIR, tampak&nbsp; penekanan fragmen posterior ke daerah canalis spinalis bagian anterior dan radix spinalis bilateral yang menyebabkan stenosis canalis spinalis dan perubahan intensitas signal medulla spinalis setinggi daerah tersebut. Discus intervertebralis Lumbal 1-2 tampak menyempit.</p>\r\n\r\n<p style=\"text-align:justify\">Tampak penebalan jaringan lunak yang memberikan signal hipointens inhomogen pada T1WI dan meningkat pada T2WI &ndash;STIR di daerah paravertebra setinggi Lumbal 1-3 kanan dan kiri, dan tampaknya meluas ke daerah epidural setinggi vertebra Lumbal 1-2.</p>\r\n\r\n<p style=\"text-align:justify\">Corpus vertebra lumbosakral lainnya yang masuk area scanning masih memberikan bentuk dan intensitas signal yang normal.</p>\r\n\r\n<p style=\"text-align:justify\">Discus intervertebralis dan facies articularis vertebra lumbosakral yang masuk area scanning dalam batas normal.</p>\r\n\r\n<p style=\"text-align:justify\">Canalis spinalis lainnya yang masuk area scanning masih memberikan bentuk dan intensitas signal yang normal</p>\r\n\r\n<p style=\"text-align:justify\">Ligamentum longitudinal anterior, ligamentum longitudinal posterior, ligamentum interspinosus, ligamentum flavum dan ligamentum supraspinosus tidak tampak kelainan.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan :</strong></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><strong>Kompresif fraktur dengan formasi gibus pada corpus vertebra Lumbal 1-2 yang mengakibatkan curve kifoscoliosis vertebra lumbalis disertai stenosis canalis spinalis dan tanda-tanda myelopati di daerah tersebut.</strong></li>\r\n	<li style=\"text-align:justify\"><strong>Curiga abses setinggi paravertebral Lumbal 1-3 kanan dan kiri yang tampaknya meluas ke daerah epidural setinggi vertebra Lumbal 1-2. </strong></li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\"><strong>-----------------------------------------</strong><strong>&agrave;</strong><strong> Sugestif Spondylitis TB curiga disertai paravertebral abses yang tampaknya meluas ke daerah epidural. </strong></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><strong>Saran : MRI Lumbosakral dengan kontras.</strong></p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (93, 'focal temporal atrofi', '<p style=\"text-align:justify\">Dilakukan MRI kepala tanpa kontras dengan potongan axial T1-WI, potongan aksial-coronal T2-WI, axial T2- FLAIR.,</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">Jaringan lunak ekstracalvaria dan calvaria masih dalam batas normal.</p>\r\n\r\n<p style=\"text-align:justify\">Bentuk dan posisi ventrikel lateralis bilateral simetris. Ukuran ventrikel lateralis bilateral, ventrikel III dan ventrikel IV masih dalam batas normal.</p>\r\n\r\n<p style=\"text-align:justify\">Ruang subaraknoid masih memberikan bentuk dan intensitas sinyal yang normal.</p>\r\n\r\n<p style=\"text-align:justify\">Sisterna ambiens dan basalis masih dalam batas normal.</p>\r\n\r\n<p style=\"text-align:justify\">Parenkim cerebri, cerebellum dan batang otak tidak memberikan lesi patologis.</p>\r\n\r\n<p style=\"text-align:justify\">Ganglia basalis, kapsula interna, thalamus bilateral tampak normal.</p>\r\n\r\n<p style=\"text-align:justify\"><strong><em>Tidak tampak atrofi dan sklerosis h</em></strong><strong><em>ip</em></strong><strong><em>pocampus.</em></strong></p>\r\n\r\n<p style=\"text-align:justify\"><strong><em>Lobus temporalis kiri tapak atrofi dengan fissura sylvii kiri lebih lebar dibandingkan kanan</em></strong></p>\r\n\r\n<p style=\"text-align:justify\">Tidak tampak adanya &rsquo;signal void&rsquo; yang merupakan tanda malformasi vaskuler.</p>\r\n\r\n<p style=\"text-align:justify\">Daerah sela tursika dan jukstasella masih tampak normal.</p>\r\n\r\n<p style=\"text-align:justify\">Daerah cerebellopontine angle bilateral masih memberikan bentuk dan intensitas sinyal yang normal.</p>\r\n\r\n<p style=\"text-align:justify\">Nervus akustikus bilateral masih memberikan bentuk dan intensitas sinyal yang normal.</p>\r\n\r\n<p style=\"text-align:justify\">Mastoid air cell bilateral masih tampak normal.</p>\r\n\r\n<p style=\"text-align:justify\">Sinus ethmoidalis, sphenoidalis, maksilaris, dan frontalis bilateral masih dalam batas normal.</p>\r\n\r\n<p style=\"text-align:justify\">Bulbus oculi, ruang retrobulber dan nervus optikus bilateral masih dalam batas normal.</p>\r\n\r\n<p style=\"text-align:justify\">Tidak tampak pergeseran struktur garis tengah.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><strong><u>Kesan:</u></strong></p>\r\n\r\n<ul>\r\n	<li><strong><em>Gambaran focal temporal atrofi (tenporal kiri) disertai fissura sylvii kiri lebih lebar dibandingkan kanan</em></strong></li>\r\n	<li style=\"text-align:justify\"><strong><em>Tidak tampak atrofi dan sklerosis h</em></strong><strong><em>ip</em></strong><strong><em>pocampus saat ini</em></strong></li>\r\n</ul>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (94, 'gct', '<p><em>Dilakukan MRI Left hand dengan potongan aksial-koronal-sagital PDWI Fat Sat,l T1WI, &nbsp;T2WI. Scanning dilakukan tanpa media kontras. </em></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li>Posisi dan &nbsp;bentuk tulang-tulang pembentuk keft hand &nbsp;masih tampak normal.</li>\r\n	<li>Intensitas sinyal <em>bone marrow</em> masih tampak normal.</li>\r\n	<li>Korteks memperlihatkan kontur yang rata tanpa disertai perubahan intensitas sinyal .</li>\r\n	<li>Tidak tampak intra-articular <em>foreign body.</em></li>\r\n	<li>Tampak bayangan massa di daerah lateral phalang &nbsp;proksimal-medial digiti IV, yang pada T1WI terlihat isointens, pada T2WI terlihat slight hiperintens dan pada PDWI Fat sat terlihat hiperintens. Pada posisi sagital, terlihat phalang digiti IV tulang scaloping dengan tulang tampaknya &nbsp;masih intac.</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><u>Kesan: </u></strong></p>\r\n\r\n<ul>\r\n	<li><strong><em>Sugestif suatu Giant cell tumor tendon sheat phalang digiti IV manus sinistra DD/fibromas tendon sheat</em></strong></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><em>Saran : MRI dengan kontras</em></strong></p>\r\n\r\n<p><strong><em>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;CT scan untuk menilai tulang</em></strong></p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (95, 'CT Scan Kepala kontras', '<p style=\"text-align:justify\"><strong>Dari pemeriksaan USG Abdomen ditemukan :</strong></p>\r\n\r\n<p style=\"text-align:justify\"><strong>Hepar </strong>: Besar &amp; bentuk normal ; permukaan rata; tepi masih tajam;&nbsp; echostruktur normal homogen; tidak tampak lesi fokal; system biliaris &amp; vascular normal;&nbsp; diameter V. Portal normal; tidak tampak ascites.</p>\r\n\r\n<p style=\"text-align:justify\"><strong>Gallbladder </strong>: Bentuk &amp; besar normal; dinding regular tidak menebal; tidak tampak batu /sludge /SOL intralumen; CBD tidak melebar.</p>\r\n\r\n<p style=\"text-align:justify\"><strong>Pankreas :</strong> Besar dan bentuk normal, echogenitas normal, tidak tampak lesi fokal ,&nbsp; duktus pankreatikus tidak melebar.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><strong>Lien</strong> : Bentuk &amp; besar normal; permukaan rata; echostruktur normal homogen;tidak tampak lesi lokal; tidak tampak ascites</p>\r\n\r\n<p style=\"text-align:justify\"><strong>Ginjal Kanan/kiri</strong> : Bentuk &amp; besar normal; echogenitas normal; kontur normal; differensiasi echocomplex struktur korteks, medulla &amp; sinus renalis normal; parechym normal; tidak tampak bayangan kalsifikasi/batu/SOL; system pelvokalises tidak melebar; ureter tidak melebar.</p>\r\n\r\n<p style=\"text-align:justify\"><strong>V. Urinaria</strong> : Bentuk &amp;besar normal; dinding regular tidak menebal; tidak tampak bayangan batu/ Sol /sludge intralumen.</p>\r\n\r\n<p style=\"text-align:justify\"><strong>Prostat :</strong> Besar dan bentuk normal ( cc) ; echogenisitas normal homogen; kontur normal; batas tegas, tidak tampak kalsifikasi</p>\r\n\r\n<p style=\"text-align:justify\"><strong>Uterus dan Adnexa:</strong> Besar dan bentuk normal (anteflexi/retroflexi), echogenitas normal homogen, tidak tampak lesi fokal, tidak tampak kelainan di adnexa kanan dan kiri</p>\r\n\r\n<p style=\"text-align:justify\"><strong>KGB paraaorta</strong> tidak membesar.</p>\r\n\r\n<p style=\"text-align:justify\"><strong>Kesan:</strong></p>\r\n\r\n<p><strong>Hepar, Gallbladder, Pankreas, Ginjal Kanan/Kiri, Lien : </strong>Saat ini tidak tampak kelainan <strong>Vesica Urinaria, Prostat/Uterus dan Adnexa: </strong>Saat ini tidak tampak kelainan. <strong>3.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;Tidak tampak pembesaran KGB Paraaorta</strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <strong>Dokter yang memeriksa,</strong></p>\r\n\r\n<p><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Terima kasih atas kepercayaan T.S&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; dr. Fellyanti DGRS. SpRad</p>\r\n\r\n<p>&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (96, 'Limfoma, tri rahayu', '<p><strong><u>Terlampir MRI kepala dengan kontras 22 April 2021, RS Borromeus</u></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><em>Dilakukan </em><em>MRI kepala dengan potongan axial-coronal T1WI, aksial-coronal-sagital T2WI, axial T2 FLAIR, aksial DWI-ADC, axial GRE, axial-sagital-coronal T1WI dengan kontras, MR spectroscopy single voxel, Scanning dilakukan tanpa dan dengan media kontras. </em></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">Jaringan lunak ekstracalvaria dan calvaria masih dalam batas normal.</p>\r\n\r\n<p style=\"text-align:justify\">Tampak lesi multiplae batas tegas berukuran lk 2,76 x 1,76 x 3 cm pada ganglia basalis sampai midbrain daerah pedunculus cerebri kanan dan berukuran lk 2,6 x 2,05 x 1,97 cm pada ganglia basalis sampai midbrain daerah pedunculus cerebri kiri, yang tampak mendesak thalamus kanan dan kiri. Lesi tersebut memberikan intensitas signal hipointens pada T1WI, slight hiperintens pada T2WI dan T2 Flair, serta memberikan gambaran restricted area pada DWI-ADC. Lesi tersebut tidak memberikan gambaran blooming artifact pada GRE. Post contrast scanning tampak enhancement inhomogen. Pada pemeriksaan spectroscopy, tampak peningkatan puncak choline dan penurunan N-Acetylaspartate (NAA). Lesi tersebut tampak bertambah besar di banding MRI sebelumnya.</p>\r\n\r\n<p style=\"text-align:justify\">Sulci dan gyri corticalis, fissura Sylvii bilateral dan fissura interhemisfer masih memberikan bentuk dan intensitas sinyal yang normal.</p>\r\n\r\n<p style=\"text-align:justify\">Bentuk dan posisi ventrikel lateralis bilateral simetris. Ukuran ventrikel lateralis bilateral, ventrikel III dan ventrikel IV masih dalam batas normal.</p>\r\n\r\n<p style=\"text-align:justify\">Ruang subaraknoid masih memberikan bentuk dan intensitas sinyal yang normal.</p>\r\n\r\n<p style=\"text-align:justify\">Sisterna ambiens dan basalis masih dalam batas normal.</p>\r\n\r\n<p style=\"text-align:justify\">Parenkim cerebri lainnya, cerebellum dan batang otak tidak memberikan lesi patologis.</p>\r\n\r\n<p style=\"text-align:justify\">Daerah hippocampus dan lobus temporalis masih tampak normal.</p>\r\n\r\n<p style=\"text-align:justify\">Tidak tampak adanya &rsquo;<em>si</em><em>gn</em><em>al void</em>&rsquo; yang merupakan tanda malformasi vaskuler.</p>\r\n\r\n<p style=\"text-align:justify\">Daerah sela tursika dan jukstasella masih tampak normal.</p>\r\n\r\n<p style=\"text-align:justify\">Daerah cerebellopontine angle bilateral masih memberikan bentuk dan intensitas sinyal yang normal.</p>\r\n\r\n<p style=\"text-align:justify\">Mastoid air cell bilateral masih tampak normal.</p>\r\n\r\n<p style=\"text-align:justify\">Sinus ethmoidalis, sphenoidalis, maksilaris, dan frontalis bilateral masih dalam batas normal.<em> </em></p>\r\n\r\n<p style=\"text-align:justify\">Bulbus oculi, ruang retrobulber dan nervus optikus bilateral masih dalam batas normal.</p>\r\n\r\n<p style=\"text-align:justify\">Tidak tampak penebalan concha nasalis bilateral.</p>\r\n\r\n<p style=\"text-align:justify\">Tidak tampak pergeseran struktur garis tengah.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><strong><u>Kesan:</u></strong></p>\r\n\r\n<p style=\"text-align:justify\"><strong>Multiple lesi di daerah ganglia basalis sampai midbrain daerah pedunculus cerebri kanan (berukuran lk 2,76 x 1,76 x 3 cm</strong><strong>) dan ganglia basalis sampai midbrain daerah pedunculus cerebri kiri (berukuran 2,6 x 2,05 x 1,97 cm</strong><strong>) </strong><strong>&agrave;</strong><strong> sugestif Limfoma, tampak bertambah besar di banding MRI sebelumnya. </strong></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (97, 'CT Scan leher', '<p>Ts.Yth,</p>\r\n\r\n<p>Dilakukan pemeriksaan MSCT-scan leher &nbsp;tanpa dan dengan kontras, dengan &nbsp;potongan aksial, coronal dan</p>\r\n\r\n<p>Sagittal, mulai dari ketinggian caput clavicular-midorbita, dan dibuat window tulang.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Jaringan lunak nasopharing,m orofaring dan hupofaring tampak normal</p>\r\n\r\n<p>Resessus pharyngeus dan torus tubarius tampak normal</p>\r\n\r\n<p>Cavum nasi dan concha nasalis bilateral tampak normal</p>\r\n\r\n<p>Septum nasi di tengah</p>\r\n\r\n<p>Kolom udara dalam faring, laring, glottis dan trachea tampak normal</p>\r\n\r\n<p>Palatum, pterogoid plate dan mandibula tampak normal</p>\r\n\r\n<p>Kelenar parotis dan submandibular bilateral tampak normal</p>\r\n\r\n<p>Retrofaringeal space, pharapharyngael space bilateral, carotic space bilateral dan fossa intratemporalis bilateral dan</p>\r\n\r\n<p>masticator space bilateral dalam batas normal</p>\r\n\r\n<p>Struktur vascular (a. carotis communis bilateral dan v. jugularis bilateral) tampak normal</p>\r\n\r\n<p>Kelenjar hyroid kanan-kiri tidak membesar. Kontur parenkim homogeny</p>\r\n\r\n<p>Laring dan os hyoid dalam batas normal</p>\r\n\r\n<p>Epiglotis dan valekula tampak normal</p>\r\n\r\n<p>Tidak tampak destruksi tulnag basis kranii dan tidak tampak infiltrasi intracranial</p>\r\n\r\n<p>Tidak tampak destruksi os maksilofacial dan mandibula</p>\r\n\r\n<p>Tidak tampak pembesaran KGB</p>\r\n\r\n<p>Sinus ethmoidalis, sphenoidalis dan maksilaria nbilateral yang tersacnning dalam batas normal</p>\r\n\r\n<p>Bulbus oculi dan rteroc=bulebre yang terscannning tampak normal</p>\r\n\r\n<p>Daerah sela tursika &amp; juxtasella serta daerah cerebello-pontin angle masih dalam batas normal</p>\r\n\r\n<p>Mastoid air-cell bilateral yang terscanning tampak normal</p>\r\n\r\n<p>Esofagus tampak normal</p>\r\n\r\n<p>Struktur vertebra cervicalis tampak normal</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n\r\n<p>KESAN&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :</p>\r\n\r\n<ul>\r\n	<li>CT &ndash;scan nasofaring saat ini &hellip;.</li>\r\n	<li>Tidak tampak pembesaran KGB</li>\r\n	<li>Tidak tampak destruksi tulang&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (98, 'M Krisna Hidayat (cervical)', '<p><strong>Pasien sulit diposisikan. Hasil pemeriksaan MRI goyang.</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">Dilakukan MRI cervical dengan potongan axial T1WI, coronal-sagital T2WI. Scanning dilakukan tanpa media kontras.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<ul>\r\n	<li>Tampak lesi yang memberikan intensitas signal isointens inhomogen pada T1WI dan hiperintens inhomogen pada T2WI, berbatas relatif tegas berukuran lk 3,96 x 2,27 x 5,44 cm di parafaringeal space kiri yang tampaknya meluas ke parafaringeal space kanan dan perivertebral space kiri. Kolom udara tampak sedikit menyempit dan terdesak ke kanan di daerah tersebut.</li>\r\n	<li>Curve dan alignment vertebra cervicalis dalam batas normal.</li>\r\n	<li>Bentuk dan intensitas sinyal corpus vertebra cervicalis C3-7 dalam batas normal.</li>\r\n	<li>Interspinous distance dalam batas normal.</li>\r\n	<li>Discus intervertebralis tidak menyempit.</li>\r\n	<li>Intensitas sinyal disci intervertebralis masih tampak normal.</li>\r\n	<li>Foramina intervertebralis tidak tampak menyempit.</li>\r\n	<li style=\"text-align:justify\">Medulla spinalis masih memberikan bentuk dan intensitas sinyal yang normal.</li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><strong><u>Kesan : </u></strong></p>\r\n\r\n<p><strong>Suspek massa semisolid berukuran lk 3,96 x 2,27 x 5,44 cm di parafaringeal space kiri yang tampaknya meluas ke parafaringeal space kanan dan perivertebral space kiri, yang menyempitkan dan mendesak ke kanan kolom udara di daerah tersebut DD/ abses peritonsilar.</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Saran : CT scan leher dengan kontras. </strong></p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (99, 'MCL - medial meniscus tear (Nopitasari)', '<p style=\"text-align:justify\"><em>Dilakukan MRI genu sinistra dengan potongan sagital T1WI, axial-coronal T2WI, axial-coronal T2 Fatsat, axial T2-SPAIR, coronal PDWI, coronal-sagital T2 mDixon, sagital-coronal T2-STIR. Scanning dilakukan tanpa media kontras. </em></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\">Posisi dan&nbsp; bentuk tulang-tulang pembentuk knee sinistra (distal femur, proksimal tibia, fibula, dan patella) masih tampak normal.</li>\r\n	<li style=\"text-align:justify\">Intensitas sinyal <em>bone marrow</em> masih tampak normal.</li>\r\n	<li style=\"text-align:justify\">Korteks memperlihatkan kontur yang rata tanpa disertai perubahan intensitas sinyal di daerah subkondral.</li>\r\n	<li style=\"text-align:justify\">Kartilago yang meliputi patella, condylus femoralis dan tibial plateau memiliki ketebalan yang normal dengan permukaan yang rata serta intensitas sinyal yang normal.</li>\r\n	<li style=\"text-align:justify\">Meniscus lateral memperlihatkan konfigurasi triangular yang normal dengan intensitas sinyal yang normal.</li>\r\n	<li style=\"text-align:justify\"><em>Tampak lesi yang memberikan signal hipointens pada T1WI dan hiperintens pada T2WI, T2FS dan T2-STIR pada meniscus medial sinistra. Tampak koleksi cairan di sekitarnya. </em></li>\r\n	<li style=\"text-align:justify\"><em>Tampak irregularitas pada l</em><em>igamentum c</em><em>olateral medial sinistra</em><em> </em><em>disertai perubahan intensitas signal yang meningkat pada T2WI, T2FS dan T2-STIR. Tampak peningkatan intensitas signal pada area di medial (superficial) dari ligamentum colateral medial sinistra. </em>Ligamentum kolateral lateral<em> </em>masih memberikan bentuk, kontur, dan intensitas sinyal yang normal.</li>\r\n	<li style=\"text-align:justify\">Ligamentum cruciatum anterior dan ligamentum cruciatum posterior masih memberikan bentuk, kontur, dan intensitas sinyal yang normal.</li>\r\n	<li style=\"text-align:justify\">Ligamentum quadriceps femoris dan ligamentum patellaris masih memberikan bentuk, kontur, dan intensitas sinyal yang normal.</li>\r\n	<li style=\"text-align:justify\"><em>Tampak koleksi cairan di bursa prepatellaris dan suprapatellaris sinistra. </em></li>\r\n	<li style=\"text-align:justify\">Tidak tampak intra-articular <em>foreign body.</em></li>\r\n	<li style=\"text-align:justify\"><em>Jaringan lunak di sekitar genu sinistra</em><em> terutama bagian medial tampak menebal dengan intensitas signal inhomogen</em>. Struktur vaskular yang terlihat masih tampak normal.</li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><strong><u>Kesan: </u></strong></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><strong>Gambaran tear pada Ligamentum </strong><strong>c</strong><strong>olateral medial sinistra (grade 2), disertai koleksi cairan di sekitarnya.</strong><em> </em></li>\r\n	<li style=\"text-align:justify\"><strong>Gambaran tear pada Meniscus medial sinistra. </strong></li>\r\n	<li style=\"text-align:justify\"><strong>Koleksi cairan di bursa prepatellaris dan suprapatellaris sinistra DD/ Bursitis. </strong></li>\r\n	<li style=\"text-align:justify\"><strong>Edema jaringan lunak genu sinistra terutama bagian medial. </strong></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (100, 'mri - gct tendon sheat of hand, khofifah 11 th', '<p><em>Dilakukan MRI manus sinistra dengan potongan aksial-koronal-sagital PDWI Fat Sat, T1WI, &nbsp;T2WI dan gradient echo. Scanning dilakukan tanpa media kontras. </em></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tampak lesi pada jaringan lunak distal phalanx proximal sampai proximal phalanx medial digiti V manus sinistra yang tampak mengobliterasi dan mengerosi tulang sekitarnya, lesi tersebut memberikan signal isointens pada T1WI, hiperintens pada T2WI dan PDWI Fat sat. Tidak tampak blooming artifact pada gradient echo.</p>\r\n\r\n<p>Posisi dan &nbsp;bentuk tulang-tulang pembentuk manus sinistra lainnya&nbsp;masih tampak normal.</p>\r\n\r\n<p>Intensitas sinyal <em>bone marrow</em> masih tampak normal.</p>\r\n\r\n<p>Tidak tampak intra-articular <em>foreign body.</em></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><u>Kesan: </u></strong></p>\r\n\r\n<p><strong>Lesi pada jaringan lunak distal phalanx proximal sampai proximal phalanx medial digiti V manus sinistra yang tampak mengobliterasi dan mengerosi tulang sekitarnya </strong></p>\r\n\r\n<p><strong>&agrave; DD/ <em>Giant cell tumor tendon sheath </em></strong></p>\r\n\r\n<p><em><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Fibromas tendon sheath</strong></em></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><em><strong>Saran : MRI dengan kontras</strong></em></p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (101, 'MRI cervical - kifosis protruded disc bulging (Vinny)', '<p><strong>Telah dilakukan pemeriksaan MRI Cervical&nbsp; potongan axial, sagital, coronal T1-T2 dan T2-Fat sat, tanpa kontras media.</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Curve kifosis ringan vertebra cervicalis dengan apex berada pada vertebra C5-6, alignment dalam batas normal.</p>\r\n\r\n<p>Bentuk dan intensitas signal corpus vertebra cervicalis, pedikel dan arcus neuralis masih dalam batas normal</p>\r\n\r\n<p>Interspinous distance dalam batas normal.</p>\r\n\r\n<p>Protruded disc tampak pada intervertebral C5-6 disertai herniasi nukleus pulposus ke arah posterior di daerah central dan paracentralis kiri, yang mengakibatkan penekanan ke daerah&nbsp; saccus thecalis bagian anterior dan penekanan radix spinalis terutama kiri.</p>\r\n\r\n<p style=\"text-align:justify\">Multiple Disc bulging tampak pada intervertebral C3-4, C4-5 dan C6-7 disertai peregangan ligamentum annulare yang masih utuh.</p>\r\n\r\n<p>Facies articularis vertebra cervical dalam batas normal</p>\r\n\r\n<p>Canalis spinalis lainnya dan medulla spinalis yang masuk area scanning masih memberikan bentuk dan intensitas signal yang normal.</p>\r\n\r\n<p>Ligamentum longitudinalis anterior, ligamentum longitudinalis posterior, ligamentum interspinosus, ligamentum supraspinosus dan ligamentum flavum tidak tampak kelainan.</p>\r\n\r\n<p>Jaringan lunak di daerah paravertebral kanan dan kiri masih dalam batas normal.</p>\r\n\r\n<p>Tidak tampak adanya SOL/massa pada intramedula, intradural, ekstramedula maupun ekstradural.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesimpulan&nbsp;&nbsp;&nbsp; : </strong></p>\r\n\r\n<ul>\r\n	<li><strong><em>Protruded disc pada intervertebral C5-6 disertai herniasi nukleus pulposus ke arah posterior di daerah central dan paracentralis kiri, yang mengakibatkan penekanan ke daerah&nbsp; saccus thecalis bagian anterior dan penekanan radix spinalis terutama kiri.</em></strong></li>\r\n	<li style=\"text-align:justify\"><strong><em>Multiple Disc bulging pada intervertebral C3-4, C4-5 dan C6-7 disertai peregangan ligamentum annulare yang masih utuh.</em></strong></li>\r\n	<li style=\"text-align:justify\"><strong><em>Kifosis ringan vertebra cervicalis dengan apex berada pada vertebra C5-6.</em></strong></li>\r\n</ul>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (102, 'MRI cervicalis (nanang warman)', '<p style=\"text-align:justify\">Dilakukan MRI cervical dengan potongan axial T1WI, coronal-sagital T2WI. Scanning dilakukan tanpa media kontras.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<ul>\r\n	<li><strong><em>Tampak lesi yang memberikan intensitas signal hiperintens pada T2WI di spinal cord setinggi VC3-4</em></strong></li>\r\n	<li><strong><em>Protruded disc tampak pada intervertebral C3-4, C4-5 dan C5-6 ke arah central &nbsp;yang mengakibatkan penekanan ke daerah&nbsp; saccus thecalis bagian anterior.</em></strong></li>\r\n	<li><strong><em>Tampak osteofit di VC3-7</em></strong></li>\r\n	<li>Curve lurus dan alignment vertebra cervicalis dalam batas normal.</li>\r\n	<li>Bentuk dan intensitas sinyal corpus vertebra cervicalis dalam batas normal.</li>\r\n	<li>Interspinous distance dalam batas normal.</li>\r\n	<li>Discus intervertebralis tidak menyempit</li>\r\n	<li>Intensitas sinyal disci intervertebralis masih tampak normal.</li>\r\n	<li>Foramina intervertebralis tidak tampak menyempit.</li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><strong><u>Kesan : </u></strong></p>\r\n\r\n<p><strong><em>Gambaran spinal cord edema setinggi VC3-4</em></strong></p>\r\n\r\n<p><strong><em>Protruded disc tampak pada intervertebral C3-4, C4-5 dan C5-6 ke arah central &nbsp;yang mengakibatkan penekanan ke daerah&nbsp; saccus thecalis bagian anterior.</em></strong></p>\r\n\r\n<p><strong><em>Osteofit di VC3-7</em></strong></p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (103, 'MRI femur - Infectious myositis (Steven)', '<p style=\"text-align:justify\">Dilakukan MRI femur dextra dengan potongan axial-coronal-sagital T1WI, axial-coronal-sagital T2WI dan T2-STIR. Scanning dilakukan tanpa dan dengan kontras media.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><em>Cutis dan subcutis sebagian besar femur dextra tampak menebal dengan gambaran cobble stone. </em></p>\r\n\r\n<p style=\"text-align:justify\"><em>Tampak m. Adductor magnus, m. Adductor brevis, m. Biceps femoris caput brevis menebal dengan parenkim inhomogen, yang memberikan intensitas signal hipointens pada T1WI dan hiperintens pada T2WI dan T2-STIR dengan gambaran multiple septa di dalamnya, yang tampak meluas serta mengobliterasi sebagian m. Gluteus maximus, m. Semimembranosus, m. Vastus medialis, m. Vastus Intermedius dan m. Vastus lateralis dextra. Pada pemberian kontras, tampak enhancement terutama pada septa dan tepinya. </em></p>\r\n\r\n<p style=\"text-align:justify\">M. sartorius, m. Semitendinosus, m. Gracilis, m. Adductor longus, m. Biceps femoris caput longus, m. Rectus femoris serta a.v femoralis dextra masih tampak baik.</p>\r\n\r\n<p style=\"text-align:justify\">Besar, bentuk, dan intensitas signal os femur&nbsp; dextra yang terscanning masih dalam batas normal.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p><strong><em>Kesimpulan&nbsp;&nbsp;&nbsp; : </em></strong></p>\r\n\r\n<ul>\r\n	<li><strong><em>Penebalan dan inhomogenitas parenkim</em></strong><strong><em> m. Adductor magnus, m. Adductor brevis, m. Biceps femoris caput brevis yang tampak meluas serta mengobliterasi sebagian m. Gluteus maximus, m. Semimembranosus, m. Vastus medialis, m. Vastus Intermedius dan m. Vastus lateralis dextra, disertai gambaran multiple septa di dalamnya </em></strong><strong><em>&agrave;</em></strong><strong><em> Karakteristik suatu infectious myositis dengan multiple loculated focal abses. </em></strong></li>\r\n	<li><strong><em>Gambaran selulitis pada cutis dan subcutis sebagian besar femur dextra. </em></strong></li>\r\n	<li><strong><em>Tidak tampak tanda-tanda osteomyelitis. </em></strong></li>\r\n</ul>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (104, 'MRI hematom m. vastus lateral-intermedial femurs kiri', '<p><em>Dilakukan MRI femur sinistra dengan potongan aksial-koronal-sagital PDWI Fat Sat, koronal-sagital T1WI, koronal-sagital T2WI, sagital PD dixon. Scanning dilakukan tanpa dan dengan media kontras. </em></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li>Posisi dan &nbsp;femur sinistra masih tampak normal.</li>\r\n	<li>Intensitas sinyal <em>bone marrow</em> masih tampak normal.</li>\r\n	<li>Korteks memperlihatkan kontur yang &nbsp;rata tanpa disertai perubahan intensitas sinyal di daerah subkondral.</li>\r\n	<li>Tampak bayangan lesi hipointens, batas &nbsp;tegas, bersepta yang berhubungan dengan &nbsp;m. vastus &nbsp;lateral-vastus &nbsp;intermedial pada T1WI dengan bayangan slight hiperintens di sekitarnya. Pada T2WI lesi terlihat iso-hiperintens &nbsp;dan pada PDWI fat sat lesi terlihat hiperintens. Pada pemberian kontras massa tampak menyangat pada dinding &nbsp;dan septanya. Lesi&nbsp; tampak meluas ke subkutis sisi posterolateral. Tulang di sekitar lesi terlihat masih baik.</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><u>Kesan: </u></strong></p>\r\n\r\n<p><strong>Gambaran suatu intramuscular hematom femur sinistra (m. vastus lateral-intermedius) yang terlihat meluas ke subkutis sisi posterolateral, disertai edema di sekitarnya</strong></p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (105, 'MRI kepala - Cadasil (Milda)', '<p style=\"text-align:justify\"><em>Dilakukan </em><em>MRI kepala dengan potongan aksial dan sagital T1WI SE, aksial dan koronal T2WI FSE, aksial T2 FLAIR, DWI-ADC dan GRE. Scanning dilakukan tanpa dan dengan media kontras. </em></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">Jaringan lunak ekstracalvaria dan calvaria masih dalam batas normal.</p>\r\n\r\n<p style=\"text-align:justify\">Sulci dan gyri corticalis, fissura Sylvii bilateral dan fissura interhemisfer masih memberikan bentuk dan intensitas sinyal yang normal.</p>\r\n\r\n<p style=\"text-align:justify\">Bentuk dan posisi ventrikel lateralis bilateral simetris. Ukuran ventrikel lateralis bilateral, ventrikel III dan ventrikel IV masih dalam batas normal.</p>\r\n\r\n<p style=\"text-align:justify\">Ruang subaraknoid masih memberikan bentuk dan intensitas sinyal yang normal.</p>\r\n\r\n<p style=\"text-align:justify\">Sisterna ambiens dan basalis masih dalam batas normal.</p>\r\n\r\n<p style=\"text-align:justify\"><em>Tampak multiple foci yang memberikan signal hiperintens pada T2WI, T2 Flair dan DWI di daerah centrum semiovale bilateral, corona radiata bilateral dan frontalis bilateral, sqa. Post contrast scanning, lesi tersebut tidak tampak memberikan enhancement. </em></p>\r\n\r\n<p style=\"text-align:justify\">Cerebellum dan batang otak tidak memberikan lesi patologis.</p>\r\n\r\n<p style=\"text-align:justify\">Ganglia basalis, kapsula interna, thalamus bilateral dan corpus callosum tampak normal.</p>\r\n\r\n<p style=\"text-align:justify\">Daerah hippocampus dan lobus temporalis masih tampak normal.</p>\r\n\r\n<p style=\"text-align:justify\">Tidak tampak adanya &rsquo;<em>si</em><em>gn</em><em>al void</em>&rsquo; yang merupakan tanda malformasi vaskuler.</p>\r\n\r\n<p style=\"text-align:justify\">Daerah sela tursika dan jukstasella masih tampak normal.</p>\r\n\r\n<p style=\"text-align:justify\">Daerah cerebellopontine angle bilateral masih memberikan bentuk dan intensitas sinyal yang normal.</p>\r\n\r\n<p style=\"text-align:justify\">Nervus akustikus bilateral masih memberikan bentuk dan intensitas sinyal yang normal.</p>\r\n\r\n<p style=\"text-align:justify\">&lsquo;Diffusion-WI&rsquo; tidak menunjukkan adanya area patologis yang merupakan area restricted yang signifikan.</p>\r\n\r\n<p style=\"text-align:justify\">Mastoid air cell bilateral masih tampak normal.</p>\r\n\r\n<p style=\"text-align:justify\">Sinus ethmoidalis, sphenoidalis, maksilaris, dan frontalis bilateral masih dalam batas normal.<em> </em></p>\r\n\r\n<p style=\"text-align:justify\">Bulbus oculi, ruang retrobulber dan nervus optikus bilateral masih dalam batas normal.</p>\r\n\r\n<p style=\"text-align:justify\">Tidak tampak penebalan concha nasalis bilateral.</p>\r\n\r\n<p style=\"text-align:justify\">Tidak tampak pergeseran struktur garis tengah.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><strong><u>Kesan:</u></strong></p>\r\n\r\n<p style=\"text-align:justify\"><strong>Multiple foci yang memberikan signal hiperintens pada T2WI, T2 Flair dan DWI di daerah centrum semiovale bilateral, corona radiata bilateral dan frontalis bilateral </strong><strong>&agrave;</strong><strong> suspek CADASIL DD/ Vaskulitis, sqa. </strong></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (106, 'mri kepala - normal', '<p style=\"text-align:justify\"><em>Dilakukan </em><em>MRI kepala dengan potongan aksial dan sagital T1WI SE, aksial dan koronal T2WI FSE, dan potongan tipis di daerah infratentorial T2WI, aksial T2 FLAIR, aksial DWI-ADC, aksial Hemo. Scanning dilakukan tanpa media kontras. </em></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">Jaringan lunak ekstracalvaria dan calvaria masih dalam batas normal.</p>\r\n\r\n<p style=\"text-align:justify\">Sulci dan gyri corticalis, fissura Sylvii bilateral dan fissura interhemisfer masih memberikan bentuk dan intensitas sinyal yang normal.</p>\r\n\r\n<p style=\"text-align:justify\">Bentuk dan posisi ventrikel lateralis bilateral simetris. Ukuran ventrikel lateralis bilateral, ventrikel III dan ventrikel IV masih dalam batas normal.</p>\r\n\r\n<p style=\"text-align:justify\">Ruang subaraknoid masih memberikan bentuk dan intensitas sinyal yang normal.</p>\r\n\r\n<p style=\"text-align:justify\">Sisterna ambiens dan basalis masih dalam batas normal.</p>\r\n\r\n<p style=\"text-align:justify\">Parenkim cerebral, cerebellum dan batang otak tidak memberikan lesi patologis.</p>\r\n\r\n<p style=\"text-align:justify\">Ganglia basalis, kapsula interna, thalamus bilateral dan corpus callosum tampak normal.</p>\r\n\r\n<p style=\"text-align:justify\">Daerah hippocampus dan lobus temporalis masih tampak normal.</p>\r\n\r\n<p style=\"text-align:justify\">Tidak tampak adanya &rsquo;<em>si</em><em>gn</em><em>al void</em>&rsquo; yang merupakan tanda malformasi vaskuler.</p>\r\n\r\n<p style=\"text-align:justify\">Daerah sela tursika dan jukstasella masih tampak normal.</p>\r\n\r\n<p style=\"text-align:justify\">Daerah cerebellopontine angle bilateral masih memberikan bentuk dan intensitas sinyal yang normal.</p>\r\n\r\n<p style=\"text-align:justify\">Nervus akustikus bilateral masih memberikan bentuk dan intensitas sinyal yang normal.</p>\r\n\r\n<p style=\"text-align:justify\">&lsquo;Diffusion-WI&rsquo; tidak menunjukkan adanya area patologis yang merupakan area restricted yang signifikan.</p>\r\n\r\n<p style=\"text-align:justify\">Mastoid air cell bilateral masih tampak normal.</p>\r\n\r\n<p style=\"text-align:justify\">Sinus ethmoidalis, sphenoidalis, maksilaris, dan frontalis bilateral masih dalam batas normal.<em> </em></p>\r\n\r\n<p style=\"text-align:justify\">Bulbus oculi, ruang retrobulber dan nervus optikus bilateral masih dalam batas normal.</p>\r\n\r\n<p style=\"text-align:justify\">Tidak tampak penebalan concha nasalis bilateral.</p>\r\n\r\n<p style=\"text-align:justify\">Tidak tampak pergeseran struktur garis tengah.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><strong><u>Kesan:</u></strong></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><strong><em>MRI Kepala tidak menunjukkan adanya SOL / neoplasma atau </em></strong><strong><em>tanda-tanda</em></strong><strong><em> malformasi </em></strong></li>\r\n	<li style=\"text-align:justify\"><strong><em>Tidak tampak adanya tanda-tanda </em></strong><strong><em>lesi iskemik ataupun perdarahan intrakranial </em></strong></li>\r\n</ul>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (107, 'mri kepala - SOL', '<p style=\"text-align:justify\">Dilakukan MRI kepala tanpa kontras dengan potongan axial T1-WI, potongan aksial T2-WI, axial T2- FLAIR. Dilanjutkan dengan pemeriksaaan MR Angiografi.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">Jaringan lunak ekstracalvaria dan calvaria masih dalam batas normal.</p>\r\n\r\n<p style=\"text-align:justify\">Tampak multiple lesi, batas tegas, tepi irregular, berukuran terbesar lk 0,83 x 1,53 x 0,72 cm, di daerah genu dan body corpus callosum, yang memberikan signal hipointens pada T1WI dan T2-Flair, hyperintens pada T2WI, lesi tersebut tidak memberikan&nbsp; &rdquo; restricted area &rdquo; pada DWI-ADC, dan tidak memberikan gambaran &rdquo; blooming artefact&rdquo; pada gradient echo. Tampak lesi yang memberikan signal hipointense pada TIWI, hiperintens pada T2WI di sekitar lesi dan substansia alba periventriculer lateralis kanan.. Sulci dan gyri di sekitar lesi tampak menyempit.</p>\r\n\r\n<p style=\"text-align:justify\">Sulci &nbsp;corticalis lainnya, fissura Sylvii bilateral dan fissura interhemisfer masih memberikan bentuk dan intensitas sinyal yang normal.</p>\r\n\r\n<p style=\"text-align:justify\">Bentuk dan posisi ventrikel lateralis bilateral simetris. Ukuran ventrikel lateralis bilateral, ventrikel III dan ventrikel IV masih dalam batas normal.</p>\r\n\r\n<p style=\"text-align:justify\">Ruang subaraknoid masih memberikan bentuk dan intensitas sinyal yang normal.</p>\r\n\r\n<p style=\"text-align:justify\">Sisterna ambiens dan basalis masih dalam batas normal.</p>\r\n\r\n<p style=\"text-align:justify\">Parenkim cerebri lainnya, cerebellum dan batang otak tidak memberikan lesi patologis.</p>\r\n\r\n<p style=\"text-align:justify\">Ganglia basalis, kapsula interna, thalamus bilateral tampak normal.</p>\r\n\r\n<p style=\"text-align:justify\">Daerah hippocampus dan lobus temporalis masih tampak normal.</p>\r\n\r\n<p style=\"text-align:justify\">Tidak tampak adanya &rsquo;signal void&rsquo; yang merupakan tanda malformasi vaskuler.</p>\r\n\r\n<p style=\"text-align:justify\">Daerah sela tursika dan jukstasella masih tampak normal.</p>\r\n\r\n<p style=\"text-align:justify\">Daerah cerebellopontine angle bilateral masih memberikan bentuk dan intensitas sinyal yang normal.</p>\r\n\r\n<p style=\"text-align:justify\">Nervus akustikus bilateral masih memberikan bentuk dan intensitas sinyal yang normal.</p>\r\n\r\n<p style=\"text-align:justify\">Mastoid air cell bilateral masih tampak normal.</p>\r\n\r\n<p style=\"text-align:justify\">Sinus ethmoidalis, sphenoidalis, maksilaris, dan frontalis bilateral masih dalam batas normal.</p>\r\n\r\n<p style=\"text-align:justify\">Bulbus oculi, ruang retrobulber dan nervus optikus bilateral masih dalam batas normal.</p>\r\n\r\n<p style=\"text-align:justify\">Tidak tampak pergeseran struktur garis tengah.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><strong><u>Pada MR Angiografi</u></strong>&nbsp; : Kontur a. carotis, a. basilaris dan a. vertebralis beserta cabang2nya yang normal. A.Cerebri media kanan dan kiri serta cabangnya masih memberikan kontur dan diameter yang normal. Tidak tampak tanda-tanda adanya stenosis, aneurisma ataupun tanda-tanda malformasi vaskuler serta tanda-tanda adanya trombus.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><strong><u>Kesan:</u></strong></p>\r\n\r\n<ul>\r\n	<li><strong>Lesi kistik multiple berukuran terbesar lk </strong><strong>0,83 x 1,53 x 0,72 </strong><strong>cm, di </strong><strong>daerah genu dan body corpus callosum</strong><strong> disertai edema vasogenik luas di sekitarnya </strong><strong>&agrave;</strong><strong> SOL (suspek tuberkuloma). </strong></li>\r\n	<li><strong>MR Angiografi tidak menunjukan tanda-tanda adanya stenosis, aneurisma ataupun tanda-tanda malformasi vaskuler serta tanda-tanda adanya trombus.</strong></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Saran : MRI Kepala dengan kontras. </strong></p>\r\n\r\n<p>&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (108, 'MRI lumbosacral - bulging protruded pffirman', '<p><strong>Telah dilakukan pemeriksaan MRI Lumbosakral&nbsp; potongan axial-sagital T1WI, axial-sagital-coronal T2WI dan Sagital T2-STIR, tanpa kontras media.</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Curve dan alignment dalam batas normal. Tidak tampak tanda-tanda listhesis maupun fraktur.</p>\r\n\r\n<p><em>Disc bulging tampak pada intervertebralis L4-5 dengan peregangan ligamentum annulare yang masih tampak utuh. </em></p>\r\n\r\n<p><em>Protruded disc tampak pada intervertebralis L5-S1 disertai herniasi nukleus pulposus ke arah posterior di daerah central, yang mengakibatkan penekanan ke daerah saccus thecalis bagian anterior.<br />\r\nTampak perubahan intensitas signal disertai penyempitan ringan discus intervertebralis L5-S1 yang memberikan signal hipointens inhomogen pada T2WI. </em></p>\r\n\r\n<p>Discus intrvertebralis lainnya dan facies articularis vertebra lumbalis dalam batas normal.</p>\r\n\r\n<p>Corpus vertebra lumbal dan thorakal yang masuk area scanning masih memberikan bentuk dan intensitas signal yang normal.</p>\r\n\r\n<p>Canalis spinalis lainnya dan medulla spinalis yang masuk area scanning masih memberikan bentuk dan intensitas signal yang normal.</p>\r\n\r\n<p>Ligamentum longitudinalis anterior, ligamentum longitudinalis posterior, ligamentum flavum, ligamentum interspinosus dan ligamentum supraspinosus tidak tampak kelainan.</p>\r\n\r\n<p>Jaringan lunak di daerah paravertebral kanan dan kiri masih dalam batas normal.</p>\r\n\r\n<p>Tidak tampak adanya SOL/massa pada intramedula, intradural, ekstramedula maupun ekstradural.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><em>Kesimpulan&nbsp;&nbsp;&nbsp; : </em></strong></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><strong><em>Protruded disc pada intervertebralis L5-S1 disertai herniasi nukleus pulposus ke arah posterior di daerah central, yang mengakibatkan penekanan ke daerah saccus thecalis bagian anterior. </em></strong></li>\r\n	<li style=\"text-align:justify\"><strong><em>Disc bulging pada </em></strong><strong><em>intervertebralis L4-5 dengan peregangan ligamentum annulare yang masih tampak utuh. </em></strong></li>\r\n	<li style=\"text-align:justify\"><strong><em>Gambaran degenerasi discus intervertebralis L5-S1 (Pffirman grade IV). </em></strong></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (109, 'MRI Lumbosacral - protruded disc', '<p>Dilakukan MRI lumbosacral potongan axial-sagital T1-WI, coronal-sagital T2-WI dan sagittal T2-Fatsat. Scaning dilakukan tanpa media kontras.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><em>Curve lurus vertebra lumbalis</em></strong> dengan alignment yang masih baik. Tidak tampak tanda-tanda listhesis maupun fraktur.</p>\r\n\r\n<p>Bentuk dan intensitas signal corpus vertebra lumbosacral masih dalam batas normal</p>\r\n\r\n<p><strong><em>Tampak penyempitan disertai perubahan intensitas signal discus intervertebralis L3-4 yang tampak hipointens pada T2-WI.&nbsp; Discus intervertebralis lumbal lainnya tidak menyempit dengan perubahan intensitas signal isointens inhomogen pada T2-WI.</em></strong></p>\r\n\r\n<p>Facies articularis vertebra lumbal dalam batas normal</p>\r\n\r\n<p><strong><em>Protruded disc tampak pada intervertebral L4-5 dan L5-S1 disertai herniasi nukleus pulposus ke arah posterior di daerah centralis, paracentralis dan foraminal bilateral terutama kanan, yang mengakibatkan penekanan ke daerah&nbsp; saccus thecalis bagian anterior dan penekanan radix spinalis bilateral terutama kanan.</em></strong></p>\r\n\r\n<p>Corpus vertebra lumbal yang masuk area scanning masih memberikan bentuk dan intensitas signal yang normal.</p>\r\n\r\n<p>Canalis spinalis lainnya dan medulla spinalis yang masuk area scanning masih memberikan bentuk dan intensitas signal yang normal.</p>\r\n\r\n<p><strong><em>Ligamentum flavum setinggi VL4-5 tampak menebal</em></strong></p>\r\n\r\n<p>Ligamentum longitudinalis anterior, ligamentum longitudinalis posterior, ligamentum interspinosus dan ligamentum supraspinosus tidak tampak kelainan.</p>\r\n\r\n<p>Jaringan lunak di daerah paravertebral kanan dan kiri masih dalam batas normal.</p>\r\n\r\n<p>Tidak tampak adanya SOL/massa pada intramedula, intradural, ekstramedula maupun ekstradural.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesimpulan&nbsp;&nbsp;&nbsp;&nbsp; : </strong></p>\r\n\r\n<ul>\r\n	<li><strong><em>Protruded disc intervertebral L4-5 dan L5-S1 disertai herniasi nukleus pulposus ke arah posterior di daerah centralis- paracentralis sisi kiri &nbsp;yang mengakibatkan penekanan ke daerah&nbsp; saccus thecalis bagian anterior.</em></strong></li>\r\n	<li><strong><em>Penyempitan discus intervertebralis L3-4 disertai gambaran degenerative disc disease (Pffirmann grade IV pada discus intervertebralis L3-4 dan Pffirmann grade III pada discus intervertebralis lumbal lainnya). </em></strong></li>\r\n	<li><strong><em>Penebalan ligamentum flavum setinggi VL4-5.</em></strong></li>\r\n</ul>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (110, 'MRI Lumbosacral Abnormal (maman)', '<p>Dilakukan MRI lumbosacral potongan axial-sagital T1 WI, axial-soronal-sagital T1 WI dengan kontras, axial-sagital-coronal T2 WI, axial-sagital- T2-Fatsat . Scaning dilakukan tanpa dan dengan media kontras :</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Curve lurus dan malaligment grade I (25%) vertebra L4-5 ke posterior</p>\r\n\r\n<p>Bentuk dan intensitas signal corpus vertebra lumbosacral masih dalam batas normal</p>\r\n\r\n<p>Discus intervertebralis L4-5 dan L5-S1 tampak menunjukkan sedikit penurunan intensitas signal</p>\r\n\r\n<p>Tampak disbulging discus intervertebralis&nbsp;&nbsp; L1-2, L2-3 dan L3-4</p>\r\n\r\n<p>Protruded dicus intervertebralis L4-5 disertai dengan herniasi nukleus pulposus ke arah central dan paracentral sisi kiri, protruded dicus intervertebralis L5-S1 disertai dengan herniasi nukleus pulposus ke arah central disertai penekanan saccus thecalis bagian anterior.</p>\r\n\r\n<p>Canalis spinalis dan medulla spinalis yang masuk area scaning masih memberikan bentuk dan intensitas signal yang normal</p>\r\n\r\n<p>Ligamentum flavum setinggi L4-5 menebal.</p>\r\n\r\n<p>Ligamentum longitudinal anterior , ligamentum longitudinal posterior , ligamentum interspinosus , ligamentum supraspinosus dan tampak kelainan</p>\r\n\r\n<p>Jaringan lunak di daerah paravertebra kanan dan kiri masih dalam batas normal</p>\r\n\r\n<p>Tidak tampak adanya SOL / massa pada intramedula, intradural, ekstramedula maupun ekstradural</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesimpulan&nbsp; : </strong></p>\r\n\r\n<p><strong>Spondilolisthesis VL4-5 ke posterior (grade I) disertai penyempitan discus intervertebralis L4-5 dan L5-S1</strong></p>\r\n\r\n<p><strong>Protruded dicus intervertebralis L4-5 disertai dengan herniasi nukleus pulposus ke arah central dan paracentral sisi kiri, protruded dicus intervertebralis L5-S1 disertai dengan herniasi nukleus pulposus ke arah central disertai penekanan saccus thecalis bagian anterior.</strong></p>\r\n\r\n<p><strong>Disbulging discus intervertebralis&nbsp;&nbsp; L3-4</strong></p>\r\n\r\n<p><strong>Penebakan ligamentum flavum setinggi VL4-5</strong></p>\r\n\r\n<p><strong>Perubahan internsitas signal discus intervertebralis L4-5 dan L5-S1 (Pffirrman grade I-II)</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (111, 'MRI Lumbosacral Abnormal (Ranto M,, spondilitis TB + psoas abses)', '<p>Dilakukan MRI thorakolumbal potongan axial-sagital T1WI, axial-sagital T2WI dan sagittal STIR. Scanning dilakukan tanpa media kontras.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Curve kifosis vertebra thorakolumbalis.</p>\r\n\r\n<p>Corpus VTH12 sedikit irrgeuler dan tampak mengalami perubahan internsitas signal (hipointens pada T1WI dan hiperintens inhomogen pada T2WI-STIR), dan tampak juga kolaps vertebra VL1 dengan formasi gibus. Lesi pada level VTH12-L1 tampak menyebaban &nbsp;penekanan fragmen posterior (ligamentum longitudinal posterior, epidural) ke daerah canalis spinalis bagian anterior dan radix spinalis bilateral yang menyebabkan stenosis canalis spinalis dan perubahan intensitas signal medulla spinalis setinggi daerah tersebut. Lesi tampaknya juga meluas ke daerah ligamentum longitudinal anterior pada level tersebut.</p>\r\n\r\n<p>Intensitas signal discus intervertebralis TH12-L1 tampak menurun dan menyempit.</p>\r\n\r\n<p>Tampak penebalan jaringan lunak yang memberikan signal hipointens inhomogen pada T1WI dan meningkat pada T2WI &ndash;STIR di daerah paravertebra setinggi VTH11-L1. Tampak bayangan massa soft tissue dengan intensitas signal yang inhomogen, batas tegas di m. psoas kanan dan kiri.</p>\r\n\r\n<p>Corpus vertebra thorakolumbal lainnya yang masuk area scanning masih memberikan bentuk dan intensitas signal yang normal.</p>\r\n\r\n<p>Discus intervertebralis dan Facies articularis vertebra thorakolumbal yang masuk area scanning lainnya dalam batas normal.</p>\r\n\r\n<p>Canalis spinalis lainnya yang masuk area scanning lainnya masih memberikan bentuk dan intensitas signal yang normal</p>\r\n\r\n<p>Ligamentum interspinosus, ligamentum flavum dan ligamentum supraspinosus tidak tampak kelainan.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan :</strong></p>\r\n\r\n<ul>\r\n	<li><strong>Kolaps VL1 (fraktur) dan perubahan internsitas signal VTH12 dengan formasi gibus pada level tersebut --&gt; sugestif vertebra body abses ec. spondylitis TB (lab?), dengan curiga disertai epidural abses dan paravertebra abses, &nbsp;lesi tampak menyebabkan stenosis canalis spinalis dan tanda-tanda myelopati di daerah tersebut yang mengakibatkan curve kifosis vertebra thorakalis.</strong></li>\r\n	<li><strong>Tampak juga gambaran massa dengan </strong><strong>intensitas signal yang inhomogen, batas tegas di m. psoas kanan dan kiri </strong><strong>&agrave;</strong><strong> sugestif suatu psoas abses </strong></li>\r\n</ul>\r\n\r\n<p>&nbsp;<strong>Saran : MRI Thorakolumbal dengan kontras</strong></p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (112, 'MRI Lumbosacral abnormal e Suntini', '<p><strong>Telah dilakukan pemeriksaan MRI lumbosacral potongan axial, sagittal T1-T2 tanpa kontras dan T2-Fat sat dengan hasil sebagai berikut :</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Mallalignment VL2,3,4,5 ke posterior dan tampak scoliosis vertebra lumbalis dengan dengan konveksitas ke kiri.</p>\r\n\r\n<p>Tampak kompres fraktur VL2-3</p>\r\n\r\n<p>VL1-VL2 protruded disc ringan disertai herniasi nukleus pulposus ke arah posterior yang menyebabkan penekanan ke daerah saccus thecalis paracentralisi kanan, disertai hypertrophy ringan ligamentum flavum dan facet joint.</p>\r\n\r\n<p>VL2-VL3 protruded disc disertai herniasi nukleus pulposus ke arah posterior yang menyebabkan penekanan ke daerah saccus thecalis paracaentralis kanan dan kiri, disertai hypertrophy ringan ligamentum flavum dan facet joint.</p>\r\n\r\n<p>VL3-VL4 protruded disc disertai herniasi nukleus pulposus ke arah posterior yang menyebabkan penekanan ke daerah saccus thecalis bagian central, disertai hypertrophy &nbsp;ligamentum flavum dan facet joint.</p>\r\n\r\n<p>VL4-VL5 protruded disc disertai herniasi nukleus pulposus ke arah posterior yang menyebabkan penekanan ke daerah saccus thecalis bagian central dan paracentral kanan, disertai hypertrophy &nbsp;ligamentum flavum dan facet joint.</p>\r\n\r\n<p>VL5-VS1 protruded disc disertai herniasi nukleus pulposus ke arah posterior yang menyebabkan penekanan ke daerah saccus thecalis bagian central , disertai hypertrophy &nbsp;ligamentum flavum dan facet joint.</p>\r\n\r\n<p>Tampak osteofit di VL1-5</p>\r\n\r\n<p>Conus modularis setinggi VL2 medulla spinalis tampak baik dengan intesitas yang normal</p>\r\n\r\n<p>Tidak tampak myelopathy, SOL maupun syringomyelia</p>\r\n\r\n<p>Tidak tampak lesi di ekstradural maupun epidural</p>\r\n\r\n<p>Jaringan lunak vertebral tampak baik. Tidak tampak infiltrate maupun SOL</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><em>Kesan :</em></p>\r\n\r\n<p><em>Fraktur kompresi VL3-4 ec. proses degeneratif &nbsp;dan spondilolisthesis </em>VL2,3,4,5 ke posterior<em> &nbsp;disertai :</em></p>\r\n\r\n<p><em>VL1-VL2 protruded disc ringan disertai herniasi nukleus pulposus </em><em>ke arah posterior yang menyebabkan penekanan ke daerah saccus thecalis paracentralisi kanan, disertai</em><em> hypertrophy ringan ligamentum flavum dan facet joint.</em></p>\r\n\r\n<p><em>VL2-VL3 protruded disc disertai herniasi nukleus pulposus </em><em>ke arah posterior yang menyebabkan penekanan ke daerah saccus thecalis paracaentralis kanan dan kiri, disertai</em><em> hypertrophy &nbsp;ligamentum flavum dan facet joint.</em></p>\r\n\r\n<p><em>VL3-VL4 protruded disc disertai herniasi nukleus pulposus </em><em>ke arah posterior yang menyebabkan penekanan ke daerah saccus thecalis bagian central, disertai</em><em> hypertrophy &nbsp;ligamentum flavum dan facet joint.</em></p>\r\n\r\n<p><em>VL4-VL5 protruded disc disertai herniasi nukleus pulposus </em><em>ke arah posterior yang menyebabkan penekanan ke daerah saccus thecalis bagian central dan paracentral kanan, disertai</em><em> hypertrophy &nbsp;ligamentum flavum dan facet joint.</em></p>\r\n\r\n<p><em>VL5-VS1 protruded disc disertai herniasi nukleus pulposus </em><em>ke arah posterior yang menyebabkan penekanan ke daerah saccus thecalis bagian central , disertai</em><em> hypertrophy &nbsp;ligamentum flavum dan facet joint.</em></p>\r\n\r\n<p><em>Spondylosis vertebra lumbalis disertai scoliosis lumbalis dengan koveksitas ke kiri</em></p>\r\n\r\n<p><em>Perubahan intensitas signal discus intervertebralis L:2-3, L3-4, L4-5 dan L5-S1</em></p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (113, 'MRI Lumbosacral Abnormal Tjahyo Asikin (degeneratif + modic)', '<p>Dilakukan MRI lumbosacral potongan axial-sagital T1 WI, axial-soronal-sagital T1 WI dengan kontras, axial-sagital-coronal T2 WI, axial-sagital- T2-Fatsat . Scaning dilakukan tanpa dan dengan media kontras :</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kifosis ringan vertebra lumbalis dan laterolisthesis VL5 ke kiri</p>\r\n\r\n<p>Discus intervertebralis L4-5 dan L5-S1 tampak menyempit&nbsp; dengan foci hiperdens di dalamnya, discus intervertebrlis L1-2, L2-3 dan L3-4 sedikit menyempit dengan foci hiperdens di dalamnya.</p>\r\n\r\n<p>Tampak corpus VL2 bikonkav dengan intensitas signal inhomogen (hiperintens dan hipointens) pada T1WI, T2WI dan T2-Fatsat.</p>\r\n\r\n<p>Tampak protruded dicus intervertebralis L1-2, L2-3, L4-5 dan L5-S1 disertai dengan herniasi nukleus pulposus ke arah posterior yang menyebabkan penekanan ke daerah saccus thecalis bagian centralis dan penebalan ligamentum flavum</p>\r\n\r\n<p>Tampak protruded dicus intervertebralis L3-4 disertai dengan herniasi nukleus pulposus ke arah posterior yang menyebabkan penekanan ke daerah saccus thecalis bagian centralis dana paracerntralis sisi kanan dan penebalan ligamentum flavum</p>\r\n\r\n<p>Endplate VL4-5 tampak mengalami perubahan signal, yaitu hiperintens pada T1WI dan T2WI</p>\r\n\r\n<p>Canalis spinalis dan medulla spinalis yang masuk area scaning masih memberikan bentuk dan intensitas signal yang normal</p>\r\n\r\n<p>Ligamentum longitudinal anterior , ligamentum longitudinal posterior , ligamentum interspinosus , ligamentum supraspinosus dan tampak kelainan</p>\r\n\r\n<p>Jaringan lunak di daerah paravertebra kanan dan kiri masih dalam batas normal</p>\r\n\r\n<p>Tidak tampak adanya SOL / massa pada intramedula, intradural, ekstramedula maupun ekstradural</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesimpulan&nbsp;&nbsp;&nbsp;&nbsp; : </strong></p>\r\n\r\n<p>Kifosis ringan vertebra lumbalis dan laterolisthesis VL5 ke kiri</p>\r\n\r\n<p>VL2 bikonkav dengan intensitas signal inhomogen (hiperintens dan hipointens) pada T1WI, T2WI dan T2-Fatsat&agrave; sugestif suatu osteoporosis fraktur</p>\r\n\r\n<p>Protruded dicus intervertebralis L1-2, L2-3, L4-5 dan L5-S1 disertai dengan herniasi nukleus pulposus ke arah posterior yang menyebabkan penekanan ke daerah saccus thecalis bagian centralis dan penebalan ligamentum flavum</p>\r\n\r\n<p>Protruded dicus intervertebralis L3-4 disertai dengan herniasi nukleus pulposus ke arah posterior yang menyebabkan penekanan ke daerah saccus thecalis bagian centralis dana paracerntralis sisi kanan dan penebalan ligamentum flavum</p>\r\n\r\n<p>Endplate VL4-5 tampak mengalami perubahan signal, yaitu hiperintens pada T1WI dan T2WI &agrave;</p>\r\n\r\n<p>sugestif ec. degeneratif (Modic type II)</p>\r\n\r\n<p>Discus intervertebralis L4-5 dan L5-S1 tampak menyempit&nbsp; dengan foci hiperdens di dalamnya, discus intervertebrlis L1-2, L2-3 dan L3-4 sedikit menyempit dengan foci hiperdens di dalamnya &agrave; Pfirrmann grade III-IV</p>\r\n\r\n<p>&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (114, 'MRI lumbosakral', '<p>Dilakukan MRI Lumbosacral potongan axial-sagital T1WI dan axial-sagital-coronal T2WI. Scanning dilakukan tanpa media kontras.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Curve scoliosis vertebra lumbalis dengan alignment yang masih baik. Tidak tampak tanda-tanda listhesis maupun fraktur.</p>\r\n\r\n<p>&lsquo;Disc bulging&rsquo; tampak pada intervertebralis L5-S1 dengan peregangan ligamentum annulare yang masih tampak utuh.</p>\r\n\r\n<p>Discus intrvertebralis lainnya dan facies articularis vertebra lumbalis dalam batas normal.</p>\r\n\r\n<p>Corpus vertebra lumbal dan thorakal yang masuk area scanning masih memberikan bentuk dan intensitas signal yang normal.</p>\r\n\r\n<p>Canalis spinalis lainnya dan medulla spinalis yang masuk area scanning masih memberikan bentuk dan intensitas signal yang normal.</p>\r\n\r\n<p>Ligamentum longitudinalis anterior, ligamentum longitudinalis posterior, ligamentum flavum, ligamentum interspinosus dan ligamentum supraspinosus tidak tampak kelainan.</p>\r\n\r\n<p>Jaringan lunak di daerah paravertebral kanan dan kiri masih dalam batas normal.</p>\r\n\r\n<p>Tidak tampak adanya SOL/massa pada intramedula, intradural, ekstramedula maupun ekstradural.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan :</strong></p>\r\n\r\n<p><strong>- Disc bulging intervertebralis L5-S1 dengan peregangan ligamentum annulare yang masih tampak utuh. </strong></p>\r\n\r\n<p><strong>- Curve scoliosis vertebra lumbalis.</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (115, 'MRI meniscus lateraltear - ACL tears (lingga P)', '<p style=\"text-align:justify\"><em>Dilakukan MRI genu dekstra dengan potongan aksial-koronal-sagital PDWI Fat Sat, koronal-sagital T1WI, koronal-sagital T2WI, sagital PD dixon. Scanning dilakukan tanpa media kontras. Aksial-sagital-koronal T1WI Fat Sat post contrast</em></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<ul>\r\n	<li>Pada&nbsp; T2WI dan T2 Fat Sat, tampak lesi iso-hiperintens di&nbsp; meniscus&nbsp; lateral dan tipis di meniscus medial disertai koleksi cairan di sekitarnya</li>\r\n	<li>Bentuk&nbsp; ligamentum cruciatum anterior&nbsp; tampak irregular&nbsp; disertai intensitas signal yang meningkat pada T2WI dan T2 Fat Sat.</li>\r\n	<li>Pada T2WI dan T2 Fat Sat tampak bayangan hiperintens di suprapatellar recess dan prepatellar</li>\r\n	<li>Posisi dan&nbsp; bentuk tulang-tulang pembentuk cruris sinistra (distal femur, proksimal tibia, fibula, dan patella) masih tampak normal.</li>\r\n	<li>Ligamentum kolateral medial dan lateral masih memberikan bentuk, kontur, dan intensitas sinyal yang normal.</li>\r\n	<li>Ligamentum cruciatum posterior masih memberikan bentuk, kontur, dan intensitas sinyal yang normal.</li>\r\n	<li>Ligamentum quadriceps femoris dan ligamentum patellaris masih memberikan bentuk, kontur, dan intensitas sinyal yang normal.</li>\r\n	<li>Tidak tampak intra-articular <em>foreign body.</em></li>\r\n	<li style=\"text-align:justify\">Jaringan lunak di sekitar genu terlihat masih tampak normal.</li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><strong><u>Kesan: </u></strong></p>\r\n\r\n<ul>\r\n	<li><strong><em>Gambaran inkomplit tears di ligamentum cruciatum anterior, </em></strong><strong><em>inkomplit tears di meniscus lateral dan curiga inkomplit tears di meniscus medial disertai sedikit fluid collection di sekitarnya </em></strong></li>\r\n	<li><strong><em>Ttampak juga fluid collection di </em></strong><strong><em>daerah suprapatellar recess dan prepatellar </em></strong><strong><em>&agrave;</em></strong><strong><em> bursitis</em></strong></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (116, 'MRI Roswati lumbosacral', '<p style=\"text-align:justify\"><strong>Telah dilakukan pemeriksaan MRI lumbosacral potongan axial, sagittal T1-T2 tanpa kontras dan T2-Fat sat dengan hasil sebagai berikut :</strong></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">Mall alignment, tampak scoliosis thorakolumbalis dengan dengan konveksitas ke kanan. Tidak tampak spondilolithesis.</p>\r\n\r\n<p style=\"text-align:justify\">Tampak penyempitan discus intervertebralis VL5-VS1 disertai peningkatan intensiotas signal di T1 dan T2 pada superior endplate corpus VL 4,5,VS1 dan inferior endplate VL5 mengesankan proses degenerative (modic type II)</p>\r\n\r\n<p style=\"text-align:justify\">Tidak tampak fraktur maupun distruksi tulang</p>\r\n\r\n<p style=\"text-align:justify\">Tampak Lesi dengan multiple bony trabeculation disertai adjacent fatty changes pada corpus VL3</p>\r\n\r\n<p style=\"text-align:justify\">Tampak penurunan intensitas signal discus intervertebralis pada level VTh12-VL1, VL1-VL2, VL2-VL3, VL3-4, VL4-5 dan VL5-VS1</p>\r\n\r\n<p style=\"text-align:justify\">VL1-VL2&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Bulging disc ringan disertai hypertrophy ringan ligamentum flavum dan facet join yang tidak menyebabkan stenosis foramen neuralis</p>\r\n\r\n<p style=\"text-align:justify\">VL2-VL3&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Bulging disc ringan disertai hypertrophy ringan ligamentum flavum dan facet join yang tidak menyebabkan stenosis foramen neuralis</p>\r\n\r\n<p style=\"text-align:justify\">VL3-VL4&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Bulging disc disertai hypertrophy ligamentum flavum dan facet join yang menyebabkan mild stenosis foramen neuralis kiri</p>\r\n\r\n<p style=\"text-align:justify\">VL4-VL5&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Bulging disc disertai hypertrophy berat ligamentum flavum yang menyebabkan moderat stenosis foramen neuralis kanan kiri dan moderate stenosis canalis centralis dengan moderate kompresi pada exiting nerve root L4 dan transversing nerve root L5 kanan kiri</p>\r\n\r\n<p style=\"text-align:justify\">VL5-VS1&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; protusi disc di para central disertai hypertrophy ligamentum flavum dan facet joint yang menyebabkan moderate stenosis foramen neuralis kanan dan severe stenosis foramen neuralis kiri dengan moderate stenosis canalis centralis</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">Conus modularis setinggi VL2 medulla spinalis tampak baik dengan intesitas yang normal</p>\r\n\r\n<p style=\"text-align:justify\">Tidak tampak myelopathy, SOL maupun syringomyelia</p>\r\n\r\n<p style=\"text-align:justify\">Tidak tampak lesi di ekstradural maupun epidural</p>\r\n\r\n<p style=\"text-align:justify\">Jaringan lunak vertebral tampak baik. Tidak tampak infiltrate maupun SOL</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><em>Kesan :</em></p>\r\n\r\n<p style=\"text-align:justify\"><em>Degeneratif disc desease dengan :</em></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><em>Bulging disc ringan VL1-VL2 dan VL2-VL3 disertai hypertrophy ringan ligamentum flavum dan facet joint yang tidak menyebabkan stenosis foramen neuralis</em></li>\r\n	<li style=\"text-align:justify\"><em>Bulging Disc VL3-VL4 disertai hypertrophy ligamentum flavum dan facet joint yang menyebabkan mild stenosis foramen neuralis kiri</em></li>\r\n	<li style=\"text-align:justify\"><em>Bulging disc VL4-VL5 disertai hypertrophy berat ligamentum flavum yang menyebabkan moderate stenosis foramen neuralis kanan koro dan moderate stenosis canalis centralis dengan moderate kompresi pada exiting nerve root L4 dan transversing nerve root L5 kanan kiri</em></li>\r\n	<li style=\"text-align:justify\"><em>Protusi disc di paracentral VL5-VS1 disertai hypertrophy ligamentum flavum dan facet joint yang menyebabkan moderate stenosis foramen neuralis kanan dan severe stenosis foramen neuralis kiri dengan moderate stenosis canalis centralis</em></li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\"><em>Lesi dengan multiple bony trabeculation disertai adjacent fatty changes pada corpus VL3 mengesankan hemangioma corpus VL3</em></p>\r\n\r\n<p style=\"text-align:justify\"><em>Spondyloarthrosis disertai scoliosis lumbalis</em></p>\r\n\r\n<p>&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (117, 'CT Scan stroke normal catatan dr. Ros', '<p>MSCT<strong><em> </em></strong>scan kepala dengan potongan axial, <strong>tanpa </strong><strong>dan dengan </strong><strong>memakai</strong> kontras + angiografi</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Jaringan lunak extracalvaria dan calvaria masih memberikan bentuk dan densitas yang normal.</p>\r\n\r\n<p>Sulci&nbsp; corticalis, Fissura Sylvii , dan fissura interhemisfer tidak melebar.</p>\r\n\r\n<p>Tampak massa isoekhoik batas kurang tegas dengan bayang hiperdens multipel di dalamnya di daerhh frontotemporalis kiri. Pada pemberian kontras massa tampak enhancemant dengan bayangan hiperdens multipel didalamnya. HU bayangan hiperdens 173-175. Massa tampak mendorong garis tengah ke kanan, mendrong vnetrikel lateral dan III ke kanan dan menyempitkannya</p>\r\n\r\n<p>Pada CT-angiografi tampak massa di daerah frontotemporalis kiri</p>\r\n\r\n<p>Ruang subarachnoid&nbsp; dalam batas normal.</p>\r\n\r\n<p>Bentuk dan posisi ventrikel lateralis kanan dan kiri simetris. Ukuran ventrikel lateralis bilateral &nbsp;&amp; ventrikel III dan ventrikel IV tidak melebar .</p>\r\n\r\n<p>Sisterna ambiens&nbsp; dan sisterna basalis dalam batas normal.</p>\r\n\r\n<p style=\"text-align:left\">Daerah sela tursika dan juxtasella serta daerah &lsquo;cerebellopontine angle&rsquo; kanan dan kiri dalam batas normal.</p>\r\n\r\n<p style=\"text-align:left\">Mastoid air cell dalam batas normal.</p>\r\n\r\n<p style=\"text-align:left\">Sinus eithmoidalis, sphenoidalis, dan frontalis dalam batas normal.</p>\r\n\r\n<p style=\"text-align:left\">Bulbus oculi dan ruang retrobulber dalam batas normal.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><em>Kesimpulan :</em></strong></p>\r\n\r\n<ul>\r\n	<li><strong><em>Gambaran massa padat dengan multipel kaslifikasi di dalamnya di daerah frontotemporalis kiri </em></strong><strong><em>&agrave; suspek&nbsp; suatu oligodendrogliom&nbsp; DD/massa vascular</em></strong></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (118, 'mri Spine terbaru', '<p style=\"text-align:justify\"><strong>Telah dilakukan pemeriksaan MRI lumbosacral potongan axial, sagittal T1-T2 tanpa kontras dan MR-Myelogram dengan hasil sebagai berikut :</strong></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">Mall alignment, tampak scoliosis thorakolumbalis dengan dengan konveksitas ke kanan. Tidak tampak spondilolithesis.</p>\r\n\r\n<p style=\"text-align:justify\">Tampak penyempitan discus intervertebralis VL5-VS1 disertai peningkatan intensiotas signal di T1 dan T2 pada superior endplate corpus VL 4,5,VS1 dan inferior endplate VL5 mengesankan proses degenerative (modic type II)</p>\r\n\r\n<p style=\"text-align:justify\">Tidak tampak fraktur maupun distruksi tulang</p>\r\n\r\n<p style=\"text-align:justify\">Tampak Lesi dengan multiple bony trabeculation disertai adjacent fatty changes pada corpus VL3</p>\r\n\r\n<p style=\"text-align:justify\">Tampak penurunan intensitas signal discus intervertebralis pada level VTh12-VL1, VL1-VL2, VL2-VL3, VL3-4, VL4-5 dan VL5-VS1</p>\r\n\r\n<p style=\"text-align:justify\">VL1-VL2&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Bulging disc ringan disertai hypertrophy ringan ligamentum flavum dan facet join yang tidak menyebabkan stenosis foramen neuralis</p>\r\n\r\n<p style=\"text-align:justify\">VL2-VL3&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Bulging disc ringan disertai hypertrophy ringan ligamentum flavum dan facet join yang tidak menyebabkan stenosis foramen neuralis</p>\r\n\r\n<p style=\"text-align:justify\">VL3-VL4&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Bulging disc disertai hypertrophy ligamentum flavum dan facet join yang menyebabkan mild stenosis foramen neuralis kiri</p>\r\n\r\n<p style=\"text-align:justify\">VL4-VL5&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Bulging disc disertai hypertrophy berat ligamentum flavum yang menyebabkan moderat stenosis foramen neuralis kanan kiri dan moderate stenosis canalis centralis dengan moderate kompresi pada exiting nerve root L4 dan transversing nerve root L5 kanan kiri</p>\r\n\r\n<p style=\"text-align:justify\">VL5-VS1&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; protusi disc di para central disertai hypertrophy ligamentum flavum dan facet joint yang menyebabkan moderate stenosis foramen neuralis kanan dan severe stenosis foramen neuralis kiri dengan moderate stenosis canalis centralis</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">Conus modularis setinggi VL2 medulla spinalis tampak baik dengan intesitas yang normal</p>\r\n\r\n<p style=\"text-align:justify\">Tidak tampak myelopathy, SOL maupun syringomyelia</p>\r\n\r\n<p style=\"text-align:justify\">Tidak tampak lesi di ekstradural maupun epidural</p>\r\n\r\n<p style=\"text-align:justify\">Jaringan lunak vertebral tampak baik. Tidak tampak infiltrate maupun SOL</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><em>Kesan :</em></p>\r\n\r\n<p style=\"text-align:justify\"><em>Degeneratif disc desease dengan :</em></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><em>Bulging disc ringan VL1-VL2 dan VL2-VL3 disertai hypertrophy ringan ligamentum flavum dan facet joint yang tidak menyebabkan stenosis foramen neuralis</em></li>\r\n	<li style=\"text-align:justify\"><em>Bulging Disc VL3-VL4 disertai hypertrophy ligamentum flavum dan facet joint yang menyebabkan mild stenosis foramen neuralis kiri</em></li>\r\n	<li style=\"text-align:justify\"><em>Bulging disc VL4-VL5 disertai hypertrophy berat ligamentum flavum yang menyebabkan moderate stenosis foramen neuralis kanan koro dan moderate stenosis canalis centralis dengan moderate kompresi pada exiting nerve root L4 dan transversing nerve root L5 kanan kiri</em></li>\r\n	<li style=\"text-align:justify\"><em>Protusi disc di paracentral VL5-VS1 disertai hypertrophy ligamentum flavum dan facet joint yang menyebabkan moderate stenosis foramen neuralis kanan dan severe stenosis foramen neuralis kiri dengan moderate stenosis canalis centralis</em></li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\"><em>Lesi dengan multiple bony trabeculation disertai adjacent fatty changes pada corpus VL3 mengesankan hemangioma corpus VL3</em></p>\r\n\r\n<p style=\"text-align:justify\"><em>Spondyloarthrosis disertai scoliosis lumbalis</em></p>\r\n\r\n<p>&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (119, 'CT Scan stroke normal', '<p>MSCT<strong><em> </em></strong>scan kepala dengan potongan axial, <strong>tanpa memakai</strong> kontras.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Jaringan lunak extracalvaria dan calvaria masih memberikan bentuk dan densitas yang normal.</p>\r\n\r\n<p>Sulci&nbsp; corticalis, Fissura Sylvii , dan fissura interhemisfer tidak melebar.</p>\r\n\r\n<p>Parenkim cerebri dan cerebelli tidak tampak kelainan</p>\r\n\r\n<p>Ruang subarachnoid&nbsp; dalam batas normal.</p>\r\n\r\n<p>Bentuk dan posisi ventrikel lateralis kanan dan kiri simetris. Ukuran ventrikel lateralis bilateral, ventrikel III &amp; Ventrikel IV tidak melebar .</p>\r\n\r\n<p>Sisterna ambiens&nbsp; dan sisterna basalis dalam batas normal.</p>\r\n\r\n<p>Daerah sela tursika dan juxtasella serta daerah &lsquo;cerebellopontine angle&rsquo; kanan dan kiri dalam batas normal.</p>\r\n\r\n<p>Mastoid air cell dalam batas normal.</p>\r\n\r\n<p>Sinus paranasalis dalam batas normal.</p>\r\n\r\n<p>Bulbus oculi dan ruang retrobulber dalam batas normal.</p>\r\n\r\n<p>Tidak tampak pergeseran struktur garis tengah.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><em>Kesimpulan :</em></strong></p>\r\n\r\n<p><strong><em>CT-scan kepala tanpa kontras saat ini tidak tampak kelainan</em></strong><em> </em></p>\r\n\r\n<p>&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (120, 'MRI thorakolumbal - spondylitis', '<p>Dilakukan MRI thorakolumbal potongan axial-sagital T1WI, axial-sagital T2WI dan sagittal STIR. Scanning dilakukan tanpa media kontras.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">Curve kifosis vertebra thorakalis.</p>\r\n\r\n<p style=\"text-align:justify\">Kompresif fraktur dengan formasi gibus tampak pada corpus vertebra thorakal 6-7 disertai perubahan intensitas signal hipointens pada T1WI dan hiperintens inhomogen pada T2WI-STIR, tampak&nbsp; penekanan fragmen posterior ke daerah canalis spinalis bagian anterior dan radix spinalis bilateral terutama kiri yang menyebabkan stenosis canalis spinalis dan perubahan intensitas signal medulla spinalis setinggi daerah tersebut (penurunan signal pada T1WI dan meningkat pada T2WI&ndash;STIR). Intensitas signal discus intervertebralis thorakal 6-7 tampak relatif baik, tidak tampak menyempit.</p>\r\n\r\n<p style=\"text-align:justify\">Tampak penebalan jaringan lunak yang memberikan signal hipointens inhomogen pada T1WI dan meningkat pada T2WI &ndash;STIR di daerah paravertebra setinggi thorakal 6-7 kanan dan kiri.</p>\r\n\r\n<p style=\"text-align:justify\">Pembentukan osteofit minimal pada endplate corpus vertebra thorakolumbalis yang masuk area scanning.</p>\r\n\r\n<p style=\"text-align:justify\">Corpus vertebra thorakolumbal lainnya yang masuk area scanning masih memberikan bentuk dan intensitas signal yang normal.</p>\r\n\r\n<p style=\"text-align:justify\">Discus intervertebralis dan Facies articularis vertebra thorakolumbal yang masuk area scanning dalam batas normal.</p>\r\n\r\n<p style=\"text-align:justify\">Canalis spinalis lainnya yang masuk area scanning masih memberikan bentuk dan intensitas signal yang normal</p>\r\n\r\n<p style=\"text-align:justify\">Ligamentum longitudinal anterior, ligamentum longitudinal posterior, ligamentum interspinosus, ligamentum flavum dan ligamentum supraspinosus tidak tampak kelainan.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan :</strong></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><strong>Kompresif fraktur dengan formasi gibus pada corpus vertebra thorakal 6-7 disertai stenosis canalis spinalis dan tanda-tanda myelopati di daerah tersebut yang mengakibatkan curve kifosis vertebra thorakalis.</strong></li>\r\n	<li style=\"text-align:justify\"><strong>Curiga abses paravertebra setinggi paravertebral thorakal 6-7 kanan dan kiri</strong></li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\"><strong>-----------------------------------------</strong><strong>&agrave;</strong><strong> Sugestif Spondylitis TB curiga disertai paravertebral abses.</strong></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><strong>Spondylosis vertebra thorakolumbalis. </strong></li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><strong>Saran : MRI Thorakolumbal dengan kontras.</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (121, 'RD Ayu Kusumah (Lumbosacral)', '<p>Dilakukan MRI lumbosacral potongan axial-sagital T1 WI, axial-soronal-sagital T1 WI dengan kontras, axial-sagital-coronal T2 WI, axial-sagital- T2-Fatsat . Scaning dilakukan tanpa dan dengan media kontras :</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><em>Curve lurus dan malaligment grade II&nbsp; (50%) &nbsp;vertebra L4-5 ke posterior</em></strong></p>\r\n\r\n<p><strong><em>Facies superoanterior corpus VL5 tampak irreguler dengan bentuk corpus VL5 yang tampak memipih pada bagian anterior, bentuk dan intensitas signal corpus vertebra lumbosacral lainnya masih dalam batas normal</em></strong></p>\r\n\r\n<p><strong><em>Discus intervertebralis L4-5 dan L5-S1 tampak menunjukkan penurunan intensitas signal dan discus intervertebralis L4-5 tampak menyempit.</em></strong></p>\r\n\r\n<p><strong><em>Tampak disbulging discus intervertebralis&nbsp;&nbsp; L2-3 dan L3-4</em></strong></p>\r\n\r\n<p><strong><em>Protruded dicus intervertebralis L4-5 dan L5-S1 disertai dengan</em></strong> <strong><em>herniasi nukleus pulposus ke arah central disertai penekanan saccus thecalis bagian anterior dan mengakibatkan canal stenosis terutama setinggi VL4-5</em></strong></p>\r\n\r\n<p><strong><em>Ligamentum flavum setinggi L4-5 dan L5-S1 menebal.</em></strong></p>\r\n\r\n<p><strong><em>Tampak ostepfit di VL1-5</em></strong></p>\r\n\r\n<p>Canalis spinalis dan medulla spinalis yang masuk area scaning masih memberikan bentuk dan intensitas signal yang normal</p>\r\n\r\n<p>Ligamentum longitudinal anterior , ligamentum longitudinal posterior , ligamentum interspinosus , ligamentum supraspinosus dan tampak kelainan</p>\r\n\r\n<p>Jaringan lunak di daerah paravertebra kanan dan kiri masih dalam batas normal</p>\r\n\r\n<p>Tidak tampak adanya SOL / massa pada intramedula, intradural, ekstramedula maupun ekstradural</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesimpulan&nbsp;&nbsp; : &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong></p>\r\n\r\n<p><strong>Fraktur VL5 disertai spondilolisthesis VL4-5 ke posterior (grade II) disertai penyempitan discus intervertebralis L4-5 dan L5-S1</strong></p>\r\n\r\n<p><strong><em>Protruded dicus intervertebralis L4-5 dan L5-S1 disertai dengan</em></strong> <strong><em>herniasi nukleus pulposus ke arah central disertai penekanan saccus thecalis bagian anterior dan mengakibatkan canal stenosis terutama setinggi VL4-5</em></strong></p>\r\n\r\n<p><strong>Perubahan intensitas signal discus intervertebralis L4-5 (Pffirrman grade IV), perubahan internsitas signal discus intervertebralis L5-S1 disertai penyempitan discus (Pffirrman grade I-II)</strong></p>\r\n\r\n<p><strong>Penebalan ligamentum flavum setinggi VL4-5 dan VL5-S1 </strong></p>\r\n\r\n<p><strong>Spondilosis vertebra lumbalis</strong></p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (122, 'CT Scan stroke normal_20200806', '<p>CT<strong><em> </em></strong>scan kepala dengan potongan axial, <strong>tanpa memakai</strong> kontras.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Jaringan lunak extracalvaria dan calvaria masih memberikan bentuk dan densitas yang normal.</p>\r\n\r\n<p>Sulci&nbsp; corticalis, fissura Sylvii , dan fissura interhemisfer tidak melebar.</p>\r\n\r\n<p><strong><em>Tampak lesi hipodens batas tidak tegas &nbsp;di daerah temporalis kiri dan frontalis kiri </em></strong></p>\r\n\r\n<p>Ruang subarachnoid&nbsp; dalam batas normal.</p>\r\n\r\n<p>Bentuk dan posisi ventrikel lateralis kanan dan kiri simetris. Ukuran ventrikel lateralis bilateral, ventrikel III &amp; ventrikel IV tidak melebar .</p>\r\n\r\n<p>Sisterna ambiens&nbsp; dan sisterna basalis dalam batas normal.</p>\r\n\r\n<p style=\"text-align:left\">Daerah sela tursika dan juxtasella serta daerah &lsquo;cerebellopontine angle&rsquo; kanan dan kiri dalam batas normal.</p>\r\n\r\n<p style=\"text-align:left\">Mastoid air cell dalam batas normal</p>\r\n\r\n<p style=\"text-align:left\">Sinus paranasal yang terscanning dalam batas normal.</p>\r\n\r\n<p style=\"text-align:left\">Bulbus oculi dan ruang retrobulber dalam batas normal.</p>\r\n\r\n<p>Tidak tampak pergeseran struktur garis tengah.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><em>Kesimpulan :</em></strong></p>\r\n\r\n<p><strong><em>Gambaran infark cerebri akut di daerah </em></strong><strong><em>temporalis kiri dan frontalis kiri </em></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (123, 'Shafa hasna (mri kepala - 2 jun, glioma)', '<p style=\"text-align:justify\">Dilakukan MRI kepala tanpa dan dengan kontras dengan potongan axial-sagital T1WI, aksial-sagital-coronal T2WI, axial T2-FLAIR, DWI-ADC, FFE serta axial-sagital-coronal T1WI dengan kontras dan MR spectroscopy single voxel.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">Jaringan lunak ekstracalvaria dan calvaria masih dalam batas normal.</p>\r\n\r\n<p style=\"text-align:justify\">Tampak mesencephalon kiri lebih lebar dibanding kanan, dengan intensitas signal hipointens inhomogen pada T1WI, hiperintens inhomogen pada T2WI dan T2 Flair. Tidak tampak gambaran restricted area pada DWI ADC, dan tidak tampak blooming artifact pada FFE. Pada pemerikaan MR spectroscopy, tampak peningkatan puncak choline. Post contrast scanning tidak tampak lesi yang memberikan enhancement kuat. Gambaran tersebut juga terlihat pada sebagian mesencephalon kanan, thalamus kiri dan pons kiri. Tidak tampak lesi solid maupun kistik yang berbatas tegas di daerah tersebut.</p>\r\n\r\n<p style=\"text-align:justify\">Multiple lesi, batas tegas, tepi irregular, berukuran terbesar lk 0,83 x 1,48 x 0,63 cm (ukuran relatif sama dibandingkan MRI sebelumnya) di daerah genu dan body corpus callosum, yang memberikan signal hipointens pada T1WI dan T2-Flair, hyperintens pada T2WI, lesi tersebut tidak memberikan restricted area pada DWI-ADC. Tidak tampak blooming artifact pada FFE. Post contrast scanning tampak enhancement minimal pada tepinya.</p>\r\n\r\n<p style=\"text-align:justify\">Sulci dan gyri &nbsp;corticalis, fissura Sylvii bilateral dan fissura interhemisfer masih memberikan bentuk dan intensitas sinyal yang normal.</p>\r\n\r\n<p style=\"text-align:justify\">Bentuk dan posisi ventrikel lateralis bilateral simetris. Ukuran ventrikel lateralis bilateral, ventrikel III dan ventrikel IV masih dalam batas normal.</p>\r\n\r\n<p style=\"text-align:justify\">Ruang subaraknoid masih memberikan bentuk dan intensitas sinyal yang normal.</p>\r\n\r\n<p style=\"text-align:justify\">Sisterna ambiens dan basalis masih dalam batas normal.</p>\r\n\r\n<p style=\"text-align:justify\">Parenkim cerebri lainnya dan cerebellum tidak memberikan lesi patologis.</p>\r\n\r\n<p style=\"text-align:justify\">Ganglia basalis dan capsula interna tampak normal.</p>\r\n\r\n<p style=\"text-align:justify\">Tidak tampak adanya &rsquo;signal void&rsquo; yang merupakan tanda malformasi vaskuler.</p>\r\n\r\n<p style=\"text-align:justify\">Daerah sela tursika dan jukstasella masih tampak normal.</p>\r\n\r\n<p style=\"text-align:justify\">Daerah cerebellopontine angle bilateral masih memberikan bentuk dan intensitas sinyal yang normal.</p>\r\n\r\n<p style=\"text-align:justify\">Nervus akustikus bilateral masih memberikan bentuk dan intensitas sinyal yang normal.</p>\r\n\r\n<p style=\"text-align:justify\">Mastoid air cell bilateral masih tampak normal.</p>\r\n\r\n<p style=\"text-align:justify\">Sinus ethmoidalis, sphenoidalis, maksilaris, dan frontalis bilateral masih dalam batas normal.</p>\r\n\r\n<p style=\"text-align:justify\">Bulbus oculi, ruang retrobulber dan nervus optikus bilateral masih dalam batas normal.</p>\r\n\r\n<p style=\"text-align:justify\">Tidak tampak pergeseran struktur garis tengah.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><strong><u>Kesan:</u></strong></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><strong>Pelebaran mesencephalon kiri disertai gambaran edema pada mesencephalon bilateral terutama kiri, thalamus kiri dan pons kiri sugestif e.c mesencephalic glioma DD/ thalamus glioma (curiga malignancy?).</strong></li>\r\n	<li><strong>Lesi kistik multiple berukuran terbesar </strong><strong>lk </strong><strong>0,83 x 1,48 x 0,63 </strong><strong>cm di </strong><strong>daerah genu dan body corpus callosum</strong><strong> </strong><strong>&agrave;</strong><strong> curiga post infeksi? (bagaimana lab?), dengan ukuran relatif sama dibandingkan MRI sebelumnya. </strong></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (124, 'Shafa hasna (mri kepala - SOL)', '<p style=\"text-align:justify\">Dilakukan MRI kepala tanpa dan dengan kontras dengan potongan axial-sagital-coronal T1-WI, potongan aksial-sagital-coronal T2-WI, axial T2- FLAIR, DWI-ADC serta axial-sagital-coronal T1WI dengan kontras.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">Jaringan lunak ekstracalvaria dan calvaria masih dalam batas normal.</p>\r\n\r\n<p style=\"text-align:justify\">Multiple lesi, batas tegas, tepi irregular, berukuran terbesar lk 0,72 x 1,45 x 0,67 cm (tampak relatif mengecil dibandingkan MRI sebelumnya) di daerah genu dan body corpus callosum, yang memberikan signal hipointens pada T1WI dan T2-Flair, hyperintens pada T2WI, lesi tersebut tidak memberikan&nbsp; &rdquo; restricted area &rdquo; pada DWI-ADC. Post contrast scanning tampak enhancement minimal pada tepinya. Tidak tampak lagi lesi yang memberikan signal hipointens pada TIWI, hiperintens pada T2WI di sekitar lesi dan substansia alba periventriculer lateralis kanan.</p>\r\n\r\n<p style=\"text-align:justify\">Tampak lagi lesi yang memberikan signal hipointens pada TIWI, hiperintens pada T2WI di thalamus kiri. Lesi tersebut tampak memberikan&nbsp; gambaran T2 shine through pada DWI-ADC</p>\r\n\r\n<p style=\"text-align:justify\">Sulci dan gyri &nbsp;corticalis, fissura Sylvii bilateral dan fissura interhemisfer masih memberikan bentuk dan intensitas sinyal yang normal.</p>\r\n\r\n<p style=\"text-align:justify\">Bentuk dan posisi ventrikel lateralis bilateral simetris. Ukuran ventrikel lateralis bilateral, ventrikel III dan ventrikel IV masih dalam batas normal.</p>\r\n\r\n<p style=\"text-align:justify\">Ruang subaraknoid masih memberikan bentuk dan intensitas sinyal yang normal.</p>\r\n\r\n<p style=\"text-align:justify\">Sisterna ambiens dan basalis masih dalam batas normal.</p>\r\n\r\n<p style=\"text-align:justify\">Parenkim cerebri lainnya, cerebellum dan batang otak tidak memberikan lesi patologis.</p>\r\n\r\n<p style=\"text-align:justify\">Ganglia basalis, kapsula interna, thalamus kanan tampak normal.</p>\r\n\r\n<p style=\"text-align:justify\">Daerah hippocampus dan lobus temporalis masih tampak normal.</p>\r\n\r\n<p style=\"text-align:justify\">Tidak tampak adanya &rsquo;signal void&rsquo; yang merupakan tanda malformasi vaskuler.</p>\r\n\r\n<p style=\"text-align:justify\">Daerah sela tursika dan jukstasella masih tampak normal.</p>\r\n\r\n<p style=\"text-align:justify\">Daerah cerebellopontine angle bilateral masih memberikan bentuk dan intensitas sinyal yang normal.</p>\r\n\r\n<p style=\"text-align:justify\">Nervus akustikus bilateral masih memberikan bentuk dan intensitas sinyal yang normal.</p>\r\n\r\n<p style=\"text-align:justify\">Mastoid air cell bilateral masih tampak normal.</p>\r\n\r\n<p style=\"text-align:justify\">Sinus ethmoidalis, sphenoidalis, maksilaris, dan frontalis bilateral masih dalam batas normal.</p>\r\n\r\n<p style=\"text-align:justify\">Bulbus oculi, ruang retrobulber dan nervus optikus bilateral masih dalam batas normal.</p>\r\n\r\n<p style=\"text-align:justify\">Tidak tampak pergeseran struktur garis tengah.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><strong><u>Kesan:</u></strong></p>\r\n\r\n<ul>\r\n	<li><strong>Gambaran edema di daerah thalamus kiri suspek e.c post tuberkuloma?</strong></li>\r\n	<li><strong>Lesi kistik multiple berukuran terbesar </strong><strong>lk </strong><strong>0,72 x 1,45 x 0,67 </strong><strong>cm di </strong><strong>daerah genu dan body corpus callosum</strong><strong> </strong><strong>tanpa edema vasogenik di sekitarnya </strong><strong>&agrave;</strong><strong> SOL (suspek tuberkuloma), tampak relatif mengecil di bandingkan MRI sebelumnya. </strong></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Saran : Follow up MRI </strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (125, 'CT Scan stroke', '<p>MSCT<strong><em> </em></strong>scan kepala dengan potongan axial, <strong>tanpa memakai</strong> kontras.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Jaringan lunak extracalvaria dan calvaria masih memberikan bentuk dan densitas yang normal.</p>\r\n\r\n<p>Sulci&nbsp; corticalis, Fissura Sylvii , dan fissura interhemisfer tidak melebar.</p>\r\n\r\n<p>Parenkim cerebri dan cerebelli tidak tampak kelainan</p>\r\n\r\n<p>Ruang subarachnoid&nbsp; dalam batas normal.</p>\r\n\r\n<p>Bentuk dan posisi ventrikel lateralis kanan dan kiri simetris. Ukuran ventrikel lateralis bilateral, ventrikel III &amp; Ventrikel IV tidak melebar .</p>\r\n\r\n<p>Sisterna ambiens&nbsp; dan sisterna basalis dalam batas normal.</p>\r\n\r\n<p style=\"text-align:left\">Daerah sela tursika dan juxtasella serta daerah &lsquo;cerebellopontine angle&rsquo; kanan dan kiri dalam batas normal.</p>\r\n\r\n<p style=\"text-align:left\">Mastoid air cell dalam batas normal.</p>\r\n\r\n<p style=\"text-align:left\">Sinus eithmoidalis, sphenoidalis, dan frontalis dalam batas normal.</p>\r\n\r\n<p style=\"text-align:left\">Bulbus oculi dan ruang retrobulber dalam batas normal.</p>\r\n\r\n<p>Tidak tampak pergeseran struktur garis tengah.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><em>Kesimpulan :</em></strong></p>\r\n\r\n<ul>\r\n	<li><strong><em>CT-scan kepala tanpa kontras saat ini tidak tampak kelainan</em></strong></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (126, 'Shoulder (lipoma)', '<p style=\"text-align:justify\"><em>MRI daerah shoulder kanan potongan aksial T1W1 dan T2FS, coronal potongan T1W1, sagital T2FS.</em></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><em>Tampak lesi hiperintens pada T1W1 dan hipointens pada T2FS di daerah cutis subcutis posterior shoulder kanan, lesi tersebut tampak mendorong musculus di sekitarnya</em></p>\r\n\r\n<p style=\"text-align:justify\"><em>Tidak tampak destruksi tulang di daerah shoulder kanan</em></p>\r\n\r\n<p style=\"text-align:justify\"><em>Tampak perubahan intensitas signal yang memberikan signal hipointens inhomogen pada T1WI,&nbsp; hiperintens inhomogen pada </em><em>T2FS</em><em> di daerah tendon m. supraspinatus,&nbsp; tendon m. infraspinatus, tendon m. subscapularis dextra disertai koleksi cairan di sekitarnya.</em></p>\r\n\r\n<p style=\"text-align:justify\">Ligamentum glenohumeral superior, ligamentum glenohumeral medius, ligamentum glenohumeral inferior masih memberikan bentuk dan intensitas signal yang normal</p>\r\n\r\n<p style=\"text-align:justify\">Ligamentum coracoacromial, acromioclavicular, coracoclavicular, coracohumeral masih memberikan bentuk dan intensitas signal yang normal</p>\r\n\r\n<p style=\"text-align:justify\">Glenoid labrum anterior dan posterior dalam batas normal</p>\r\n\r\n<p style=\"text-align:justify\">Struktur musculi dan tendon m. long biceps brachii, m. deltoid, dan m. teres minor dextra saat ini masih memberikan bentuk dan intensitas yang normal.</p>\r\n\r\n<p style=\"text-align:justify\">Intensitas signal bone marrow masih memberikan intensitas signal normal.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><strong><u>KESIMPULAN :&nbsp; </u></strong></p>\r\n\r\n<p style=\"text-align:justify\"><strong>-</strong> <strong><em>Massa soft tissue di daerah posterior shoulder kanan sugestif suatu lipoma.</em></strong></p>\r\n\r\n<p style=\"text-align:justify\"><strong><em>- Tidak tampak destruksi tulang disekitarnya.</em></strong></p>\r\n\r\n<p style=\"text-align:justify\"><strong><em>- Partial tear tendon m. supraspinatus, tendon m. infraspinatus, tendon m. subscapularis dextra disertai koleksi cairan di sekitarnya.</em></strong></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (127, 'tendinosis supraspinatus', '<p style=\"text-align:justify\">Dilakukan MRI scanning daerah shoulder joint sinistra potongan sagital T1WI, axial-coronal-sagital T2WI dan axial sagittal coronal T2FS. Scanning tanpa memakai kontras media.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">Glenoid labrum sinistra tampak masih memberikan intensitas signal yang masih normal.</p>\r\n\r\n<p style=\"text-align:justify\">Tulang humerus sinistra bentuk, struktur dan intensitas signal bone marow, tulang-tulang sendi bahu sinistra masih dalam batas normal.</p>\r\n\r\n<p style=\"text-align:justify\">Tampak perubahan intensitas signal yang memberikan signal hipointens pada T1WI,&nbsp; hiperintens pada T2WI dan T2FS di daerah tendon m.supraspinatus sinistra, disertai koleksi cairan di sekitarnya.</p>\r\n\r\n<p style=\"text-align:justify\">Struktur musculi dan tendon m. Deltoid, m. Infraspinatus, m. Teres mayor, m. Teres minor, dan m.Subscapularis sinistra saat ini masih memberikan bentuk dan intensitas yang normal.</p>\r\n\r\n<p style=\"text-align:justify\">Tampak koleksi cairan di daerah bursa subscapularis, subcoracoid dan subdeltoid shoulder sinistra.</p>\r\n\r\n<p style=\"text-align:justify\">Ligamentum coracoacromiale, acromioclaviculare, coracoclavicular, coracohumerale, glenohumeral superior, medial dan inferior sinistra masih memberikan bentuk dan intensitas signal yang normal.</p>\r\n\r\n<p style=\"text-align:justify\">Lemak/subcutis : Masih memberikan intensitas signal normal.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><strong>Kesimpulan :&nbsp; </strong></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><strong>Perubahan intensitas signal di daerah tendon m. Supraspinatus sinistra disertai koleksi cairan di sekitarnya ec. Tendinosis supraspinatus sinistra.</strong></li>\r\n	<li style=\"text-align:justify\"><strong>Bursitis subscapularis, subcoracoid dan subdeltoid sinistra. </strong></li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (128, 'CT Scan stroke2', '<p>MSCT<strong><em> </em></strong>scan kepala dengan potongan axial, <strong>tanpa memakai</strong> kontras.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Jaringan lunak extracalvaria dan calvaria masih memberikan bentuk dan densitas yang normal.</p>\r\n\r\n<p>Sulci&nbsp; corticalis, Fissura Sylvii , dan fissura interhemisfer tidak melebar.</p>\r\n\r\n<p>Samar terlihat lesi hipodens, kecil-kecil di capsula interna kanan dan kiri.</p>\r\n\r\n<p>Ruang subarachnoid&nbsp; dalam batas normal.</p>\r\n\r\n<p>Bentuk dan posisi ventrikel lateralis kanan dan kiri simetris. Ukuran ventrikel lateralis bilateral, ventrikel III agal lebar &amp; Ventrikel IV tidak melebar .</p>\r\n\r\n<p>Sisterna ambiens&nbsp; dan sisterna basalis dalam batas normal.</p>\r\n\r\n<p style=\"text-align:left\">Daerah sela tursika dan juxtasella serta daerah &lsquo;cerebellopontine angle&rsquo; kanan dan kiri dalam batas normal.</p>\r\n\r\n<p style=\"text-align:left\">Mastoid air cell dalam batas normal.</p>\r\n\r\n<p style=\"text-align:left\">Sinus eithmoidalis, sphenoidalis, dan frontalis dalam batas normal.</p>\r\n\r\n<p style=\"text-align:left\">Bulbus oculi dan ruang retrobulber dalam batas normal.</p>\r\n\r\n<p>Tidak tampak pergeseran struktur garis tengah.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><em>Kesimpulan : Sugestif infark lakuner di capsula interna </em><em>kanan dan kiri.</em></strong></p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (129, 'Thoraks kardiomegali', '<p><strong>THORAX </strong></p>\r\n\r\n<p>Soft tissue dan skeletal tampak normal.</p>\r\n\r\n<p>Trachea posisi midline.</p>\r\n\r\n<p>Cor membesar ke lateral kiri dengan apex tertanam pada diafragma, pinggang jantung normal.</p>\r\n\r\n<p>Sinuses dan&nbsp; diafragma normal.</p>\r\n\r\n<p>Pulmo :</p>\r\n\r\n<p>Hilli normal.</p>\r\n\r\n<p>Corakan bronkhovaskuler normal.</p>\r\n\r\n<p>Tidak tampak infiltrat.</p>\r\n\r\n<p>Kranialisasi (-)</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan : </strong></p>\r\n\r\n<p><strong>Pulmo saat ini tidak tampak kelainan. </strong></p>\r\n\r\n<p><strong>Kardiomegali tanpa bendungan paru</strong></p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (130, 'Thoraks normal', '<p><strong>THORAX </strong></p>\r\n\r\n<p>Soft tissue dan skeletal tampak normal.</p>\r\n\r\n<p>Trachea posisi midline.</p>\r\n\r\n<p>Cor tidak membesar.</p>\r\n\r\n<p>Sinuses dan diafragma normal.</p>\r\n\r\n<p>Pulmo :</p>\r\n\r\n<p>Hilli normal.</p>\r\n\r\n<p>Corakan bronkhovaskuler normal.</p>\r\n\r\n<p>Tidak tampak infiltrat.</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n\r\n<p><strong>Kesan : </strong></p>\r\n\r\n<p><strong>Pulmo saat ini tidak tampak kelainan. </strong></p>\r\n\r\n<p><strong>Tidak tampak kardiomegali. </strong></p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (131, 'CT Scan Thorax Baru', '<p>Dilakukan CT scan thoraks potongan axial dengan ketebalan 8 mm, dilakukan rekontruksi potongan coronal dan sagittal dengan ketebalan 3 mm. Scanning <strong>tanpa dan dengan</strong> memakai kontras intravena</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n\r\n<p><strong><em><u>Scanning window mediastinum</u></em></strong></p>\r\n\r\n<ul>\r\n	<li>A.karotis, a.subclavia kiri, truncus brachiocephalica , v.brachiocephalica masih tampak normal</li>\r\n	<li>Vena cava superior, aorta ascenden, arcus aorta dan aorta descenden masih dalam batas normal</li>\r\n	<li>Trakhea masih di tengah</li>\r\n	<li>Cabang utama bronchus kanan dan kiri terbuka. Cabang dari bronchus utama kanan yang menuju ke lobus superior dan lobus medius tampak agak menyempit</li>\r\n	<li>Tampak bayangan hipodens berupa nodul, batas cukup tegas di peribronkhial kanan</li>\r\n	<li>Tidak tampak pembesaran jantung</li>\r\n	<li>Tampak bayangan hipodens di hemithoraks kanan tengah-bawah sisi lateral</li>\r\n	<li>Pada post kontras, tampak bayangan isodens, batas cukup tegas di lobus medius paru kanan dengan air-bronkhogram (+)</li>\r\n	<li>Tampak bayangan hipodens di soft tissue dinding thoraks kanan medial yang tampaknya berhubungan dengan lesi di hemithoraks kanan</li>\r\n	<li>Tampak nodul hupoekhoik multipel di daerah axila kanan-kiri yang tervisualisasi</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><em><u>Scanning window paru</u></em></strong></p>\r\n\r\n<ul>\r\n	<li>Tampak bayangan hiperdens di hemithoraks kanan tengah-bawah sisi lateral dan hemithoraks kanan bawah</li>\r\n	<li>Tampak nodul hiperdens di peribronkhial kanan</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><u>Window tulang : </u></strong></p>\r\n\r\n<ul>\r\n	<li>Tampak sternum seinggi medial dinidng thoraks dengan beberapa costa kanan aspect anterior sisi medial tampak irreguler</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><em><u>Kesan :</u></em></strong></p>\r\n\r\n<ul>\r\n	<li><strong><em>Efusi pleura kanan curiga suatu empyema disertai bayangan hipodens di soft tissue dinding thoraks kanan medial dengan destruksi sebagian sternum&nbsp; dan berebapa costa kanan aspect anterior </em></strong><strong><em>&agrave;</em></strong><strong><em> &nbsp;curiga ec. infiltrasi dari lesi di hemithoraks kanan</em></strong></li>\r\n	<li><strong><em>Bayangan isodens-hiperdens, batas cukup tegas di lobus medius paru kanan dengan air-bronkhogram (+) </em></strong><strong><em>&agrave;</em></strong><strong><em> ec, atelatasis DD/pneumonia (lab?)</em></strong></li>\r\n	<li><strong><em>Tampak pembesaran KGB di peribronkhial kanan dan axila kanan-kiri</em></strong></li>\r\n	<li><strong><em>Tidak tampak kardiomegali</em></strong></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (132, 'Tita Maryati (Lumbosacral)', '<p>Dilakukan MRI lumbosacral potongan axial-sagital T1 WI, axial-soronal-sagital T1 WI dengan kontras, axial-sagital-coronal T2 WI, axial-sagital- T2-Fatsat . Scaning dilakukan tanpa dan dengan media kontras :</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><em>Curve lurus dan malaligment grade I (25%) vertebra L4-5 ke posterior</em></strong></p>\r\n\r\n<p><strong><em>Bentuk dan intensitas signal corpus vertebra lumbosacral masih dalam batas normal</em></strong></p>\r\n\r\n<p><strong><em>Discus intervertebralis L4-5 dan L5-S1 tampak menunjukkan sedikit penurunan intensitas signal dan discus intervertebralis L4-5 tampak menyempit.</em></strong></p>\r\n\r\n<p><strong><em>Tampak disbulging discus intervertebralis&nbsp;&nbsp; L2-3 dan L3-4</em></strong></p>\r\n\r\n<p><strong><em>Protruded dicus intervertebralis L4-5 dan L5-S1 disertai dengan</em></strong> <strong><em>herniasi nukleus pulposus ke arah central disertai penekanan saccus thecalis bagian anterior.</em></strong></p>\r\n\r\n<p>Canalis spinalis dan medulla spinalis yang masuk area scaning masih memberikan bentuk dan intensitas signal yang normal</p>\r\n\r\n<p><strong><em>Ligamentum flavum setinggi L4-5 dan L5-S1 menebal.</em></strong></p>\r\n\r\n<p>Ligamentum longitudinal anterior , ligamentum longitudinal posterior , ligamentum interspinosus , ligamentum supraspinosus dan tampak kelainan</p>\r\n\r\n<p>Jaringan lunak di daerah paravertebra kanan dan kiri masih dalam batas normal</p>\r\n\r\n<p>Tidak tampak adanya SOL / massa pada intramedula, intradural, ekstramedula maupun ekstradural</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesimpulan&nbsp;&nbsp; : &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong></p>\r\n\r\n<p><strong>Spondilolisthesis VL4-5 ke posterior (grade I) disertai penyempitan discus intervertebralis L4-5 dan L5-S1</strong></p>\r\n\r\n<p><strong>Protruded dicus intervertebralis L4-5 dan L5-S1 disertai dengan herniasi nukleus pulposus ke arah central disertai penekanan saccus thecalis bagian anterior.</strong></p>\r\n\r\n<p><strong>Disbulging discus intervertebralis&nbsp; L2-3 dan &nbsp;L3-4</strong></p>\r\n\r\n<p><strong>Perubahan intensitas signal discus intervertebralis L5-S1 (Pffirrman grade I-II), perubahan internsitas signal discus intervertebralis L4-5 disertai penyempitan discus (Pffirrman grade II-III)</strong></p>\r\n\r\n<p><strong>Penebalan ligamentum flavum setinggi VL4-5 dan VL5-S1 </strong></p>\r\n\r\n<p>&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (133, 'CT Scan Thorax', '<p>MSCT<strong><em> </em></strong>scan thoraks dengan potongan axial, <strong>tanpa</strong><strong> dan dengan</strong><strong> memakai</strong> kontras + window paru :</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Hepar:&nbsp;&nbsp;&nbsp; tidak&nbsp; membesar , permukaan&nbsp; licin,&nbsp; parenkim&nbsp;&nbsp; homogen, post kontras tidak mencurigakan &nbsp;metastasis /HCC. Intra dan extrahepatic bile ducts&nbsp; tidak melebar. Corakan vaskuler porta baik . Kandung empedu&nbsp; tidak&nbsp; tampak&nbsp; batuopak didalamnya.</p>\r\n\r\n<p>Lien : normal, parenkim baik. V.lienalis tidak melebar.</p>\r\n\r\n<p>Pancreas: normal, parenkim &nbsp;tidak mencurigakan s.o.l. D.pancreaticus tidak melebar.</p>\r\n\r\n<p>Ginjal: bentuk kedua ginjal baik,&nbsp; parenkim tidak mencurigakan renal cyst maupun solid mass. Fungsi excresi baik, kedua system pelviocalyces dan ureter tidak melebar.&nbsp; Tidak tampak batu atau bendungan kedua tr.urinarius.&nbsp; Perirenal space baik. &nbsp;Kel.adrenal normal .</p>\r\n\r\n<p>Kelenjar paraaortal: tidak membesar,&nbsp; tidak tampak ascites.</p>\r\n\r\n<p>Lambung &ndash;usus : Lambung tidak tampak kelainan, usus dalam batas normal, tidak tampak massa diantara usus, tidak tampak ileus. Fecal mass penuh sepanjang colon sampai rectum,suspect constipasi .</p>\r\n\r\n<p>Pelvis: Kel.prostat&nbsp; membesar, memberikan indentasi ke buli buli, capsul masih licin intact, tidak tampak invasi tumor ke buli atau vesica seminalis. Periprostatic fat bersih. Kgb regional dirongga pelvis tidak membesar. Buli menunjukan difus mukosa menebal ringan suspect khronik cystitis. &nbsp;&nbsp;</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n\r\n<p><strong>KESAN:</strong>&nbsp; Prostat hypertrofi dengan khronik cystitis, tidak tampak tumor invasi ke jaringan sekitar,capsul prostat intact, vesica seminalis baik. Kgb regional dan paraaortal tidak membesar. Tidak tampak bendungan tr.urinarius, kedua ginjal baik.</p>\r\n\r\n<p>Tidak tampak ascites. Fecal mass banyak sepanjang colorectal, constipasi ?</p>\r\n\r\n<p>Organ organ abdomen&nbsp;&nbsp; lainnya &nbsp;baik.&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (134, 'CT scan tumor kepala', '<p>MSCT scan kepala dengan potongan axial tanpa memakai kontras.</p>\r\n\r\n<p>Jaringan lunak extracalvaria dan calvaria masih memberikan bentuk dan densitas yang normal.<br />\r\nSulci&nbsp; corticalis, Fissura Sylvii , dan fissura interhemisfer tidak melebar.<br />\r\nTampak lesi iso-hiperdens, batas tegas yang tervisualisasi mulai&nbsp; setinggi periventrukuler lateral kiri daerah corona radiata. Massa tampak meluas sampai ke thalamus kiri dan cerebellum kiri,&nbsp; serta menekan ventrikel 4 dan&nbsp; mendesak venterikel 3 ke kanan. Tampak bayangan hipodens di sekitar massa.<br />\r\nRuang subarachnoid&nbsp; dalam batas normal.<br />\r\nVentrikel 3 dan ventrikel lateral kanan-kiri melebar.<br />\r\nTampak pergeseran struktur garis tengah ke kanan</p>\r\n\r\n<p>Kesimpulan :<br />\r\nGambaran massa intracerebri yang tervisualisasi mulai&nbsp; setinggi nperiventrukuler lateral kiri daerah corona radiata. Mass tampak meluas sampai ke thalamus kiri dan cerebellum kiri&nbsp; curiga suatu glioma low grade disertai edema di sekitarnya dan hydrocephalus non-komunikans</p>\r\n\r\n<p>Saran : CT-scan dengan kontras</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (135, 'CT Scan Window Thorax', '<p>Dilakukan MSCT<strong><em> </em></strong>scan thoraks dengan potongan axial dengan slice thicjnes&hellip;.mm dan slice interval &hellip;mm. Dilakukan rekonstruksi potongan koronal dan sagittal dengan slice thicknes&hellip;mm dan slice interval&hellip;.&nbsp; Scaning&nbsp; <strong>tanpa</strong><strong> dan dengan</strong><strong> </strong>&nbsp;memakai kontras intravena</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><em><u>Scanning window mediastinum</u></em></strong></p>\r\n\r\n<ul>\r\n	<li>Trunkus brakiosefalika beserta cabangnya ( arteri karotis komunis kanan dan arteri subklavia kanan , arteri karotis komunis kiri, dan arteri subklavia kiri masih tampak normal</li>\r\n	<li>Aorta ascenden , arcus aorta, dan aorta descenden serta arteri dan vena pulmonalis masih tampak normal</li>\r\n	<li>Vena brakosefalika kanan/kiri/bilateral masih tampak normal</li>\r\n	<li>Vena cava superior dan vena cava inferior masih tampak normal</li>\r\n	<li>Tidak tampak pelebaran pembuluh darah kolateral pada dinding torak dan abdomen, vena azigos maupun vena hemiazigos</li>\r\n	<li>Tidak tampak pembesaran jantung</li>\r\n	<li>Esofagus tampak normal</li>\r\n	<li>Trakhea masih di tengah</li>\r\n	<li>Cabang utama bronki kanan dan kiri terbuka.( Jika ada massa apakah jaraknya &gt;/&lt; 2 cm dari karina atau mengobliterasi karina)</li>\r\n	<li>Bronki lobaris paru kanan dan kiri terbuka</li>\r\n	<li>Tidak tampak bayangan massa isodens/hipodens/hiperdens di mediastinum</li>\r\n	<li>Tidak tampak bayangan hipodens dengan ring enhancement</li>\r\n	<li>Diafragma dalam batas normal</li>\r\n	<li>Tidak tampak kelainan pada pleura</li>\r\n	<li>Tidak tampak kelainan pada jaringan lunak dan skeletal dinding torak maupun pada vertebra torakalis yang terscaning</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><em><u>Scanning window paru</u></em></strong></p>\r\n\r\n<ul>\r\n	<li>Tidak tampak infiltrat pada kedua lapang paru</li>\r\n	<li>Tidak tampak bayangan massa hipodens/hiperdens pada kedua lapang paru</li>\r\n	<li>Tidak tampak nodul hipodens / hiperdens pada kedua lapang paru</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><em><u>Abdomen yang terscanning</u></em></strong></p>\r\n\r\n<ul>\r\n	<li>Tidak tampak metastasis pada hepar, adrenal dan limpa</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kesan&nbsp;&nbsp;&nbsp;&nbsp; :</p>\r\n\r\n<ul>\r\n	<li>Tidak tampak massa mediastinum ataupun massa paru</li>\r\n	<li>Tidak tampak pembesaran KGB&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (136, 'Distal colografi  (Hirschprung Disease)', '<p><strong>Pemeriksaan Distal colografi (kontras water soluble)&nbsp; :</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>- Tampak kotras mengisi rektum hingga colon ascenden.</strong></p>\r\n\r\n<p><strong>- Kaliber rektum hingga rektosigmoid mengecil dengan rektum :sigmoid indeks </strong></p>\r\n\r\n<p><strong>&nbsp;&nbsp; &lt;</strong><strong>1. </strong></p>\r\n\r\n<p><strong>- Transitional zone berada pada daerah rektosigmoid.</strong></p>\r\n\r\n<p><strong>- Dinding rektum dan colon masih tampak reguler</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan :</strong></p>\r\n\r\n<p><strong>Menyokong Hirschprung Disease short segment dengan transitional zone pada </strong></p>\r\n\r\n<p><strong>rektosigmoid.</strong><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong></p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (137, 'Ankle joint', '<p><strong>Ankle joint sin</strong><strong>istra</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Besar, bentuk dan struktur trabekula normal</p>\r\n\r\n<p>Sela dan permukaaan sendi normal</p>\r\n\r\n<p>Tidak tampak lesi litik/sklerotik</p>\r\n\r\n<p>Tidak tampak osteofit</p>\r\n\r\n<p>Tidak tampak fraktur</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan :</strong></p>\r\n\r\n<ul>\r\n	<li><strong>Foto ankle joint</strong><strong> sinistra</strong><strong> tidak tampak kelainan</strong></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n', 'drganjar');
INSERT INTO `xray_template` VALUES (138, 'Antebrachi', '<p><strong><u>Antebrachi :</u></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li>Besar,bentuk dan struktur trabekula normal</li>\r\n	<li>Tidak tampak lesi litik / sklerotik</li>\r\n	<li>Tidak tampak garis fraktur</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan: &nbsp;&nbsp;&nbsp;&nbsp; Foto antebrachi saat ini dalam batas normal</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n', 'drganjar');
INSERT INTO `xray_template` VALUES (139, 'BNO', '<p><strong>BNO</strong><strong> </strong><strong>&nbsp;:</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Pre peritoneal fat dan&nbsp; psoas line &nbsp;jelas, &nbsp;kontur kedua ginjal tidak jelas</p>\r\n\r\n<p>Tidak tampak konkremen opak di sepanjang traktus urinarius</p>\r\n\r\n<p>Distribusi udara di colon dan rongga pelvis masih dalam batas normal</p>\r\n\r\n<p>Skeletal normal</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan&nbsp; : </strong></p>\r\n\r\n<ul>\r\n	<li><strong>Tidak tampak gambaran urolithiasis opak</strong></li>\r\n</ul>\r\n', 'drganjar');
INSERT INTO `xray_template` VALUES (140, 'Clavicula normal', '<p><strong><u>Clavicula </u></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li>Besar bentuk dan struktur trabecula tulang-tulang pembentuk artikulatio glenohumeral normal</li>\r\n	<li>Tidak tampak lesi litik / sklerotik</li>\r\n	<li>Tidak tampak fraktur dan dislokasi</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; : Foto clavicula saat ini masih dalam batas normal</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n', 'drganjar');
INSERT INTO `xray_template` VALUES (141, 'genu', '<p><strong><u>Genu </u></strong><strong><u>dextra :</u></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li>Besar, bentuk dan struktur trabekula tulang baik</li>\r\n	<li>Sela sendi dan permukaan sendi baik</li>\r\n	<li>Eminentia intercodyloidea tidak meruncing.</li>\r\n	<li>Tidak tampak lesi litik dan sklerotik</li>\r\n	<li>Tidak tampak fraktur dan dislokasi.</li>\r\n	<li>Tidak tampak osteofit</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; </strong><strong> </strong><strong>Foto genu dextra saat ini dalam batas normal</strong></p>\r\n', 'drganjar');
INSERT INTO `xray_template` VALUES (142, 'EKSPERTISI  USG', '<p style=\"text-align:center\"><strong>USG Hepar Normal</strong></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p><strong>Hepar : </strong></p>\r\n\r\n<p>Besar dan bentuk normal. Permukaaan reguler, sudut tajam, kapsul tidak menebal.<br />\r\nParenkhim homogen, tidak tampak SOL. Vena porta, duktus biliaris dan vena hepatika tidak melebar. Jaringan disekitarnya normal.</p>\r\n\r\n<p>Tidak tampak bayangan bebas gema.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><strong>USG Gallbladder Normal</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Gallbladder&nbsp; : </strong></p>\r\n\r\n<p>Besar dan bentuk normal. Dinding tidak menebal. Tidak tampak batu/SOL/Sludge</p>\r\n\r\n<p>CBD tidak melebar. Jaringan disekitarnya normal.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><strong>USG Limpa Normal</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Limpa : </strong></p>\r\n\r\n<p>Besar dan bentuk normal. Permukaan reguler. Parenkhim homogen, tidak tampak SOL. Vena lienalis tidak melebar. Jaringan disekitarnya normal. Tidak tampak bayangan bebas gema.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><strong>USG Pancreas Normal</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Pancreas : </strong></p>\r\n\r\n<p>Besar dan bentuk normal. Permukaan reguler. Parenkhim homogen, tidak tampak SOL. Duktus pancreaticus tidak melebar. Jaringan disekitarnya normal.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><strong>USG Ginjal Normal</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Ginjal : </strong></p>\r\n\r\n<p>Besar dan bentuk normal. Permukaan reguler. Batas cortex dengan central echo jelas, sistem pelvocalises tidak melebar. Tidak tampak batu/SOL. Jaringan disekitarnya normal.Ureter tidak melebar.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><strong>USG Vesikaurinaria Normal</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Vesikaurinaria : </strong></p>\r\n\r\n<p>Besar dan bentuk normal, dinding tidak menebal. Tidak tampak batu/SOL/Sludge</p>\r\n\r\n<p>Jaringan disekitarnya normal.</p>\r\n\r\n<p>Tidak tampak bayangan bebas gama.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><strong>USG Paraaorta/Iliaca Normal</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Paraaorta/iliaca: </strong></p>\r\n\r\n<p>Aorta/iliaca tidak melebar. Tidak tampak nodul hipo/hiperekhoik</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><strong>USG Prostat Normal</strong></p>\r\n\r\n<p><strong>Prostat : </strong></p>\r\n\r\n<p>Tidak membesar, ukuran&nbsp;&nbsp; x&nbsp; x&nbsp; mm (vol.&nbsp;&nbsp;&nbsp; )</p>\r\n\r\n<p>Parenkhim homogen. Tidak tampak SOL.</p>\r\n\r\n<p>Tidak tampak kalsifikasi.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><strong>USG Uterus dan Adneksa Normal</strong></p>\r\n\r\n<p><strong>Uterus&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong>:</p>\r\n\r\n<p>Posisi antefleksi/retrofleksi. Besar dan bentuk normal. Parenkim. Endometrium tipis. Tidak tampak SOL.</p>\r\n\r\n<p><strong>Adneksa : </strong></p>\r\n\r\n<p>Tidak tampak massa hipo/hiperekhoik.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Cavum douglas :</strong></p>\r\n\r\n<p>Tidak tampak bayangan bebas gema di cavum douglass</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><strong>USG Abdomen kanan Bawah (Apendix)</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Abdomen kanan bawah : </strong></p>\r\n\r\n<p>Tidak tampak bayangan tubular buntu dan bayangan bebas gema..</p>\r\n\r\n<p>Tidak tampak pelebaran usus disekitarnya.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><strong>USG Abdomen Atas dan Bawah Normal</strong></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p><strong>Abdomen atas dan bawah : </strong></p>\r\n\r\n<p>Tidak tampak bayangan bebas gema pada abdomen atas dan bawah.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><strong>USG Toraks Normal</strong></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p><strong>Toraks : </strong></p>\r\n\r\n<p>Tidak tampak bayangan bebas gema diatas diafragma</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><strong>USG Mammae Normal (Usia kurang dari 35 tahun)</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Mammae : </strong></p>\r\n\r\n<p>Cutis dan subcutis normal. Areola mammae normal.</p>\r\n\r\n<p>Jaringan fibroglanduler masih padat, belum mengalami involusi lemak.</p>\r\n\r\n<p>Dultus laktiferus tidak melebar.</p>\r\n\r\n<p>Tidak tampak massa an/hipo/hiperekhoik pada kedua mammae.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Axila : </strong></p>\r\n\r\n<p>Tidak tampak nodul hipo/hiperekhoik pada kedua axila</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><strong>USG Mammae Normal (Usia 35-50 tahun)</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Mammae : </strong></p>\r\n\r\n<p>Cutis dan subcutis normal. Areola mammae normal</p>\r\n\r\n<p>Jaringan fibroglanduler kurang padat, sebagian sudah mengalami involusi lemak.</p>\r\n\r\n<p>Dultus laktiferus tidak melebar.</p>\r\n\r\n<p>Tidak tampak massa an/hipo/hiperekhoik pada kedua mammae.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Axila</strong> :</p>\r\n\r\n<p>Tidak tampak nodul hipo/hiperekhoik pada kedua axila</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><strong>USG Mammae Normal (Usia lebih dari 50 tahun)</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Mammae : </strong></p>\r\n\r\n<p>Cutis dan subcutis normal. Areola mammae normal</p>\r\n\r\n<p>Jaringan fibroglanduler tidak padat, sudah mengalami involusi lemak.</p>\r\n\r\n<p>Dultus laktiferus tidak melebar.</p>\r\n\r\n<p>Tidak tampak massa an/hipo/hiperekhoik pada kedua mammae.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Axila :</strong></p>\r\n\r\n<p>Tidak tampak nodul hipo/hiperekhoik pada kedua axila</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><strong>USG Tiroid Normal</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Tiroid :</strong></p>\r\n\r\n<p>Tidak membesar, ukuran&nbsp;&nbsp; x&nbsp;&nbsp; x&nbsp;&nbsp; mm (vol.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; )</p>\r\n\r\n<p>Parenkhim homogen.</p>\r\n\r\n<p>Tidak tampak nodul hipo/hiper/anekhoik</p>\r\n\r\n<p><strong>Istmus :</strong></p>\r\n\r\n<p>Tidak membesar</p>\r\n\r\n<p>Parenkhim homogen.</p>\r\n\r\n<p>Tidak tampak massa an/hipo/hiperekhoik</p>\r\n\r\n<p style=\"text-align:center\"><strong>USG Testis Normal</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Testis :</strong></p>\r\n\r\n<p>Tidak membesar, ukuran&nbsp; x&nbsp;&nbsp; x&nbsp;&nbsp; mm</p>\r\n\r\n<p>Parenkhim homogen.</p>\r\n\r\n<p>Tidak tampak SOL</p>\r\n\r\n<p>Tidak tampak bayangan bebas gema</p>\r\n\r\n<p><strong>Epididimis</strong></p>\r\n\r\n<p>Tidak membesar</p>\r\n\r\n<p>Parenkhim homogen.</p>\r\n\r\n<p>Tidak tampak SOL</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><strong>USG Kepala Bayi Normal</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kepala :</strong></p>\r\n\r\n<p>Calvaria dan ekstracalvaria normal</p>\r\n\r\n<p>Sulci dan gyri corticalis normal</p>\r\n\r\n<p>Ventrikel tidak melebar</p>\r\n\r\n<p>Parenkhim homogen</p>\r\n\r\n<p>Tidak tampak lesi an/hipo/hiperekhoik</p>\r\n\r\n<p>Tidak tampak pergeseran garis tengah</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><strong>USG Kandungan Trimester I </strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Uterus antefleksi/retrofleksi dan tampak membesar.</p>\r\n\r\n<p>Tampak sebuah gestational sac di daerah fundus dengan bayangan fetus didalamnya ukuran mm.</p>\r\n\r\n<p>DJJ (+).</p>\r\n\r\n<p>OUI tertutup.</p>\r\n\r\n<p>Adneksa, tidak tampak massa hipo/hiperekhoik.</p>\r\n\r\n<p>Cavum dauglas, tidak tampak bayangan bebas gema.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan :</strong></p>\r\n\r\n<p><strong>Kehamilan tunggal intra uterin, hidup,&nbsp; dengan usia kehamilan .....</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><strong>Kandungan Trimester II </strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tampak bayangan fetus intrauterin. DJJ bpm</p>\r\n\r\n<p>Gerakan aktif.</p>\r\n\r\n<p>BPD mm, FTA&nbsp; cm, FL&nbsp; mm</p>\r\n\r\n<p>BB. gr</p>\r\n\r\n<p>AFI&nbsp;</p>\r\n\r\n<p>Placenta di&nbsp;&nbsp;&nbsp; , dengan ketebalan&nbsp;&nbsp;&nbsp; , echogenitas grade&nbsp;&nbsp;&nbsp;</p>\r\n\r\n<p>SDAU : PI&nbsp;&nbsp; , RI&nbsp;&nbsp; , S/D ratio</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan : </strong></p>\r\n\r\n<p><strong>Kehamilan tunggal intra uterin, hidup, BB. Gr</strong></p>\r\n\r\n<p><strong>Usia kehamilan</strong></p>\r\n\r\n<p><strong>Tanggal persalinan</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><strong>USG Kandungan Trimester II-III :</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tampak fetus tunggal intrauterin, kepala&nbsp;&nbsp; , punggung,&nbsp;&nbsp;&nbsp; bokong&nbsp;&nbsp; .</p>\r\n\r\n<p>Gerakan aktif, tidak tampak kelainan kongenital mayor.</p>\r\n\r\n<p>DJJ bpm.</p>\r\n\r\n<p>BPD&nbsp; &nbsp;mm, FTA&nbsp; cm, FL&nbsp; mm, BB&nbsp; gr</p>\r\n\r\n<p>AFI</p>\r\n\r\n<p>Placenta di&nbsp;&nbsp; , dengan ketebalan&nbsp;&nbsp;&nbsp; , echogenitas grade</p>\r\n\r\n<p>SDAU : PI&nbsp;&nbsp; , RI&nbsp;&nbsp; ,S/D ratio</p>\r\n\r\n<p>Jenis kelamin</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan : </strong></p>\r\n\r\n<p><strong>Kehamilan tunggal intra intra uterin, letak</strong></p>\r\n\r\n<p><strong>BB&nbsp; gr</strong></p>\r\n\r\n<p><strong>Jenis kelamin </strong></p>\r\n\r\n<p><strong>Usia kehamilan</strong></p>\r\n\r\n<p><strong>Tanggal persalinan</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>USG up-low normal</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>USG abdomen :</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Hepar : </strong>Besar dan bentuk normal. Permukaaan reguler, sudut tajam, kapsul tidak menebal. Parenkhim&nbsp;&nbsp;&nbsp;&nbsp; homogen, tidak tampak SOL. Vena porta, duktus biliaris dan vena hepatika tidak melebar. Jaringan disekitarnya normal. Tidak tampak bayangan bebas gema.</p>\r\n\r\n<p><strong>Gallbladder&nbsp; : </strong></p>\r\n\r\n<p>Besar dan bentuk normal. Dinding tidak menebal. Tidak tampak batu/SOL/sludge.</p>\r\n\r\n<p>CBD tidak melebar. Jaringan disekitarnya normal.</p>\r\n\r\n<p><strong>Limpa : </strong></p>\r\n\r\n<p>Besar dan bentuk normal. Permukaan reguler. Parenkhim homogen, tidak tampak SOL. Vena lienalis tidak melebar. Jaringan disekitarnya normal. Tidak tampak bayangan bebas gema.</p>\r\n\r\n<p><strong>Pancreas : </strong></p>\r\n\r\n<p>Besar dan bentuk normal. Permukaan reguler. Parenkhim homogen, tidak tampak SOL. Duktus pancreaticus tidak melebar.Jaringan disekitarnya normal.</p>\r\n\r\n<p><strong>Ginjal : </strong></p>\r\n\r\n<p>Besar dan bentuk normal. Permukaan reguler. Batas cortex dengan central echo jelas, sistem pelvocalises tidak melebar. Tidak tampak batu/SOL. Jaringan disekitarnya normal.Ureter tidak melebar.</p>\r\n\r\n<p><strong>Vesikaurinaria : </strong></p>\r\n\r\n<p>Besar dan bentuk normal, dinding tidak menebal. Tidak tampak batu/SOL/sludge</p>\r\n\r\n<p>Jaringan disekitarnya normal.Tidak tampak bayangan bebas gema.</p>\r\n\r\n<p><strong>Paraaorta/iliaca: </strong></p>\r\n\r\n<p>Aorta/iliaca tidak melebar. Tidak tampak nodul hipo/hiperekhoik</p>\r\n\r\n<p><strong>Uterus dan adneksa :</strong></p>\r\n\r\n<p>Posisi antefleksi/retroflesi. Besar dan bentuk normal. Densitas homogen, endometrium tipis. Tidak tampak SOLs/gestational sac.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan : </strong></p>\r\n\r\n<p><strong>USG Hepatobilier, limpa, pankreas, traktus urinarius, parailiaka/paraaorta, uterus dan Adneksa tidak ada kelainan.</strong></p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (143, 'Lumbosacral_normal', '<p><strong><u>Vertebra Lumbosacral&nbsp; :</u></strong></p>\r\n\r\n<p>Kurve lurus dan skoliosis ringan vertebra lumbalis ke kanan</p>\r\n\r\n<p>Alignment baik</p>\r\n\r\n<p>Besar, bentuk dan struktur trabekula normal</p>\r\n\r\n<p>Foramina dan disci intervertebralis tidak menyempit</p>\r\n\r\n<p>Pedicle normal</p>\r\n\r\n<p>Tidak tampak osteofit</p>\r\n\r\n<p>Tidak tampak fraktur</p>\r\n\r\n<p>Tidak tampak lesi litik dan sklerotik</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan : &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong></p>\r\n\r\n<ul>\r\n	<li>Skoliosis ringan vertebra lumbalis ke kanan</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n', 'drganjar');
INSERT INTO `xray_template` VALUES (144, 'manus', '<p><u>Manus :</u></p>\r\n\r\n<ul>\r\n	<li>Besar, bentuk dan struktur trabekula carpal, metacarpal dan phalanges normal.</li>\r\n	<li>Sela dan permukaan sendi normal.</li>\r\n	<li>Tidak tampak lesi litik / sklerotik.</li>\r\n	<li>Tidak tampak osteophyt.</li>\r\n	<li>Tidak tampak fraktur dan dislokasi</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><u>Kesan</u>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; Foto </strong><strong>manus saat ini &nbsp;tidak tampak kelainan.</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n', 'drganjar');
INSERT INTO `xray_template` VALUES (145, 'schedel', '<p><u>Schedel AP / Lateral</u></p>\r\n\r\n<ul>\r\n	<li>Besar, bentuk dan struktur trabekula normal</li>\r\n	<li>Tabula eksterna-interna dan diploe normal</li>\r\n	<li>Vascular dan convulational marking normal</li>\r\n	<li>Sella tursika normal. Tidak tampal lesi litik dan sklerotik</li>\r\n	<li>Tidak tampak garis fraktur.</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan :&nbsp;&nbsp;&nbsp;&nbsp; Foto Schedel AP &ndash; Lateral </strong><strong>saat ini </strong><strong>tidak tampak kelainan&nbsp;&nbsp;&nbsp; </strong></p>\r\n', 'drganjar');
INSERT INTO `xray_template` VALUES (146, 'Thorax Normal', '<p><strong><u>Thorax :</u></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Cor tidak membesar.</p>\r\n\r\n<p>Sinuses &amp; diafragma normal</p>\r\n\r\n<p>Pulmo&nbsp;&nbsp;&nbsp;&nbsp; : Hilli normal</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Corakan bronkhovaskuler normal</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Tidak tampak infiltrat</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>:</strong><strong> </strong><strong>Tidak tampak TB paru aktif</strong></p>\r\n\r\n<p><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Tidak tampak kardiomegali.</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n', 'drganjar');
INSERT INTO `xray_template` VALUES (147, 'Top Lordotik', '<p><strong>Tidak tampak bercak lunak di apex dan lapang atas kedua paru</strong></p>\r\n', 'drganjar');
INSERT INTO `xray_template` VALUES (148, 'Waters normal', '<p><strong><u>Water&rsquo;s</u></strong><strong><u> :</u></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li>Sinus frontalis kanan-kiri, &nbsp;maksilaris kanan-kiri dan sphenoid kanan-kiri cerah</li>\r\n	<li>Dinding antrum tidak tebal</li>\r\n	<li>Septum nasi di tengah</li>\r\n	<li>Concha nasalis tidak menebal</li>\r\n	<li>Tulang-tulang sekitar sinus baik</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; Foto water&rsquo;s tidak tampak kelainan</strong></p>\r\n', 'drganjar');
INSERT INTO `xray_template` VALUES (149, 'adnixitis', '<p><strong>Kt Kemih :&nbsp; </strong></p>\r\n\r\n<p>Besar bentuk dinding dlm batas normal. Tak tampak batu ataupun SOL</p>\r\n\r\n<p><strong>Uterus &amp; adnexa:</strong></p>\r\n\r\n<p>Besar &amp; bentuk dalam batas normal.</p>\r\n\r\n<p>Tampak koleksi cairan di posterior uterus</p>\r\n\r\n<p><strong>Abdomen kanan bawah : </strong></p>\r\n\r\n<p>Tidak tampak bayangan tubuler buntu. Tidak tampak koleksi cairan</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; -Curiga adnexitis</strong></p>\r\n\r\n<p><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; -USG Kt kemih &amp; uterus dalam batas normal.</strong></p>\r\n\r\n<p><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; -Tidak tampak gambaran appendicitis saat ini</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n', 'drganjar');
INSERT INTO `xray_template` VALUES (150, 'EKSPERTISI RONGTEN', '<p style=\"text-align:center\"><strong>Toraks Dewasa Normal :</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Cor, sinuses dan diafragma normal.</p>\r\n\r\n<p>Pulmo : Hilli normal.</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;Corakan bronkhovaskuler normal.</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;Tidak tampak infiltrate.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kesan :&nbsp; Pulmo saat ini tidak tampak kelainan</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;Cor saat ini tidak tampak kelainan</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><strong>Toraks Anak Normal :</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Cor, sinuses dan diafragma normal.</p>\r\n\r\n<p>Pulmo : Hilli normal.</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;Corakan bronkhovaskuler normal.</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;Tidak tampak infiltrate.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kesan : Pulmo saat ini tidak tampak kelainan</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;Cor saat ini tidak tampak kelainan</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><strong>Toraks Lateral Dewasa dan Anak Normal</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Sinus anterior dan posterior normal</p>\r\n\r\n<p>Retrosternal dan retrocardiac space cerah</p>\r\n\r\n<p>Tidak tampak infiltrat/perselubugan</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan : </strong></p>\r\n\r\n<ul>\r\n	<li><strong>Foto toraks saat ini lateral tidak tampak kelainan</strong></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><strong>Cardiomegali ringan :</strong></p>\r\n\r\n<p>Cor sedikit membesar</p>\r\n\r\n<p>Sinuses dan diafragma normal</p>\r\n\r\n<p>Pulmo&nbsp; : Hilli normal</p>\r\n\r\n<p>&nbsp; Corakan bronkhovaskuler agak bertambah</p>\r\n\r\n<p>&nbsp; Tidak tampak infiltrate</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan&nbsp; : </strong></p>\r\n\r\n<ul>\r\n	<li><strong>Cardiomegali ringan</strong></li>\r\n	<li><strong>Pulmo saat ini tidak tampak kelainan </strong></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><strong>Cardiomegali :</strong></p>\r\n\r\n<p>Cor membesar</p>\r\n\r\n<p>Sinuses dan diafragma normal.</p>\r\n\r\n<p>Pulmo&nbsp; : Hilli normal</p>\r\n\r\n<p>&nbsp; Corakan bronkhovaskuler bertambah</p>\r\n\r\n<p>&nbsp; Tidak tampak infiltrate</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan&nbsp; : </strong></p>\r\n\r\n<ul>\r\n	<li><strong>Cardiomegali tanpa bendungan paru.</strong></li>\r\n	<li><strong>Pulmo saat ini tidak tampak kelainan</strong></li>\r\n</ul>\r\n\r\n<p><strong>&nbsp;&nbsp;&nbsp;&nbsp; </strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><strong>TB paru anak :</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Cor, sinuses dan diafragma normal</p>\r\n\r\n<p>Pulmo&nbsp; : Hilli kasar</p>\r\n\r\n<p>&nbsp; Corakan bronkhovaskuler agak bertambah</p>\r\n\r\n<p>&nbsp; Tampak infiltrate dan noda keras di perihiler dan paracardial</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan : </strong></p>\r\n\r\n<ul>\r\n	<li><strong>TB paru aktif</strong></li>\r\n	<li><strong>Tidak tampak cardiomegali</strong></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><strong>TB paru dewasa :</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Cor sinuses dan diafragma normal</p>\r\n\r\n<p>Pulmo&nbsp; : Hilli kasar</p>\r\n\r\n<p>&nbsp; Corakan paru agak bertambah</p>\r\n\r\n<p>&nbsp; Tampak infiltrate di lapang paru</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan : </strong></p>\r\n\r\n<ul>\r\n	<li><strong>TB paru aktif</strong></li>\r\n	<li><strong>Tidak tampak cardiomegali</strong></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><strong>Bronkopneumonia anak :</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Cor, sinuses dan diafragma normal</p>\r\n\r\n<p>Pulmo&nbsp; : Hilli kasar</p>\r\n\r\n<p>&nbsp;Corakan paru agak bertambah</p>\r\n\r\n<p>&nbsp;Tampak infiltrate di perihiler dan paracardial</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan&nbsp; : </strong></p>\r\n\r\n<ul>\r\n	<li>Bronkhopneumonia</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><strong>Bronkopneumonia dewasa:</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Cor, sinuses dan diafragma normal</p>\r\n\r\n<p>Pulmo&nbsp; : Hilli kasar</p>\r\n\r\n<p>&nbsp; Corakan paru agak bertambah</p>\r\n\r\n<p>&nbsp; Tampak infiltrate di lapang paru</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan&nbsp; : </strong></p>\r\n\r\n<ul>\r\n	<li><strong>Bronkopneumonia </strong></li>\r\n	<li><strong>Tidak tampak cardiomegali</strong></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><strong>Bronkitis</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Cor, sinuses dan diafragma normal</p>\r\n\r\n<p>Pulmo :&nbsp; Hilli kasar</p>\r\n\r\n<p>&nbsp; Corakan paru bertambah, retikuler</p>\r\n\r\n<p>&nbsp;&nbsp;Tidak tampak infiltrat</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan : </strong></p>\r\n\r\n<ul>\r\n	<li><strong>Bronkitis</strong></li>\r\n	<li><strong>Tidak tampak TB paru aktif</strong></li>\r\n	<li><strong>Tidak tampak cardiomegali</strong></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><strong>Schedel Normal :</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Besar, bentuk dan struktur trabekula normal</p>\r\n\r\n<p>Tabula eksterna, diploe dan interna normal</p>\r\n\r\n<p>Vascular dan convulational marking normal</p>\r\n\r\n<p>Sela tursika normal</p>\r\n\r\n<p>Tidak tampak lesi litik/sclerotic</p>\r\n\r\n<p>Tidak tampak garis fraktur</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan : </strong></p>\r\n\r\n<ul>\r\n	<li><strong>Foto schedel tidak tampak kelainan</strong></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><strong>Schedel (Massa Sela)</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Besar, bentuk dan struktur trabekula normal</p>\r\n\r\n<p>Tabula eksterna, diploe dan interna normal</p>\r\n\r\n<p>Vascular dan convulational marking normal</p>\r\n\r\n<p>Sela tursika mendatar</p>\r\n\r\n<p>Prosessus clinoideus posterior menghilang, anterior masih baik</p>\r\n\r\n<p>Tidak tampak lesi litik/sclerotik</p>\r\n\r\n<p>Tidak tampak garis fraktur</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><strong>Kesan :</strong></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><strong>Sugestive massa di daerah sella tursika </strong></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><strong>Foto TMJ (Temporo-Mandibula Joint) Normal</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tutup mulut :</p>\r\n\r\n<ul>\r\n	<li>Caput mandibula berada pada fossa mandibula</li>\r\n	<li>Posisi simetris</li>\r\n	<li>Sela dan permukaan sendi normal</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Buka mulut :</p>\r\n\r\n<ul>\r\n	<li>Caput mandibula terletak di bawah tuberkulum artikulare</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan : </strong></p>\r\n\r\n<ul>\r\n	<li><strong>Foto Temporo-mandibula joint tidak tampak kelainan</strong></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\">&nbsp;<strong>Foto TMJ (Temporo-Mandibula Joint) Dislokasi</strong></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p><u>Tutup mulut &amp; Buka mulut :</u></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\">Pada waktu buka dan tutup&nbsp; mulut, caput mandibula kanan/kiri/bilateral terletak didepan tuberkulum artikulare.</li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><strong>Kesan :</strong></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><strong>Menyokong suatu dislokasi temporo-mandibula joint kanan/kiri/ bilateral </strong></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><strong>Foto Submentovertex (Basis Cranii) Normal</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Fossa anterior, medial, lateral dan posterior cerah</p>\r\n\r\n<p>Foramen ovale dan basis cranii cerah</p>\r\n\r\n<p>Odontoid dan rotundum cerah</p>\r\n\r\n<p>Tidak tampak perselubungan</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan : </strong></p>\r\n\r\n<ul>\r\n	<li><strong>Foto Submentovertex (basis cranii) tidak tampak kelainan</strong></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><strong>Waters Normal</strong></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p>Sinus maksilaris, frontalis dan ethmoidalis cerah</p>\r\n\r\n<p>Dinding antrum tidak tebal</p>\r\n\r\n<p>Septum nasi ditengah</p>\r\n\r\n<p>Cavum nasi cerah</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan :</strong></p>\r\n\r\n<ul>\r\n	<li><strong>Foto waters tidak tampak kelainan</strong></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><strong>Mastoid Normal :</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Mastoid air cell normal</p>\r\n\r\n<p>Trigonum timfani normal</p>\r\n\r\n<p>MAE cerah</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan : </strong></p>\r\n\r\n<ul>\r\n	<li><strong>Foto mastoid tidak tampak kelainan</strong></li>\r\n</ul>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><strong>Panoramic</strong></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p>Tampak sisa akar gigi 1-2, 2-8, 3-8, 4-8</p>\r\n\r\n<p>Tampak caries gigi 1-7</p>\r\n\r\n<p>Tidak tampak destruksi /erosi tulang mandibula dan maxila</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><strong>Panoramic (Massa)</strong></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">Tampak lesi osteolitik ekspansif dengan septa-septa</p>\r\n\r\n<p style=\"text-align:justify\">Cortex tampak menipis</p>\r\n\r\n<p style=\"text-align:justify\">Tidak tampak reaksi periosteal di daerah submandibula</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><strong>Kesan :</strong></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><strong>Ameloblastoma</strong></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><strong>Foto Soft Tissue Leher Normal</strong></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p>Kolom udara dalam faring dan laring normal</p>\r\n\r\n<p>Retrofaring dan retrolaring space tidak melebar</p>\r\n\r\n<p>Epiglotis dan valekula normal</p>\r\n\r\n<p>Tidak tampak perselubungan</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan : </strong></p>\r\n\r\n<ul>\r\n	<li><strong>Foto soft tissue leher tidak tampak kelainan</strong></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><strong>Cervical AP- LatNormal</strong></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p>Curve normal</p>\r\n\r\n<p>Besar, bentuk dan struktur trabekula normal</p>\r\n\r\n<p>Sela dan permukaan sendi normal</p>\r\n\r\n<p>Pedicle normal</p>\r\n\r\n<p>Tidak tampak osteofit</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan :</strong></p>\r\n\r\n<ul>\r\n	<li><strong>Foto cervical tidak tampak kelainan</strong></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><strong>Cervical AP-Lat-Obl Normal</strong></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p>Curve dan alignment normal</p>\r\n\r\n<p>Besar, bentuk dan struktur trabekula normal</p>\r\n\r\n<p>Sela dan permukaan sendi normal</p>\r\n\r\n<p>Foramen intervertebralis normal</p>\r\n\r\n<p>Pedicle normal</p>\r\n\r\n<p>Tidak tampak osteofit</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan :</strong></p>\r\n\r\n<ul>\r\n	<li><strong>Foto cervical tidak tampak kelainan</strong></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><strong>Shoulder Normal</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Besar, bentuk dan struktur trabekula tulang-tulang pembentuk artikulatio glenohumeral normal</p>\r\n\r\n<p>Sela dan permukaan sendi normal</p>\r\n\r\n<p>Tidak tampak lesi litik/sklerotik</p>\r\n\r\n<p>Tidak tampak osteofit</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan : </strong></p>\r\n\r\n<ul>\r\n	<li><strong>Foto shoulder tidak tampak kelainan</strong></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><strong>Elbow joint Normal</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Besar, bentuk dan struktur trabekula normal</p>\r\n\r\n<p>Sela dan permukaaan sendi normal</p>\r\n\r\n<p>Tidak tampak lesi litik/sklerotik</p>\r\n\r\n<p>Tidak tampak osteofit</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan :</strong></p>\r\n\r\n<ul>\r\n	<li><strong>Foto elbow joint&nbsp; tidak tampak kelainan</strong></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><strong>Wrist joint Normal</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Besar, bentuk dan struktur trabekula normal</p>\r\n\r\n<p>Sela dan permukaaan sendi normal</p>\r\n\r\n<p>Tidak tampak lesi litik/sklerotik</p>\r\n\r\n<p>Tidak tampak osteofit</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan :</strong></p>\r\n\r\n<ul>\r\n	<li><strong>Foto wrist joint tidak tampak kelainan</strong></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><strong>Wrist Joint (Pertumbuhan)</strong></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">Tulang-tulang pertumbuhan pada wrist joint menurut metode Greulich-Pyle sesuai dengan dengan usia 2 thn&nbsp; 8 bl.</p>\r\n\r\n<p style=\"text-align:justify\">Sela sendi dan permukaan sendi tampak baik.</p>\r\n\r\n<p style=\"text-align:justify\">Tidak tampak lesi litik maupun sklerotik.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><strong>Kesan :</strong></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><strong>Keterlambatan pertumbuhan tulang-tulang pembentuk wrist joint bilateral</strong></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><strong>Manus Normal</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Besar, bentuk dan struktur trabekula normal</p>\r\n\r\n<p>Sela dan permukaaan sendi normal</p>\r\n\r\n<p>Tidak tampak lesi litik/sklerotik</p>\r\n\r\n<p>Tidak tampak osteofit</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan :</strong></p>\r\n\r\n<ul>\r\n	<li><strong>Foto manus tidak tampak kelainan</strong></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><strong>Manus (Osteoartrosis)</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">Besar, bentuk, dan struktur trabekula tulang carpal, metacarpal dan falang normal.</p>\r\n\r\n<p style=\"text-align:justify\">Sela sendi dan permukaan sendi normal</p>\r\n\r\n<p style=\"text-align:justify\">Tidak tampak garis fraktur.</p>\r\n\r\n<p style=\"text-align:justify\">Tidak tampak lesi kistik/sklerotik</p>\r\n\r\n<p style=\"text-align:justify\">Tampak osteofit pada interphalanx distal digiti 3 &amp; 4 kanan/kiri/bilateral</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><strong>Kesan :</strong></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><strong>Osteoarthrosis interphalanx distal digiti 3 &amp; 4</strong> <strong>manus kanan/kiri/bilateral</strong></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><strong>Torakal Normal</strong></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p>Curve dan alignment normal</p>\r\n\r\n<p>Besar, bentuk dan struktur trabekula normal</p>\r\n\r\n<p>Sela dan permukaan sendi normal</p>\r\n\r\n<p>Foramen intervertebralis normal</p>\r\n\r\n<p>Pedicle normal</p>\r\n\r\n<p>Tidak tampak osteofit</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan : </strong></p>\r\n\r\n<ul>\r\n	<li><strong>Foto torakal tidak tampak kelainan</strong></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><strong>Torakolumbal Normal:</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Curve dan alignment normal</p>\r\n\r\n<p>Besar, bentuk dan struktur trabekula normal</p>\r\n\r\n<p>Sela dan permukaan sendi normal</p>\r\n\r\n<p>Foramen intervertebralis normal</p>\r\n\r\n<p>Pedicle normal</p>\r\n\r\n<p>Tidak tampak osteofit</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan : </strong></p>\r\n\r\n<ul>\r\n	<li><strong>Foto torakolumbal tidak tampak kelainan</strong></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Lumbosacral Normal:</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Curve dan alignment normal</p>\r\n\r\n<p>Besar, bentuk dan struktur trabekula normal</p>\r\n\r\n<p>Sela dan permukaan sendi normal</p>\r\n\r\n<p>Foramen intervertebralis normal</p>\r\n\r\n<p>Pedicle normal</p>\r\n\r\n<p>Tidak tampak osteofit</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan&nbsp; : </strong></p>\r\n\r\n<ul>\r\n	<li><strong>Foto lumbosacral tidak tampak kelainan</strong></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><strong>Osteoartrosis Vertebare Lumbosacral</strong></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p>Curve dan alignment normal</p>\r\n\r\n<p>Besar, bentuk dan struktur trabekula normal</p>\r\n\r\n<p>Sela dan permukaan sendi normal</p>\r\n\r\n<p>Foramen intervertebralis normal</p>\r\n\r\n<p>Pedicle normal</p>\r\n\r\n<p>Tampak osteofit</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan&nbsp; : </strong></p>\r\n\r\n<ul>\r\n	<li><strong>Osteoartrosis vertebrae lumbal</strong></li>\r\n</ul>\r\n\r\n<p style=\"text-align:center\"><strong>Spondilolystesis Vertebrae Lumbosacral</strong></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p>Curve normal</p>\r\n\r\n<p>Malignment Verterbrae lumbal</p>\r\n\r\n<p>Besar, bentuk dan struktur trabekula normal</p>\r\n\r\n<p>Sela dan permukaan sendi normal</p>\r\n\r\n<p>Foramen intervertebralis lumbal menyempit</p>\r\n\r\n<p>Pedicle normal</p>\r\n\r\n<p>Tidak tampak osteofit</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan&nbsp; : </strong></p>\r\n\r\n<ul>\r\n	<li><strong>Spondilolystesis dan penyempitan foramen intervertebralis vertebrae lumbal </strong></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><strong>Osteoartrosis dan Spondilolystesis Vertebrae Lumbosacral</strong></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p>Curve normal</p>\r\n\r\n<p>Malignment Verterbrae lumbal</p>\r\n\r\n<p>Besar, bentuk dan struktur trabekula normal</p>\r\n\r\n<p>Sela dan permukaan sendi normal</p>\r\n\r\n<p>Foramen intervertebralis lumbal menyempit</p>\r\n\r\n<p>Pedicle normal</p>\r\n\r\n<p>Tampak osteofit</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan&nbsp; : </strong></p>\r\n\r\n<ul>\r\n	<li><strong>Osteoartrosis disertai dengan spondilolystesis dan penyempitan foramen intervertebralis vertebrae lumbal </strong></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><strong>Pelvis Normal</strong></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p>Besar, bentuk dan struktur trabekula tulang-tulang pembentuk pelvis normal</p>\r\n\r\n<p>Sela dan permukaaan sendi normal</p>\r\n\r\n<p>Caput femoris dan acetabulum normal.</p>\r\n\r\n<p>Tidak tampak lesi litik/sklerotik</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan :</strong></p>\r\n\r\n<ul>\r\n	<li><strong>Foto pelvis tidak tampak kelainan</strong></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><strong>Pelvis (Sacroilietis)</strong></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p>Besar, bentuk dan struktur trabekula tulang-tulang pembentuk pelvis normal</p>\r\n\r\n<p style=\"text-align:justify\">Sela sendi dan permukaan sendi sacroiliaca kanan/kiri/bilateral tampak kasar dan&nbsp; sklerotik</p>\r\n\r\n<p>Caput femoris dan acetabulum normal.</p>\r\n\r\n<p style=\"text-align:justify\">Sela dan permukaaan sendi tulang lainnya normal</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><strong>Kesan :</strong></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><strong>Sacroilietis kanan/kiri/bilatera</strong><strong>l</strong></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><strong>Femur Normal</strong></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p>Besar, bentuk dan struktur trabekula normal</p>\r\n\r\n<p>Sela dan permukaaan sendi normal</p>\r\n\r\n<p>Tidak tampak lesi litik/sklerotik</p>\r\n\r\n<p>Tidak tampak garis fraktur</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan :</strong></p>\r\n\r\n<ul>\r\n	<li><strong>Foto femur tidak tampak kelainan</strong></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><strong>Genu Normal</strong></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p>Besar, bentuk dan struktur trabekula normal</p>\r\n\r\n<p>Sela dan permukaaan sendi normal</p>\r\n\r\n<p>Eminentia intercondiloidea normal</p>\r\n\r\n<p>Tidak tampak osteofit</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan :</strong></p>\r\n\r\n<ul>\r\n	<li><strong>Foto genu tidak tampak kelainan</strong></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><strong>Osteoartrosis Grade 1 Genu Kanan/Kiri/Bilateral</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Besar, bentuk dan struktur trabekula normal</p>\r\n\r\n<p>Sela dan permukaaan sendi normal</p>\r\n\r\n<p>Eminentia intercondiloidea normal</p>\r\n\r\n<p style=\"text-align:justify\">Tampak osteofit pada condylus lateralis dan medialis os femur dan tibia serta patela.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan :</strong></p>\r\n\r\n<ul>\r\n	<li><strong>Osteoartrosis grade 1 genu kanan/kiri/bilateral</strong></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><strong>Osteoartrosis Grade 2 Genu Kanan/Kiri/Bilateral</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Besar, bentuk dan struktur trabekula normal</p>\r\n\r\n<p>Sela sendi normal</p>\r\n\r\n<p>Permukaaan sendi sklerotik</p>\r\n\r\n<p>Eminentia intercondiloidea runcing</p>\r\n\r\n<p>Tampak osteofit pada condylus lateralis dan medialis os femur dan tibia serta patela.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan :</strong></p>\r\n\r\n<ul>\r\n	<li><strong>Osteoartrosis grade 2 genu kanan/kiri/bilateral</strong></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><strong>Osteoartrosis Grade 3 Genu Kanan/Kiri/Bilateral</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Besar, bentuk dan struktur trabekula normal</p>\r\n\r\n<p>Sela sendi menyempit.</p>\r\n\r\n<p>Permukaaan sendi sklerotik</p>\r\n\r\n<p>Eminentia intercondiloidea runcing</p>\r\n\r\n<p>Tampak osteofit pada condylus lateralis dan medialis os femur dan tibia serta patela.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan :</strong></p>\r\n\r\n<ul>\r\n	<li><strong>Osteoartrosis grade 3 genu kanan/kiri/bilateral</strong></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><strong>Tibia fibula Normal</strong></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p>Besar, bentuk dan struktur trabekula normal</p>\r\n\r\n<p>Sela dan permukaaan sendi normal</p>\r\n\r\n<p>Tidak tampak lesi litik/sklerotik</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan :</strong></p>\r\n\r\n<ul>\r\n	<li><strong>Foto tibia fibula tidak tampak kelainan</strong></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><strong>Tibia fibula Normal (Pertumbuhan)</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">Besar, bentuk, dan struktur trabekula tulang tibia dan fibula normal.</p>\r\n\r\n<p style=\"text-align:justify\">Sela sendi dan permukaan sendi tampak baik.</p>\r\n\r\n<p style=\"text-align:justify\">Tidak tampak garis fraktur.</p>\r\n\r\n<p style=\"text-align:justify\">Tidak tampak lesi litik maupun sklerotik.</p>\r\n\r\n<p style=\"text-align:justify\">Epifise line sudah menutup</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><strong>Kesan :</strong></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><strong>Foto cruris&nbsp; dalam batas normal.</strong></li>\r\n	<li style=\"text-align:justify\"><strong>Epifise line sudah menutup</strong></li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><strong>Ankle joint Normal</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Besar, bentuk dan struktur trabekula normal</p>\r\n\r\n<p>Sela dan permukaaan sendi normal</p>\r\n\r\n<p>Tidak tampak lesi litik/sklerotik</p>\r\n\r\n<p>Tidak tampak osteofit</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan :</strong></p>\r\n\r\n<ul>\r\n	<li><strong>Foto ankle joint tidak tampak kelainan</strong></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><strong>Pedis Normal</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Besar, bentuk dan struktur trabekula carpal, metacarpal, dan phalanges normal</p>\r\n\r\n<p>Sela dan permukaaan sendi normal</p>\r\n\r\n<p>Tidak tampak lesi litik/sklerotik</p>\r\n\r\n<p>Tidak tampak osteofit</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan :</strong></p>\r\n\r\n<ul>\r\n	<li><strong>Foto pedis tidak tampak kelainan</strong></li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><strong>Pedis (Osteoartrosis)</strong></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">Besar, bentuk, dan struktur trabekula tulang carpal, metacarpal, dan falang normal.</p>\r\n\r\n<p style=\"text-align:justify\">Sela sendi masih baik, dan permukaan sendi agak sklerotik.</p>\r\n\r\n<p style=\"text-align:justify\">Tidak tampak garis fraktur.</p>\r\n\r\n<p style=\"text-align:justify\">Tampak osteofit pada interphalang digiti 1</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><strong>Kesan :</strong></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><strong>Osteoarthrosis interphalang digiti 1 pedis kanan/kiri/bilateral</strong></li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><strong>Pedis (Peritendinitis)</strong></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">Besar, bentuk, dan struktur trabekula tulang carpal, metacarpal, dan falang normal.</p>\r\n\r\n<p style=\"text-align:justify\">Sela dan permukaan sendi normal</p>\r\n\r\n<p style=\"text-align:justify\">Tidak tampak garis fraktur.</p>\r\n\r\n<p style=\"text-align:justify\">Tidak tampak lesi litik maupun sklerotik.</p>\r\n\r\n<p style=\"text-align:justify\">Tidak tampak osteofit</p>\r\n\r\n<p style=\"text-align:justify\">Tampak kalsifikasi jaringan lunak periartikular di sebelah lateral distal metacarpal digiti 1 kanan/kiri/bilateral</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><strong>Kesan :</strong></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><strong>Peritendinitis digiti 1 kanan/kiri/bilateral</strong></li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><strong>Pedis (GOUT)</strong></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">Tampak lesi dengan densitas jaringan lunak di daerah periartikuler metatarsal phalang digiti I yang menyebabkan erosi pada daerah periartikuler dengan gambaran hanging.</p>\r\n\r\n<p style=\"text-align:justify\">Tulang-tulang lainnya normal</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><strong>Kesan :</strong></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><strong>Curiga pembentukan topus pada gout artritis</strong></li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><strong>BNO Normal:</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Pre peritoneal fat, psoas line dan kontur ginjal normal</p>\r\n\r\n<p>Tidak tampak konkremen opak di sepanjang traktus urinarius</p>\r\n\r\n<p>Distribusi udara normal</p>\r\n\r\n<p>Skeletal normal</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan&nbsp; : </strong></p>\r\n\r\n<ul>\r\n	<li><strong>Tidak tampak urolithiasis opak</strong></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><strong>Abdomen 3 Posisi Normal:</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Pre peritoneal fat dan psoas line normal</p>\r\n\r\n<p>Tidak tampak konkremen opak disepanjang traktus urinarius</p>\r\n\r\n<p>Distribusi udara normal</p>\r\n\r\n<p>Tidak tampak air fluid level/udara bebas</p>\r\n\r\n<p>Skeletal normal</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan&nbsp; : </strong></p>\r\n\r\n<ul>\r\n	<li><strong>Tidak tampak tanda-tanda ileus</strong></li>\r\n</ul>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><strong>Mammografi Normal</strong> <strong>(Usia kurang dari 35 tahun)</strong></p>\r\n\r\n<p><strong>Mammae :</strong></p>\r\n\r\n<p>Cutis dan subcutis normal.</p>\r\n\r\n<p>Jaringan fibroglanduler masih padat, belum mengalami involusi lemak.</p>\r\n\r\n<p>Tidak tampak massa opak/lusen pada kedua mammae.</p>\r\n\r\n<p>Tidak tampak bayangan opak kecil-kecil pada kedua mammae</p>\r\n\r\n<p><strong>Axila</strong></p>\r\n\r\n<p>Tidak tampak nodul opak/lusen</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan :</strong></p>\r\n\r\n<ul>\r\n	<li><strong>Tidak tampak massa/kalsifikasi pada kedua mammae</strong></li>\r\n	<li><strong>Tidak tampak pembesaran KGB kedua axila</strong></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><strong>Mammografi Normal&nbsp; (Usia 35-50 tahun)</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Mammae :</strong></p>\r\n\r\n<p>Cutis dan subcutis normal.</p>\r\n\r\n<p>Jaringan fibroglanduler kurang padat, sebagian sudah mengalami involusi lemak.</p>\r\n\r\n<p>Tidak tampak massa opak/lusen pada kedua mammae.</p>\r\n\r\n<p>Tidak tampak bayangan opak kecil-kecil</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Axila :</strong></p>\r\n\r\n<p>Tidak tampak nodul opak/lusen</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan :</strong></p>\r\n\r\n<ul>\r\n	<li><strong>Tidak tampak massa/kalsifikasi pada kedua mammae</strong></li>\r\n	<li><strong>Tidak tampak pembesaran KGB kedua axila</strong></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><strong>Mammografi (Usia lebih dari 50 tahun)</strong></p>\r\n\r\n<p><strong>Mammae :</strong></p>\r\n\r\n<p>Cutis dan subcutis normal.</p>\r\n\r\n<p>Jaringan fibroglanduler tidak padat, sudah mengalami involusi lemak.</p>\r\n\r\n<p>Tidak tampak massa opak/lusen pada kedua mammae.</p>\r\n\r\n<p>Tidak tam[ak bayangan opak&nbsp; kecil-kecil pada kedua mammae</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Axila :</strong></p>\r\n\r\n<p>Tidak tampak nodul opak/lusen</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan :</strong></p>\r\n\r\n<ul>\r\n	<li><strong>Tidak tampak massa/kalsifikasi pada kedua mammae</strong></li>\r\n	<li><strong>Tidak tampak pembesaran KGB kedua axila</strong></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><strong>BNO-IVP Normal</strong></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>BNO :</strong></p>\r\n\r\n<ul>\r\n	<li>Tidak tampak konkremen opak di sepanjang traktus&nbsp; urinarius</li>\r\n</ul>\r\n\r\n<p><strong>IVP :</strong></p>\r\n\r\n<ul>\r\n	<li>Fase nefrogram dan eksresi kedua ginjal sudah terlihat pada menit ke- 5</li>\r\n	<li>Sistem pelvocalises normal</li>\r\n	<li>Ureter normal</li>\r\n	<li>Vesikaurinaria, besar, bentuk dan posisi normal, tidak tampak filling defect/indentasi</li>\r\n	<li>Post Voiding : sisa kontras Vesica Urinaria</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan : </strong></p>\r\n\r\n<ul>\r\n	<li><strong>Fungsi kedua ginjal normal</strong></li>\r\n	<li><strong>Tidak tampak nefrolithiasis&nbsp;&nbsp; </strong></li>\r\n</ul>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><strong>Oesofagografi Normal</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kontras diminumkan peroral</p>\r\n\r\n<p>Tampak kontras dengan lancar mengisi oesofagus1/3 proksimal, 1/3 tengah dan1/3 distal, kemudia kontras masuk kedalam gaster.</p>\r\n\r\n<ul>\r\n	<li>Besar, bentuk dan posisi normal</li>\r\n	<li>Mukosa normal, reguler</li>\r\n	<li>Tidak tampak filling defect</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan :</strong></p>\r\n\r\n<ul>\r\n	<li><strong>Oesofagografi tidak tampak kelainan</strong></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><strong>Oesofagogarfi (Esofagitis dan GERD)</strong></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p>Kontras dimasukkan per oral.</p>\r\n\r\n<p>Tampak kontras dengan lancar mengisi esofagus 1/3 proximal, 1/3medial esofagus dan 1/3 distal esofagus, kemudian kontras masuk ke dalam gaster,</p>\r\n\r\n<p>Esofagus 1/3 proksimal dan medial:</p>\r\n\r\n<ul>\r\n	<li>Besar, bentuk dan posisi normal.</li>\r\n	<li>Mukosa&nbsp; dan dinding masih tampak normal.</li>\r\n</ul>\r\n\r\n<p>Esofagus 1/3 distal, tampak mukosa kasar dengan dinding irreguler.</p>\r\n\r\n<p>Pada valsava maneuver, tampak kotras reflux ke esofagus bagian distal</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan :</strong></p>\r\n\r\n<ul>\r\n	<li><strong>Tampak tanda-tanda&nbsp; esofagitis di daerah 1/3 distal esofgus.</strong></li>\r\n	<li><strong>Tampak tanda-tanda GERD di esofagus bagian distal</strong></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><strong>Oesofagomaagduodenografi</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kontras diminumkan peroral</p>\r\n\r\n<p>Tampak kontras dengan lancar mengisi oesofagus, gaster dan duodenum.</p>\r\n\r\n<p>Oesofagus :</p>\r\n\r\n<ul>\r\n	<li>Besar, bentuk dan posisi normal</li>\r\n	<li>Mukosa normal, reguler</li>\r\n	<li>Tidak tampak filling defect</li>\r\n</ul>\r\n\r\n<p>Gaster :</p>\r\n\r\n<ul>\r\n	<li>Besar, bentuk dan posisi normal</li>\r\n	<li>Mukosa normal, reguler</li>\r\n	<li>Tidak tampak filling defect</li>\r\n</ul>\r\n\r\n<p>Duodenum</p>\r\n\r\n<ul>\r\n	<li>Besar, bentuk dan posisi normal</li>\r\n	<li>Mukosa normal, reguler</li>\r\n	<li>Tidak tampak filling defect</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan : </strong></p>\r\n\r\n<ul>\r\n	<li><strong>Oesofagomaagduodenografi tidak tampak kelainan</strong></li>\r\n</ul>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><strong>Maagduodenografi</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kontras diminumkan peroral</p>\r\n\r\n<p>Tampak kontras dengan lancar mengisi gaster dan duodenum.</p>\r\n\r\n<p>Gaster :</p>\r\n\r\n<ul>\r\n	<li>Besar, bentuk dan posisi normal</li>\r\n	<li>Mukosa normal, reguler</li>\r\n	<li>Tidak tampak filling defect</li>\r\n</ul>\r\n\r\n<p>Duodenum</p>\r\n\r\n<ul>\r\n	<li>Besar, bentuk dan posisi normal</li>\r\n	<li>Mukosa normal, reguler</li>\r\n	<li>Tidak tampak filling defek</li>\r\n</ul>\r\n\r\n<p><strong>Kesan : </strong></p>\r\n\r\n<ul>\r\n	<li><strong>Maagduodenografi tidak tampak kelainan</strong></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><strong>Colon In Loop Normal</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kontras dimasukkan melalui anus dengan menggunakan kateter.</p>\r\n\r\n<p>Tampak kontras dengan lancar mengisi rectum , colon sigmoid, colon desendens, colon transversum, colon asendens, caecum dan akhirnya refluks ke ileum terminalis.</p>\r\n\r\n<p>Double kontras :</p>\r\n\r\n<ul>\r\n	<li>Besar, bentuk dan posisi normal</li>\r\n	<li>Mukosa normal, reguler</li>\r\n	<li>Tidak tampak filling defect</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan :</strong></p>\r\n\r\n<ul>\r\n	<li><strong>Pasase kontras lancar</strong></li>\r\n	<li><strong>Tidak tampak tanda-tanda inflamasi/neoplasma</strong></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><strong>Histerosalfingografi Normal</strong></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p>Kontras dimasukkan melalui orificium uteri eksternum</p>\r\n\r\n<p>Tampak kontras dengan lancar mengisi cavum uteri dan kedua tuba.</p>\r\n\r\n<p>Uterus :</p>\r\n\r\n<ul>\r\n	<li>Posisi antefleksi</li>\r\n	<li>Besar dan bentuk normal</li>\r\n	<li>Tidak tampak filling defect</li>\r\n</ul>\r\n\r\n<p>Tuba falopii :</p>\r\n\r\n<ul>\r\n	<li>Besar dan bentuk normal</li>\r\n	<li>Tampak spill pada kedua tuba</li>\r\n</ul>\r\n\r\n<p>Post pengecatan, tampak sisa kontras di rongga peritoneum.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan : </strong></p>\r\n\r\n<ul>\r\n	<li><strong>Uterus antefleksi</strong></li>\r\n	<li><strong>Kedua tuba paten</strong></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><strong>Histerosalfingografi (Obstruksi)</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kontras dimasukkan melalui orificium uteri eksternum.</p>\r\n\r\n<p>Tampak kontras dengan lancar mengisi cavum uteri dan kedua tuba.</p>\r\n\r\n<p>Uterus :</p>\r\n\r\n<ul>\r\n	<li>Posisi antefleksi</li>\r\n</ul>\r\n\r\n<ul>\r\n	<li>Besar dan bentuk normal</li>\r\n	<li>Tidak tampak filling defect</li>\r\n</ul>\r\n\r\n<p>Tuba falopii :</p>\r\n\r\n<ul>\r\n	<li>Tidak tampak spill pada kedua ujung tuba.</li>\r\n</ul>\r\n\r\n<p>Post pengecatan : Tidak tampak sisa kontras pada rongga peritonium</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan : </strong></p>\r\n\r\n<ul>\r\n	<li><strong>Uterus antefleksi</strong></li>\r\n	<li><strong>Obstruktif kedua tuba </strong></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><strong>Histerosalfingografi (Obstruksi dengan Hidrosalfing)</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kontras dimasukkan melalui orificium uteri eksternum.</p>\r\n\r\n<p>Tampak kontras dengan lancar mengisi cavum uteri dan kedua tuba.</p>\r\n\r\n<p>Uterus :</p>\r\n\r\n<ul>\r\n	<li>Posisi antefleksi</li>\r\n</ul>\r\n\r\n<ul>\r\n	<li>Besar dan bentuk normal</li>\r\n	<li>Tidak tampak filling defect</li>\r\n</ul>\r\n\r\n<p>Tuba falopii :</p>\r\n\r\n<ul>\r\n	<li>Tampak tuba kanan melebar</li>\r\n	<li>Tidak tampak spill pada kedua ujung tuba.</li>\r\n</ul>\r\n\r\n<p>Post pengecatan : Tidak tampak sisa kontras pada rongga peritoneum.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan : </strong></p>\r\n\r\n<ul>\r\n	<li><strong>Uterus antefleksi</strong></li>\r\n	<li><strong>Obstruksi kedua tuba dengan&nbsp; Hydrosalfing kanan</strong></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><strong>Histerosalfingografi (Perlengketan Tuba)</strong></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p>Kontras dimasukkan melalui orificium uteri eksternum.</p>\r\n\r\n<p>Tampak kontras dengan lancar mengisi cavum uteri dan kedua tuba.</p>\r\n\r\n<p>Uterus :</p>\r\n\r\n<ul>\r\n	<li>Posisi antefleksi</li>\r\n</ul>\r\n\r\n<ul>\r\n	<li>Besar dan bentuk normal</li>\r\n	<li>Tidak tampak filling defect</li>\r\n</ul>\r\n\r\n<p>Tuba falopii :</p>\r\n\r\n<ul>\r\n	<li>Besar dan bentuk normal</li>\r\n	<li>Tampak spill pada kedua ujung tuba.</li>\r\n</ul>\r\n\r\n<p>Post pengecatan : Tampak sisa kontras pada rongga peritoneum dan tampak kontras</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; terlokalisir di sekitar tuba kiri</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan : </strong></p>\r\n\r\n<ul>\r\n	<li><strong>Uterus antefleksi </strong></li>\r\n	<li><strong>Kedua tuba paten disertai perlengketan tuba kiri.</strong></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><strong>Cholangiografi Normal</strong></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p>Kontras dimasukkan melalui saluran T tube.</p>\r\n\r\n<p>Tampak kontras dengan lancar masuk ke dalam duktus hepatikus kiri dan kanan sampai ke bagian distal duktus hepatikus communis, kemudian masuk ke dalam duodenum.</p>\r\n\r\n<p>Tampak dilatasi ringan duktus hepatikus komunis sampai bagian distal.</p>\r\n\r\n<p>Tidak tampak filling defect.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan :</strong></p>\r\n\r\n<ul>\r\n	<li><strong>Kontras berjalan lancar sampai ke duodenum, tidak tampak penyempitan atau filling defect.</strong></li>\r\n	<li><strong>Pelebaran ringan duktus hepatikus communis sampai&nbsp; bagian distal </strong></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><strong>Distal Kolografi (MAR)</strong></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">Kontras dimasukkan melalui lubang kolostomi yang ke arah distal dengan kateter Kontras tampak mengisi sigmoid dan rectum dengan lancar.</p>\r\n\r\n<p>Tampak&nbsp; rectum bagian distal&nbsp; menyempit dan tidak tampak kontras keluar melalui uretra.</p>\r\n\r\n<p>Jarak rectum yang menyempit dengan anal dimple kurang lebih 3,5 cm.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kesan :</p>\r\n\r\n<ul>\r\n	<li>MAR letak tinggi, tidak tampak tanda-tanda fistula.</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><strong>Distal Kolografi (MAR dengan Fistel)</strong></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">Kontras dimasukkan melalui lubang kolostomi yang ke arah distal dengan&nbsp; kateter</p>\r\n\r\n<p style=\"text-align:justify\">Kontras tampak mengisi sigmoid dan rektum dengan lancar.</p>\r\n\r\n<p>Tampak&nbsp; rectum bagian distal&nbsp; menyempit dan tampak kontras keluar melalui uretra.</p>\r\n\r\n<p>Jarak rectum yang menyempit dengan anal dimple kurang lebih 2 cm.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kesan :</p>\r\n\r\n<ul>\r\n	<li>MAR letak tinggi dengan Fistula rectouretra</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><strong>Fistulografi</strong></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">Kontras dimasukkan ke dalam orificium fistula ani (orificium abnormal).</p>\r\n\r\n<p style=\"text-align:justify\">Tampak kontras mengisi fistel dengan bentuk suatu kantung pada cutan di daerah anal.</p>\r\n\r\n<p style=\"text-align:justify\">Tidak tampak fistel berhubungan dengan organ sekitarnya.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p><strong>Kesan : </strong></p>\r\n\r\n<ul>\r\n	<li><strong>Fistula cutocutaneus</strong></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Barium Follow&nbsp; Through Normal</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kontras dimasukkan peroral</p>\r\n\r\n<p>Tampak&nbsp; kontras&nbsp; dengan lancar mengisi gaster, duodenum dan yeyenum pada menit ke-5. Kontras tampak mengisi seluruh yeyenum dan sebagian ileum pada menit ke-15&nbsp; dan&nbsp; kontras tampak mengisi seluruh ileum sampai ke colon pada&nbsp; menit ke-45.</p>\r\n\r\n<p>Gaster, duodenum, yeyenum dan ileum :</p>\r\n\r\n<ul>\r\n	<li>Mukosa normal dinding reguler.</li>\r\n	<li>Tidak tampak filling defect maupun indentasi</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><u>Kesan </u></strong><strong>: </strong></p>\r\n\r\n<ul>\r\n	<li><strong>Tampak kontras mengisi gaster, duodenum, yeyenum,ileum dan ke dalam&nbsp; colon&nbsp; pada menit ke-45. </strong></li>\r\n	<li><strong>Pasase kontras terlalu cepat.</strong></li>\r\n	<li><strong>Tidak&nbsp; tampak&nbsp; tanda-tanda inflamsi/neoplasma di dalam usus halus</strong></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:center\"><strong>Barium Follow Through Abnormal</strong></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<p>Tampak kontras terlalu cepat sampai yeyenum tetapi kontras terlambat masuk kedalam caecum (3 jam belum masuk ke caecum) &egrave;&nbsp; Ada hambatan dari ileum terminalis ke caecum</p>\r\n\r\n<ul>\r\n	<li>Besar, bentuk, lokalisasi gaster, duodenum, yeyenum serta ileum masih normal.</li>\r\n	<li>Mukosa :\r\n	<ul>\r\n		<li>Gaster :</li>\r\n	</ul>\r\n	</li>\r\n</ul>\r\n\r\n<ul>\r\n	<li>Lipatan gaster tampak melebar dan menebal</li>\r\n	<li>Tidak jelas ulcus.\r\n	<ul>\r\n		<li>Duodenum\r\n		<ul>\r\n			<li>Bulbus duodeni : - Besar, bentuk normal</li>\r\n		</ul>\r\n		</li>\r\n	</ul>\r\n	</li>\r\n</ul>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - Mukosa kasar</p>\r\n\r\n<ul>\r\n	<li>\r\n	<ul>\r\n		<li>\r\n		<ul>\r\n			<li>C Loop :- Besar, bentuk normal</li>\r\n		</ul>\r\n		</li>\r\n	</ul>\r\n	</li>\r\n</ul>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - Mukosa tampak menebal</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - Tampak divertikel di pars II</p>\r\n\r\n<ul>\r\n	<li>\r\n	<ul>\r\n		<li>Yeyenum : - Mukosa kasar.</li>\r\n	</ul>\r\n	</li>\r\n</ul>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - Tampak divertikel</p>\r\n\r\n<ul>\r\n	<li>\r\n	<ul>\r\n		<li>Ileum :-&nbsp; Mukosa kasar, irreguler</li>\r\n	</ul>\r\n	</li>\r\n</ul>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; -&nbsp; Dibeberapa tempat tampak penyempitan.</p>\r\n\r\n<p><strong>Kesan : </strong></p>\r\n\r\n<ul>\r\n	<li><strong>Pasase kontras dari gaster ke yeyenum terlalu cepat (15 menit masuk yeyenum)</strong></li>\r\n	<li><strong>Gangguan pasase kontras dari ileum ke caecum (baru masuk 4 jam)</strong></li>\r\n	<li><strong>Striktur di beberapa tempat dari ileum (Inflamasi ? ec TB ?)</strong></li>\r\n	<li><strong>Gastritis, duodenitis, yeyenitis&nbsp; dan ileitis</strong></li>\r\n	<li><strong>Divertikel dari duodenum pars II dan di yeyenum</strong></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (151, 'ascites', '<p>Hepar&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; Sedikit membesar,densitas parenkhim kasar, tepi irreguler dan tumpul . Ductus biliaris intra-ekstra hepatal tidak lebar,, vena hepatika, vena porta tak melebar. Tampak koleksi cairan di sekitar hepar.</p>\r\n\r\n<p>Kt Empedu :&nbsp; Besar bentuk normal. Dinding menebal dan terlihat &ldquo;double layer &ldquo; Tidak tampak Tak tampak batu / SOL / sludge</p>\r\n\r\n<p>Limpa&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; Besar bentuk dlm batas normal. Textur parenkhim&nbsp; homogen.Tak tampak SOL. Vena Lienalis tak melebar.Tampak koleksi cairan di sekitar limpa</p>\r\n\r\n<p>Pankreas&nbsp;&nbsp;&nbsp; :&nbsp; Besar bentuk dlm batas normal. Textur homogen</p>\r\n\r\n<p>Paraaorta&nbsp;&nbsp;&nbsp; :&nbsp; Besar &amp; bentuk aorta abdominalis dalam batas normal.Tak tampak bayangan noduler hypo ataupun hyper echoic disekitar aorta abdominalis.</p>\r\n\r\n<p>Ginjal&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; Besar bentuk kedua ginjal normal. Batas textur parenkhim dg central echo complex normal. Tak tampak batu ataupun SOL</p>\r\n\r\n<p>Kt Kemih&nbsp;&nbsp; :&nbsp; Besar bentuk dinding dlm batas normal. Tak tampak batu ataupun SOL Tampak koleksi cairan di sekitar Vu</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; -Ascites massive disertai densitas hepar yang kasar dengan tepi </strong></p>\r\n\r\n<p><strong>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;irreguler</strong><strong>&agrave;</strong><strong> gambaran sirosis hepatis</strong></p>\r\n\r\n<p><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; -Cholecystitis</strong></p>\r\n\r\n<p><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; -USG limpa, pankreas, kedua ginjal, &amp; kt kemih dalam batas </strong></p>\r\n\r\n<p><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; normal.</strong></p>\r\n\r\n<p><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; -Tak tampak pembesaran KGB paraaorta.</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n', 'drganjar');
INSERT INTO `xray_template` VALUES (152, 'hernia_hydrokel', '<p><strong><u>Inguinal kiri</u></strong></p>\r\n\r\n<p>Tampak bayangan usus pada saat non valsava , dan pada saat valsava terlihat pergerakan usus melalui kanalis inguinal</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><u>Testis Kiri</u></strong></p>\r\n\r\n<p>Besar &amp; bentuk normal. Densitas homogen. Tampak sedikit koleksi cairan di sekitarnya</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kesan&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; -Gambaran hernia inguinalis kiri</p>\r\n\r\n<p><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; -Curiga hydrokel dengan koleksi cairan minimal</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n', 'drganjar');
INSERT INTO `xray_template` VALUES (153, 'massa', '<p>Hepar&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; Tidak membesar,tepi irreguler dan tumpul. Densitas parenkhim inhomogen. Tampak bay.massa isoekhoik, bulat , batas tegas, ukuran &plusmn; 4,66X4,52 Cm. Pada color doppler tidak tampak vaskularisaasi . Duktus biliaris intra dan ekstra hepatal tidak lebar. V.porta dan V.hepatica tidak lebar.</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Tampak koleksi cairan disektar hepar&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n\r\n<p>Kt Empedu :&nbsp; Besar bentuk dan dinding normal. Tak tampak batu ataupun SOL</p>\r\n\r\n<p>Limpa&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; Besar bentuk dlm batas normal. Textur parenkhim&nbsp; homogen.Tak tampak SOL. Vena Lienalis tak melebar. Tampak koleksi cairan di sekitar limpa</p>\r\n\r\n<p>Pankreas&nbsp;&nbsp;&nbsp; :&nbsp; Besar bentuk dlm batas normal. Textur homogen</p>\r\n\r\n<p>Paraaorta&nbsp;&nbsp;&nbsp; :&nbsp; Besar &amp; bentuk aorta abdominalis dalam batas normal.Tak tampak bayangan noduler hypo ataupun hyper echoic disekitar aorta abdominalis.</p>\r\n\r\n<p>Ginjal&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; Besar bentuk kedua ginjal normal. Batas textur parenkhim dg central echo complex normal. Tak tampak batu ataupun SOL</p>\r\n\r\n<p>Kt Kemih&nbsp;&nbsp; :&nbsp; Besar bentuk dinding dlm batas normal. Tak tampak batu ataupun SOL Tampak koleksi cairan di sekitarnya</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; -Massa padat hepar disertai ascites</strong></p>\r\n\r\n<p><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; -USG K.empedu, limpa, pankreas, kedua ginjal, &amp; kt kemih dalam </strong></p>\r\n\r\n<p><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; batas normal.</strong></p>\r\n\r\n<p><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; -Tak tampak pembesaran KGB paraaorta.</strong></p>\r\n', 'drganjar');
INSERT INTO `xray_template` VALUES (154, 'Elbow joint 2', '<p>ELBOW JOINT DEXTRA AP/LAT &nbsp;:</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Besar, bentuk dan struktur trabekula distal os humerus, proksimal os ulna dan radius dextra normal.</p>\r\n\r\n<p>Sela dan permukaan sendi normal.</p>\r\n\r\n<p>Tidak tampak fraktur dan dislokasi.</p>\r\n\r\n<p>Tidak tampak lesi litik maupun sklerotik.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan : </strong></p>\r\n\r\n<p><strong>Foto elbow joint dextra saat ini tidak tampak kelainan</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (155, 'usg_stl', '<p><strong>Soft tissue ar / abdomen kanan bawah</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tampak bayangan massa hipoekhoik, cenderung kistik, ukuran &plusmn; 1,45 X</p>\r\n\r\n<p>0,81 X 1,85 Cm. Posterior enhancement (+)</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; Massa kistik di soft tissue a/r abdomen kanan bawah</strong><strong>&agrave;</strong><strong> gambaran benigna</strong></p>\r\n', 'drganjar');
INSERT INTO `xray_template` VALUES (156, 'Soft tissue ar', '<p><strong><u>Soft tissue ar. Colli sinistra sisi lateral :</u></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li>Kutis sebagian tampak hiperemis</li>\r\n	<li>Subkutis sebagian irreguler</li>\r\n	<li>Tampak bayangan massa padat iso-hipoekhoik, batas cukup tegas,</li>\r\n</ul>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; tepi berbenjol, ukuran&nbsp; manual &plusmn; 10 x 8 cm. Pada color doppler</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; tampak hipervaskularisasi, kalsifikasi (+).</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; </strong><strong>Massa padat iso-hipoekhoik di colli sinistra suspek ec. </strong></p>\r\n\r\n<p><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; malignancy soft tisseu DD/</strong></p>\r\n', 'drganjar');
INSERT INTO `xray_template` VALUES (157, 'Elbow joint', '<p><strong><u>Elbow Joint</u></strong><strong><u> dextra :</u></strong></p>\r\n\r\n<ul>\r\n	<li>Besar bentuk dan struktur trabecula normal</li>\r\n	<li>Sela dan permukaan sendi normal</li>\r\n	<li>Tidak tampak lesi litik dan sklerotik</li>\r\n	<li>Tidak tampak osteofit</li>\r\n	<li>Tidak tampak fraktur</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong>Foto elbow joint dextra saat ini masih dalam batas normal</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (158, 'a', '<p style=\"text-align:justify\"><strong><em>Klinis : Stroke ulang kedua beda sisi&nbsp; ec infark sistem karotis kanan</em></strong></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">MSCT<strong><em> </em></strong>scan kepala dengan potongan axial slice interval 10 mm di daerah vertex dan 5 mm di daerah basis, <strong>tanpa memakai</strong> kontras.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">Jaringan lunak extracalvaria dan calvaria masih memberikan bentuk dan densitas yang normal.</p>\r\n\r\n<p style=\"text-align:justify\">Sulci&nbsp; corticalis, Fissura Sylvii , dan fissura interhemisfer tidak melebar.</p>\r\n\r\n<p style=\"text-align:justify\"><em>Tampak lesi hipodens berbatas tidak tegas di daerah cortical subcortical temporoparietalis kanan.</em></p>\r\n\r\n<p style=\"text-align:justify\">Ruang subarachnoid&nbsp; dalam batas normal.</p>\r\n\r\n<p style=\"text-align:justify\">Bentuk dan posisi ventrikel lateralis kanan dan kiri simetris. Ukuran ventrikel lateralis bilateral , ventrikel III dan ventrikel IV tidak melebar .</p>\r\n\r\n<p style=\"text-align:justify\">Sisterna ambiens&nbsp; dan sisterna basalis dalam batas normal.</p>\r\n\r\n<p style=\"text-align:justify\">Tidak tampak lesi yang memberikan densitas patologis pada parenkim cerebri lainnya, cerebellum dan pons</p>\r\n\r\n<p style=\"text-align:justify\">Daerah sela tursika dan juxtasella serta daerah &lsquo;cerebellopontine angle&rsquo; kanan dan kiri dalam batas normal.</p>\r\n\r\n<p style=\"text-align:justify\">Mastoid air cell dalam batas normal.</p>\r\n\r\n<p style=\"text-align:justify\">Sinus eithmoidalis, sphenoidalis, dan frontalis dalam batas normal.</p>\r\n\r\n<p style=\"text-align:justify\">Bulbus oculi dan ruang retrobulber dalam batas normal.</p>\r\n\r\n<p style=\"text-align:justify\">Tidak tampak pergeseran struktur garis tengah.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><strong><em>Kesimpulan :</em></strong></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><strong><em>Infark serebri di daerah cortical subcortical </em></strong><strong><em>temporoparietalis kanan ec. emboli</em></strong></li>\r\n</ul>\r\n', 'drganjar');
INSERT INTO `xray_template` VALUES (159, 'femur', '<p><strong><u>Femur sinistra :</u></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li>Besar bentuk dan struktur trabecula tulang normal</li>\r\n	<li>Tidak tampak lesi litik dan sklerotik</li>\r\n	<li>Tidak tampak fraktur</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; Foto Femur sinistra saat ini dalam batas normal.</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (160, 'Abdomen 3 posisi', '<p><strong><u>Abdomen 3 posisi</u></strong><strong> </strong><strong>&nbsp;</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Preperitoneal fat&nbsp; normal</p>\r\n\r\n<p>Psoas line normal</p>\r\n\r\n<p>Kontur kedua&nbsp; ginjal&nbsp; normal</p>\r\n\r\n<p>Tidak tampak&nbsp; konkremen opak di sepanjang traktus urinarius</p>\r\n\r\n<p>Tidak tampak distribusi udara berlebih di usus halus dan colon</p>\r\n\r\n<p>Tidak tampak air fluid level</p>\r\n\r\n<p>Tidak tampak free air</p>\r\n\r\n<p>Kesan :</p>\r\n\r\n<p>Abdomen 3 posisi&nbsp; dalam batas normal</p>\r\n', 'drganjar');
INSERT INTO `xray_template` VALUES (161, 'An Alrayen Ghani', '<p>Cor, sinuses dan diafragma normal</p>\r\n\r\n<p>Pulmo :&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Hilli normal</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Corakan bronkhovaskuler normal</p>\r\n\r\n<p>Tampak bercak lunak di lapang atas dan tengah paru kanan</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesa</strong><strong>n :</strong><strong>&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Gambaran Tb Paru aktif DD/ bronchopneumoni dekstra (Lab ?)</strong></p>\r\n\r\n<p><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Cor tidak tampak kelainan</strong></p>\r\n\r\n<p><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong></p>\r\n', 'drganjar');
INSERT INTO `xray_template` VALUES (162, 'genu', '<p><strong><u>Genu Sinistra AP/Lateral</u></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Besar, bentuk dan struktur trabekula tulang-tulang pembentuk genu sinistra dalam batas normal.</p>\r\n\r\n<p>Sela sendi dan permukaan sendi baik.</p>\r\n\r\n<p>Eminentia intercodyloidea tidak meruncing.</p>\r\n\r\n<p>Tidak tampak lesi litik dan sklerotik.</p>\r\n\r\n<p>Tidak tampak fraktur dan dislokasi.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; </strong><strong> </strong><strong>Foto genu sinistra saat ini dalam batas normal.</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (163, 'Ankle joint', '<p>&nbsp;</p>\r\n\r\n<p><strong>Ankle Joint </strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Besar, bentuk dan struktur trabekula normal</p>\r\n\r\n<p>Sela dan permukaaan sendi normal</p>\r\n\r\n<p>Tidak tampak lesi litik/sklerotik</p>\r\n\r\n<p>Tidak tampak osteofit</p>\r\n\r\n<p>Tidak tampak fraktur</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan :</strong></p>\r\n\r\n<ul>\r\n	<li><strong>Ankle joint</strong><strong> </strong><strong>tidak tampak kelainan</strong></li>\r\n</ul>\r\n', 'drganjar');
INSERT INTO `xray_template` VALUES (164, 'Antebrachi', '<p><strong><u>Antebrachi :</u></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li>Besar,bentuk dan struktur trabekula normal</li>\r\n	<li>Tidak tampak lesi litik / sklerotik</li>\r\n	<li>Tidak tampak garis fraktur</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan: &nbsp;&nbsp;&nbsp;&nbsp; Foto antebrachi saat ini dalam batas normal</strong></p>\r\n', 'drganjar');
INSERT INTO `xray_template` VALUES (165, 'BNO 1', '<p><strong>BNO</strong><strong> </strong><strong>&nbsp;:<em> </em></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Pre peritoneal fat normal, psoas line jelas dan kontur kedua ginjal tidak jelas</p>\r\n\r\n<p>Tidak tampak bayangan konkremen opak di sepanjang traktus urinarius</p>\r\n\r\n<p>Tidak tampak distribusi udara berlebih di usus halus dan collon</p>\r\n\r\n<p>Skeletal normal</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan : </strong></p>\r\n\r\n<p>Foto BNO saat ni tidak tampak kelainan</p>\r\n', 'drganjar');
INSERT INTO `xray_template` VALUES (166, 'BNO 2', '<p><strong>BNO</strong><strong> </strong><strong>&nbsp;:<em> </em></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Pre peritoneal fat normal, psoas line &nbsp;dan kontur kedua ginjal tidak jelas</p>\r\n\r\n<p>Tidak tampak bayangan konkremen opak di sepanjang traktus urinarius</p>\r\n\r\n<p>Tidak tampak distribusi udara berlebih di usus halus dan colon</p>\r\n\r\n<p>Skeletal normal</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan : </strong><strong>BNO tidak tampak kelainan</strong></p>\r\n', 'drganjar');
INSERT INTO `xray_template` VALUES (167, 'BNO 11', '<p><strong>BNO</strong><strong> </strong><strong>&nbsp;:<em> </em></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Pre peritoneal fat normal, psoas line &nbsp;dan kontur kedua ginjal tidak jelas</p>\r\n\r\n<p>Tidak tampak bayangan konkremen opak di sepanjang traktus urinarius</p>\r\n\r\n<p>Tidak tampak distribusi udara berlebih di usus halus dan colon</p>\r\n\r\n<p>Skeletal normal</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan : </strong><strong>BNO tidak tampak kelainan</strong></p>\r\n', 'drganjar');
INSERT INTO `xray_template` VALUES (168, 'BNO', '<p>&nbsp;</p>\r\n\r\n<p><strong>Thorax </strong></p>\r\n\r\n<p>Cor tidak membesar.</p>\r\n\r\n<p>Sinuses dan diafragma normal.</p>\r\n\r\n<p>Pulmo : Hilli normal.</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Corakan bronkhovaskuler normal.</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Tidak tampak infiltrat. &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n\r\n<p><strong>Kesan : Pulmo saat ini tidak tampak kelainan</strong></p>\r\n\r\n<p><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;Tidak tampak kardiomegali</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>BNO</strong><strong> </strong></p>\r\n\r\n<p>Pre peritoneal fat normal, psoas line &nbsp;dan kontur kedua ginjal tidak jelas</p>\r\n\r\n<p>Tampak konkremen opak multipel di rongga pelvis</p>\r\n\r\n<p>Tidak tampak distribusi udara berlebih di usus halus dan colon</p>\r\n\r\n<p>Skeletal normal</p>\r\n\r\n<p><strong>Kesan : </strong><strong>Vesicolithiasis dan suspek ureterolithiasis kiri</strong></p>\r\n', 'drganjar');
INSERT INTO `xray_template` VALUES (169, 'HSG', '<p><strong><u>HSG </u></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><u>Plain Foto :</u></strong></p>\r\n\r\n<p>Daerah pelvis tidak tampak kelainan, tidak tampak konkremen opak atau kalsifiaksi patologis.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><u>HSG :</u></strong></p>\r\n\r\n<p>Dengan menggunakan kateter HSG, kontras sebanyak 4 cc di masukkan ke dalam cavum uteri. Tampak kontras mengisi cavum uteri dan tuba kiri tetapi belum terlihat spill di tuba kiri. Tuba kanan belum terisi kontras. Ditambahkan kontras sebanyak 4 cc, tuba kanan mulai terlihat terisi kontras tetapi tidak terlihat spill dan tuba kiri tetap belum terlihat spill</p>\r\n\r\n<p>Ditambahkan kontras lagi sebanyak 2 cc, tuba kanan dan kiri tetap tidak spill.</p>\r\n\r\n<p>Cavum uteri normal, tidak tampak filling defect ataupun affect</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan :<br />\r\nTuba kanan dan kiri tidak patent</strong></p>\r\n\r\n<p><strong>Uterus saat ini tampak dalam batas normal</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (170, 'Bunyamin Bin Ubed, Tn, Ct Abd Nk, hidronefrosis sin', '<p>Dilakukan pemeriksaan MSCT/CT scan abdomen sampai pelvis potongan axial. Dibuat rekonstruksi potongan koronal dan sagital. Scanning tanpa memakai kontras</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><em><u>Scanning daerah :</u></em></strong></p>\r\n\r\n<p>Hepar&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;: Ukuran tidak membesar, permukaan rata, densitas homogeny. Tidak tampak massa. Vena porta&nbsp;</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; tidak melebar. Vena hepatica tidak melebar. Vena cava inferior tidak melebar. Duktus biliaris</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; intrahepatal tidak melabar.Tidak tampak koleksi cairan di sekitarnya</p>\r\n\r\n<p>Kt empedu&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :Ukuran tidak membesar, dinding tidak menebal, regular. Tidak tampak massa/batu. Duktus</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp; biliaris ekstrahepatal tidak melebar</p>\r\n\r\n<p>Limpa&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp; :Ukuran tidak membesar, densitas homogeny. Vena lienalis tidak melebar. Tidak tampak&nbsp;</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; koleksi&nbsp; cairan di sekitarnya</p>\r\n\r\n<p>Pankreas&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp; :Ukuran tidak membesar, densitas homogeny, ductus pankreatikus tidak melebar</p>\r\n\r\n<p>Ginjal kanan &nbsp; :Ukuran tidak membesar, struktur parenkhim homogen, system pelvokalises tidak melebar, &nbsp;</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <em>Tampak lesi hipodens batas tegas tepi reguler berdiameter lk 26.5 mm di pole atas ginjal </em></p>\r\n\r\n<p><em>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; kanan. Tampak lesi hiperdens berdiameter lk 4.1 mm di pole tengah ginjal kanan.</em></p>\r\n\r\n<p>Ginjal kiri&nbsp; &nbsp; &nbsp;&nbsp;&nbsp;:Ukuran tidak membesar, struktur parenkhim homogen, <em>system pelvokalises melebar</em><em> dengan &nbsp;&nbsp;</em></p>\r\n\r\n<p><em>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; dilatasi ureter</em><em>, </em><em>Tampak lesi hiperdens berdiameter lk 7.6 mm pada 1/3 distal ureter kiri. </em></p>\r\n\r\n<p><em>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Tampak lesi hipoechoic multiple batas tegas tepi reguler berdiameter terbesar lk 16.0 mm.</em>&nbsp;&nbsp;</p>\r\n\r\n<p>Tidak tampak bayangan hipodens di paraaorta dan parailiaka</p>\r\n\r\n<p>Tidak tampak kalsifikasi pada aorta abdominalis, arteri iliaka komunis, arteri iliaka eksterna dan interna</p>\r\n\r\n<p>Vesica urinaria: Terisi penuh, Tidak tampak batu/massa. Perivesical fat normal</p>\r\n\r\n<p>Prostat&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp; &nbsp;&nbsp;&nbsp; :<em>Ukuran membesar</em>, densitas homogen.</p>\r\n\r\n<p>Rektum&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp; :Posisi normal, dinding tidak menebal, perirectal fat normal</p>\r\n\r\n<p>Appendix&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; : Tampak gambaran tubuler buntu berdiameter lk 2.5-3.8 mm, tidak tampak infiltrat</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; disekitarnya&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n\r\n<p>Struktur gastrointestinal tidak tampak kelainan, divertikel, polip atau kolitis</p>\r\n\r\n<p>Tidak terlihat koleksi cairan bebas intrabdomen</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>KESAN&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; : </strong></p>\r\n\r\n<p><strong><em>Kista ginjal kanan</em></strong></p>\r\n\r\n<p><strong><em>Nefrolithiasis kanan</em></strong></p>\r\n\r\n<p><strong><em>Pelvokaliektasis ginjal kiri dengan dilatasi ureter kiri ec ureterolithiasis pada 1/3 distal ureter kiri</em></strong></p>\r\n\r\n<p><strong><em>Pembesaran prostat</em></strong></p>\r\n\r\n<p><strong><em>Appendix tervisualisasi, tidak tampak infiltrat disekitarnya.</em></strong></p>\r\n\r\n<p><strong><em>Hepar, </em></strong><strong><em>kt. empedu, limpa,</em></strong><strong><em> pankreas</em></strong><strong><em>, dan VU </em></strong><strong><em>tidak dijumpai kelainan</em></strong></p>\r\n\r\n<p><strong><em>Struktu</em></strong><strong><em>r</em></strong><strong><em> gastrointestinal tidak tampak kelainan, divertikel, polip atau kolitis</em></strong></p>\r\n\r\n<p><strong><em>Tidak terlihat koleksi cairan bebas intrabdomen</em></strong></p>\r\n\r\n<p><strong><em>Tidak tampak pembearan KGB paraaorta</em></strong></p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n', 'drganjar');
INSERT INTO `xray_template` VALUES (171, 'Calcaneus', '<p><strong><u>Calcaneus &nbsp;</u></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li>Besar bentuk dan struktur trabecula normal</li>\r\n	<li>Tidak tampak lesi litik / sklerotik</li>\r\n	<li>Tidak tampak fraktur</li>\r\n	<li>Tidak tampak osteofit</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; : Foto </strong><strong>calcaneus</strong><strong> saat ini masih dalam batas normal</strong></p>\r\n', 'drganjar');
INSERT INTO `xray_template` VALUES (172, 'Cardiomegalia Ringan', '<p><strong><u>Thorax</u></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kardiomegali ringan.</p>\r\n\r\n<p>Sinuses dan Diafragma normal.</p>\r\n\r\n<p>Pulmo&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; Hilli normal.</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Corakan bronkhovaskuler normal.</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Tidak tampak infiltrat.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; </strong><strong>K</strong><strong>ardiomegali </strong><strong>r</strong><strong>ingan</strong><strong> (EKG?)</strong></p>\r\n\r\n<p><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Pulmo tidak tampak kelainan.</strong></p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;</p>\r\n', 'drganjar');
INSERT INTO `xray_template` VALUES (173, 'Cardiomegalia', '<p><strong><u>Thorax</u></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kardiomegali ringan</p>\r\n\r\n<p>Sinuses dan difragma normal</p>\r\n\r\n<p>Pulmo&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; Hilli normal</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Corakan bronkhovaskuler normal</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp; Tidak tampak infiltrat</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n\r\n<p><strong>Kesan&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; </strong><strong>K</strong><strong>ardiomegali ringan (EKG?)</strong></p>\r\n\r\n<p><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>&nbsp;&nbsp; Pulmo tidak tampak kelainan</strong></p>\r\n\r\n<p><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong></p>\r\n', 'drganjar');
INSERT INTO `xray_template` VALUES (174, 'Cecep Dichy Iskandar', '<p>Ts.Yth,</p>\r\n\r\n<p>Dilakukan pemeriksaan MSCT whole abdomen tanpa dan dengan kontras potongan aksial, coronal dan sagital</p>\r\n\r\n<p>mulai dari diafragma sampai perineum, sbb;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>HEPAR&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; : Bentuk dan ukuran normal, permukaan rata, parenkhim normal. Tidak dijumpai massa solid atau &nbsp;</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; kistik. Sistem billier dan vaskuler normal</p>\r\n\r\n<p>V.FELEA&nbsp;&nbsp;&nbsp; : Bentuk dan ukuran normal, dinding reguler. Tidak dijumpai batu hiperdens. Tidak tampak SOL</p>\r\n\r\n<p>PANKREAS: Bentuk, ukuran dan struktur parenkhim normal, tidak tampak lesi pathologis</p>\r\n\r\n<p>LIEN&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; : Bentuk, ukuran dan struktur parenkhim normal, tidak tampak lesi pathologis</p>\r\n\r\n<p>GINJAL&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; : Kedua ginjal, besar dan bentuk normal, fungsi baik, sistem pelviokalises tidak</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; melebar. Tidak dijumpai batu/SOL</p>\r\n\r\n<p>VU&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; : Kurang terisi.Tidak dijumpai batu/SOL</p>\r\n\r\n<p>PROSTAT&nbsp; : Bentuk, ukuran dan struktur parenkhim normal, tidak tampak lesi pathologis</p>\r\n\r\n<p>AORTA&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; : Bentuk dan kaliber normal. Tidak tampak pembesaran KGB</p>\r\n\r\n<p>APPENDIK : Tampak membesar</p>\r\n\r\n<p>Tampak KGB multipel di mesenterial</p>\r\n\r\n<p>Tampak koleksi cairan intraabdomen</p>\r\n\r\n<p>Pada thorak yang terscanning : setelah pemberian cairan peroral tampak esofagus bagian distal tampak</p>\r\n\r\n<p>melebar dan terisi cairan.</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n\r\n<p>KESAN&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; : Appendicitis</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp; Asites</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; KGB multipel di mesenterial</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp; Dilatasi esofagus bagian distal ec suspek gastroesofageal refluks</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp; Hepar, vesika&nbsp; felea, lien, pankreas, ginjal kanan-kiri, VU, prostat tidak dijumpai kelainan</p>\r\n', 'drganjar');
INSERT INTO `xray_template` VALUES (175, 'Cervical', '<p>&nbsp;</p>\r\n\r\n<p><u>Cervical</u><u> AP+ lateral kiri</u><u> :</u></p>\r\n\r\n<ul>\r\n	<li>Kurva&nbsp; lurus dan alignment baik</li>\r\n	<li>Besar, bentuk dan struktur trabekula vertebrae cervicalis dalam batas normal</li>\r\n	<li>Curiga foramen intervertebralis C4-5 dan C5-6 menyempit</li>\r\n	<li>Pedicle baik</li>\r\n	<li>Tidak tampak lesi litik dan sklerotik</li>\r\n	<li>Tidak tampak fraktur.</li>\r\n	<li>Tidak tampak osteofit</li>\r\n</ul>\r\n\r\n<p><strong>Kesan : &nbsp;&nbsp;&nbsp; </strong>Foto vertebra cervicalis saat ini masih dalam batas normal</p>\r\n', 'drganjar');
INSERT INTO `xray_template` VALUES (176, 'Clavicula normal', '<p><strong><u>Clavicula </u></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li>Besar bentuk dan struktur trabecula clavicula normal</li>\r\n	<li>Tidak tampak lesi litik / sklerotik</li>\r\n	<li>Tidak tampak fraktur</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; : Foto clavicula saat ini masih dalam batas normal</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n', 'drganjar');
INSERT INTO `xray_template` VALUES (177, 'cruris - Copy', '<p><strong><u>Cruris</u></strong><strong><u> Sinistra</u></strong><strong><u> </u></strong><strong><u>: </u></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Dibandingkan dengan foto tanggal 18-05-2016:</p>\r\n\r\n<p>Terpasang fiksasi interna pada tibia sinistra, posisi stabil, posisi tulang baik, kalus (+), masih tampak sedikit garis fraktur</p>\r\n', 'drganjar');
INSERT INTO `xray_template` VALUES (178, 'cruris', '<p><strong><u>Cruris</u></strong><strong><u>&nbsp; </u></strong><strong><u>&nbsp;</u></strong><strong><u>: </u></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Besar bentuk dan struktur trabecula tibia dan fibula normal</p>\r\n\r\n<p>Sela dan permukaan sendi normal</p>\r\n\r\n<p>Tidak tampak lesi litik dan sklerotik</p>\r\n\r\n<p>Tidak tampak osteofit</p>\r\n\r\n<p>Tidak tampak fraktur</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong>Cruris saat ini masih dalam batas normal</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;</p>\r\n', 'drganjar');
INSERT INTO `xray_template` VALUES (179, 'CT Abdomen pria', '<p>Dilakukan pemeriksaan MSCT/CT scan abdomen sampai pelvis potongan axial. Dibuat rekonstruksi potongan koronal dan sagital. Scanning dengan memakai kontras oral,I.V., dan per rektal</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><em><u>Scanning daerah :</u></em></strong></p>\r\n\r\n<p>Hepar&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;: Ukuran tidak membesar, permukaan rata, densitas homogeny. Tidak tampak massa. Vena porta&nbsp;</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; tidak melebar. Vena hepatica tidak melebar. Vena cava inferior tidak melebar. Duktus biliaris</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; intrahepatal tidak melabar.Tidak tampak koleksi cairan di sekitarnya</p>\r\n\r\n<p>Kt empedu&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :Ukuran tidak membesar, dinding tidak menebal, regular. Tidak tampak massa/batu. Duktus</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp; biliaris ekstrahepatal tidak melebar</p>\r\n\r\n<p>Limpa&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp; :Ukuran tidak membesar, densitas homogeny. Vena lienalis tidak melebar. Tidak tampak&nbsp;</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;koleksi&nbsp; cairan di sekitarnya</p>\r\n\r\n<p>Pankreas&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp; :Ukuran tidak membesar, densitas homogeny, ductus pankreatikus tidak melebar</p>\r\n\r\n<p>Ginjal kanan &nbsp; :Ukuran tidak membesar, struktur parenkhim homogen, system pelvokalises tidak melebar, tidak</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;tampak batu/massa</p>\r\n\r\n<p>Ginjal kiri&nbsp; &nbsp; &nbsp;&nbsp;&nbsp;:Ukuran tidak membesar, struktur parenkhim homogen, system pelvokalises tidak melebar,</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; tidak tampak batu/massa</p>\r\n\r\n<p>Tidak tampak bayangan hipodens dengan ring enhancement di paraaorta dan parailiaka</p>\r\n\r\n<p>Tidak tampak kalsifikasi pada aorta abdominalis, arteri iliaka komunis, arteri iliaka eksterna dan interna</p>\r\n\r\n<p>Vesica urinaria: Terisis penuh, Tidak tampak batu/massa. Perivesical fat normal</p>\r\n\r\n<p>Prostat&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp; &nbsp;&nbsp;&nbsp; :Ukuran tidak membesar, densitas homogen.</p>\r\n\r\n<p>Rektum&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp; :Posisi normal, dinding tidak menebal, perirectal fat normal</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>KESAN&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; : Hepar, kt. empedu, limpa, pankreas, ginjal kanan-kiri dan VU tidak dijumpai kelainan</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Struktur gastrointestinal tidak tampak kelainan, divertikel, polip atau kolitis</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Tidak terlihat koleksi cairan bebas intrabdomen</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Tidak tampak pembearan KGB paraaorta</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n', 'drganjar');
INSERT INTO `xray_template` VALUES (180, 'CT Angio Ny. Ayi rohaeni', '<p><u>CT Angiografi</u></p>\r\n\r\n<p>Dilakukan MSCT angigrafi cerebal, di lakukan rekonstruksi pembuluh darah kepala :</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li>Tampak pelebaran berbentuk kantong di a.cerebri anterior kanan ukuran 15,6x5,6mm</li>\r\n	<li>Tampak pelebaran berbentuk kantong multipel di a. cerebri posterior kanan ukuran terbesar</li>\r\n</ul>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 11,2x27,3mm</p>\r\n\r\n<ul>\r\n	<li>Besar, bentuk dan posisi a. carotis interna kanan-kiri, a. cereberi media kanan-kiri, a. cerebri</li>\r\n</ul>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; anterior kiri masih dalam batas normal</p>\r\n\r\n<ul>\r\n	<li>Besar, bentuk dan posisi a. cerebri posterior kiri masih dalam batas normal.</li>\r\n	<li>Besar, bentuk dan posisi a. vertebralis kanan-kiri, a. basilaris masih dalam batas normal</li>\r\n	<li>Besar, bentuk dan posisi a. communicating anterior dan posterior, a. perocallosa masih dalam batas</li>\r\n</ul>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; normal</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kesimpulan :</p>\r\n\r\n<p><strong>Karakteristik suatu gambaran Aneurisma a. cerebri anterior kanan dan a. cerebri posterior kanan</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Saran : DSA</strong></p>\r\n', 'drganjar');
INSERT INTO `xray_template` VALUES (181, 'CT Angio Ny. Teti', '<p><u>CT Angiografi</u></p>\r\n\r\n<p>Dilakukan MSCT angigrafi cerebal, di lakukan rekonstruksi pembuluh darah kepala :</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li>Tampak pelebaran berbentuk kantong di a.cerebri anterior kiri segmen A1</li>\r\n</ul>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Besar, bentuk dan posisi a. carotis interna kanan-kiri, a. cereberi media kanan-kiri, a. cerebri</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; anterior kanan masih dalam batas normal</p>\r\n\r\n<ul>\r\n	<li>Besar, bentuk dan posisi a. cerebri posterior bilateral masih dalam batas normal.</li>\r\n	<li>Besar, bentuk dan posisi a. vertebralis kanan-kiri, a. basilaris masih dalam batas normal</li>\r\n	<li>Besar, bentuk dan posisi a. communicating anterior dan posterior, a. perocallosa masih dalam batas</li>\r\n</ul>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; normal</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kesimpulan :</p>\r\n\r\n<ul>\r\n	<li><strong>Karakteristik suatu gambaran Aneurisma a. cerebri anterior (segmen A1)</strong></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Saran : DSA</strong></p>\r\n', 'drganjar');
INSERT INTO `xray_template` VALUES (182, 'humerus', '<p><strong><u>Humerus</u></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li>Besar bentuk dan struktur trabecula tulang normal</li>\r\n	<li>Tidak tampak lesi litik dan sklerotik</li>\r\n	<li>Tidak tampak fraktur</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; Foto Humerus saat ini dalam batas normal.</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (183, 'CT Angio Tn. Husni', '<p><u>CT Angiografi</u></p>\r\n\r\n<p>Dilakukan MSCT angigrafi cerebal, di lakukan rekonstruksi pembuluh darah kepala :</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li>Tampak pelebaran berbentuk kantong multipel ukuran 3,1x1,9mm dan 2,0x2,9x2,0mm di</li>\r\n</ul>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; a. cerebri posterior kanan</p>\r\n\r\n<ul>\r\n	<li>Besar, bentuk dan posisi a. carotis interna kanan-kiri, a. cereberi media kanan-kiri, a. cerebri</li>\r\n</ul>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; anterior kanan dan kiri masih dalam batas normal</p>\r\n\r\n<ul>\r\n	<li>Besar, bentuk dan posisi a. cerebri posterior kirimasih dalam batas normal.</li>\r\n	<li>Besar, bentuk dan posisi a. vertebralis kanan-kiri, a. basilaris masih dalam batas normal</li>\r\n	<li>Besar, bentuk dan posisi a. communicating anterior dan posterior, a. perocallosa masih dalam batas</li>\r\n</ul>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Normal</p>\r\n\r\n<p>Tampak massa isodens dengan perdarahan disertai edema disekitarnya di daerah frontalis kiri</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kesimpulan :</p>\r\n\r\n<ul>\r\n	<li><strong>Karakteristik suatu gambaran Aneurisma a. cerebri posterior kanan multipel &nbsp;</strong></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Saran : DSA</strong></p>\r\n', 'drganjar');
INSERT INTO `xray_template` VALUES (184, 'CT Angio Tn. Muslihat', '<p><u>CT Angiografi</u></p>\r\n\r\n<p>Dilakukan MSCT angigrafi cerebal, di lakukan rekonstruksi pembuluh darah kepala :</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li>Tampak hubungan antara arteri yang melebar di permukaan kortex cerebri dengan vena yang</li>\r\n</ul>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; melebar di temporalis kiri</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Besar, bentuk dan posisi a. carois interna kanan-kiri, a. cereberi media kanan-kiri, a. cerebri</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; anterior kanan-kiri masih dalam batas normal</p>\r\n\r\n<ul>\r\n	<li>Besar, bentuk dan posisi a. cerebri posterior bilateral masih dalam batas normal.</li>\r\n	<li>Besar, bentuk dan posisi a. vertebralis kanan-kiri, a. basilaris masih dalam batas normal</li>\r\n	<li>Besar, bentuk dan posisi a. communicating anterior dan posterior, a. perocallosa masih dalam batas</li>\r\n</ul>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; normal</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kesimpulan :</p>\r\n\r\n<ul>\r\n	<li><strong>Curiga suatu </strong><strong>Pial AVF</strong></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Saran : DSA</strong></p>\r\n', 'drganjar');
INSERT INTO `xray_template` VALUES (185, 'CT Angio Tn. Yayan', '<p><u>CT Angiografi</u></p>\r\n\r\n<p>Dilakukan MSCT angigrafi cerebal, di lakukan rekonstruksi pembuluh darah kepala :</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tampak lesi hiperdens di daerah temporalis kanan lobus inferior</p>\r\n\r\n<p>Tampak lesi hiperdens di daerah temporalis kanan, fisurra sylvii kanan</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li>Tampak pelebaran berbentuk kantong multipel ukuran 2,6x3,4mm dan 2,9x2,0mm di a.cerebri</li>\r\n</ul>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; media kanan segmen M1&nbsp;</p>\r\n\r\n<ul>\r\n	<li>Besar, bentuk dan posisi a. carotis interna kanan-kiri, a. cereberi media kiri, a. cerebri</li>\r\n</ul>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; anterior kanan dan kiri masih dalam batas normal</p>\r\n\r\n<ul>\r\n	<li>Besar, bentuk dan posisi a. cerebri posterior bilateral masih dalam batas normal.</li>\r\n	<li>Besar, bentuk dan posisi a. vertebralis kanan-kiri, a. basilaris masih dalam batas normal</li>\r\n	<li>Besar, bentuk dan posisi a. communicating anterior dan posterior, a. perocallosa masih dalam batas</li>\r\n</ul>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; normal</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kesimpulan :</p>\r\n\r\n<ul>\r\n	<li><strong>Karakteristik suatu gambaran Aneurisma a. cerebri media kanan multipel &nbsp;(segmen M1) </strong></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Saran : DSA</strong></p>\r\n', 'drganjar');
INSERT INTO `xray_template` VALUES (186, 'Kardiomegali  dengan apex membulat di atas diafragma.', '<p>Kardiomegali&nbsp; dengan apex membulat di atas diafragma.<br />\r\nSisinuses dan diafragma normal<br />\r\nPulmo : -&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Hilli normal<br />\r\nCorakan bronkhovaskuler normal<br />\r\nTidak tampak infiltrat<br />\r\nKranialisasi (-)</p>\r\n\r\n<p><br />\r\nKesan : &nbsp;&nbsp;&nbsp; &nbsp;Kardiomegali tanpa bendungan paru<br />\r\n&nbsp;&nbsp; &nbsp; Pulmo tidak tampak kelainan</p>\r\n\r\n<p>&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (187, 'Kardiomegali (20)', '<p>Cor membesar</p>\r\n\r\n<p>Sinuses dan diafragma normal.</p>\r\n\r\n<p>Pulmo&nbsp; : Hilli normal</p>\r\n\r\n<p>&nbsp; Corakan bronkhovaskuler normal</p>\r\n\r\n<p>&nbsp; Tidak tampak infiltrate</p>\r\n\r\n<p>&nbsp; Tampak garis kras di lapang bawah kanan</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan&nbsp; : </strong></p>\r\n\r\n<ul>\r\n	<li><strong>Kardiomegali tanpa bendungan paru.</strong></li>\r\n	<li><strong>Pulmo saat ini tidak tampak kelainan</strong></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (188, 'Kardiomegali, membulat', '<p><strong><u>Thorak :</u></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Cor membesar ke lateral kiri</p>\r\n\r\n<p>Sinuses dan diafragma normal</p>\r\n\r\n<p>Pulmo : &nbsp;&nbsp;</p>\r\n\r\n<p>Hilli normal.</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Corakan bronkhovaskuler normal</p>\r\n\r\n<p>Tidak tampak infiltrat</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Kranialisasi (-)</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Kesan</strong><strong> </strong><strong>: </strong><strong>&nbsp;Kardiomegali tanpa bendungan paru</strong></p>\r\n\r\n<p><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Pulmo dalam batas normal</strong></p>\r\n\r\n<p><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong></p>\r\n\r\n<p>&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (189, 'Knee joint (dede binti Ano) tear lig suprapatellaris', '<p style=\"text-align:justify\"><em>Dilakukan MRI genu sinistra dengan potongan Sagital T1WI, Coronal-Sagital T2WI, axial-coronal-sagital T2 Fatsat, axial T2 SPAIR, Coronal-Sagital PDWI, Coronal-sagital T2 mDixon. Scanning dilakukan tanpa media kontras. </em></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><em>&bull;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Posisi dan&nbsp; bentuk tulang proksimal tibia, fibula, dan patella tampak baik dengan intensitas signal masih tampak normal. </em></p>\r\n\r\n<p style=\"text-align:justify\"><em>&bull;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Tampak ligamentum suprapatellaris terlihat inhomogen yaitu terlihagaris isointens pada T1WI dan hiperintens pada T2WI dan T2STIR</em></p>\r\n\r\n<p style=\"text-align:justify\"><em>&bull;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Tampak gambaran hipointens pada T1WI dan hiperintens pada T2WI dan T2 FatSat di daerah bursa suprapatellaris dan di bursa poplitea.</em></p>\r\n\r\n<p style=\"text-align:justify\"><em>&bull;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Tampak fokal hipointens dengan batas tegas pada T1W terlihat slight hiprintens pada T2WI dan hiperintens pada T2STIR di proksimal os tibia sisi medial</em></p>\r\n\r\n<p style=\"text-align:justify\"><em>&bull;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Meniscus medial dan meniscus lateralis, masih memberikan bentuk, kontur, dan intensitas sinyal yang normal.</em></p>\r\n\r\n<p style=\"text-align:justify\"><em>&bull;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Ligamentum cruciatum anterior, ligamentum cruciatum posterior, ligamentum kolateral medial dan ligamentum lateral masih memberikan bentuk, kontur, dan intensitas sinyal yang normal.</em></p>\r\n\r\n<p style=\"text-align:justify\"><em>&bull;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Ligamentum infrapatellaris masih memberikan bentuk, kontur, dan intensitas sinyal yang normal.</em></p>\r\n\r\n<p style=\"text-align:justify\"><em>&bull;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Jaringan lunak di sekitar genu dan struktur vaskular yang terlihat masih tampak normal.</em></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><em>Kesan: </em></p>\r\n\r\n<p style=\"text-align:justify\"><em>- &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Gambaran tars di ligamentum suprapatellaris</em></p>\r\n\r\n<p style=\"text-align:justify\"><em>- &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Koleksi cairan di daerah bursa suprapatellari dan bursa poplitea (bursitis)</em></p>\r\n\r\n<p style=\"text-align:justify\"><em>- &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Fokal hipointens dengan batas tegas pada T1WI, slight hiper9ntens pada T2WI dan </em></p>\r\n\r\n<p style=\"text-align:justify\"><em>&nbsp;&nbsp; hiperintens pada T2STIR di proskimal os tibia sisi medial --&gt; suspek intracortical </em></p>\r\n\r\n<p style=\"text-align:justify\"><em>&nbsp;&nbsp; osteid&nbsp; osteoma&nbsp; DD/intracortical hemangioma</em></p>\r\n\r\n<p style=\"text-align:justify\"><em>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; brodie&#39;s abses</em></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><em>Saran : MRI dengan kontras</em></p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (190, 'Knee Joint', '<p><strong><u>Knee Joint</u></strong><strong><u> </u></strong><strong><u>&nbsp;:</u></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li>Besar, bentuk dan struktur trabekula tulang baik</li>\r\n	<li>Sela sendi dan permukaan sendi baik</li>\r\n	<li>Eminentia intercodyloidea tidak meruncing.</li>\r\n	<li>Tidak tampak lesi litik dan sklerotik</li>\r\n	<li>Tidak tampak fraktur dan dislokasi.</li>\r\n	<li>Tidak tampak osteofit</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; </strong><strong> </strong><strong>Foto Knee Joint saat ini dalam batas normal</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (191, 'Kusnadi', '<p>MSCT<strong><em> </em></strong>scan kepala dengan potongan axial, <strong>tanpa memakai</strong> kontras.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Jaringan lunak extracalvaria dan calvaria masih memberikan bentuk dan densitas yang normal.</p>\r\n\r\n<p><em>Tampak lesi hiperdens yang mengisi fisura interhemisfer, sulci corticalis didaerah frontotemporoparietalis bilateral, fissura sylvii bilateral. Sisterna ambiens dan sisterna basalis.</em></p>\r\n\r\n<p><em>Sulci&nbsp; corticalis, Fissura Sylvii , dan fissura interhemisfer tampak menyempit.</em></p>\r\n\r\n<p>Parenkim cerebri dan cerebelli tidak tampak kelainan</p>\r\n\r\n<p>Ruang subarachnoid&nbsp; dalam batas normal.</p>\r\n\r\n<p>Bentuk dan posisi ventrikel lateralis kanan dan kiri simetris. Ukuran ventrikel lateralis bilateral, ventrikel III &amp; Ventrikel IV tidak melebar .</p>\r\n\r\n<p>Daerah sela tursika dan juxtasella serta daerah &lsquo;cerebellopontine angle&rsquo; kanan dan kiri dalam batas normal.</p>\r\n\r\n<p>Mastoid air cell dalam batas normal.</p>\r\n\r\n<p>Sinus eithmoidalis, sphenoidalis dan frontalis dalam batas normal.</p>\r\n\r\n<p>Bulbus oculi dan ruang retrobulber dalam batas normal.</p>\r\n\r\n<p>Tidak tampak pergeseran struktur garis tengah.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><em>Kesimpulan :</em></strong></p>\r\n\r\n<ul>\r\n	<li><strong><em>Perdarahan subarachnoid </em></strong><strong><em>yang mengisi fisura interhemisfer, sulci corticalis didaerah frontotemporoparietalis bilateral, fissura sylvii bilateral. Sisterna ambiens dan sisterna basalis.</em></strong></li>\r\n	<li><strong><em>Edema cerebri.</em></strong></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (192, 'LS liisthesis', '<p>Vertebra lumbosacral :</p>\r\n\r\n<p>Curve lurus dan skoliosis ringan vertebra lumbalis ke kiri<br />\r\nMalalignment VL5 dan S1 ke posterior<br />\r\nBesar, bentuk dan struktur trabekula normal<br />\r\nForamen intervertebralis L4-5 dan L5-S1 menyempit<br />\r\nPedicle normal<br />\r\nTampak osteofit di VL3-5<br />\r\nTidak tampak fraktur<br />\r\nTidak tampak lesi litik dan sklerotik</p>\r\n\r\n<p><br />\r\nKesan&nbsp;&nbsp; &nbsp;: &nbsp;&nbsp; &nbsp;<br />\r\nSpondilolisthesis VL5 dan S1 ke posterior disertai penyempitan foramen intervertebralis L4-5 dan L5-S1<br />\r\nSkoliosis ringan vertebra lumbalis ke kiri dan spondiloartrosis lumbalis</p>\r\n\r\n<p>&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (193, 'Lumbosacral 2', '<p><strong><u>Vertebra lumbosacral :</u></strong></p>\r\n\r\n<p>Curve lurus dan skoliosis ringan vertebra lumbalis ke kanan</p>\r\n\r\n<p>Malalignment VL5 ke posterior</p>\r\n\r\n<p>Besar, bentuk dan struktur trabekula normal</p>\r\n\r\n<p>Foramen intervertebralis L4-5 dan&nbsp; L5-S1 menyempit</p>\r\n\r\n<p>Pedicle normal</p>\r\n\r\n<p>Tampak osteofit di VL2-5</p>\r\n\r\n<p>Tidak tampak fraktur</p>\r\n\r\n<p>Tidak tampak lesi litik dan sklerotik</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan : &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong></p>\r\n\r\n<ul>\r\n	<li><strong>Spondilolisthesis VL5 ke posterior disertai p</strong><strong>enyempitan </strong></li>\r\n</ul>\r\n\r\n<p><strong>foramen</strong><strong> intervertebralis L4-5 dan L5-S1</strong></p>\r\n\r\n<ul>\r\n	<li>Skoliosis ringan vertebra lumbalis ke kanan &nbsp;dan spondilosis vertebra lumbalis</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (194, 'Lumbosacral_normal', '<p><strong><u>Vertebra Lumbosacral&nbsp; :</u></strong></p>\r\n\r\n<p>Kurve lurus dan skoliosis ringan vertebra lumbalis ke kanan</p>\r\n\r\n<p>Alignment baik</p>\r\n\r\n<p>Besar, bentuk dan struktur trabekula normal</p>\r\n\r\n<p>Foramina dan disci intervertebralis tidak menyempit</p>\r\n\r\n<p>Pedicle normal</p>\r\n\r\n<p>Tidak tampak osteofit</p>\r\n\r\n<p>Tidak tampak fraktur</p>\r\n\r\n<p>Tidak tampak lesi litik dan sklerotik</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan : &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong></p>\r\n\r\n<ul>\r\n	<li>Skoliosis ringan vertebra lumbalis ke kanan</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (195, 'Manus 2', '<p>MANUS DEXTRA AP/OBLIQUE &nbsp;:</p>\r\n\r\n<p>Tampak pembengkakan jaringan lunak pada manus dextra, disertai gambaran lusen densitas udara pada jaringan lunak digiti 4-5 manus dextra.</p>\r\n\r\n<p>Tampak lesi litik sklerotik pada distal os phalanx proksimal digiti IV manus dextra.</p>\r\n\r\n<p>Besar, bentuk dan struktur trabekula carpal, metacarpal dan phalanges lainnya normal.</p>\r\n\r\n<p>Sela dan permukaan sendi normal.</p>\r\n\r\n<p>Tidak tampak osteophyt.</p>\r\n\r\n<p>Tidak tampak fraktur dan dislokasi.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan : </strong></p>\r\n\r\n<p><strong>Pembengkakan jaringan lunak manus dextra, curiga adanya gas gangrene pada digiti 4-5 manus dextra.</strong></p>\r\n\r\n<p><strong>Mencurigai tanda awal osteomyelitis pada </strong><strong>distal os phalanx proksimal digiti IV manus dextra.</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (196, 'manus', '<p><u>Manus &nbsp;:</u></p>\r\n\r\n<ul>\r\n	<li>Besar, bentuk dan struktur trabekula carpal, metacarpal</li>\r\n</ul>\r\n\r\n<p>dan phalanges normal.</p>\r\n\r\n<ul>\r\n	<li>Sela dan permukaan sendi normal.</li>\r\n	<li>Tidak tampak lesi litik / sklerotik.</li>\r\n	<li>Tidak tampak osteophyt.</li>\r\n	<li>Tidak tampak fraktur dan dislokasi</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Kesan&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Foto </strong><strong>Manus </strong><strong>saat ini &nbsp;tidak tampak kelainan.</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (197, 'MRI Genu Normal', '<p style=\"text-align:justify\"><em>Dilakukan MRI genu dekstra / sinistra dengan potongan aksial-koronal-sagital PDWI Fat Sat, koronal-sagital T1WI, koronal-sagital T2WI, sagital PD dixon. Scanning dilakukan tanpa media kontras. Aksial-sagital-koronal T1WI Fat Sat post contrast</em></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<ul>\r\n	<li>Posisi dan&nbsp; bentuk tulang-tulang pembentuk cruris sinistra (distal femur, proksimal tibia, fibula, dan patella) masih tampak normal.</li>\r\n	<li>Intensitas sinyal <em>bone marrow</em> masih tampak normal.</li>\r\n	<li>Korteks memperlihatkan kontur yang rata tanpa disertai perubahan intensitas sinyal di daerah subkondral.</li>\r\n	<li>Kartilago yang meliputi patella, kondilus femoralis dan tibial plateau memiliki ketebalan yang normal dengan permukaan yang rata serta intensitas sinyal yang normal.</li>\r\n	<li>Meniscus medial dan lateral memperlihatkan konfigurasi triangular yang normal dengan intensitas sinyal yang normal.</li>\r\n	<li>Ligamentum kolateral medial dan lateral masih memberikan bentuk, kontur, dan intensitas sinyal yang normal.</li>\r\n	<li>Ligamentum cruciatum anterior dan posterior masih memberikan bentuk, kontur, dan intensitas sinyal yang normal.</li>\r\n	<li>Ligamentum quadriceps femoris dan ligamentum patellaris masih memberikan bentuk, kontur, dan intensitas sinyal yang normal.</li>\r\n	<li>Tidak tampak fluid collection berlebih di daerah celah sendi.</li>\r\n	<li>Tidak tampak intra-articular <em>foreign body.</em></li>\r\n	<li style=\"text-align:justify\">Tidak tampak <em>fluid collection</em> di daerah subligamentum kolateral medial dan lateral serta subligamentum quadriceps femoris dan subligamentum patella.</li>\r\n	<li style=\"text-align:justify\">Jaringan lunak di sekitar genu dan struktur vaskular yang terlihat masih tampak normal.</li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><strong><u>Kesan: </u></strong></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><strong><em>MRI genu </em></strong><strong><em>dekstra </em></strong><strong><em>/</em></strong><strong><em> sinistra masih tampak normal.</em></strong></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (198, 'mri kepala - paccioni', '<p style=\"text-align:justify\">Dilakukan MRI kepala tanpa kontras dengan potongan axial T1-WI, potongan aksial T2-WI, axial T2- FLAIR.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">Jaringan lunak ekstracalvaria dan calvaria masih dalam batas normal.</p>\r\n\r\n<p style=\"text-align:justify\">Sulci &nbsp;corticalis lainnya, fissura Sylvii bilateral dan fissura interhemisfer masih memberikan bentuk dan intensitas sinyal yang normal.</p>\r\n\r\n<p style=\"text-align:justify\">Bentuk dan posisi ventrikel lateralis bilateral simetris. Ukuran ventrikel lateralis bilateral, ventrikel III dan ventrikel IV masih dalam batas normal.</p>\r\n\r\n<p style=\"text-align:justify\">Ruang subaraknoid masih memberikan bentuk dan intensitas sinyal yang normal.</p>\r\n\r\n<p style=\"text-align:justify\">Sisterna ambiens dan basalis masih dalam batas normal.</p>\r\n\r\n<p style=\"text-align:justify\">Parenkim cerebri lainnya, cerebellum dan batang otak tidak memberikan lesi patologis.</p>\r\n\r\n<p style=\"text-align:justify\">Ganglia basalis, kapsula interna, thalamus bilateral tampak normal.</p>\r\n\r\n<p style=\"text-align:justify\">Daerah hippocampus dan lobus temporalis masih tampak normal.</p>\r\n\r\n<p style=\"text-align:justify\">Tidak tampak adanya &rsquo;signal void&rsquo; yang merupakan tanda malformasi vaskuler.</p>\r\n\r\n<p style=\"text-align:justify\">Daerah sela tursika dan jukstasella masih tampak normal.</p>\r\n\r\n<p style=\"text-align:justify\">Daerah cerebellopontine angle bilateral masih memberikan bentuk dan intensitas sinyal yang normal.</p>\r\n\r\n<p style=\"text-align:justify\">Nervus akustikus bilateral masih memberikan bentuk dan intensitas sinyal yang normal.</p>\r\n\r\n<p style=\"text-align:justify\">Mastoid air cell bilateral masih tampak normal.</p>\r\n\r\n<p style=\"text-align:justify\">Sinus ethmoidalis, sphenoidalis, maksilaris, dan frontalis bilateral masih dalam batas normal.</p>\r\n\r\n<p style=\"text-align:justify\">Bulbus oculi, ruang retrobulber dan nervus optikus bilateral masih dalam batas normal.</p>\r\n\r\n<p style=\"text-align:justify\">Tidak tampak pergeseran struktur garis tengah.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><strong><u>Kesan:</u></strong></p>\r\n\r\n<p><em>MRI kepala tanpa kontras saat ini masih terlihat dalam batas normal</em></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (199, 'MRI Lumbosacral Normal', '<p>Dilakukan MRI lumbosacral potongan axial-sagital T1 WI, axial-soronal-sagital T1 WI dengan kontras, axial-sagital-coronal T2 WI, axial-sagital- T2-Fatsat . Scaning dilakukan tanpa dan dengan media kontras :</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Curve dan aligment vertebra lumbal dalam batas normal</p>\r\n\r\n<p>Bentuk dan intensitas signal corpus vertebra lumbosacral masih dalam batas normal</p>\r\n\r\n<p>Discus intervertebralis dan facies articularis vertebra lumbal dalam batas normal</p>\r\n\r\n<p>Canalis spinalis dan medulla spinalis yang masuk area scaning masih memberikan bentuk dan intensitas signal yang normal</p>\r\n\r\n<p>Ligamentum longitudinal anterior , ligamentum longitudinal posterior , ligamentum interspinosus , ligamentum supraspinosus dan ligamentum flavum tidak tampak kelainan</p>\r\n\r\n<p>Jaringan lunak di daerah paravertebra kanan dan kiri masih dalam batas normal</p>\r\n\r\n<p>Tidak tampak adanya SOL / massa pada intramedula, intradural, ekstramedula maupun ekstradural</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesimpulan&nbsp; : - MRI Lumbosacral masih dalam batas normal</strong></p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (200, 'MRI LUMBOSACRAL', '<p>Telah dilakukan pemeriksaan MRI Lumbosacral dengan potongan sagittal T1, T2, dan T2 Fast Supp &amp; water sup dan aksial T1, T2 tanpa pemberian kontras, T1 fat sat post aplikasi kontras; hasil sebagai berikut :</p>\r\n\r\n<p>Tampak listhesis ke posterior corpus L5 terhadap S1 ( &lt; 25 % ) dengan&hellip;&hellip;&hellip;&hellip;&hellip;melurus<br />\r\nTak tampak kompresi maupun signal patologis sepanjang corpus lumbalis, tampak degenerasi&hellip;&hellip;&hellip;&hellip;&hellip;..superior L5 dan inferior S1<br />\r\nTampak spur formation di sepanjang copus lumbalis<br />\r\nIntensitas discus intervertebralis tampak menurun<br />\r\nDiskus Tht 2-L1 protrusio terutanma ke posterolateral kanan dengan penekanan dural sac dan iritasi radiks sisi kanan<br />\r\nDiskus L1-2, L2-3dan L3 / L4 , bulging posterolateral kanan &ndash; kiri dengan kemungkinan iritasi radiks kanan-kiri<br />\r\nDiskus L4-L5, protusio posterolateral kanan &ndash; kiri terutama kiri dengan penekanan radiks kanan-kiri terutama kiri<br />\r\nDiscus L5/S1, protusio posterolateral kanan &ndash;kiri dengan&hellip;&hellip;&hellip;.&nbsp; Dengan penekanan radiks kanan-kiri<br />\r\nLigamentum flavum dan facet joint tampak normal. Tapak hipertrofi facet joint kanan-kiri discus L5/S1<br />\r\nConus medularis terlihat setinggi TH 12, tak tampak signal patologis maupun lesi intradural maupun intramedular<br />\r\nTampak lesi bulat diameter 1 X 1,5 Cm yang hiperdens pada T2 dan hipodens pada T1di posterolateral corpus L5 yang pasca aplikasi kontras tampak penyangatan pada tepi lesi bentuk cincin yang meluas ke mengikuti radiks keluar foramen intervertebralis dan tampak pula penyangatan pada sisi dural di foramen intervertebralis sisi kanan dan diural anterior sampai S1 dan facet joint kanan-kiri setinggi S1<br />\r\nJaringan lunak paravertebral normal</p>\r\n\r\n<p>Kesan&nbsp;&nbsp; &nbsp;:</p>\r\n\r\n<p>-Spondylolisthesis L5-S1( grade I ) dengan spondylosis dan straight lumbal<br />\r\n-Diskus Tht 2-L1 protrusio terutanma ke posterolateral kanan dengan penekanan dural sac dan iritasi radiks sisi<br />\r\n&nbsp;kanan<br />\r\n-Diskus L1-2, L2-3dan L3 / L4 , bulging posterolateral kanan &ndash; kiri dengan kemungkinan iritasi radiks kanan-kiri<br />\r\n-Diskus L4-L5, protusio posterolateral kanan &ndash; kiri terutama kiri dengan penekanan radiks kanan-kiri terutama kiri<br />\r\n-Discus L5/S1, protusio posterolateral kanan &ndash;kiri dengan&hellip;&hellip;&hellip;.&nbsp; Dengan penekanan radiks kanan-kiri<br />\r\n-Hipertrofi facet joint kanan-kiri discus L5/S1<br />\r\n-Tampak lesi bulat diameter 1 X 1,5 Cm yang hiperdens pada T2 dan hipodens pada T1di posterolateral corpus L5<br />\r\n&nbsp;yang pasca aplikasi kontras tampak penyangatan pada tepi lesi bentuk cincin yang meluas ke mengikuti radiks<br />\r\n&nbsp;keluar foramen intervertebralis dan tampak pula penyangatan pada sisi dural di foramen intervertebralis sisi kanan<br />\r\n&nbsp;dan diural anterior sampai S1 dan facet joint kanan-kiri setinggi S1 sesuai dengan abses extradural</p>\r\n\r\n<p>&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (201, 'MRI shoulder (Bankart + HIll sachs)', '<p style=\"text-align:justify\"><em>Dilakukan MRI shoulder dengan potongan sagital T1WI, aksial-koronal-sagital T2 FSE Fat Sat, aksial PDWI Fat Sat.</em></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<ul>\r\n	<li>Posisi caput humerus terhadap cavum glenoid dalam batas normal.</li>\r\n	<li>Intensitas sinyal tulang-tulang pembentuk sendi glenohumeral dalam batas normal dengan sinyal yang ekuivalen dengan lemak.</li>\r\n	<li>Tidak tampak defek pada labrum glenoidalis</li>\r\n	<li>Sendi akromioklavikular memiliki konfigurasi normal tanpa hipertrofi. Jaringan lemak subakromial tampak normal.</li>\r\n	<li>Konfigurasi otot-otot dan tendon pembentuk rotator cuff tampak normal.</li>\r\n	<li>Musculus dan tendon biceps tampak normal dengan posisi tendon long head biceps berada di bicipital groove.</li>\r\n	<li>Musculus lain yang terscanning tampak normal.</li>\r\n	<li><strong><em>Tampak defect di superoposterior ccaput humerus</em></strong></li>\r\n	<li><strong><em>Tampak defect di labrum anteoinferior posisi jam 3 sampai jam 6</em></strong></li>\r\n	<li><strong><em>Tampak koleksi cairan di subcoracoid</em></strong></li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><strong><u>Kesan: </u></strong></p>\r\n\r\n<ul>\r\n	<li><strong><em>Defect di superoposterior ccaput humerus </em></strong><strong><em>&agrave;</em></strong><strong><em> Hill sachs lesion</em></strong></li>\r\n	<li><strong><em>Tampak defect di labrum anteoinferior posisi jam 3 sampai jam 6 </em></strong><strong><em>&agrave;</em></strong><strong><em> sugesdtif bankart lesion</em></strong></li>\r\n	<li><strong><em>&nbsp;&nbsp;Tampak koleksi cairan di subcoracoid (bursitis)</em></strong></li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p><em>Terima kasih atas kepercayaan teman sejawat.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </em>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (202, 'MSCT scan abdomen dengan potongan axial', '<p>MSCT scan thoraks dengan potongan axial, tanpa dan dengan memakai kontras sertai dengan window paru</p>\r\n\r\n<p>Jaringan lunak ekstra dan intraabdominalis masih memberikan bentuk dan densitas yang normal.<br />\r\nHepar, kt. Empedu, pankreas, limpa dan ginjal kanan-kiri terlihat masih dalam batas normal<br />\r\nTampak bayangan massa isoekhoik (samar terlihat sedikit bagian hipoekhoi), batas tegas, tepi reguler yang tervisualisasi mulai dari abdomen atas (dibawah hepar lobus kiri) samapi abdomen tengah&nbsp; sisi kiri (di atas ginjal kiri). Massa tampak berbatasan langsung dengan bagian inferior hepar lobus kiri dan pole atas ginjal kiri. Massa tampak mendesak corpus dan cauda pankreas ke inferior. HU massa tanpa kontras &plusmn; 19-33.<br />\r\nTidak tampak gambaran dilatasi usus<br />\r\nTampak kalsifikasi aorta</p>\r\n\r\n<p>Kesimpulan :<br />\r\nMassa isoekhoik (samar terlihat sedikit bagian hipoekhoi) intraperironial, batas tegas, tepi reguler yang tervisualisasi mulai dari abdomen atas-abdomen tengah&nbsp; sisi kiri. Massa tampak berbatasan langsung dengan bagian inferior hepar lobus kiri dan pole atas ginjal kiri. Massa tampak mendesak corpus dan cauda pankreas ke inferior&nbsp; sugestif suatu massa semisolid (karakteristik curiga benign), lab?</p>\r\n\r\n<p>Saran : CT-scan abdomen dengan kontras</p>\r\n\r\n<p>&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (203, 'No 7 thorax', '<p>Foto dibandingkan dengan tanggal 30-7-12</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Cor membesar ke lateral kiri dengan apex tertanam pada diafragma pinggang&nbsp; jantung normal</p>\r\n\r\n<p>sinuses tumpul , diafragma bilateral tampak tenting</p>\r\n\r\n<p>Pulmo&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; : Hilli tampak tertarik ke superior</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Corakan bronchovaskular bertambah</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Tampak infiltrat disertai garis fibrotik dan noda keras di lapang tengah sampai bawah bilateral</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><em>Kesan&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; </em><em>TB paru lama aktif disertai fibrosis paru dan pleura belum jelas perbaikan</em></p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (204, 'no.2', '<p>Cor, sinuses, &amp; diafragma normal.</p>\r\n\r\n<p>Pulmo&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; Hilli normal</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Corakan bronkhovaskuler bertambah dengan peribronkhial menebal</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Tidak tampak infiltrat</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><em>Kesan&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; -&nbsp;&nbsp;&nbsp; Gambaran bronkhitis</em></p>\r\n\r\n<ul>\r\n	<li><em>Tidak tampak&nbsp; gambaran KP aktif</em></li>\r\n	<li><em>Tidak tampak kardiomegali</em></li>\r\n</ul>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (205, 'Panoramic', '<p><strong><u>Panoramic :</u></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li>Gigi geligi rahang atas-bawah tidak lengkap</li>\r\n	<li>Dental missing gigi &nbsp;</li>\r\n	<li>Sisa akar gigi &nbsp;</li>\r\n	<li>Post tambal gigi</li>\r\n	<li>Tampak lesi lusen di pulpa gigi &nbsp;</li>\r\n	<li>Impaksi gigi</li>\r\n	<li>Tulang mandibula intac, tidak tampak lesi litik dan sklerotik</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (206, 'Pedis', '<p><u>Pedis dextra :</u></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li>Besar, bentuk dan struktur trabekula tarsal, metatarsal dan phalanges normal.</li>\r\n	<li>Sela dan permukaan sendi normal.</li>\r\n	<li>Tidak tampak lesi litik / sklerotik.</li>\r\n	<li>Tidak tampak fraktur dan dislokasi</li>\r\n	<li>Tidak tampak osteofit</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><u>Kesan</u>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; </strong>Foto pedis dextra saat ini tidak tampak kelainan</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (207, 'Pelvis', '<p><strong><u>PELVIS AP/LATERAL</u></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Besar ,bentuk dan struktur trabecula tulang-tulang pembentuk pelvis normal</p>\r\n\r\n<p>Sela dan permukaan sendi normal</p>\r\n\r\n<p>Caput femoralis dan acetabulum normal</p>\r\n\r\n<p>Tidak tampak lesi litik/sklerotik</p>\r\n\r\n<p>Tidak tampak fraktur</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan</strong><strong>&nbsp; </strong><strong>: </strong><strong>Foto pelvis </strong><strong>saat ini masih dalam batas normal</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (208, 'schedel', '<p><u>Schedel AP / Lateral :</u></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li>Besar, bentuk dan struktur trabekula normal</li>\r\n	<li>Tabula eksterna-interna dan diploe normal</li>\r\n	<li>Vascular dan convulational marking normal</li>\r\n	<li>Sella tursika normal. Tidak tampak lesi litik dan sklerotik</li>\r\n	<li>Tidak tampak garis fraktur.</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan :&nbsp;&nbsp;&nbsp;&nbsp; Foto Schedel AP &ndash; Lateral </strong><strong>saat ini </strong><strong>tidak tampak kelainan&nbsp;&nbsp;&nbsp;&nbsp; </strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (209, 'Shoulder normal - Copy', '<p><strong><u>Shoulder Joint</u></strong><strong><u> sinistra :</u></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li>Besar ,bentuk dan struktur trabecula tulang normal</li>\r\n	<li>Sela dan permukaan sendi normal</li>\r\n	<li>Caput humeri dan fossa glenoid normal</li>\r\n	<li>Tidak tampak lesi litik/sklerotik</li>\r\n	<li>Tidak tampak fraktur</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan</strong><strong>&nbsp; </strong><strong>:&nbsp;&nbsp;&nbsp; </strong><strong>Foto Shoulder joint saat ini tidak tampak kelainan</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (210, 'Shoulder normal', '<p><strong><u>Shoulder Joint</u></strong><strong><u> sinistra :</u></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li>Besar ,bentuk dan struktur trabecula tulang normal</li>\r\n	<li>Sela dan permukaan sendi normal</li>\r\n	<li>Caput humeri dan fossa glenoid normal</li>\r\n	<li>Tidak tampak lesi litik/sklerotik</li>\r\n	<li>Tidak tampak fraktur</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan</strong><strong>&nbsp; </strong><strong>:&nbsp;&nbsp;&nbsp; </strong><strong>Foto Shoulder joint saat ini tidak tampak kelainan</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (211, 'Sinus paranasalis CT', '<p>MSCT<strong><em> </em></strong>Sinus paranasalis, posotngan axila-corona dan sagital, <strong>tanpa memakai</strong> kontras.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:left\">Tampak bayangan hipodens di sinus maksilaris kiri dan sedikit di sinus maksilaris kanan</p>\r\n\r\n<p style=\"text-align:left\">Setum nasi tampak deviasi kr kiri</p>\r\n\r\n<p style=\"text-align:left\">Cocha nasalis inferior kanan-kiri, terutama kanan tampak menebal</p>\r\n\r\n<p style=\"text-align:left\">Sinus eithmoidalis, &nbsp;sphenoidalis, dan frontalis dalam batas normal.</p>\r\n\r\n<p style=\"text-align:left\">Bulbus oculi dan ruang retrobulber dalam batas normal.</p>\r\n\r\n<p>Tidak tampak pergeseran struktur garis tengah.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><em>Kesimpulan :</em></strong></p>\r\n\r\n<ul>\r\n	<li><strong><em>CT-scan kepala tanpa kontras saat ini tidak tampak kelainan</em></strong></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (212, 'Skoliosis bending', '<p>Skoliosis ringan vertebra lumbalis ke kiri, pada bending kanan dan kiri terkoreksi.<br />\r\nMalalignment ringan (grade I)&nbsp; VL5 ke posterior<br />\r\nBesar, bentuk dan struktur trabekula normal<br />\r\nForamen intervertebralis L4-5 dan L5-S1 sedikit&nbsp; menyempit<br />\r\nPedicle normal<br />\r\nTidak tampak osteofit . Tidak tampak fraktur<br />\r\nTidak tampak lesi litik dan sklerotik</p>\r\n\r\n<p>Kesan&nbsp;&nbsp; &nbsp;: &nbsp;&nbsp; &nbsp;<br />\r\nSpondilolisthesis ringan (grade I) VL5 posterior disertai penyempitanringan&nbsp; foramen intervertebralis L4-5 dan L5-S1<br />\r\nSkoliosis ringan vertebra lumbalis ke kiri&nbsp; yang fleksibel</p>\r\n\r\n<p>&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (213, 'Soft Nasal', '<p><strong><u>Os. Nasal :</u></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li>Tidak tampak garis fraktur pada os nasal</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; Foto os nasal posisi lateral saat ini dalam batas normal</strong></p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (214, 'Soft tissue ar', '<p>Soft tissue ar. Colli sinistra sisi lateral :</p>\r\n\r\n<p>Kutis sebagian tampak hiperemis<br />\r\nSubkutis sebagian irreguler<br />\r\nTampak bayangan massa padat iso-hipoekhoik, batas cukup tegas,<br />\r\n&nbsp;&nbsp; &nbsp;tepi berbenjol, ukuran&nbsp; manual &plusmn; 10 x 8 cm. Pada color doppler<br />\r\n&nbsp;&nbsp; &nbsp;tampak hipervaskularisasi, kalsifikasi (+).</p>\r\n\r\n<p><br />\r\nKesan&nbsp;&nbsp; &nbsp;:&nbsp;&nbsp; &nbsp;Massa padat iso-hipoekhoik di colli sinistra suspek ec.<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;malignancy soft tisseu DD/</p>\r\n\r\n<p>&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (215, 'STL AP+LAT', '<p><u>Soft Tissue Leher AP/Lateral</u></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li>Kolom udara dalam faring dan laring normal</li>\r\n	<li>Retrofaring &amp; retrolaring&nbsp; space tidak melebar</li>\r\n	<li>Epiglotis dan valekula normal</li>\r\n	<li>Tidak tampak perselubungan.</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kesan : &nbsp;&nbsp; Foto Soft Tissue Leher dalam batas normal.</p>\r\n\r\n<p>&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (216, 'Takayasu', '<p>MSCT<strong><em> </em></strong>Scan Angiografi Aorta:</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Aorta Ascendens sedikit melebar</p>\r\n\r\n<p>Aorta Descendens setinggi Vertebra Thorakal 8 tampak menyempit</p>\r\n\r\n<p>Arcus aorta dan cabang-cabang nya (a.brachiocephalica, a.subclavia kiri, a.carotis communis), Aorta Abdominalis beserta cabang-cabangnya (a.coeliacus, a.renalis) tidak tampak kelainan</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><em>Kesimpulan :</em></strong></p>\r\n\r\n<ul>\r\n	<li><strong><em>Sugestif suatu takayasu disease (Type IIb)</em></strong></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (217, 'Testis kanan hernia', '<p>Testis kanan :<br />\r\nBesar dan bentuk normal, densitas echo parenkim normal. Tidak tampak SOL.<br />\r\nTidak tampak koleksi cairan di sekitarnya.</p>\r\n\r\n<p>Testis kiri :<br />\r\nBesar dan bentuk normal, densitas echo parenkim normal. Tidak tampak SOL.<br />\r\nTidak tampak koleksi cairan di sekitarnya. Testis kiri tampak di liputi oleh<br />\r\nbayangan hiperekhoik dengan vaskularisasi (+).</p>\r\n\r\n<p>Epididimia kanan-kiri :<br />\r\nBesar dan bentuk normal. densitas echo parenkim normal. Tidak tampak SOL.</p>\r\n\r\n<p>Inguinal kiri :<br />\r\nTampak bayangan hipoekhoik (usus) yang terlihat pada non-valsava dan pada<br />\r\nvalsava tampak ukuran membesar, peristaltik (+). Pada color doppler masih<br />\r\nterlihat vaskularisasi</p>\r\n\r\n<p>Kesan&nbsp;&nbsp; &nbsp;:&nbsp; Hernia scrotalis sinistra<br />\r\n&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;USG kedua testis saat ini dalam batas normal</p>\r\n\r\n<p>&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (218, 'Thorakolumbal', '<p><strong><u>Vertebra thorakolumba</u></strong><strong><u>lis</u></strong></p>\r\n\r\n<p>Kurve dan alignment normal</p>\r\n\r\n<p>Besar, bentuk dan struktur trabekula normal</p>\r\n\r\n<p>Foramina dan disci &nbsp;intervertebralis tidak &nbsp;menyempit</p>\r\n\r\n<p>Pedicle normal</p>\r\n\r\n<p>Tidak tampak fraktur</p>\r\n\r\n<p>Tidak tampak lesi litik dan sklerotik</p>\r\n\r\n<p>Tidak tampak osteofit</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan : &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong></p>\r\n\r\n<ul>\r\n	<li>Foto vertebra thorakolumbalis saat ini masih dalam batas normal</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (219, 'Thoraks normal', '<p><strong><u>Thorax </u></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Cor tidak membesar.</p>\r\n\r\n<p>Sinuses dan diafragma normal.</p>\r\n\r\n<p>Pulmo :</p>\r\n\r\n<p>Hilli normal.</p>\r\n\r\n<p>Corakan bronkhovaskuler normal.</p>\r\n\r\n<p>Tidak tampak infiltrat</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan : </strong></p>\r\n\r\n<p>Pulmo saat ini dalam batas normal</p>\r\n\r\n<p>Tidak tampak kardiomegali</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (220, 'Thoraks trauma normal', '<p><strong><u>Thorax </u></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Cor tidak membesar.</p>\r\n\r\n<p>Sinuses dan diafragma normal.</p>\r\n\r\n<p>Pulmo : Hilli normal.</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;Corakan bronkhovaskuler normal.</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;Tidak tampak infiltrat.</p>\r\n\r\n<p>Skeletal : Tidak tampak garis fraktur pada os clavicula, scapula dan costae yang terscanning.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan : Tidak tampak contusio paru/traumatic wet lung.</strong></p>\r\n\r\n<p><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Tidak tampak kardiomegali.</strong></p>\r\n\r\n<p><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Tidak tampak fraktur </strong><strong>os clavicula, scapula dan costae.</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (221, 'Thorax MCU normal', '<p><strong><u>Thorax </u></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Cor, sinuses dan diafragma normal.</p>\r\n\r\n<p>Pulmo : Hilli normal.</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;Corakan bronkhovaskuler normal.</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;Tidak tampak infiltrate.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan : Pulmo saat ini tidak tampak kelainan </strong></p>\r\n\r\n<p><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp; Cor saat ini tidak tampak kelainan</strong></p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (222, 'Thorax No 21', '<p>Kardiomegali ringan</p>\r\n\r\n<p>Sinuses dan diafragma normal</p>\r\n\r\n<p>Pulmo &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; &nbsp;&nbsp; Hilli normal</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Corakan bronkhovaskuler normal</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Tidak tampak infiltrat</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; &nbsp;&nbsp; Kardiomegali ringan ( EKG? )</strong></p>\r\n\r\n<p><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Pulmo tidak tampak kelainan</strong></p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (223, 'Thorax Normal 2', '<p><strong><u>Thorax </u></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Cor, sinuses dan diafragma normal.</p>\r\n\r\n<p>Pulmo : Hilli normal.</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;Corakan bronkhovaskuler normal.</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;Tidak tampak infiltrate.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan : Pulmo saat ini tidak tampak kelainan </strong></p>\r\n\r\n<p><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp; Tidak tampak kardiomegali</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (224, 'Thorax normal in english', '<ul>\r\n	<li>The lungs field clear</li>\r\n	<li>Heart is normal in size and configuration.</li>\r\n	<li>Sinuses and hemidiaphragm are normal.</li>\r\n	<li>Hilli and bronchovasculer within normal limit</li>\r\n	<li>The visualizes bones are unremarkable.</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Impression :&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Normal chest</p>\r\n\r\n<p>&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (225, 'Thorax Normal1', '<p><strong>Dibandingkan foto tgl 10/08/2015</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Cor sqa</p>\r\n\r\n<p>Pulmo&nbsp;&nbsp;&nbsp;&nbsp; : Tak tampak bercak</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan&nbsp;&nbsp;&nbsp;&nbsp; : Post KP tak tampak aktifitas</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (226, 'Thorax Patologis', '<p><u>Thorax</u></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Cor , sinuses &amp; diafragma normal</p>\r\n\r\n<p>Pulmo&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; : Hilli normal</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp; Corakan bronkhovaskuler normal</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp; Tidak tampak infiltrat</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan : &nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Pulmo tidak tampak kelainan</strong></p>\r\n\r\n<p><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Tidak tampak kardiomegali</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (227, 'THORAX', '<p>&laquo;Cor&raquo;</p>\r\n\r\n<p>Pulmo&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; &laquo;Hili&raquo; &laquo;Bercak&raquo; &laquo;Perselubungan&raquo; &laquo;Crk_Paru&raquo;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><em>Kesan&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; </em><em>&laquo;Kesan&raquo;</em></p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (228, 'abortus inkomplit', '<p>Hepar&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp;&nbsp; Tidak membesar. Parenkim homogen, tak tampak SOL. Ductus biliaris, vena hepatika, vena porta tak melebar. Tak tampak koleksi cairan di sekitarnya.</p>\r\n\r\n<p>Gallbladder&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp;&nbsp; Besar &amp; bentuk normal. Dinding tidak menebal. Tak tampak batu ataupun SOL.</p>\r\n\r\n<p>Limpa&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp;&nbsp; Besar &amp; bentuk dlm batas normal. Textur parenkhim&nbsp; homogen.Tak tampak SOL. Vena Lienalis tak melebar. Tak tampak koleksi cairan di sekitarnya.</p>\r\n\r\n<p>Pankreas&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp;&nbsp; Besar &amp; bentuk dlm batas normal. Textur homogen.</p>\r\n\r\n<p>Paraaorta&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp;&nbsp; Ukuran aorta abdominalis dalam batas normal.Tak tampak nodul hipo/hiperechoic disekitar aorta abdominalis.</p>\r\n\r\n<p>Parailiaka&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp;&nbsp; Tak tampak nodul hipo/hiperechoic disekitar parailiaka bilateral.</p>\r\n\r\n<p>Ginjal&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp;&nbsp; Besar &amp; bentuk normal. Batas textur parenkhim dengan central echocomplex normal. Sistim pelvocalises tidak melebar. Tak tampak batu ataupun SOL</p>\r\n\r\n<p>V. Urinaria&nbsp;&nbsp; :&nbsp; Besar &amp; bentuk&nbsp; normal, dinding menebal. Tak tampak batu ataupun SOL.</p>\r\n\r\n<p>Uterus&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; Tampak membesar, endometrium tampak menebal disertai gambaran echogenitas heterogen intracavum uteri, dengan lesi hiperekhoik di daerah fundus dan koleksi cairan di daerah cervix dan vagina superior. Tidak tampak jelas gestasional sac. Tidak tampak koleksi cairan di cavum douglas. Tidak tampak lesi kistik/solid pada adnexa bilateral.</p>\r\n\r\n<p>Scan Abdomen Kanan Bawah :</p>\r\n\r\n<p>Tidak tampak gambaran usus tubuler buntu tanpa peristaltic yang pada potongan transversal membentuk target sign, Tidak tampak koleksi cairan bebas. Tidak tampak massa. Tidak terdapat nyeri tekan probe pada area Mc Burney (tenderness on probe ultrasound).</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; </strong></p>\r\n\r\n<ul>\r\n	<li><strong>Sugestif suatu gambaran abortus inkomplit. </strong></li>\r\n	<li><strong>Gambaran cystitis.</strong></li>\r\n	<li><strong>Tidak jelas tanda-tanda apendisitis. </strong></li>\r\n	<li><strong>USG Hepar, gallbladder, limpa, pancreas &amp; kedua ginjal dalam batas normal. </strong></li>\r\n	<li><strong>Tidak tampak pembesaran KGB paraaorta/parailiaka. </strong></li>\r\n</ul>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (229, 'TMJ Normal', '<p>Foto TMJ kiri :</p>\r\n\r\n<p><strong><u>Tutup mulut </u></strong><strong><u>&nbsp;:</u></strong></p>\r\n\r\n<ul>\r\n	<li>Caput mandibula berada pada fossa mandibula</li>\r\n	<li>Posisi simetris</li>\r\n	<li>Sela dan permukaan sendi normal</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><u>Buka mulut</u></strong><strong><u> :</u></strong></p>\r\n\r\n<ul>\r\n	<li>Caput mandibula terletak di bawah tuberkulum artikulare</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><u>Kesan</u></strong><strong>: &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Foto temporo mandibula joint </strong><strong>kiri</strong><strong> saat ini masih dalam batas normal</strong></p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (230, 'ca nasofaring', '<p style=\"text-align:justify\">Dilakukan MSCT/CT scan leher potongan aksial dengan <em>slice interval 0.6</em> mm dimulai dari ketinggian frontoocipital ke os vertebra cervical 7. Potongan dibuat sejajar <strong>palatum durum.</strong> Dibuat rekonstruksi axial coronal dan sagital. Scanning dilakukan <strong>tanpa dan dengan </strong>memakai kontras media.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><em>Tampak lesi isodens inhomogen, batas tidak tegas pada fossa rossen muller kiri, meluas ke rescessus pharingeus kiri, torus tubarius kiri serta mengobliterasi tuba eustachius kiri, kearah lateral tampak mengobliterasi parafaringeal space kiri, m. pterygoid medial kiri dan m. pterygoid lateral kiri, meliputi a. carotis interna kiri serta meluas ke inferior mengobliterasi sebagian orofaring dan hipofaring. Lesi tersebut tampak mendesak kolom udara ke kanan setinggi corpus vertebra C2-5 serta menyempitkan kolom udara pada daerah setinggi corpus vertebra C4-6. Post contrast scanning memberikan enhancement inhomogen. </em></p>\r\n\r\n<p style=\"text-align:justify\"><em>Tampak lesi isodens inhomogen, multiple, batas tegas, tepi reguler berukuran terbesar 7,87 x 4,72 x 8,05 cm di colli dextra. Post contrast scanning memberikan enhancement di tepinya.</em></p>\r\n\r\n<p style=\"text-align:justify\">Tidak tampak penebalan jaringan lunak preoccipital</p>\r\n\r\n<p style=\"text-align:justify\">Septum nasi masih di tengah.</p>\r\n\r\n<p style=\"text-align:justify\">Kelenjar parotis kanan dan submandibula bilateral tampak normal. Kelenjar parotis kiri tampak terdesak. Retropharyngeal space, carotid space, fossa infratemporalis dan masticator space kanan dalam batas normal.</p>\r\n\r\n<p style=\"text-align:justify\">Struktur vaskuler (v. Jugularis interna) tampak normal.</p>\r\n\r\n<p style=\"text-align:justify\">Kelenjar tiroid kanan dan kiri tidak membesar. Kontur parenkim tampak homogen</p>\r\n\r\n<p style=\"text-align:justify\">Laring dan os hyoid dalam batas normal. Epiglotis dan valekula tampak normal.</p>\r\n\r\n<p style=\"text-align:justify\">Tidak tampak destruksi os maksilofasial dan mandibula.</p>\r\n\r\n<p style=\"text-align:justify\">Bulbus oculi dan ruang retrobulber yang terscanning tampak normal.</p>\r\n\r\n<p style=\"text-align:justify\">Mastoid air cell yang terscanning tampak normal.</p>\r\n\r\n<p style=\"text-align:justify\">Struktur vertebra cervicalis tampak normal.</p>\r\n\r\n<p style=\"text-align:justify\">Tidak tampak metastasis intracranial pada parenkim cerebri yang terscanning.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><strong><em>Kesimpulan :</em></strong></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><strong><em>Lesi solid heterogen, batas tidak tegas pada fossa rossen muller kiri, meluas ke rescessus pharingeus kiri, torus tubarius kiri serta mengobliterasi tuba eustachius kiri, kearah lateral tampak mengobliterasi parafaringeal space kiri, m. pterygoid medial kiri dan m. pterygoid lateral kiri, meliputi a. carotis interna kiri serta meluas ke inferior mengobliterasi sebagian orofaring dan hipofaring. Lesi tersebut tampak mendesak kolom udara ke kanan setinggi corpus vertebra C2-5 serta menyempitkan kolom udara pada daerah setinggi corpus vertebra C4-6 disertai pembesaran KGB multiple di colli kiri</em></strong><strong><em>&agrave; Menyokong gambaran massa nasofaring sugestif malignant.</em></strong></li>\r\n	<li style=\"text-align:justify\"><strong><em>Tidak tampak metastasis intracranial.</em></strong></li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><strong><em>Saran : Biopsi</em></strong></p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (231, 'charcot joint', '<p><strong>PEDIS SINISTRA</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Deformitas os phalang proksimal dengan lesi litik dan sklerotik disertai pelebaran sendi interphlangeal digiti I pedis sinistra.</p>\r\n\r\n<p>Deformitas os metatarsal IV dan phalang proksimal digiti IV pedis sinistra, disertai penyempitan sendi metatarsophalangeal.</p>\r\n\r\n<p>Disartikulasi os phalang proksimal digiti V pedis sinistra.</p>\r\n\r\n<p>Besar, bentuk, dan struktur trabekula tulang-tulang pembentuk pedis sinistra lainnya dalam batas normal.</p>\r\n\r\n<p>Pembengkakan jaringan lunak pedis sinistra.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan&nbsp;&nbsp;&nbsp; : </strong></p>\r\n\r\n<p><strong>Sugestif suatu charcot joint pedis sinistra. </strong></p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (232, 'Top Lordotik', '<p><strong><u>Top Lordotik</u></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Tampak infiltrat &nbsp;disertai garis keras di lapang atas kedua paru </strong><strong>.</strong></p>\r\n\r\n<p><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong></p>\r\n\r\n<p><strong>Kesan : TB Paru Aktif</strong><strong>.</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (233, 'CT Scan abdomen - wulan', '<p><strong>CT SCAN ABDOMEN DENGAN KONTRAS</strong></p>\r\n\r\n<p>- Gaster : Besar,bentuk, posisi dalam batas normal.</p>\r\n\r\n<p>- Hepar : Ukuran tidak membesar, permukaan rata, densitas homogen. Tidak tampak massa. Vena porta tidak melebar. Vena hepatika tidak melebar. Vena cava inferior tidak melebar. Duktus biliaris intrahepatal tidak melebar. Tidak tampak koleksi cairan di sekitarnya.</p>\r\n\r\n<p>- Kandung empedu : Ukuran tidak membesar, dinding tidak menebal, reguler. Tidak tampak massa/batu. Duktus biliaris ekstrahepatal tidak melebar.</p>\r\n\r\n<p>- Limpa : Ukuran tidak membesar, densitas homogen. Vena lienalis tidak melebar. Tidak tampak koleksi cairan di sekitarnya.</p>\r\n\r\n<p>- Pankreas : Ukuran tidak membesar, densitas homogen, duktus pankreatikus tidak melebar.</p>\r\n\r\n<p>- Ginjal kanan : Ukuran tidak membesar, struktur parenkim homogen, sistem pelvokalises tidak melebar, tidak tampak batu/ massa.</p>\r\n\r\n<p>- Ginjal&nbsp; kiri : Ukuran tidak membesar, struktur parenkim homogen, sistem pelvokalises tidak melebar, tidak tampak batu/ massa.</p>\r\n\r\n<p>- Tidak tampak bayangan hipodens di paraaorta dan parailiaka.</p>\r\n\r\n<p>- Appendix : Tampak gambaran appendix dengan struktur tubuler mengarah ke superior mendekati pole bawah ginjal kanan dengan diameter lk 0,65 &ndash; 0,75 cm.</p>\r\n\r\n<p>- Vesika urinaria : Terisi penuh, dinding menebal. tidak tampak batu/massa. Perivesical fat normal.</p>\r\n\r\n<p style=\"text-align:justify\">- Uterus :&nbsp; Ukuran tidak membesar, endometrium tampak menebal, dengan koleksi cairan bebas pada cavum douglas. Post contrast scanning tampak enhancement inhomogen.</p>\r\n\r\n<p>- Rektum :&nbsp; Posisi normal, dinding tidak menebal, perirectal fat normal.</p>\r\n\r\n<p>Thorax yang terscanning : Tampak infiltrat di segmen posterobasal paru kiri. Tampak gambaran isodens dengan tepi ireguler di hemithorax lateral bawah bilateral.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan : </strong></p>\r\n\r\n<p><strong>- Penebalan endometrium dengan koleksi cairan bebas pada cavum douglass </strong><strong>&agrave; sugestif endometritis. </strong></p>\r\n\r\n<p><strong>- Appendix berdiameter lk 0,65 &ndash; 0,75 cm </strong><strong>&agrave; suspek appendicitis kronis.</strong></p>\r\n\r\n<p><strong>- Gambaran cystitis.</strong></p>\r\n\r\n<p><strong>- Infiltrat di segmen posterobasal paru kiri dan penebalan pleura di hemithorax lateral bawah bilateral </strong><strong>&agrave; inflamasi e.c proses spesifik?</strong></p>\r\n\r\n<p><strong>- Scanning hepar, kandung empedu, limpa, pankreas, ginjal kanan dan kiri dan rektum tidak tampak kelainan</strong></p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (234, 'ct thorax - massa mediastinum', '<p>Dilakukan CT scan thoraks potongan axial dengan ketebalan 8 mm, dilakukan rekontruksi potongan coronal dan sagittal dengan ketebalan 3 mm. Scanning <strong>tanpa dan dengan</strong> memakai kontras intravena</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n\r\n<p><strong><em><u>Scanning window mediastinum</u></em></strong></p>\r\n\r\n<ul>\r\n	<li>A.karotis communis kiri, a.subclavia kiri, truncus brachiocephalica , v.brachiocephalica tampak terdorong ke kiri.</li>\r\n	<li>Vena cava superior, aorta ascenden, arcus aorta dan aorta descenden tampak terdorong ke kiri.</li>\r\n	<li>Tampak dilatasi main pulmonary artery (diameter lk 3,26 cm), aorta ascendens lk 3,09 cm. Ratio main pulmonary artery dengan aorta ascendens lk 1,06.</li>\r\n	<li>Trakhea terdorong ke kiri. Trachea inferior tampak sedikit menyempit.</li>\r\n	<li>Cabang utama bronchus kanan dan kiri terbuka. Cabang dari bronchus utama kanan yang menuju ke lobus superior dan lobus medius tampak menyempit.</li>\r\n	<li>Tampak massa hipodens inhomogen (HU 22-31) dengan lesi hipodens kecil dengan densitas lemak di dalamnya, batas tegas tepi regular, berukuran lk14,23 x 10,1 x 18,61 cm di hemithorax anterior atas sampai bawah kanan. Post contrast scanning tampak memberikan enhancement pada tepinya.</li>\r\n	<li>Tampak gambaran hipodens berbentuk lenticular, split pleural sign (+), batas tegas tepi regular, berukuran lk&nbsp; 12,05 x 3,74&nbsp; x 8,52 cm di hemithorax posterior bawah kanan.</li>\r\n	<li>Tampak konsolidasi di lapang bawah paru kanan.</li>\r\n	<li>Tampak nodul hipodens multiple di lapang bawah paru kanan.</li>\r\n	<li>Tidak tampak pembesaran jantung, tampak terdorong ke kiri.</li>\r\n	<li>Tampak multiple nodul hipodens dengan ring enhancement pada paratrakhea inferior bilateral (4RL), subaorta (5) dan paraaorta (6).</li>\r\n</ul>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n\r\n<p><strong><em><u>Scanning window paru</u></em></strong></p>\r\n\r\n<ul>\r\n	<li>Tampak konsolidasi pada lobus inferior paru kanan.</li>\r\n	<li>Tampak ground glass pada hampir seluruh segmen lobus superior dan inferior paru kiri.</li>\r\n	<li>Tampak fibrosis pada segmen lingula inferior lobus superior paru kiri.</li>\r\n	<li>Tampak nodul isodens multiple di segmen anterobasal lobus inferior paru kanan. Post contrast scanning memberikan enhancement inhomogen.</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><u>Kesan&nbsp; :</u></strong></p>\r\n\r\n<ul>\r\n	<li><strong>Massa semisolid heterogen dengan komponen lemak di dalamnya, berukuran lk</strong><strong> 14,23 x 10,1 x 18,61 cm di hemithorax anterior atas sampai bawah kanan, yang mendorong great vessel dan organ mediastinum ke kiri, sedikit menyempitkan trachea bagian inferior dan cabang bronchus utama kanan yang menuju ke lobus superior dan lobus medius </strong><strong>&agrave;</strong><strong> mengarah pada karakteristik massa mediastinum (sugestif anterior mediastinal germ cell tumor). </strong></li>\r\n	<li><strong>Gambaran empyema di hemithorax posterior bawah kanan.</strong></li>\r\n	<li><strong>Konsolidasi pada </strong><strong>lobus inferior paru kanan; ground glass pada hampir seluruh segmen lobus superior dan inferior paru kiri; fibrosis pada segmen lingula inferior lobus superior paru kiri </strong><strong>&agrave;</strong><strong> sugestif proses inflamasi kronis.</strong></li>\r\n	<li><strong>Multiple nodul di segmen anterobasal lobus inferior paru kanan </strong><strong>&agrave;</strong><strong> Suspek metastasis DD/ Tuberkuloma. </strong></li>\r\n	<li><strong>Pembesaran KGB multiple pada paratrakhea inferior bilateral (4RL), subaorta (5) dan paraaorta (6).</strong></li>\r\n	<li><strong>Tidak tampak kardiomegali disertai tanda-tanda hipertensi pulmonal. </strong></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (235, 'USG Abdomen Expertise besar', '<p>Hepar&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; Tidak membesar. Textur parenkhim homogen, tak tampak SOL. Ductus biliaris, vena hepatika, vena porta tak melebar.</p>\r\n\r\n<p>Kt Empedu: Besar &amp; bentuk normal. Dinding tidak menebal. Tak tampak batu ataupun SOL</p>\r\n\r\n<p>Limpa&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; Besar &amp; bentuk dlm batas normal. Textur parenkhim&nbsp; homogen.Tak tampak SOL. Vena Lienalis tak melebar.</p>\r\n\r\n<p>Pankreas&nbsp;&nbsp;&nbsp; :&nbsp; Besar &amp; bentuk dlm batas normal. Textur homogen</p>\r\n\r\n<p>Paraaorta&nbsp;&nbsp; :&nbsp; Besar &amp; bentuk aorta abdominalis dalam batas normal.Tak tampak bayangan noduler hypo ataupun hyper echoic disekitar aorta abdominalis.</p>\r\n\r\n<p>Ginjal&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; Besar &amp; bentuk normal. Batas textur parenkhim dg central echo complex normal. Sistim pelvocalises tidak lebar.Tak tampak batu ataupun SOL</p>\r\n\r\n<p>Kt Kemih&nbsp; :&nbsp; Besar &amp; bentuk&nbsp; normal, dinding tidak menebal. Tak tampak batu ataupun SOL</p>\r\n\r\n<p>Uterus&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; Besar &amp; bentuk dalam batas normal.</p>\r\n\r\n<p>Ovarium&nbsp;&nbsp;&nbsp; :&nbsp; Besar, bentuk, &amp; textur dalam batas normal.</p>\r\n\r\n<p>Prostat&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; Besar, bentuk, &amp; textur dalam batas normal.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kesan&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; USG Hepatobilier, gallbladder, limpa, pankreas, kedua ginjal, &amp; kt kemih dalam batas normal.</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Tak tampak pembesaran KGB paraaorta.</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (236, 'Direct hernia inguinalis', '<p>Inguinal Kanan :</p>\r\n\r\n<p>Jaringan kutis dan subkutis inguinalis kanan tampak normal.</p>\r\n\r\n<p>Diameter canalis inguinalis kanan melebar, lk 5,7 mm, tampak bertambah lebar dengan manuver valsava (lk 7,5 mm).</p>\r\n\r\n<p>Tampak gambaran struktur menyerupai kantung berisi cairan dan struktur tubuler dengan peristaltis di dalamnya pada scrotum kanan.</p>\r\n\r\n<p>Tampak gambaran tubuler menyerupai usus pada canalis inguinalis kanan.</p>\r\n\r\n<p>Tidak tampak nodul hipo/hiperekhoik.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Inguinal Kiri :</p>\r\n\r\n<p>Jaringan kutis dan subkutis inguinalis kiri tampak normal.</p>\r\n\r\n<p>Diameter canalis inguinalis kiri lk 4,8 mm, tidak tampak melebar dengan manuver valsava.</p>\r\n\r\n<p>Tidak tampak struktur berbentuk kantung berisi gambaran menyerupai usus dengan peristaltic pada medial a. Epigastrica inferior kiri, disertai bulging peritoneum dengan manuver valsava.</p>\r\n\r\n<p>Tidak tampak nodul hipo/hiperekhoik.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; </strong></p>\r\n\r\n<p><strong>Sugestif direct hernia inguinalis kiri.</strong></p>\r\n\r\n<p><strong>USG inguinalis kanan dalam batas normal. </strong></p>\r\n\r\n<p><strong>Tidak tampak pembesaran KGB di inguinal bilateral.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong></p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (237, 'USG ABdomen Lengkap ', '<p>Hepar&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; Tidak membesar. Textur parenkhim homogen, tak tampak SOL. Ductus biliaris, vena hepatika, vena porta tak melebar.</p>\r\n\r\n<p>Kt Empedu&nbsp;&nbsp; :&nbsp; Besar &amp; bentuk normal. Dinding tidak menebal. Tak tampak batu ataupun SOL</p>\r\n\r\n<p>Limpa&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; Besar &amp; bentuk dlm batas normal. Textur parenkhim&nbsp; homogen.Tak tampak SOL. Vena Lienalis tak melebar.</p>\r\n\r\n<p>Pankreas&nbsp;&nbsp;&nbsp; :&nbsp; Besar &amp; bentuk dlm batas normal. Textur homogen</p>\r\n\r\n<p>Paraaorta&nbsp;&nbsp;&nbsp; :&nbsp; Besar &amp; bentuk aorta abdominalis dalam batas normal.Tak tampak bayangan noduler hypo ataupun hyper echoic disekitar aorta abdominalis.</p>\r\n\r\n<p>Ginjal&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; Besar &amp; bentuk normal. Batas textur parenkhim dg central echo complex normal. Sistim pelvocalises tidak lebar.Tak tampak batu ataupun SOL</p>\r\n\r\n<p>Kt Kemih&nbsp;&nbsp; :&nbsp; Besar &amp; bentuk&nbsp; normal, dinding tidak menebal. Tak tampak batu ataupun SOL</p>\r\n\r\n<p>Uterus&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; Besar &amp; bentuk dalam batas normal.</p>\r\n\r\n<p>Ovarium&nbsp;&nbsp;&nbsp; :&nbsp; Besar, bentuk, &amp; textur dalam batas normal.</p>\r\n\r\n<p>Prostat&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; Besar, bentuk, &amp; textur dalam batas normal.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kesan&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; USG Hepatobilier, kt empedu, limpa, pankreas, kedua ginjal, &amp; kt kemih dalam batas normal.</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Tak tampak pembesaran KGB paraaorta</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (238, 'Ectrodactily', '<p><strong>MANUS SINISTRA</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Tampak 4 ruas jari dengan defisiensi 1 digiti centralis (digiti IV) disertai celah yang lebar dan dalam antara digiti III dan V manus sinistra, membentuk claw like appearance. </strong></p>\r\n\r\n<p>Densitas tulang masih dalam batas normal.</p>\r\n\r\n<p>Tidak tampak lesi litik dan sklerotik.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan :<br />\r\nMenyokong gambaran ectrodactily pada manus sinistra. </strong></p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (239, 'hps usg', '<p>Pylorus &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp;&nbsp; Tampak elongasi dan menebal, dengan ketebalan muskulus pylorus lk 3,2-3,4 mm, panjang pylorus lk 21,5 mmdengan diameter lk 9,4 mm.</p>\r\n\r\n<p>Hepar&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp;&nbsp; Tidak membesar. Parenkim homogen, tak tampak SOL. Ductus biliaris, vena hepatika, vena porta tak melebar. Tak tampak koleksi cairan di sekitarnya.</p>\r\n\r\n<p>Gallbladder&nbsp;&nbsp; :&nbsp;&nbsp; Besar &amp; bentuk normal. Dinding tidak menebal. Tak tampak batu ataupun SOL.</p>\r\n\r\n<p>Limpa&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp;&nbsp; Besar &amp; bentuk dlm batas normal. Textur parenkhim&nbsp; homogen.Tak tampak SOL. Vena Lienalis tak melebar. Tak tampak koleksi cairan di sekitarnya.</p>\r\n\r\n<p>Pankreas&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp;&nbsp; Sulit dinilai, tertutup reverberasi udara.</p>\r\n\r\n<p>Paraaorta&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp;&nbsp; Sulit dinilai, tertutup reverberasi udara.</p>\r\n\r\n<p>Parailiaka&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp;&nbsp; Sulit dinilai, tertutup reverberasi udara.</p>\r\n\r\n<p>Ginjal kanan :&nbsp;&nbsp; Besar &amp; bentuk normal. Sistem pelvocalises tidak melebar. Tak tampak batu ataupun SOL</p>\r\n\r\n<p>Ginjal kiri&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp;&nbsp; Besar &amp; bentuk normal. Sistem pelvocalises tampak melebar ringan. Tak tampak batu ataupun SOL</p>\r\n\r\n<p>V. Urinaria&nbsp;&nbsp; :&nbsp;&nbsp; Sulit dinilai, tidak terisi penuh.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; </strong></p>\r\n\r\n<ul>\r\n	<li><strong>Menyokong gambaran hypertrophic pyloric stenosis (HPS).</strong></li>\r\n	<li><strong>Mild hydronephrosis ginjal kiri. </strong></li>\r\n	<li><strong>USG Hepar, gallbladder, limpa, ginjal kanan dalam batas normal. </strong></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (240, 'USG ABdomen Lengkap_2', '<p>Hepar&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; Tidak membesar.Tepi tajam permukaan rata. Textur parenkhim homogen, tak tampak SOL. Ductus biliaris, vena hepatika, vena porta tak melebar.</p>\r\n\r\n<p>Kt Empedu&nbsp;&nbsp; :&nbsp; Besar &amp; bentuk dalam batas normal, dinding tidak menebal. Tak tampak batu ataupun SOL</p>\r\n\r\n<p>Limpa&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; Besar &amp; bentuk dlm batas normal. Textur parenkhim&nbsp; homogen.Tak tampak SOL. Vena Lienalis tak melebar.</p>\r\n\r\n<p>Pankreas&nbsp;&nbsp;&nbsp; :&nbsp; Besar &amp; bentuk dlm batas normal. Textur homogen</p>\r\n\r\n<p>Paraaorta&nbsp;&nbsp;&nbsp; :&nbsp; Besar &amp; bentuk aorta abdominalis dalam batas normal.Tak tampak bayangan noduler hypo ataupun hyper echoic disekitar aorta abdominalis.</p>\r\n\r\n<p>Ginjal&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; Besar &amp; bentuk normal. Batas textur parenkhim dg central echo complex normal. Sistim pelvocalises tidak melebar. Tak tampak batu ataupun SOL</p>\r\n\r\n<p>Kt Kemih&nbsp;&nbsp; :&nbsp; Besar &amp; bentuk dlm batas normal, dinding tidak menebal. Tak tampak batu ataupun SOL</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kesan&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; USG Hepatobilier, kt empedu, limpa, pankreas, kedua ginjal &amp; kt kemih dalam batas normal.</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Tak tampak pembesaran KGB paraaorta.</p>\r\n\r\n<p>&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (241, 'Leg length discrepancy', '<p><strong>FEMUR &ndash; CRURIS BILATERAL </strong></p>\r\n\r\n<p>Besar, bentuk dan struktur trabekula os femur, tibia dan fibula bilateral dalam batas normal.</p>\r\n\r\n<p>Epiphyseal plate belum menutup.</p>\r\n\r\n<p>Permukaan dan sela sendi dalam batas normal.</p>\r\n\r\n<p>Panjang os femur : 19,82 cm (kanan)&nbsp; 19,48 cm (kiri)</p>\r\n\r\n<p>Panjang os tibia : 16,14 cm (kanan)&nbsp; 15,98 cm (kiri)</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan&nbsp;&nbsp;&nbsp; : </strong></p>\r\n\r\n<p><strong>Gambaran leg length discrepancy dengan perbedaan panjang extremitas inferior kanan dan kiri lk 0,5 cm. </strong></p>\r\n\r\n<p>&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (242, 'osteogenesis imperfecta (2)', '<p><strong>THORAX </strong></p>\r\n\r\n<p>Foto asimetris, kurang inspirasi.</p>\r\n\r\n<p>Skeletal :</p>\r\n\r\n<p>Tampak gambaran multiple beaded pada os costae bilateral.</p>\r\n\r\n<p>Tampak deformitas clavicula, proksimal os humerus dan glenoid bilateral yang terscanning.</p>\r\n\r\n<p>Densitas os clavicula, costae dan proksimal humerus yang terscanning tampak menurun.</p>\r\n\r\n<p>Cor tidak membesar. Sinuses dan diafragma normal.</p>\r\n\r\n<p>Pulmo :</p>\r\n\r\n<p>Hilli superposisi bayangan jantung. Corakan bronkhovaskuler normal.</p>\r\n\r\n<p>Tidak tampak infiltrat.</p>\r\n\r\n<p>Ujung OGT terproyeksi setinggi paravertebral Th12-L1 kiri.</p>\r\n\r\n<p>Ujung umbilical catheter terproyeksi setinggi paravertebral Th10-11 kanan.</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n\r\n<p><strong>Kesan : </strong></p>\r\n\r\n<p><strong>Sugestif osteogenesis imperfecta. </strong></p>\r\n\r\n<p><strong>Pulmo saat ini tidak tampak kelainan. </strong></p>\r\n\r\n<p><strong>Tidak tampak kardiomegali. </strong></p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (243, 'USG Abdomen', '<p>Ginjal kanan :&nbsp; Besar dan bentuk normal. Batas textur parenkhim dg central echo complex normal. Sistim pelvocalises tidak lebar. Tak tampak batu ataupun SOL.</p>\r\n\r\n<p>Ginjal kiri&nbsp; :&nbsp; Besar dan bentuk normal. Batas textur parenkhim dg central echo complex normal. Sistim pelvocalises tidak lebar. Tak tampak batu ataupun SOL</p>\r\n\r\n<p>Kt Kemih&nbsp; :&nbsp; Besar, bentuk dan &nbsp;dinding dlm batas normal. Tak tampak batu ataupun SOL</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kesan&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; USG ginjal bilateral &amp; kt kemih dalam batas normal.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Hepar&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Membesar. Parenkhim inhomogen, tampak massa isoekhoik, batas tegas, ukuran 79,6x89,3 mm di hepar lobus kiri. Ductus biliaris &amp; vena hepatika, tak melebar. Vena porta tampak sedikit lebar dengan thrombus di dalamnya. Tampak koleksi cairan di sekitar hepar.</p>\r\n\r\n<p>Kt Empedu&nbsp;&nbsp; :&nbsp; Besar &amp; bentuk dalam batas normal, dinding menebal. Tak tampak SOL. Tampak batu, ukuran 9,5 mm.</p>\r\n\r\n<p>Limpa&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; Besar &amp; bentuk dlm batas normal. Textur parenkhim&nbsp; homogen.Tak tampak SOL. Vena Lienalis tak melebar.</p>\r\n\r\n<p>Pankreas&nbsp;&nbsp;&nbsp; :&nbsp; Besar &amp; bentuk dlm batas normal. Textur homogen</p>\r\n\r\n<p>Paraaorta&nbsp;&nbsp;&nbsp; :&nbsp; Besar &amp; bentuk aorta abdominalis dalam batas normal.Tak tampak bayangan noduler hypo ataupun hyper echoic disekitar aorta abdominalis.</p>\r\n\r\n<p>Ginjal kanan :&nbsp; Besar &amp; bentuk normal. Batas textur parenkhim dg central echo complex normal. Sistim pelvocalises tidak melebar. Tak tampak batu ataupun SOL</p>\r\n\r\n<p>Ginjal kiri&nbsp; :&nbsp; Besar &amp; bentuk normal. Batas textur parenkhim dg central echo complex normal. Sistim pelvocalises tidak melebar. Tak tampak batu ataupun SOL</p>\r\n\r\n<p>Kt Kemih&nbsp;&nbsp; :&nbsp; Besar &amp; bentuk dlm batas normal, dinding tidak menebal. Tak tampak batu ataupun SOL</p>\r\n\r\n<p>Abdomen bawah kanan-kiri : tampak koleksi cairan.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kesan&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; USG Hepatobilier, gallbladder, limpa, pankreas, kedua ginjal &amp; kt kemih dalam batas normal.</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Tak tampak pembesaran KGB paraaorta.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (244, 'osteogenesis imperfecta', '<p><strong>BABYGRAM</strong></p>\r\n\r\n<p>Foto asimetris.</p>\r\n\r\n<p><strong>Skeletal : </strong></p>\r\n\r\n<p><strong>Multiple fraktur dan gambaran beaded pada os costae bilateral.</strong></p>\r\n\r\n<p><strong>Deformitas os clavicula, os humerus dan glenoid bilateral.</strong></p>\r\n\r\n<p><strong>Fraktur 1/3 tengah os radius-ulna kanan dan os ulna kiri. </strong></p>\r\n\r\n<p><strong>Fraktur os femur bilateral dan os fibula kanan. </strong></p>\r\n\r\n<p><strong>Gambaran bowing pada os humerus bilateral, femur bilateral dan cruris bilateral. </strong></p>\r\n\r\n<p><strong>Densitas tulang-tulang yang terscanning tampak menurun.</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Thorax : </strong></p>\r\n\r\n<p>Cor tidak membesar. Sinuses dan diafragma normal.</p>\r\n\r\n<p>Pulmo : &nbsp;Hilli superposisi bayangan jantung. Corakan bronkhovaskuler normal.</p>\r\n\r\n<p>Tidak tampak infiltrat.</p>\r\n\r\n<p>Ujung OGT terproyeksi setinggi paravertebral L1 kiri.</p>\r\n\r\n<p>Ujung umbilical catheter terproyeksi setinggi paravertebral Th 11-12 kanan.</p>\r\n\r\n<p>Lateral : Tampak gambaran multiple fraktur pada os costae.</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Sinus anterior dan posterior tumpul (posisi?)</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Retrosternal dan retrocardiac space cerah.</p>\r\n\r\n<p><strong>Kesan : </strong></p>\r\n\r\n<p><strong>Sugestif osteogenesis imperfecta. </strong></p>\r\n\r\n<p><strong>Pulmo saat ini tidak tampak kelainan. </strong></p>\r\n\r\n<p><strong>Tidak tampak kardiomegali. </strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>BNO : </strong></p>\r\n\r\n<p>Preperitoneal fat jelas.</p>\r\n\r\n<p>Kontur kedua ginjal tak jelas.</p>\r\n\r\n<p>Tampak distribusi udara berlebih pada usus-usus di abdomen atas sampai bawah dengan hilangnya pola mozaik, membentuk gambaran bag of sausage.</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n\r\n<p><strong>Kesan : </strong></p>\r\n\r\n<p><strong>Curiga ileus obstruktif letak rendah (bagaimana klinis?)</strong></p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (245, 'USG kepala', '<p><em>USG Kepala</em></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Pada scanning coronal dan sagital: sulci dan gyri masih tampak jelas</p>\r\n\r\n<p>Fissura interhemisfer masih tampak normal.</p>\r\n\r\n<p>Ventrikel lateralis kanan dan kiri serta ventrikel 3 tampak normal</p>\r\n\r\n<p>Tidak tampak lesi hipo/hiperekhoik pada thalamus, cerebri bilateral dan cerebellum.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan:</strong></p>\r\n\r\n<p><strong>USG kepala saat ini tidak tampak kelainan</strong></p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (246, 'USG Mammae', '<p><strong><u>Mammae Kanan&nbsp; :</u></strong></p>\r\n\r\n<p>Kutis dan sub kutis&nbsp; normal. Areola normal.Jaringan fibroglandular kurang</p>\r\n\r\n<p>padat, sebagian telah mengalami involusi lemak. Tidak tampak bayangan massa</p>\r\n\r\n<p>iso-hiper-hipo dan anekhoik,</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><u>Mammae Kiri&nbsp; :</u></strong></p>\r\n\r\n<p>Kutis dan sub kutis&nbsp; normal. Areola normal.Jaringan fibroglandular kurang</p>\r\n\r\n<p>padat, sebagian telah mengalami involusi lemak. Tidak tampak bayangan massa</p>\r\n\r\n<p>iso-hiper-hipo dan anekhoik,</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n\r\n<p><strong><u>Axilla Kanan-kiri :</u></strong></p>\r\n\r\n<p>Tidak tampak nodul hipo/anekhoik</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kesan:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - USG Mammae kanan-kiri saat ini dalam batas normal</p>\r\n\r\n<p><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - Tidak tampak pembesaran KGB axilla kanan-kiri.</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (247, 'polydactyly', '<p><strong>MANUS</strong><strong> </strong><strong>SINISTRA</strong><strong> AP/OBLIQUE</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tampak tambahan dua os phalanges pada sisi lateral digiti I manus sinistra (extra digiti).</p>\r\n\r\n<p>Besar, bentuk, dan struktur trabekula tulang-tulang pembentuk manus sinistra lainnya dalam batas normal.</p>\r\n\r\n<p>Sela dan permukaan sendi normal.</p>\r\n\r\n<p>Tidak tampak lesi litik/sklerotik.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan&nbsp;&nbsp;&nbsp; :</strong></p>\r\n\r\n<p><strong>Gambaran pre-axial polydactyly pada digiti I manus sinistra.</strong></p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (248, 'USG Mammae_nona', '<p><strong><u>Mammae Kanan&nbsp; :</u></strong></p>\r\n\r\n<p>Kutis dan sub kutis&nbsp; normal. Areola normal. Jaringan fibroglandular masih</p>\r\n\r\n<p>padat, belum mengalami involusi lemak. Tidak tampak bayangan massa</p>\r\n\r\n<p>iso-hiper-hipo dan anekhoik,</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><u>Mammae Kiri&nbsp; :</u></strong></p>\r\n\r\n<p>Kutis dan sub kutis&nbsp; normal. Areola normal. Jaringan fibroglandular masih</p>\r\n\r\n<p>padat, belum mengalami involusi lemak. Tidak tampak bayangan massa</p>\r\n\r\n<p>iso-hiper-hipo dan anekhoik,</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><u>Axilla Kanan-kiri&nbsp; :</u></strong></p>\r\n\r\n<p>Tidak tampak nodul hipo/anekhoik</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kesan:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - USG mammae kanan-kiri saat ini masih daam batas normal</p>\r\n\r\n<p><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - Tidak tampak pembesaran KGB axilla kanan-kiri.</strong></p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (249, 'post arthrodesis', '<p><strong>ANKLE JOINT SINISTRA</strong><strong> (dibandingkan foto ankle joint sinistra 17-6-2020)</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li><strong>Post arthrodesis, tampak penyempitan sela sendi talocruralis sinistra. </strong></li>\r\n	<li><strong>Tampak reaksi periosteal pada distal os tibia sinistra.</strong></li>\r\n	<li><strong>Tampak gambaran opak pada soft tissue anterior cruris sinistra (myositis ossificans DD/ fragmen tulang)</strong></li>\r\n	<li><strong>Tidak tampak garis fraktur. </strong></li>\r\n	<li><strong>Tidak tampak gambaran osteomyelitis.</strong></li>\r\n	<li><strong>Besar, bentuk, dan struktur trabekula tulang-tulang pembentuk ankle joint sinistra lainnya&nbsp; dalam batas normal.</strong></li>\r\n</ul>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (250, 'USG testis', '<p><strong><u>Testis kanan</u></strong><strong><u>-kiri </u></strong><strong><u>&nbsp;:</u></strong></p>\r\n\r\n<p>Besar dan bentuk normal, densitas echo parenkim normal. Tidak tampak SOL.</p>\r\n\r\n<p>Tidak tampak koleksi cairan di sekitarnya.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><u>Epididimi</u></strong><strong><u>s </u></strong><strong><u>kanan-kiri :</u></strong></p>\r\n\r\n<p>Besar dan bentuk normal. densitas echo parenkim normal. Tidak tampak SOL.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Inguinal kanan-kiri :</p>\r\n\r\n<p>Tidak tampak bayangan usus</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; : &nbsp;&nbsp;&nbsp; USG testis kanan-kiri saat ini masih dalam batas normal</strong></p>\r\n\r\n<p><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Tidak tampak gambaran hernia inguinalis kanan-kiri saat ini</strong></p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (251, 'USG thorak', '<p><strong><u>Supradiafragma</u></strong><strong><u> kanan</u></strong><strong><u>-kiri</u></strong><strong><u> :</u></strong></p>\r\n\r\n<p>Tidak tampak koleksi cairan di supradiafragma kanan-kiri. Terpasang marker di</p>\r\n\r\n<p>pleura kanan dengan jarak 40 mm.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; : &nbsp;Efusi pleura kanan-kiri</strong></p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (252, 'schuller s + stenver s', '<p><strong>SCHULLER&rsquo;S DAN STENVER&rsquo;S</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Cellulae mastoidea dextra tampak berselubung.</p>\r\n\r\n<p>Angulus Citelli tampak normal.</p>\r\n\r\n<p>Meatus acusticus eksternus tampak normal.</p>\r\n\r\n<p>Tidak tampak lesi lusen dan destruksi.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><u>Kesan :</u></strong><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong></p>\r\n\r\n<p><strong>Menyokong gambaran mastoiditis dextra.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong></p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (253, 'USG thyroid', '<p><strong><u>Thyroid&nbsp; Kanan dan Kiri :</u></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li>Tidak membesar, densitas homogen.</li>\r\n	<li>Tidak tampak nodul iso/hipo/hiper/anekhoik</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li>Isthmus&nbsp;&nbsp;&nbsp;&nbsp; : Tidak melebar</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><em>Kesan&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; : USG Kedua thyroid saat ini dalam batas normal</em></p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (254, 'sol herniasi', '<p>Dilakukan CT scan kepala potongan aksial dengan slice interval 5 mm di daerah basis sampai vertex. Potongan dibuat sejajar orbitomeatal. Scanning tanpa dan dengan memakai kontras media.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tampak defek os parietalis kanan (post op).</p>\r\n\r\n<p>Tampak massa intraaxial dengan densitas isodens inhomogen batas tegas tepi ireguler berukuran lk 7,69 x 8,7 x 7,08 cm (tampak relatif lebih besar dibandingkan CT scan sebelumnya, lk 6,8 x 7 cm) di daerah midline lobus parietalis, yang tampaknya mendesak ventrikel lateralis bilateral dan ventrikel 3 ke inferior dan mengalami herniasi melalui defek pada os parietal kanan dengan ukuran lk 7,8 x 13,12 x 6,83 cm, tampak densitas heterogen dengan gambaran hiperdens di dalamnya. Tampak lesi hipodens di sekitarnya. Post contrast scanning tampak memberikan enhancement kuat.</p>\r\n\r\n<p>Sulci dan gyri corticalis, fissura Sylvii bilateral dan fissura interhemisfer tampak menyempit.</p>\r\n\r\n<p>Tampak pelebaran cornu temporalis ventrikel lateralis bilateral.</p>\r\n\r\n<p>Sisterna ambiens dan basalis tampak normal.</p>\r\n\r\n<p>Daerah sela tursika dan jukstasella serta daerah cerebello-pontin angle masih dalam batas normal.</p>\r\n\r\n<p>Parenkim cerebri lainnya, cerebellum dan pons tidak menunjukkan densitas patologis.</p>\r\n\r\n<p>Tampak kalsifikasi fisiologis di daerah glandula pinealis dan plexus choroideus bilateral.</p>\r\n\r\n<p>Mastoid air cell bilateral yang terscanning tampak normal.</p>\r\n\r\n<p>Sinus paranasalis yang terscanning dalam batas normal.</p>\r\n\r\n<p>Bulbus okuli dan ruang retrobulber bilateral dalam batas normal.</p>\r\n\r\n<p>Tidak tampak pergeseran struktur garis tengah.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan :</strong></p>\r\n\r\n<p><strong>Massa heterogen berukuran lk 7,69 x 8,7 x 7,08 cm (tampak relatif lebih besar dibandingkan CT scan sebelumnya, lk 6,8 x 7 cm) di daerah midline lobus parietalis, yang tampaknya mendesak ventrikel lateralis bilateral dan ventrikel 3 ke inferior dan mengalami herniasi melalui defek pada os parietal kanan dengan ukuran lk 7,8 x 13,12 x 6,83 cm disertai edema perifokal </strong><strong>&agrave; suspek suatu meningioma, curiga disertai perdarahan.</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (255, 'USG_Abdomen', '<p>Hepar&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; Tepi tajam permukaan rata. Textur parenkhim homogen, tak tampak SOL. Ductus biliaris, vena hepatika, vena porta tak melebar.</p>\r\n\r\n<p>Kt Empedu: Besar bentuk dan dinding normal. Tak tampak batu ataupun SOL</p>\r\n\r\n<p>Limpa&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; Besar bentuk dlm batas normal. Textur parenkhim&nbsp; homogen.Tak tampak SOL. Vena Lienalis tak melebar.</p>\r\n\r\n<p>Pankreas&nbsp;&nbsp;&nbsp; :&nbsp; Besar bentuk dlm batas normal. Textur homogen</p>\r\n\r\n<p>Paraaorta&nbsp;&nbsp; :&nbsp; Besar &amp; bentuk aorta abdominalis dalam batas normal.Tak tampak bayangan noduler hypo ataupun hyper echoic disekitar aorta abdominalis.</p>\r\n\r\n<p>Ginjal&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; Besar bentuk kedua ginjal normal. Batas textur parenkhim dg central echo complex normal. Tak tampak batu ataupun SOL</p>\r\n\r\n<p>Kt Kemih&nbsp; :&nbsp; Besar bentuk dinding dlm batas normal. Tak tampak batu ataupun SOL</p>\r\n\r\n<p>Uterus&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; Besar &amp; bentuk dalam batas normal.</p>\r\n\r\n<p>Ovarium&nbsp;&nbsp;&nbsp; :&nbsp; Besar, bentuk, &amp; textur dalam batas normal.</p>\r\n\r\n<p>Prostat&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; Besar, bentuk, &amp; textur dalam batas normal.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kesan&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; USG Hepatobilier, limpa, pankreas, kedua ginjal, &amp; kt kemih dalam batas normal.</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Tak tampak pembesaran KGB paraaorta.</p>\r\n\r\n<p>&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (256, 'tati nurjanah', '<p>Hepar&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp;&nbsp; Tidak membesar. Parenkim homogen, tak tampak SOL. Ductus biliaris, vena hepatika, vena porta tak melebar. Tampak koleksi cairan di sekitar hepar dan supradiafragma kanan.</p>\r\n\r\n<p>Gallbladder&nbsp;&nbsp; :&nbsp;&nbsp; Besar &amp; bentuk normal. Dinding menebal. Tak tampak batu ataupun SOL.</p>\r\n\r\n<p>Limpa&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp;&nbsp; Besar &amp; bentuk dlm batas normal. Textur parenkhim&nbsp; homogen.Tak tampak SOL. Vena Lienalis tak melebar. Tak tampak koleksi cairan di sekitarnya.</p>\r\n\r\n<p>Pankreas&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp;&nbsp; Sulit dinilai, tertutup reverberasi udara.</p>\r\n\r\n<p>Paraaorta&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp;&nbsp; Sulit dinilai, tertutup reverberasi udara.</p>\r\n\r\n<p>Parailiaka&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp;&nbsp; Sulit dinilai, tertutup reverberasi udara.</p>\r\n\r\n<p>Ginjal&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp;&nbsp; Besar &amp; bentuk normal. Batas textur parenkhim dengan central echocomplex tidak jelas. Sistem pelvocalises ginjal bilateral dan ureter proksimal kanan melebar. Ureter proksimal kiri terdeteksi, tidak melebar. Tak tampak batu ataupun SOL</p>\r\n\r\n<p>V. Urinaria&nbsp;&nbsp; :&nbsp;&nbsp; Tampak membesar, dinding menebal. Tak tampak batu ataupun SOL.</p>\r\n\r\n<p>Tampak koleksi cairan di antara usus-usus.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; </strong></p>\r\n\r\n<ul>\r\n	<li><strong>Ascites dan efusi pleura kanan.</strong></li>\r\n	<li><strong>Mild hydronephrosis ginjal bilateral dan hydroureter kanan suspek e.c ureterolithiasis.</strong></li>\r\n	<li><strong>Vesika urinaria membesar </strong><strong>&agrave;</strong><strong> sugestif neurogenic bladder.</strong></li>\r\n	<li><strong>USG limpa dalam batas normal. </strong></li>\r\n</ul>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (257, 'Waters normal', '<p><strong><u>Water&rsquo;s</u></strong><strong><u> :</u></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li>Sinus frontalis kanan-kiri, &nbsp;maksilaris kanan-kiri dan sphenoid</li>\r\n</ul>\r\n\r\n<p>kanan-kiri cerah</p>\r\n\r\n<ul>\r\n	<li>Septum nasi di tengah</li>\r\n	<li>Concha nasalis kanan-kiri tidak menebal</li>\r\n	<li>Tulang-tulang sekitar sinus baik</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; Foto water&rsquo;s saat ini tidak tampak kelainan</strong></p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (258, 'thyroglossal duct cyst', '<p><strong>Tampak lesi hipoekhoik dengan posterior enhancement (+) bulat batas tegas tepi regular berukuran lk 8,9 x 7,6 x 10 mm di daerah colli, midline, inferior dari thyroid.&nbsp; Pada pemeriksaan color Doppler tidak tampak vaskularisasi intralesi. </strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Thyroid bilateral yang terscanning :</p>\r\n\r\n<p>Ukuran tidak membesar, struktur parenkim homogen, tidak tampak nodul / massa.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; </strong></p>\r\n\r\n<p><strong>Lesi kistik berukuran lk 8,9 x 7,6 x 10 mm di daerah colli, midline, inferior dari thyroid </strong><strong>&agrave;</strong><strong> menyokong gambaran thyroglossal duct cyst. </strong></p>\r\n\r\n<p><strong>&nbsp;</strong></p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (259, 'torsio ovarium dd TOA', '<p><strong>USG ABDOMEN DAN APPENDIKS</strong></p>\r\n\r\n<p>Hepar&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp;&nbsp; Tidak membesar. Textur parenkhim homogen, tak tampak SOL. Ductus biliaris, vena hepatika, vena porta tak melebar. Tidak tampak koleksi cairan di sekitarnya.</p>\r\n\r\n<p>Kd Empedu&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp;&nbsp; Besar &amp; bentuk normal. Dinding tidak menebal. Tak tampak batu ataupun SOL.</p>\r\n\r\n<p>Limpa&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp;&nbsp; Besar &amp; bentuk dlm batas normal. Textur parenkhim&nbsp; homogen.Tak tampak SOL. Vena Lienalis tak melebar. Tidak tampak koleksi cairan di sekitarnya.</p>\r\n\r\n<p>Pankreas&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp;&nbsp; Besar &amp; bentuk dlm batas normal. Textur homogen</p>\r\n\r\n<p>Paraaorta&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp;&nbsp; Besar &amp; bentuk aorta abdominalis dalam batas normal.Tak tampak bayangan noduler hypo ataupun hyper echoic disekitar aorta abdominalis.</p>\r\n\r\n<p>Parailiaka&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp;&nbsp; Tak tampak bayangan noduler hypo ataupun hyper echoic disekitar parailiaka bilateral.</p>\r\n\r\n<p>Ginjal ka-ki&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp;&nbsp; Besar &amp; bentuk normal. Batas textur parenkhim dg central echo complex normal. Tak tampak batu ataupun SOL. Sistim pelvocalises tidak melebar. Ureter proksimal tidak terdeteksi</p>\r\n\r\n<p>Kd Kemih&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp;&nbsp; Besar &amp; bentuk&nbsp; normal, dinding menebal. Tak tampak batu ataupun SOL.</p>\r\n\r\n<p>Uterus&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;:&nbsp; Besar dan bentuk normal, parenkim homogen. Tampak gambaran hipoekhoik inhomogen batas tegas berukuran lk 4,76 x 3,83 x 4,86 cm di posterior uterus cenderung mengarah sedikit ke kanan, pada pemeriksaan color Doppler tampak vaskularisasi minimal ditepinya. Nyeri tekan probe ultrasound (+) di suprapubis. Tampak koleksi cairan minimal di sekitarnya Tidak tampak kontur ovarium kiri.</p>\r\n\r\n<p><strong>Scan Abdomen Kanan Bawah</strong></p>\r\n\r\n<p>Tidak tampak gambaran usus tubuler buntu tanpa peristaltik yang pada potongan transversal membentuk target sign, Tidak tampak koleksi cairan bebas di abdomen kanan bawah. Tidak tampak massa. Tidak terdapat nyeri tekan probe pada area Mc Burney (tenderness on probe ultrasound)</p>\r\n\r\n<p><strong>Kesan :&nbsp; </strong></p>\r\n\r\n<ul>\r\n	<li><strong>Mencurigai torsio ovarium kanan DD/ tuboovarian abses. </strong></li>\r\n	<li><strong>Gambaran cystitis.</strong></li>\r\n	<li><strong>Tidak tampak kontur appendix (post op).</strong></li>\r\n	<li><strong>USG Hepar, gallbladder, limpa, pancreas, ginjal bilateral dan uterus dalam batas normal</strong></li>\r\n	<li><strong>Tidak tampak pembesaran KGB paraaorta/parailiaka. </strong></li>\r\n</ul>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (260, 'wrist joint', '<p><strong><u>Wrist</u></strong><strong><u> Joint</u></strong><strong><u> </u></strong><strong><u>sinistra</u></strong><strong><u> :</u></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Besar, bentuk dan struktur trabekula normal</p>\r\n\r\n<p>Sela dan permukaaan sendi normal</p>\r\n\r\n<p>Tidak tampak lesi litik/sklerotik</p>\r\n\r\n<p>Tidak tampak osteofit</p>\r\n\r\n<p>Tidak tampak fraktur</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan :</strong></p>\r\n\r\n<ul>\r\n	<li><strong>Foto </strong><strong>wri</strong><strong>st</strong><strong> join</strong><strong>t sinistra</strong><strong> saat ini</strong><strong> tidak tampak kelainan</strong></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (261, 'ujang sujana', '<p>Hepar&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp;&nbsp; Tidak membesar. Parenkim inhomogen, tampak multiple lesi hipoekhoik bulat berdiameter terbesar lk 5,67 cm di hepar lobus kanan. Tampak lesi kistik berukuran lk 4,35 x 3,51 x 2,91 cm di hepar lobus kanan. Ductus biliaris, vena hepatika, vena porta tak melebar. Tak tampak koleksi cairan di sekitarnya.</p>\r\n\r\n<p>Gallbladder&nbsp;&nbsp; :&nbsp;&nbsp; Besar &amp; bentuk normal. Dinding tidak menebal. Tak tampak batu ataupun SOL.</p>\r\n\r\n<p>Limpa&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp;&nbsp; Besar &amp; bentuk dlm batas normal. Textur parenkhim&nbsp; homogen.Tak tampak SOL. Vena Lienalis tak melebar. Tak tampak koleksi cairan di sekitarnya.</p>\r\n\r\n<p>Pankreas&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp;&nbsp; Sulit dinilai, tertutup reverberasi udara.</p>\r\n\r\n<p>Paraaorta&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp;&nbsp; Sulit dinilai, tertutup reverberasi udara.</p>\r\n\r\n<p>Ginjal&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp;&nbsp; Besar &amp; bentuk normal. Batas textur parenkhim dengan central echocomplex normal. Sistem pelvocalises tidak melebar. Tak tampak batu ataupun SOL.</p>\r\n\r\n<p>V. Urinaria&nbsp;&nbsp; :&nbsp;&nbsp; Sulit dinilai, tidak terisi penuh.</p>\r\n\r\n<p>Scan Abdomen Kanan Bawah :</p>\r\n\r\n<p>Tidak tampak gambaran usus tubuler buntu tanpa peristaltic yang pada potongan transversal membentuk target sign, Tidak tampak koleksi cairan bebas. Tidak tampak massa. Tidak terdapat nyeri tekan probe pada area Mc Burney (tenderness on probe ultrasound).</p>\r\n\r\n<p><strong>Kesan&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; </strong></p>\r\n\r\n<ul>\r\n	<li><strong>Multiple nodul berbagai ukuran di hepar lobus kanan (diameter terbesar lk 5,67 cm) </strong><strong>&agrave;</strong><strong> suspek hepatoma DD/ metastasis intrahepatal.</strong></li>\r\n	<li><strong>Massa kistik di hepar lobus kanan. </strong></li>\r\n	<li><strong>Kontur appendix tidak tervisualisasi. </strong></li>\r\n	<li><strong>USG gallbladder, limpa, kedua ginjal dalam batas normal. </strong></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Saran : CT scan abdomen dengan kontras (3 fase). </strong></p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (262, 'Antebrachii', '<p><strong>ANTEBRACHII</strong><strong> </strong><strong>DEX</strong><strong>TRA</strong></p>\r\n\r\n<p>Besar bentuk dan struktur trabecula os radius dan ulna dextra dalam batas normal.</p>\r\n\r\n<p>Permukaan dan sela sendi dalam batas normal.</p>\r\n\r\n<p>Tidak tampak fraktur.</p>\r\n\r\n<p>Tidak tampak lesi litik dan sklerotik.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan :&nbsp; </strong></p>\r\n\r\n<p><strong>Foto antebrachii dextra saat ini dalam batas normal.</strong></p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (263, 'Barium enema', '<p><strong>Barium Enema :</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Didapatkan penyempitan lumen daerah rectum disertai pelebaran lumen di </strong></p>\r\n\r\n<p><strong>bagian proksimalnya, dengan transtitional zone pada daerah rectosigmoid. </strong></p>\r\n\r\n<p><strong>Ratio rectum : sigmoid &lt; 1</strong></p>\r\n\r\n<p><strong>Mucosa colon tampak masih terlihat dalam atas normal</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Keasan : </strong></p>\r\n\r\n<p><strong>Menyokong hirschsprung&#39;s disease dengan transtitional zone pada daerah </strong></p>\r\n\r\n<p><strong>rectosigmoid (short segment)</strong></p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (264, 'BNO normal', '<p><strong>BNO</strong><strong> </strong><strong>&nbsp;</strong></p>\r\n\r\n<p>Preperitoneal fat jelas.</p>\r\n\r\n<p>Psoas line jelas.</p>\r\n\r\n<p>Kontur ginjal bilateral jelas. Tidak tampak konkramen opak.</p>\r\n\r\n<p>Distribusi udara usus halus dan colon dalam batas normal.</p>\r\n\r\n<p>Tampak gambaran usus di rongga pelvis.</p>\r\n\r\n<p>Skeletal : Tampak osteofit pada endplate corpora vertebra lumbalis.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan&nbsp; :</strong></p>\r\n\r\n<p><strong>Tidak tampak tanda-tanda ileus. </strong></p>\r\n\r\n<p><strong>Tidak tampak urolithiasis opak. </strong></p>\r\n\r\n<p><strong>Spondylosis vertebra lumbalis. </strong></p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (265, 'BNO tegak', '<p>&nbsp;</p>\r\n\r\n<p><strong>BNO TEGAK</strong></p>\r\n\r\n<p>Preperitoneal fat jelas, psoas line jelas.</p>\r\n\r\n<p>Kontur kedua ginjal jelas.</p>\r\n\r\n<p>Free air (-), air fluid level (-).</p>\r\n\r\n<p>Distribusi udara usus halus dan colon sulit dinilai pada posisi tegak.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan : </strong></p>\r\n\r\n<p><strong>Tidak tampak gambaran pneumoperitoneum. </strong></p>\r\n\r\n<p><strong>Tidak tampak tanda-tanda ileus. </strong></p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (266, 'BNO', '<p><strong>BNO</strong><strong> </strong><strong>&nbsp;</strong></p>\r\n\r\n<p>Pre peritoneal fat jelas.</p>\r\n\r\n<p>Psoas line kanan tidak jelas, psoas line kiri jelas.</p>\r\n\r\n<p>Kontur ginjal kanan jelas, kontur ginjal kiri tidak jelas.</p>\r\n\r\n<p>Distribusi udara usus halus tampak berlebih di abdomen atas sampai bawah kiri disertai penebalan sebagian dindingnya.</p>\r\n\r\n<p>Distribusi udara colon dalam batas normal dengan fecal material di dalamnya.</p>\r\n\r\n<p>Posisi tegak : air fluid level (+), free air (-).</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan&nbsp; :</strong></p>\r\n\r\n<p><strong>Ileus lokal di abdomen atas sampai bawah kiri.</strong></p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (267, 'Bronchopneumonia', '<p><strong><u>THORAX </u></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Soft tissue dan skeletal tampak normal.</p>\r\n\r\n<p>Trachea posisi midline</p>\r\n\r\n<p>Cor tidak membesar.</p>\r\n\r\n<p>Sinuses dan diafragma normal.</p>\r\n\r\n<p>Pulmo&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; Hilli normal.</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Corakan bronkhovaskuler normal.</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Tampak infiltrat di perihiler-parakardial kanan dan suprahiler kiri.</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n\r\n<p><strong>Kesan&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; Curiga bronchopneumonia bilateral (bagaimana klinis dan lab?)</strong></p>\r\n\r\n<p><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Tidak tampak kardiomegali.</strong></p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (268, 'Cervical', '<p>&nbsp;</p>\r\n\r\n<p><strong>CERVICAL</strong><strong> </strong><strong>AP/OBLIQUE KIRI</strong></p>\r\n\r\n<p>Curve dan alignment baik.</p>\r\n\r\n<p>Besar dan struktur trabekula normal.</p>\r\n\r\n<p>Tampak osteofit pada endplate corpus vertebra cervicalis.</p>\r\n\r\n<p>Tampak penyempitan ringan foramen intervertebralis C6-7.</p>\r\n\r\n<p>Discus dan foramen intervertebralis lainnya tidak menyempit.</p>\r\n\r\n<p>Pedicle normal.</p>\r\n\r\n<p>Tidak tampak lesi litik dan sklerotik.</p>\r\n\r\n<p>Tidak tampak fraktur.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan&nbsp;&nbsp;&nbsp; : </strong></p>\r\n\r\n<p><strong>Spondylosis cervicalis disertai penyempitan ringan </strong><strong>foramen intervertebralis C6-7. </strong></p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (269, 'Cruris', '<p><strong>CRURIS</strong><strong> </strong><strong>DEX</strong><strong>TRA</strong></p>\r\n\r\n<p>Besar bentuk dan struktur trabecula os tibia dan fibula dextra dalam batas normal.</p>\r\n\r\n<p>Permukaan dan sela sendi dalam batas normal.</p>\r\n\r\n<p>Tidak tampak fraktur.</p>\r\n\r\n<p>Tidak tampak lesi litik dan sklerotik.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan :&nbsp; </strong></p>\r\n\r\n<p><strong>Foto cruris dextra saat ini dalam batas normal.</strong></p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (270, 'CT knee - tibial plateau fracture', '<p>Dilakukan CT scan genu dextra dengan slice thickness 0,9 mm. Dilakukan rekonstruksi 3D. Scanning tanpa memakai kontras media.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tampak fraktur kominutif intraartikular mulai dari epifisis sampai metadiafisis os tibia dextra dengan displacement dan depresif pada tibial plateau.</p>\r\n\r\n<p>Besar, bentuk dan struktur trabekula distal os femur dan proksimal os fibula dextra dalam batas normal.</p>\r\n\r\n<p>Sela sendi genu dextra tidak tampak menyempit.</p>\r\n\r\n<p>Tidak tampak lesi litik maupun sklerotik.</p>\r\n\r\n<p>Jaringan kutis dan otot-otot lainnya yang terscanning di daerah genu dextra masih dalam batas normal.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan :</strong></p>\r\n\r\n<p><strong>- Fraktur kominutif intra intraartikular mulai dari epifisis sampai metadiafisis os tibia dextra dengan displacement dan depresif pada tibial plateau </strong><strong>&agrave; Fraktur tibial plateau tipe II (klasifikasi Schatzker)</strong></p>\r\n\r\n<p><strong>- Tidak tampak gambaran osteomyelitis. </strong></p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (271, 'CT Nasofaring', '<p style=\"text-align:justify\"><strong><em>Susp. Tumor nasofaring </em></strong></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">Dilakukan MSCT/CT scan leher potongan aksial dengan <em>slice interval 0.6</em> mm dimulai dari ketinggian frontoocipital ke os vertebra cervical 7. Potongan dibuat sejajar <strong>palatum durum.</strong> Dibuat rekonstruksi axial coronal dan sagital. Scanning dilakukan <strong>tanpa dan dengan </strong>memakai kontras media.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">Jaringan lunak nasofaring, orofaring, dan hipofaring tampak normal.</p>\r\n\r\n<p style=\"text-align:justify\">Recessus pharyngeus dan torus tubarius tampak normal.</p>\r\n\r\n<p style=\"text-align:justify\">Tidak tampak penebalan jaringan lunak preoccipital(N&lt;1,5cm).</p>\r\n\r\n<p style=\"text-align:justify\">Cavum nasi dan concha nasalis bilateral tampak normal.</p>\r\n\r\n<p style=\"text-align:justify\">Septum nasi masih di tengah.</p>\r\n\r\n<p style=\"text-align:justify\">Kolom udara dalam faring, laring, glottis, dan trakea tampak normal.</p>\r\n\r\n<p style=\"text-align:justify\">Palatum, pterygoid plate dan mandibula tampak normal.</p>\r\n\r\n<p style=\"text-align:justify\">Kelenjar parotis dan submandibula bilateral tampak normal.</p>\r\n\r\n<p style=\"text-align:justify\">Retropharyngeal space, parapharyngeal space bilateral,carotid space bilateral, fossa infratemporalis bilateral dan masticator space bilateral dalam batas normal.</p>\r\n\r\n<p style=\"text-align:justify\">Struktur vaskuler (a.carotis comunis bilateral dan v.jugularis interna) tampak normal.</p>\r\n\r\n<p style=\"text-align:justify\">Kelenjar tiroid kanan dan kiri tidak membesar. Kontur parenkim tampak homogen</p>\r\n\r\n<p style=\"text-align:justify\">Laring dan os hyoid dalam batas normal.</p>\r\n\r\n<p style=\"text-align:justify\">Epiglotis dan valekula tampak normal.</p>\r\n\r\n<p style=\"text-align:justify\">Tidak tampak destruksi tulang basis kranii dan tidak tampak infiltrasi intracranial.</p>\r\n\r\n<p style=\"text-align:justify\">Tidak tampak destruksi os maksilofasial dan mandibula.</p>\r\n\r\n<p style=\"text-align:justify\">Tidak tampak pembesaran KGB.</p>\r\n\r\n<p style=\"text-align:justify\">Sinus ethmoidalis, sphenoidalis, dan maksilaris bilateral yang terscanning dalam batas normal.</p>\r\n\r\n<p style=\"text-align:justify\">Bulbus oculi dan ruang retrobulber yang terscanning tampak normal.</p>\r\n\r\n<p style=\"text-align:justify\">Daerah sela tursika dan jukstasella serta daerah &ldquo;cerebello-pontin angle&rdquo; masih dalam batas normal.</p>\r\n\r\n<p style=\"text-align:justify\">Mastoid air cell bilateral yang terscanning tampak normal.</p>\r\n\r\n<p style=\"text-align:justify\">Esofagus tampak normal.</p>\r\n\r\n<p style=\"text-align:justify\">Struktur vertebra cervicalis tampak normal.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><strong><em>Kesimpulan :</em></strong></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><strong><em>CT scan nasofaring saat ini tidak menunjukkan adanya tanda-tanda massa / SOL serta kelainan lainnya</em></strong></li>\r\n	<li style=\"text-align:justify\"><strong><em>Tidak tampak pembesaran KGB.</em></strong></li>\r\n	<li style=\"text-align:justify\"><strong><em>Tidak tampak destruksi tulang.</em></strong></li>\r\n</ul>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (272, 'CT Pelvis 3D', '<p style=\"text-align:justify\"><strong>Dilakukan CT Scan </strong><strong>pelvis</strong><strong> dengan ketebalan potongan axial 3,5 mm</strong><strong> dilakukan rekonstruksi 3D</strong></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">Tampak osteofit pada caput femoris kanan yang tampaknya sedikit menyempitkan sela sendi femoroacetabular kanan.</p>\r\n\r\n<p style=\"text-align:justify\">Tampak entesofit pada tuberositas os ischium kanan.</p>\r\n\r\n<p style=\"text-align:justify\">Acetabulum bilateral tampak normal.</p>\r\n\r\n<p style=\"text-align:justify\">Sela sendi hip joint kiri, dan sacroiliac joint bilateral tidak menyempit.</p>\r\n\r\n<p style=\"text-align:justify\">Besar, bentuk dan struktur trabekula os ischium kiri, ilium bilateral, pubis bilateral dan femur kiri masih dalam batas normal.</p>\r\n\r\n<p style=\"text-align:justify\">Tidak tampak fraktur.</p>\r\n\r\n<p style=\"text-align:justify\">Tidak tampak lesi litik sklerotik.</p>\r\n\r\n<p style=\"text-align:justify\">Struktur musculus, jaringan lunak dan vascular masih dalam batas normal.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><strong>KESAN:</strong></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><strong>Gambaran hip arthritis kanan. </strong></li>\r\n	<li style=\"text-align:justify\"><strong>Entesopathy tuberositas os ischium kanan. </strong></li>\r\n	<li style=\"text-align:justify\"><strong>Hip joint kiri saat ini tidak tampak kelainan. </strong></li>\r\n</ul>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (273, 'CT scan abdomen - appendix', '<p>PEMERIKSAAN CT SCAN ABDOMEN TANPA DAN DENGAN KONTRAS</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Scanning daerah :</p>\r\n\r\n<p>- Gaster &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;: Besar,bentuk, posisi dalam batas normal.</p>\r\n\r\n<p>- Hepar &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;: Ukuran membesar, permukaan rata, densitas homogen. Tidak tampak massa. Vena porta tidak melebar. Vena hepatika tidak melebar. Vena cava inferior tidak melebar. Duktus biliaris intrahepatal tidak melebar. Tidak tampak koleksi cairan di sekitarnya. Tampak gambaran hiperdens di daerah fossa gallbladder (surgical clip?)</p>\r\n\r\n<p>- Kandung empedu : Tidak tervisualisasi (post op).</p>\r\n\r\n<p>- Limpa &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;: Ukuran tidak membesar, densitas homogen. Vena lienalis tidak melebar. Tidak tampak koleksi cairan di sekitarnya.</p>\r\n\r\n<p>- Pankreas &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;: Ukuran tidak membesar, densitas homogen, duktus pankreatikus tidak melebar.</p>\r\n\r\n<p>- Ginjal kanan &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;: Ukuran tidak membesar, struktur parenkim homogen, sistem pelvokalises tidak melebar, tidak tampak batu/ massa.</p>\r\n\r\n<p>- Ginjal&nbsp; kiri &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;: Ukuran tidak membesar, struktur parenkim homogen, sistem pelvokalises tidak melebar, tidak tampak batu/ massa.</p>\r\n\r\n<p>- Tidak tampak nodul hipodens di paraaorta. Tampak nodul hipodens multiple berukuran terbesar lk 1,51 cm di parailiaka kanan dan mesenterial kanan bawah.</p>\r\n\r\n<p>&nbsp; Tampak kalsifikasi aorta abdominalis.</p>\r\n\r\n<p>- Vesika urinaria &nbsp;&nbsp;&nbsp;&nbsp;: Terisi penuh, tidak tampak batu/massa. Perivesical fat normal.</p>\r\n\r\n<p>- Uterus &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;: Ukuran tidak membesar, densitas homogen. Parametrium bilateral tampak normal.</p>\r\n\r\n<p>- Rektum &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;: Posisi normal, dinding tidak menebal, perirectal fat normal.</p>\r\n\r\n<p>Appendix tervisualisasi, tampak melebar dengan diameter lk 0,86 &ndash; 1,11 cm. Tampak fat stranding di sekitarnya.</p>\r\n\r\n<ul>\r\n	<li>Skeletal&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; : Tampak osteofit pada endplate corpora vertebra lumbalis. Vacuum phenomenon pada discus intervertebralis L5-S1.</li>\r\n</ul>\r\n\r\n<p>Thorax yang terscanning : Tampak fibrosis pada segmen lateral lobus medius paru kanan dan segmen posterobasal lobus inferior paru kiri. Tampak lesi isodens dengan tepi ireguler pada hemithorax posterior bawah kiri.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan : </strong></p>\r\n\r\n<p><strong>- </strong><strong>Appendix tampak melebar (diameter lk 0,86-1,11 cm) disertai fat stranding dan pembesaran KGB multiple di mesenterial kanan bawah dan parailiaka kanan </strong><strong>&agrave;</strong><strong> curiga appendicitis. </strong></p>\r\n\r\n<p><strong>- Hepatomegali. </strong></p>\r\n\r\n<p><strong>- Atherosklerosis aorta abdominalis. </strong></p>\r\n\r\n<p><strong>- Fibrosis pada segmen lateral lobus medius paru kanan dan segmen posterobasal lobus inferior paru kiri; Penebalan pleura pada hemithorax posterior bawah kiri </strong><strong>&agrave;</strong><strong> inflamasi kronis. </strong></p>\r\n\r\n<p><strong>- Spondylosis vertebra lumbalis disertai degenerative disc disease. </strong></p>\r\n\r\n<p><strong>- </strong><strong>Scanning limpa, pankreas, ginjal </strong><strong>kanan-</strong><strong>kiri, vesika urinaria, </strong><strong>uterus</strong><strong> dan rektum tidak tampak kelainan.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong></p>\r\n\r\n<p>&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (274, 'CT Scan Abdomen Pria', '<p><strong>PEMERIKSAAN CT SCAN ABDOMEN NON KONTRAS</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>- Gaster &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;: Besar,bentuk, posisi dalam batas normal.</p>\r\n\r\n<p>- Hepar &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;: Ukuran tidak membesar, permukaan rata, densitas homogen. Tidak tampak massa. Vena porta tidak melebar. Vena hepatika tidak melebar. Vena cava inferior tidak melebar. Duktus biliaris intrahepatal tidak melebar. Tidak tampak koleksi cairan di sekitarnya.</p>\r\n\r\n<p>- Kandung empedu : Ukuran tidak membesar, dinding tidak menebal, reguler. Tidak tampak massa/batu. Duktus biliaris ekstrahepatal tidak melebar.</p>\r\n\r\n<p>- Limpa &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;: Ukuran tidak membesar, densitas homogen. Vena lienalis tidak melebar. Tidak tampak koleksi cairan di sekitarnya.</p>\r\n\r\n<p>- Pankreas &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;: Ukuran tidak membesar, densitas homogen, duktus pankreatikus tidak melebar.</p>\r\n\r\n<p>- Ginjal kanan &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;: Ukuran tidak membesar, struktur parenkim homogen, sistem pelvokalises tidak melebar, tidak tampak batu/ massa.</p>\r\n\r\n<p>- Ginjal&nbsp; kiri &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;: Ukuran tidak membesar, struktur parenkim homogen, sistem pelvokalises tidak melebar, tidak tampak batu/ massa.</p>\r\n\r\n<p>- Tidak tampak bayangan hipodens di paraaorta dan parailiaka.</p>\r\n\r\n<p>- Vesika urinaria &nbsp;&nbsp;&nbsp;&nbsp;: Terisi penuh, tidak tampak batu/massa. Perivesical fat normal.</p>\r\n\r\n<p>- Prostat &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;: Ukuran tidak membesar (volume lk 23,22 mL), densitas homogen.</p>\r\n\r\n<p>- Rektum &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;:&nbsp; Posisi normal, dinding tidak menebal, perirectal fat normal.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kesan :</p>\r\n\r\n<p>- Scanning hepar, kandung empedu, limpa, pankreas, ginjal kanan-kiri, vesika urinaria, prostat dan rektum tidak tampak kelainan.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (275, 'CT Angio', '<p><u>CT Angiografi</u></p>\r\n\r\n<p>Dilakukan MSCT angigrafi cerebal, di lakukan rekonstruksi pembuluh darah kepala :</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li>Besar, bentuk dan posisi a. carois interna kanan-kiri, a. cereberi media kanan-kiri, a. cerebri</li>\r\n</ul>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; anterior kanan-kiri masih dalam batas normal</p>\r\n\r\n<ul>\r\n	<li>Besar, bentuk dan posisi a. cerebri posterior bilateral masih dalam batas normal.</li>\r\n	<li>Besar, bentuk dan posisi a. vertebralis kanan-kiri, a. basilaris masih dalam batas normal</li>\r\n	<li>Besar, bentuk dan posisi a. communicating anterior dan posterior, a. perocallosa masih dalam batas</li>\r\n</ul>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; normal</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kesimpulan :</p>\r\n\r\n<ul>\r\n	<li><strong>Curiga suatu </strong><strong>Pial AVF</strong></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Saran : DSA</strong></p>\r\n', 'drganjar');
INSERT INTO `xray_template` VALUES (276, 'CT Scan Abdomen Wanita', '<p>PEMERIKSAAN CT SCAN ABDOMEN NON KONTRAS</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Scanning daerah :</p>\r\n\r\n<p>- Gaster &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;: Besar,bentuk, posisi dalam batas normal.</p>\r\n\r\n<p>- Hepar &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;: Ukuran tidak membesar, permukaan rata, densitas homogen. Tidak tampak massa. Vena porta tidak melebar. Vena hepatika tidak melebar. Vena cava inferior tidak melebar. Duktus biliaris intrahepatal tidak melebar. Tidak tampak koleksi cairan di sekitarnya.</p>\r\n\r\n<p>- Kandung empedu : Ukuran tidak membesar, dinding tidak menebal, reguler. Tidak tampak massa/batu. Duktus biliaris ekstrahepatal tidak melebar.</p>\r\n\r\n<p>- Limpa &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;: Ukuran tidak membesar, densitas homogen. Vena lienalis tidak melebar. Tidak tampak koleksi cairan di sekitarnya.</p>\r\n\r\n<p>- Pankreas &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;: Ukuran tidak membesar, densitas homogen, duktus pankreatikus tidak melebar.</p>\r\n\r\n<p>- Ginjal kanan &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;: Ukuran tidak membesar, struktur parenkim homogen, sistem pelvokalises tidak melebar, tidak tampak batu/ massa.</p>\r\n\r\n<p>- Ginjal&nbsp; kiri &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;: Ukuran tidak membesar, struktur parenkim homogen, sistem pelvokalises tidak melebar, tidak tampak batu/ massa.</p>\r\n\r\n<p>- Tidak tampak bayangan hipodens di paraaorta dan parailiaka.</p>\r\n\r\n<p>- Vesika urinaria &nbsp;&nbsp;&nbsp;&nbsp;: Terisi penuh, tidak tampak batu/massa. Perivesical fat normal.</p>\r\n\r\n<p>- Uterus &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;: Ukuran tidak membesar, densitas homogen. Parametrium bilateral tampak normal.</p>\r\n\r\n<p>- Rektum &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;: Posisi normal, dinding tidak menebal, perirectal fat normal.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kesan :</p>\r\n\r\n<p>- Scanning hepar, kandung empedu, limpa, pankreas, ginjal kanan-kiri, vesika urinaria, uterus dan rektum tidak tampak kelainan.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (277, 'ct scan angio', '<p style=\"text-align:justify\"><strong><u>CT SCAN ANGIOGRAPHY LOWER EXTREMITY DEXTRA. </u></strong></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><strong><em>Scanning daerah pelvis yang terscanning :</em></strong></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\">Arteri iliaka komunis dextra&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; : tidak tampak stenosis/dilatasi.</li>\r\n	<li style=\"text-align:justify\">Arteri iliaka komunis sinistra&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; : tidak tampak stenosis/dilatasi</li>\r\n	<li style=\"text-align:justify\">Arteri iliaka interna dextra dan sinistra&nbsp;&nbsp; : tidak tampak stenosis/dilatasi</li>\r\n	<li style=\"text-align:justify\">Arteri iliaka externa dextra dan sinistra&nbsp;&nbsp; : tidak tampak stenosis/dilatasi</li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><strong><em>Ekstremitas inferior dextra :</em></strong></p>\r\n\r\n<p style=\"text-align:justify\">-&nbsp;&nbsp;&nbsp; Arteri femoralis communis dextra, arteri femoralis superfisialis dextra, arteri femoralis profunda dextra, arteri poplitea dextra, Arteri tibialis anterior dextra, arteri tibialis posterior dextra tampak normal, tidak tampak stenosis/dilatasi.</p>\r\n\r\n<p style=\"text-align:justify\">-&nbsp;&nbsp;&nbsp; Tampak distal runoff sampai arteri dorsalis pedis dextra.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><strong><em>Skeletal yang terscanning :</em></strong></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><em>Tidak tampak fraktur.</em></li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><strong><u>KESIMPULAN : </u></strong></p>\r\n\r\n<p style=\"text-align:justify\"><strong>Tidak tampak oklusi maupun stenosis </strong><strong>arteri femoralis communis dextra, arteri femoralis </strong><strong>superfisi</strong><strong>alis</strong><strong> dextra, arteri femoralis profunda dextra</strong><strong>, arteri poplitea dextra</strong><strong>, Arteri tibialis anterior dextra</strong><strong>, arteri tibialis posterior dextra dan arteri dorsalis pedis dextra. </strong></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (278, 'CT Scan HIE', '<p>Dilakukan CT scan kepala potongan aksial dengan slice interval 5 mm di daerah basis sampai vertex. Potongan dibuat sejajar orbitomeatal. Scanning tanpa memakai kontras media.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Jaringan lunak ekstra calvaria, dan calvaria masih memberikan bentuk dan densitas yang&nbsp; normal. Fontanel anterior masih terbuka.</p>\r\n\r\n<p>Tampak hipoatenuasi relatif pada ganglia basalis bilateral.</p>\r\n\r\n<p>Tampak peningkatas atenuasi cerebellum (white cerebellum sign).</p>\r\n\r\n<p>Sulci dan gyri corticalis, fissura Sylvii bilateral dan fissura interhemisfer masih tampak normal</p>\r\n\r\n<p>Bentuk dan posisi ventrikel lateralis bilateral simetris. Ukuran ventrikel lateralis kanan dan kiri, ventrikel 3 dan 4 tampak normal.</p>\r\n\r\n<p>Ruang subaraknoid tampak normal.</p>\r\n\r\n<p>Sisterna ambiens dan basalis tampak normal.</p>\r\n\r\n<p>Daerah sela tursika dan jukstasella serta daerah cerebello-pontin angle masih dalam batas normal.</p>\r\n\r\n<p>Parenkim cerebri lainnya dan pons tidak menunjukkan densitas patologis.</p>\r\n\r\n<p>Mastoid air cell bilateral yang terscanning tampak normal (sesuai usia).</p>\r\n\r\n<p>Sinus paranasalis yang terscanning dalam batas normal.</p>\r\n\r\n<p>Bulbus okuli dan ruang retrobulber bilateral dalam batas normal.</p>\r\n\r\n<p>Tidak tampak pergeseran struktur garis tengah.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan :</strong></p>\r\n\r\n<p><strong>- Mencurigai suatu hypoxic ischemic encephalopathy (HIE).</strong></p>\r\n\r\n<p><strong>- </strong><strong>Scan kepala tanpa kontras saat ini tidak menunjukkan adanya perdarahan maupun SOL.</strong></p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (279, 'CT Cardiac', '<p style=\"text-align:justify\">Jaringan lunak nasofaring , orofaring dan hipofaring tampak normal</p>\r\n\r\n<p style=\"text-align:justify\">Recessus pharyngeus dan torus tubarius tampak normal</p>\r\n\r\n<p style=\"text-align:justify\">Tidak tampak penebalan jaringan lunak preoccipital ( N,1,5cm )</p>\r\n\r\n<p style=\"text-align:justify\">Cavum nasi dan concha nasalis bilateral tampak normal</p>\r\n\r\n<p style=\"text-align:justify\">Septum nasi masih di tengah</p>\r\n\r\n<p style=\"text-align:justify\">Kolom udara dalam faring , laring, glottis dan trakea tampak nromal</p>\r\n\r\n<p style=\"text-align:justify\">Palatum pterygoid plate dan mandibula tampak normal</p>\r\n\r\n<p style=\"text-align:justify\">Kelenjar parotis dan submandibula bilateral tampak normal</p>\r\n\r\n<p style=\"text-align:justify\">Retropharyngeal space , parapharyngeal space bilateral , carotid space bilateral , fossa infratemporalis bilateral dan masticator space bilateral dalam batas normal</p>\r\n\r\n<p style=\"text-align:justify\">Struktur vaskuler (a.carotis comunis bilateral dan v. Jugularis interna ) tampak normal</p>\r\n\r\n<p style=\"text-align:justify\">Kelenjar tiroid kanan dan kiri tidak membesar. Kontur parenkhim tampak homogen</p>\r\n\r\n<p style=\"text-align:justify\">Laring dan os hyoid dalam batas normal</p>\r\n\r\n<p style=\"text-align:justify\">Epiglotis dan valekula tampak nromal</p>\r\n\r\n<p style=\"text-align:justify\">Tidak tampak destruksi tulang bais kranii dan tidak tampak infiltrasi intracranial</p>\r\n\r\n<p style=\"text-align:justify\">Tidak tampak destruksi os maksilofasial dan mandibula</p>\r\n\r\n<p style=\"text-align:justify\">Tidak tampak pembesran KGB</p>\r\n\r\n<p style=\"text-align:justify\">Sinus ethmoidalis, sphenoidalis dan maksilaris bilateral yang terscanning dalam batas normal</p>\r\n\r\n<p style=\"text-align:justify\">Bulbus oculli dan ruang retrobulber yang terscanning tampak normal</p>\r\n\r\n<p style=\"text-align:justify\">Daerah sela tursika dan juktasela serta daerah &ldquo; cerebello-pointin angle &ldquo;masih dalam batas normal</p>\r\n\r\n<p style=\"text-align:justify\">Mastoid air cell bilateral yang terscanning tampak normal</p>\r\n\r\n<p style=\"text-align:justify\">Esofagus tampak normal</p>\r\n\r\n<p style=\"text-align:justify\">Struktur vertebra cervicalis tampak normal</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><strong>Kesimpulan :</strong></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><strong>CT Scan nasofaring saat ini tidak menunjukkan adanya tanda-tanda massa / SOL serta kelainan lainnya</strong></li>\r\n	<li style=\"text-align:justify\"><strong>Tidak tampak pembesaran KGB</strong></li>\r\n	<li style=\"text-align:justify\"><strong>Tidak tampak destruksi tulang</strong></li>\r\n</ul>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n', 'drganjar');
INSERT INTO `xray_template` VALUES (280, 'CT Scan kepala normal', '<p>Dilakukan CT scan kepala potongan aksial dengan slice interval 5 mm di daerah basis sampai vertex. Potongan dibuat sejajar orbitomeatal. Scanning tanpa memakai kontras media.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Jaringan lunak ekstra calvaria, dan calvaria masih memberikan bentuk dan densitas yang&nbsp; normal.</p>\r\n\r\n<p>Sulci dan gyri corticalis, fissura Sylvii bilateral dan fissura interhemisfer tampak normal</p>\r\n\r\n<p>Bentuk dan posisi ventrikel lateralis bilateral simetris. Ukuran ventrikel lateralis kanan dan kiri, ventrikel 3 dan 4 tampak normal.</p>\r\n\r\n<p>Ruang subaraknoid tampak normal.</p>\r\n\r\n<p>Sisterna ambiens dan basalis tampak normal.</p>\r\n\r\n<p>Daerah sela tursika dan jukstasella serta daerah cerebello-pontin angle masih dalam batas normal.</p>\r\n\r\n<p>Parenkim cerebri, cerebellum dan pons tidak menunjukkan densitas patologis.</p>\r\n\r\n<p>Tampak kalsifikasi fisiologis di daerah glandula pinealis dan plexus choroideus bilateral.</p>\r\n\r\n<p>Mastoid air cell bilateral yang terscanning tampak normal.</p>\r\n\r\n<p>Sinus paranasalis yang terscanning dalam batas normal.</p>\r\n\r\n<p>Bulbus okuli dan ruang retrobulber bilateral dalam batas normal.</p>\r\n\r\n<p>Tidak tampak pergeseran struktur garis tengah.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan :</strong></p>\r\n\r\n<p><strong>- CT Scan kepala tanpa kontras saat ini tidak menunjukkan adanya lesi iskemik/infark, </strong><strong>perdarahan maupun SOL.</strong></p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (281, 'CT Scan nasofaring', '<p style=\"text-align:justify\">Dilakukan MSCT/CT scan leher potongan aksial dengan <em>slice interval 0.6</em> mm dimulai dari ketinggian frontoocipital ke os vertebra cervical 7. Potongan dibuat sejajar <strong>palatum durum.</strong> Dibuat rekonstruksi axial coronal dan sagital. Scanning dilakukan <strong>tanpa dan dengan </strong>memakai kontras media.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">Jaringan lunak nasofaring, orofaring, dan hipofaring tampak normal.</p>\r\n\r\n<p style=\"text-align:justify\">Recessus pharyngeus dan torus tubarius tampak normal.</p>\r\n\r\n<p style=\"text-align:justify\">Tidak tampak penebalan jaringan lunak preoccipital(N&lt;1,5cm).</p>\r\n\r\n<p style=\"text-align:justify\">Cavum nasi dan concha nasalis bilateral tampak normal.</p>\r\n\r\n<p style=\"text-align:justify\">Septum nasi masih di tengah.</p>\r\n\r\n<p style=\"text-align:justify\">Kolom udara dalam faring, laring, glottis, dan trakea tampak normal.</p>\r\n\r\n<p style=\"text-align:justify\">Palatum, pterygoid plate dan mandibula tampak normal.</p>\r\n\r\n<p style=\"text-align:justify\">Kelenjar parotis dan submandibula bilateral tampak normal.</p>\r\n\r\n<p style=\"text-align:justify\">Retropharyngeal space, parapharyngeal space bilateral,carotid space bilateral, fossa infratemporalis bilateral dan masticator space bilateral dalam batas normal.</p>\r\n\r\n<p style=\"text-align:justify\">Struktur vaskuler (a.carotis comunis bilateral dan v.jugularis interna) tampak normal.</p>\r\n\r\n<p style=\"text-align:justify\">Kelenjar tiroid kanan dan kiri tidak membesar. Kontur parenkim tampak homogen</p>\r\n\r\n<p style=\"text-align:justify\">Laring dan os hyoid dalam batas normal.</p>\r\n\r\n<p style=\"text-align:justify\">Epiglotis dan valekula tampak normal.</p>\r\n\r\n<p style=\"text-align:justify\">Tidak tampak destruksi tulang basis kranii dan tidak tampak infiltrasi intracranial.</p>\r\n\r\n<p style=\"text-align:justify\">Tidak tampak destruksi os maksilofasial dan mandibula.</p>\r\n\r\n<p style=\"text-align:justify\">Tidak tampak pembesaran KGB.</p>\r\n\r\n<p style=\"text-align:justify\">Sinus ethmoidalis, sphenoidalis, dan maksilaris bilateral yang terscanning dalam batas normal.</p>\r\n\r\n<p style=\"text-align:justify\">Bulbus oculi dan ruang retrobulber yang terscanning tampak normal.</p>\r\n\r\n<p style=\"text-align:justify\">Daerah sela tursika dan jukstasella serta daerah &ldquo;cerebello-pontin angle&rdquo; masih dalam batas normal.</p>\r\n\r\n<p style=\"text-align:justify\">Mastoid air cell bilateral yang terscanning tampak normal.</p>\r\n\r\n<p style=\"text-align:justify\">Esofagus tampak normal.</p>\r\n\r\n<p style=\"text-align:justify\">Struktur vertebra cervicalis tampak normal.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><strong><em>Kesimpulan :</em></strong></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><strong><em>CT scan nasofaring saat ini tidak menunjukkan adanya tanda-tanda massa / SOL serta kelainan lainnya</em></strong></li>\r\n	<li style=\"text-align:justify\"><strong><em>Tidak tampak pembesaran KGB.</em></strong></li>\r\n	<li style=\"text-align:justify\"><strong><em>Tidak tampak destruksi tulang.</em></strong></li>\r\n</ul>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (282, 'CT Scan SOL', '<p>Dilakukan CT scan kepala potongan aksial dengan slice interval 5 mm di daerah basis sampai vertex. Potongan dibuat sejajar orbitomeatal. Scanning tanpa memakai kontras media.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Jaringan lunak ekstra calvaria, dan calvaria masih memberikan bentuk dan densitas yang&nbsp; normal.</p>\r\n\r\n<p>Tampak lesi hipodens dengan konfigurasi fingerlike di subcortical white matter lobus frontotemporoparietal kanan.</p>\r\n\r\n<p>Sulci dan gyri corticalis, fissura Sylvii kanan dan fissura interhemisfer anterior tampak menyempit.</p>\r\n\r\n<p>Bentuk dan posisi ventrikel lateralis bilateral asimetris. Ventrikel lateralis kanan tampak menyempit dan terdorong ke kiri. Ventrikel lateralis kiri dan ventrikel 3 tampak melebar dan terdorong ke kiri. Ventrikel 4 tampak normal.</p>\r\n\r\n<p>Ruang subaraknoid tampak normal.</p>\r\n\r\n<p>Sisterna ambiens dan basalis tampak normal.</p>\r\n\r\n<p>Daerah sela tursika dan jukstasella serta daerah &ldquo;cerebello-pontin angle&rdquo; masih dalam batas normal.</p>\r\n\r\n<p>Parenkim cerebri lainnya, cerebellum dan pons tidak menunjukkan densitas patologis.</p>\r\n\r\n<p>Tampak kalsifikasi fisiologis di daerah glandula pinealis dan plexus choroideus kiri.</p>\r\n\r\n<p>Mastoid air cell bilateral yang terscanning tampak normal.</p>\r\n\r\n<p>Sinus paranasalis yang terscanning dalam batas normal.</p>\r\n\r\n<p>Bulbus okuli dan ruang retrobulber bilateral dalam batas normal.</p>\r\n\r\n<p>Tampak pergeseran struktur garis tengah ke kiri sejauh lk 1,54 cm.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan :</strong></p>\r\n\r\n<p><strong>- Gambaran edema vasogenik luas di daerah subcortical white matter lobus frontotemporoparietal kanan disertai hidrocephalus obstruktif dan midline shift ke kiri sejauh lk 1,54 cm </strong><strong>&agrave; mencurigai SOL. </strong></p>\r\n\r\n<p><strong>- </strong><strong>CT Scan kepala tanpa kontras saat ini tidak menunjukkan adanya perdarahan.</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Saran : CT scan kepala dengan kontras. </strong></p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (283, 'CT Scan Thorax pectus excavatum', '<p>Dilakukan CT scan thoraks tanpa kontras media, potongan axial, sagital dan coronal, rekontruksi bone window.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><em><u>Window tulang</u></em></strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n\r\n<ul>\r\n	<li>Tampak depresi processus xyphoideus os sternum dengan kalkulasi :</li>\r\n</ul>\r\n\r\n<ul>\r\n	<li>Haller index (HI) = 239,6 / 55,1 = 4,34</li>\r\n	<li>Correction index (CI) = [(84,8-55,1)/84,8] x 100% = 35,02%</li>\r\n	<li>Ideal Chest Index (ICI) = 239,6 / 84,8 = 2,83</li>\r\n	<li>Depression Index (DI) = 24,9 / 29,8 = 0,84</li>\r\n</ul>\r\n\r\n<ul>\r\n	<li>Tidak tampak garis fraktur.</li>\r\n	<li>Tidak tampak lesi litik maupun sklerotik.</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><em><u>Window mediastinum</u></em></strong></p>\r\n\r\n<ul>\r\n	<li>Trakhea masih di tengah</li>\r\n	<li>Cabang utama bronchus kanan dan kiri terbuka.</li>\r\n	<li>Tidak tampak pembesaran jantung. Jantung tampak terdesak oleh processus xyphoideus os sternum.</li>\r\n	<li>Tidak tampak nodul maupun konsolidasi.</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><em><u>Window paru</u></em></strong></p>\r\n\r\n<ul>\r\n	<li>Tidak tampak ground glass opacity.</li>\r\n	<li>Tidak tampak infiltrat.</li>\r\n	<li>Tidak tampak dilatasi dan penebalan bronchus.</li>\r\n	<li>Tidak tampak gambaran honey-combing.</li>\r\n	<li>Tidak tampak nodul maupun konsolidasi.</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><u>Kesan :</u></strong></p>\r\n\r\n<ul>\r\n	<li><strong>Menyokong gambaran pectus excavatum dengan depresi processus xyphoideus</strong></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><strong>Haller index (HI) = 4,34</strong></li>\r\n	<li><strong>Correction index (CI) = 35,02%</strong></li>\r\n	<li><strong>Ideal Chest Index (ICI) = 2,83</strong></li>\r\n	<li><strong>Depression Index (DI) = 0,84</strong></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><strong>Tidak tampak kardiomegali, jantung tampak terdesak oleh processus xyphoideus os sternum.</strong></li>\r\n	<li><strong>Tidak tampak gambaran bronchopneumonia/pneumonia.</strong></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (284, 'CT Scapula', '<p>Dilakukan CT scan scapula dextra, dilakukan rekonstruksi bone window dan 3D.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tampak eksostosis tulang berukuran lk 0,67 cm pada pertengahan margo medial os scapula dextra aspek posterior.</p>\r\n\r\n<p>Epiphyseal plate pada os acromion tampak belum menutup sempurna.</p>\r\n\r\n<p>Sela sendi glenohumeralis dextra dalam batas normal.</p>\r\n\r\n<p>Tidak tampak garis fraktur.</p>\r\n\r\n<p>Tidak tampak lesi litik maupun sklerotik.</p>\r\n\r\n<p>Jaringan kutis dan otot-otot yang terscanning di daerah scapula dextra masih dalam batas normal.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan :</strong></p>\r\n\r\n<p><strong>Menyokong gambaran osteochondroma pada pertengahan margo medial os scapula dextra aspek posterior (berukuran lk 0,67 cm). </strong></p>\r\n\r\n<p>&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (285, 'CT SPN', '<p>&nbsp;</p>\r\n\r\n<p>MSCT<strong> </strong>Sinus paranasalis, potongan axial-coronal dengan slice thickness 2 mm, tanpa memakai kontras.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align:left\">Lesi isodens dengan kalsifikasi mengisi seluruh sinus maksilaris kiri yang tampak melebar dengan cortex tulang menipis dan sebagian mengalami erosi pada dinding anterior dan medial sinus maksilaris kiri. Cavum nasalis kiri terisi lesi isodens, tampak terdesak serta menyempit.</p>\r\n\r\n<p style=\"text-align:left\">Septum nasi tampak deviasi ke kanan.</p>\r\n\r\n<p style=\"text-align:left\">Cocha nasalis tidak tampak menebal.</p>\r\n\r\n<p style=\"text-align:left\">Sinus maksilaris kanan, ethmoidalis, &nbsp;sphenoidalis, dan frontalis bilateral dalam batas normal.</p>\r\n\r\n<p style=\"text-align:left\">Bulbus oculi dan ruang retrobulber dalam batas normal.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesimpulan :</strong></p>\r\n\r\n<p><strong>Lesi isodens dengan kalsifikasi mengisi seluruh sinus maksilaris kiri yang tampak melebar dengan cortex tulang menipis dan sebagian mengalami erosi pada dinding anterior dan medial sinus maksilaris kiri serta mendesak dan menyempitkan cavum nasalis kiri. </strong></p>\r\n\r\n<p><strong>Lesi isodens tampak mengisi cavum nasalis kiri. </strong></p>\r\n\r\n<p><strong>-----------------------------------------------------------------</strong><strong>&agrave; DD/ Mucocele sinus maksilaris kiri. </strong></p>\r\n\r\n<p><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Tumor sinonasal. </strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Saran : </strong></p>\r\n\r\n<p><strong>CT scan sinus paranasal dengan kontras dengan slice thickness 1-3 mm. </strong></p>\r\n\r\n<p>&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (286, 'CT Temporal + NK - Normal', '<p style=\"text-align:justify\">Dilakukan MSCT scan temporal bone potongan aksial dengan <em>slice interval</em> . . . mm dan <em>slice thickness</em> . . . mm dimulai dari bawah processus mastoideus sampai di atas petrous ridge. Dibuat rekonstruksi koronal. Potongan dibuat sejajar dengan infraorbitomeatal line. Dibuat bone window. Scanning tanpa<strong> </strong>memakai kontras media.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">Pars petrosus os temporal bilateral tampak normal dan simetris.</p>\r\n\r\n<p style=\"text-align:justify\">Kanalis akustikus internal bilateral tampak normal dengan diameter kanan . . . dan kiri . . .</p>\r\n\r\n<p style=\"text-align:justify\">Kontur kanalis akustikus internal bilateral tampak normal (Tidak tampak dilatasi bulbous di daerah distal kanalis akustikus internal)</p>\r\n\r\n<p style=\"text-align:justify\">Cochlea dan kanalis semisirkularis bilateral tampak normal.</p>\r\n\r\n<p style=\"text-align:justify\">Round window tampak normal.</p>\r\n\r\n<p style=\"text-align:justify\">N.facialis pada kanalis di segmen labirin, timpani dan mastoid tampak normal</p>\r\n\r\n<p style=\"text-align:justify\">Diameter endolymphatic duct/vestibular aqueduct tampak normal (Lebih kecil dari diameter kanalis semisirkularis posterior)</p>\r\n\r\n<p style=\"text-align:justify\">Mastoid air cell bilateral tampak normal.</p>\r\n\r\n<p style=\"text-align:justify\">Cavum timpani bilateral dan struktur scutum tampak normal.</p>\r\n\r\n<p style=\"text-align:justify\">Auditory ossicle bilateral tampak normal.</p>\r\n\r\n<p style=\"text-align:justify\">Kanalis akustikus eksternal bilateral tampak normal.</p>\r\n\r\n<p style=\"text-align:justify\">Aurikula bilateral tampak normal.</p>\r\n\r\n<p style=\"text-align:justify\">Tidak tampak kelainan di daerah batang otak yang terscanning.</p>\r\n\r\n<p style=\"text-align:justify\">Struktur dan perjalanan a.karotis interna, v.jugular dan sinus sigmoid bilateral yang terscanning tampak normal.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><strong><em>Kesimpulan :</em></strong></p>\r\n\r\n<ul>\r\n	<li style=\"text-align:justify\"><strong><em>CT Scan temporal bone saat ini tidak menunjukkan adanya kelainan.</em></strong></li>\r\n</ul>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (287, 'ctev', '<p><strong>PEDIS BILATERAL</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Deformitas pedis bilateral dengan posisi adduksi dan supinasi dari forefoot dan midfoot, terutama sinistra.</strong></p>\r\n\r\n<p><strong>Tampak long axis os talus dan calcaneus hampir sejajar. </strong></p>\r\n\r\n<p><strong>Besar, bentuk dan struktur trabekula tulang-tulang pembentuk pedis bilateral dalam batas normal.</strong></p>\r\n\r\n<p><strong>Tidak tampak garis fraktur. </strong></p>\r\n\r\n<p><strong>Tidak tampak lesi litik maupun sklerotik. </strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan :<br />\r\nMenyokong gambaran talipes equinovarus pedis bilateral terutama sinistra.</strong></p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (288, 'Elbow - genu', '<p><strong>ELBOW JOINT</strong><strong> DEXTRA</strong></p>\r\n\r\n<p>Besar bentuk dan struktur trabecula tulang-tulang pembentuk elbow joint dextra dalam batas normal.</p>\r\n\r\n<p>Sela dan permukaan sendi baik.</p>\r\n\r\n<p>Tidak tampak lesi litik dan sklerotik</p>\r\n\r\n<p>Tidak tampak fraktur</p>\r\n\r\n<p><strong>Kesan : </strong><strong>Foto elbow joint dextra saat ini dalam batas normal</strong></p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;</p>\r\n\r\n<p><strong>GENU DEXTRA </strong></p>\r\n\r\n<p>Besar, bentuk dan struktur trabekula tulang-tulang pembentuk genu dextra dalam batas normal.</p>\r\n\r\n<p>Sela dan permukaan sendi baik.</p>\r\n\r\n<p>Eminentia intercodyloidea tidak meruncing.</p>\r\n\r\n<p>Tidak tampak lesi litik dan sklerotik.</p>\r\n\r\n<p>Tidak tampak fraktur dan dislokasi.</p>\r\n\r\n<p><strong>Kesan : </strong><strong>Foto genu dextra saat ini dalam batas normal.</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (289, 'femur', '<p><strong>FEMUR SINISTRA</strong></p>\r\n\r\n<p>Besar bentuk dan struktur trabecula os femur sinistra dalam batas normal</p>\r\n\r\n<p>Tidak tampak lesi litik dan sklerotik</p>\r\n\r\n<p>Tidak tampak garis fraktur</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan : Foto Femur sinistra saat ini dalam batas normal.</strong></p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (290, 'hernia umbilicalis', '<p>Hepar&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp;&nbsp; Tidak membesar. Textur parenkhim homogen, tak tampak SOL. Ductus biliaris, vena hepatika, vena porta tak melebar. Tidak tampak koleksi cairan di sekitarnya.</p>\r\n\r\n<p>Kt Empedu&nbsp;&nbsp; :&nbsp;&nbsp; Ukuran membesar. Dinding menebal. Tampak sludge di dalamnya.</p>\r\n\r\n<p>Limpa&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp;&nbsp; Besar &amp; bentuk dlm batas normal. Textur parenkhim&nbsp; homogen.Tak tampak SOL. Vena Lienalis tak melebar. Tidak tampak koleksi cairan di sekitarnya.</p>\r\n\r\n<p>Pankreas&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp;&nbsp; Besar &amp; bentuk dlm batas normal. Textur homogen</p>\r\n\r\n<p>Paraaorta&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp;&nbsp; Besar &amp; bentuk aorta abdominalis dalam batas normal.Tak tampak bayangan noduler hypo ataupun hyper echoic disekitar aorta abdominalis.</p>\r\n\r\n<p>Parailiaka&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp;&nbsp; Tak tampak bayangan noduler hypo ataupun hyper echoic disekitar parailiaka bilateral.</p>\r\n\r\n<p>Ginjal&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp;&nbsp; Besar &amp; bentuk normal. Batas textur parenkhim dg central echo complex normal. Sistim pelvocalises tidak lebar.Tak tampak batu ataupun SOL</p>\r\n\r\n<p>Kd Kemih&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp;&nbsp; Tidak terisi penuh, sulit dinilai.</p>\r\n\r\n<p>Scan area umbilical : tampak struktur menyerupai kantung berisi gambaran usus berukuran lk 3,9 x 3,33 x 4 cm di daerah umbilical yang tampak berhubungan dengan rongga abdomen melalui defek pada dinding abdomen berukuran lk 1,57 x 1,21 cm.</p>\r\n\r\n<p>Tampak pelebaran usus-usus di abdomen atas sampai bawah dengan dinding yang menebal dan gambaran back and forth di dalamnya.</p>\r\n\r\n<p><strong>Kesan&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; </strong></p>\r\n\r\n<ul>\r\n	<li><strong>Menyokong gambaran hernia umbilicalis curiga mengalami strangulasi, sugestif disertai ileus obstruktif. </strong></li>\r\n	<li><strong>Sludge gallbladder dengan tanda-tanda cholesistitis.</strong></li>\r\n	<li><strong>USG hepar, limpa, pancreas, kedua ginjal dalam batas normal. </strong></li>\r\n	<li><strong>Tidak tampak pembesaran KGB paraaorta/parailiaka. </strong></li>\r\n</ul>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (291, 'invertogram', '<p><strong>Abdomen posisi invertogram</strong></p>\r\n\r\n<p><strong>Jarak antara ujung distal udara pada usus dengan marker logam yang di letakkan pada area anus lk 4 cm, disertai gambaran air fluid level (+) membentuk step ladder. &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</strong></p>\r\n\r\n<p><strong>Kesan :<br />\r\nMenyokong gambaran ileus obstruktif letak rendah e.c atresia ani (high type)&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong></p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (292, 'kifosis', '<p><strong><u>FOTO WHOLE SPINE</u></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kurve kifosis vertebra thorakolumbalis dengan cobb&rsquo;s angle 70<strong>&deg;</strong>.</p>\r\n\r\n<p>Tampak kompresif vertebra Th 11, Th 12 dan L1, terutama pada aspek anterior, disertai penyempitan discus dan foramen intervertebralis Th 11-12 dan Th 12-L1.</p>\r\n\r\n<p>Besar dan bentuk vertebra lainnya dalam batas normal, densitas tulang tampak menurun.</p>\r\n\r\n<p>Pedicle normal.</p>\r\n\r\n<p>Tidak tampak osteofit.</p>\r\n\r\n<p>Tidak tampak lesi litik dan sklerotik.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; Sugestif fraktur kompresi pada vertebra Th 11, Th 12, L1 disertai penyempitan discus dan foramen intervertebralis Th 11-12 dan Th 12-L1 serta kifosis vertebra thorakolumbalis (cobb&rsquo;s angle 70&deg;). </strong></p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (293, 'Lumbosakral', '<p>VERTEBRA LUMBOSAKRAL AP/LATERAL</p>\r\n\r\n<p>Curve dan alignment vertebra lumbalis normal.</p>\r\n\r\n<p>Besar, bentuk dan struktur trabekula vertebra lumbosakral dalam batas normal.</p>\r\n\r\n<p>Foramina dan disci intervertebralis tidak menyempit.</p>\r\n\r\n<p>Pedicle normal.</p>\r\n\r\n<p>Tidak tampak osteofit.</p>\r\n\r\n<p>Tidak tampak fraktur.</p>\r\n\r\n<p>Tidak tampak lesi litik dan sklerotik</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kesan :</p>\r\n\r\n<p>Foto vertebra lumbosakral saat ini dalam batas normal.&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (294, 'mamad', '<p>Dilakukan CT scan kepala potongan aksial dengan slice interval 5 mm di daerah basis sampai vertex. Potongan dibuat sejajar orbitomeatal. Scanning tanpa dan dengan memakai kontras media.</p>\r\n\r\n<p>Jaringan lunak ekstra calvaria, dan calvaria masih memberikan bentuk dan densitas yang&nbsp; normal.<br />\r\nTampak lesi hipodens batas tidak tegas di daerah frontotemporoparietooccipitalis kanan.&nbsp;<br />\r\nTampak lesi hipodens batas tegas di daerah pons kiri.&nbsp;<br />\r\nSulci dan gyri corticalis, fissura Sylvii bilateral dan fissura interhemisfer tampak normal.&nbsp;<br />\r\nBentuk dan posisi ventrikel lateralis bilateral simetris. Ukuran ventrikel lateralis kanan dan kiri, ventrikel 3 dan 4 tampak normal.&nbsp;<br />\r\nRuang subaraknoid tampak normal.<br />\r\nSisterna ambiens dan basalis tampak normal.<br />\r\nDaerah sela tursika dan jukstasella serta daerah cerebello-pontin angle masih dalam batas normal.<br />\r\nParenkim cerebri lainnya dan cerebellum tidak menunjukkan densitas patologis.<br />\r\nTampak kalsifikasi fisiologis di daerah falx cerebri, ganglia basalis bilateral, glandula pinealis dan plexus choroideus bilateral.<br />\r\nMastoid air cell bilateral yang terscanning tampak normal.<br />\r\nSinus paranasalis yang terscanning dalam batas normal.<br />\r\nBulbus okuli dan ruang retrobulber bilateral dalam batas normal.<br />\r\nTidak tampak pergeseran struktur garis tengah.<br />\r\nPost contrast scanning tampak meningeal enhancement berlebih di daerah sulci corticalis lobus temporoparietooccipitalis bilateral terutama kana, sisterna ambiens, sisterna basalis, sisterna sylvii dan fissura interhemisfer.&nbsp;</p>\r\n\r\n<p>Kesan :<br />\r\n- Menyokong gambaran meningitis disertai lesi iskemik di daerah frontotemporoparietooccipitalis kanan.<br />\r\n- Gambaran infark lama di daerah pons kiri.&nbsp;<br />\r\n- CT Scan kepala tanpa kontras saat ini tidak menunjukkan adanya perdarahan maupun SOL.</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (295, 'Manus', '<p><strong>MANUS</strong><strong> </strong><strong>DEXTRA</strong><strong> AP/OBLIQUE</strong></p>\r\n\r\n<p>Besar, bentuk, dan struktur trabekula tulang-tulang pembentuk manus dextra dalam batas normal.</p>\r\n\r\n<p>Sela dan permukaan sendi normal.</p>\r\n\r\n<p>Tidak tampak fraktur maupun dislokasi.</p>\r\n\r\n<p>Tidak tampak lesi litik/sklerotik.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan&nbsp;&nbsp;&nbsp; : Foto manus dextra saat ini dalam batas normal</strong></p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (296, 'Manus', '<p><strong>MANUS</strong><strong> </strong><strong>DEXTRA</strong><strong> AP/OBLIQUE</strong></p>\r\n\r\n<p>Besar, bentuk, dan struktur trabekula tulang-tulang pembentuk manus dextra dalam batas normal.</p>\r\n\r\n<p>Sela dan permukaan sendi normal.</p>\r\n\r\n<p>Tidak tampak fraktur maupun dislokasi.</p>\r\n\r\n<p>Tidak tampak lesi litik/sklerotik.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan&nbsp;&nbsp;&nbsp; : Foto manus dextra saat ini dalam batas normal.</strong></p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (297, 'MCU KARDIOMEGALI', '<p><strong>PEMERIKSAAN FOTO THORAX PA</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Soft tissue dan skeletal tampak normal.</p>\r\n\r\n<p>Trachea posisi midline.</p>\r\n\r\n<p>Cor membesar ke lateral kiri dengan apeks tertanam pada diafragma, pinggang jantung normal.</p>\r\n\r\n<p>Sinuses dan diafragma normal.</p>\r\n\r\n<p>Pulmo :</p>\r\n\r\n<p>Hilli normal.</p>\r\n\r\n<p>Corakan bronkhovaskuler normal.</p>\r\n\r\n<p>Tidak tampak infiltrat.</p>\r\n\r\n<p>Kranialisasi (-).</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan : </strong></p>\r\n\r\n<p><strong>Kardiomegali tanpa bendungan paru. </strong></p>\r\n\r\n<p><strong>Pulmo saat ini tidak tampak kelainan. </strong></p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (299, 'MCU NORMAL', '<p><strong>PEMERIKSAAN FOTO THORAX PA</strong></p>\r\n\r\n<p><strong>&nbsp;</strong></p>\r\n\r\n<p>Soft tissue dan skeletal tampak normal.</p>\r\n\r\n<p>Trachea posisi midline.</p>\r\n\r\n<p>Cor tidak membesar.</p>\r\n\r\n<p>Sinuses dan diafragma normal.</p>\r\n\r\n<p>Pulmo :</p>\r\n\r\n<p>Hilli normal.</p>\r\n\r\n<p>Corakan bronkhovaskuler normal.</p>\r\n\r\n<p>Tidak tampak infiltrat.</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n\r\n<p><strong>Kesan : </strong></p>\r\n\r\n<p><strong>Pulmo saat ini tidak tampak kelainan. </strong></p>\r\n\r\n<p><strong>Tidak tampak kardiomegali. </strong></p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (300, 'MRI ligamentum tibiofibular, ligamentum patellar, fat pad patellaris', '<p><em>Dilakukan MRI genu sinistra dengan potongan sagital T1WI, axial-coronal T2WI, axial-coronal T2 Fatsat, axial T2-SPAIR, coronal PDWI, coronal-sagital T2 mDixon, sagital-coronal T2-STIR. Scanning dilakukan tanpa media kontras. </em></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li>Posisi dan &nbsp;bentuk tulang-tulang pembentuk knee sinistra (distal femur dan proksimal tibia) masih tampak normal.</li>\r\n	<li>Intensitas sinyal <em>bone marrow</em> masih tampak normal.</li>\r\n	<li>Korteks memperlihatkan kontur yang rata tanpa disertai perubahan intensitas sinyal di daerah subkondral.</li>\r\n	<li>Kartilago yang meliputi patella, condylus femoralis dan tibial plateau memiliki ketebalan yang normal dengan permukaan yang rata serta intensitas sinyal yang normal.</li>\r\n	<li>Meniscus lateral memperlihatkan konfigurasi triangular yang normal dengan intensitas sinyal yang normal.</li>\r\n	<li><em>Tampak lesi yang memberikan signal hipointens pada T1WI dan hiperintens pada T2WI, T2FS dan T2-STIR pada meniscus medial horn posterior. &nbsp;</em></li>\r\n	<li><em>&nbsp;Tampak lesi yang memberikan signal hipointens pada T1WI dan hiperintens pada T2WI, T2FS dan T2-STIR pada ligamentum patellaris </em></li>\r\n	<li><em>Tampak lesi yang memberikan signal hipointens pada T1WI dan hiperintens pada T2WI, T2FS dan T2-STIR pada ligamentum anterosuperior tibiofular</em></li>\r\n	<li><em>Tampak fat pad infrapatellaris menebal dan irreguler</em></li>\r\n	<li><em>Tampak edema pada proksimal os fibula</em></li>\r\n	<li><em>Tampak posisi patella tinggi (patella alta)</em></li>\r\n	<li>Ligamentum kolateral medial dan lateral<em> </em>masih memberikan bentuk, kontur, dan intensitas sinyal yang normal.<em> </em></li>\r\n	<li>Ligamentum cruciatum anterior dan ligamentum cruciatum posterior masih memberikan bentuk, kontur, dan intensitas sinyal yang normal.</li>\r\n	<li>Ligamentum quadriceps femoris &nbsp;masih memberikan bentuk, kontur, dan intensitas sinyal yang normal.</li>\r\n	<li><em>Tampak koleksi cairan di bursa prepatellaris dan suprapatellaris sinistra. </em></li>\r\n	<li>Tidak tampak intra-articular <em>foreign body.</em></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><u>Kesan: </u></strong></p>\r\n\r\n<ul>\r\n	<li><em><strong>Gambaran tear pada meniscus medial horn posterior</strong></em></li>\r\n	<li><em><strong>Gambaran tear pada ligamentum patellaris </strong></em></li>\r\n	<li><em><strong>Gambaran tear pada ligamentum anterosuperior tibiofular</strong></em></li>\r\n	<li><em><strong>Edema pada proksimal os fibula dan posisi patella tinggi (patella alta)<u> </u></strong></em></li>\r\n	<li><em><strong>Fat pad infrapatellaris menebal dan irreguler sugestif ec. inflamasi<u> </u></strong></em></li>\r\n	<li><em><strong>Koleksi cairan di bursa prepatellaris dan suprapatellaris sinistra DD/ Bursitis.</strong></em><strong> </strong></li>\r\n</ul>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (301, 'MRI of synovial mass in knee', '<p>Dilakukan MRI genu&nbsp; sinistra dengan potongan aksial-koronal-sagital PDWI Fat Sat, koronal-sagital T1WI, koronal-sagital T2WI, sagital PD dixon. Scanning dilakukan tanpa media kontras. Aksial-sagital-koronal T1WI Fat Sat post contrast</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&bull;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Posisi dan&nbsp; bentuk tulang-tulang pembentuk cruris sinistra (distal femur, proksimal tibia, fibula, dan patella) masih tampak normal.</p>\r\n\r\n<p>&bull;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Intensitas sinyal bone marrow masih tampak normal.</p>\r\n\r\n<p>&bull;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Korteks memperlihatkan kontur yang rata tanpa disertai perubahan intensitas sinyal di daerah subkondral.</p>\r\n\r\n<p>&bull;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Kartilago yang meliputi patella, kondilus femoralis dan tibial plateau memiliki ketebalan yang normal dengan permukaan yang rata serta intensitas sinyal yang normal.</p>\r\n\r\n<p>&bull;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Meniscus medial dan lateral memperlihatkan konfigurasi triangular yang normal dengan intensitas sinyal yang normal.</p>\r\n\r\n<p>&bull;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Ligamentum kolateral medial dan lateral masih memberikan bentuk, kontur, dan intensitas sinyal yang normal.</p>\r\n\r\n<p>&bull;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Ligamentum cruciatum anterior dan posterior masih memberikan bentuk, kontur, dan intensitas sinyal yang normal.</p>\r\n\r\n<p>&bull;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Ligamentum quadriceps femoris dan ligamentum patellaris masih memberikan bentuk, kontur, dan intensitas sinyal yang normal.</p>\r\n\r\n<p>&bull;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Tidak tampak intra-articular foreign body.</p>\r\n\r\n<p>&bull;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Jaringan lunak di sekitar genu dan struktur vaskular yang terlihat masih tampak normal.</p>\r\n\r\n<p>&bull;Tampak bayangan slight hipointens pada T1WI, slight hiperintens pada T2WI, iso-hiperintens pada T2Fat Sat, isointens pada PDW dan pada GRE terlihat isontens di daerah deep bursa infrapatellaris yang&nbsp; curiga menginfiltrasi Hoffa&#39;s fat</p>\r\n\r\n<p>&bull;Tampak bayangan hipointens, bersepta, batas tegas pada T1WI dan terlihat hiperintens pada T2WI san T2Fat Sat di daerah muskulus poplitea.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kesan:</p>\r\n\r\n<p>Bayangan slight hipointens pada T1WI, slight hiperintens pada T2WI, iso-hiperintens pada T2Fat Sat, isointens pada PDW dan pada GRE terlihat isontens di daerah deep bursa infrapatellaris yang&nbsp; curiga menginfiltrasi Hoffa&#39;s fat&nbsp; --&gt; suspek synovitis deep infrapatellaris bursitis DD/synovial mass</p>\r\n\r\n<p>Gambaran multipel cyst di muskulus poplitea</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (302, 'NEC', '<p><strong>BNO</strong><strong> </strong><strong>&nbsp;</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tampak distribusi udara usus tidak merata dengan hilangnya pola mozaik.</p>\r\n\r\n<p>Tidak tampak bayangan lusen pada dinding usus.</p>\r\n\r\n<p>Tidak tampak bayangan lusen yang terproyeksi di daerah hepar.</p>\r\n\r\n<p>Ujung OGT terproyeksi setinggi paravertebra Th12-L1 kiri.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan&nbsp;&nbsp;&nbsp; : </strong></p>\r\n\r\n<p><strong>Gambaran ileus paralitik tanpa disertai pneumatosis intestinalis maupun portal venous gas </strong><strong>&agrave;</strong><strong> curiga suatu NEC grade I.</strong></p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (303, 'OA genu', '<p><strong>GENU DEXTRA AP/LATERAL</strong></p>\r\n\r\n<p>Tampak osteofit pada condylus medialis serta lateralis os femur dan tibia dextra</p>\r\n\r\n<p>Tampak osteofit pada aspek posterosuperior dan inferior os patella dextra.</p>\r\n\r\n<p>Sela sendi femorotibial tampak menyempit terutama bagian medial dengan permukaan sendi sklerotik.</p>\r\n\r\n<p>Eminentia intercodyloidea meruncing.</p>\r\n\r\n<p>Tidak tampak fraktur dan dislokasi.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan&nbsp;&nbsp;&nbsp; : </strong><strong>Osteoarthritis genu dextra grade II. </strong></p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (305, 'Panoramic 3', '<p><strong>PANORAMIC</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Impaksi vertikal dental 38 dan 48.</strong></p>\r\n\r\n<p><strong>Missing dental 36. </strong></p>\r\n\r\n<p><strong>Tidak tampak lesi lusen di sekitar akar gigi. </strong></p>\r\n\r\n<p><strong>Besar, bentuk dan struktur trabekula os mandibula dalam batas normal.</strong></p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (306, 'Panoramic', '<p><strong>PANORAMIC</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Impaksi dental 28, 38 dan 48.</strong></p>\r\n\r\n<p><strong>Caries dental 34. </strong></p>\r\n\r\n<p><strong>Tampak lesi lusen di sekitar radix dental 35 (periapikal abses). </strong></p>\r\n\r\n<p><strong>Besar, bentuk dan struktur trabekula os mandibula dalam batas normal.</strong></p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (307, 'Pedis', '<p><strong>PEDIS DEXTRA</strong></p>\r\n\r\n<p>Besar, bentuk, dan struktur trabekula tarsal, metatarsal dan phalanges pedis dextra dalam batas normal.</p>\r\n\r\n<p>Sela dan permukaan sendi normal.</p>\r\n\r\n<p>Tidak tampak lesi litik / sklerotik.</p>\r\n\r\n<p>Tidak tampak fraktur maupun dislokasi.</p>\r\n\r\n<p>Tidak tampak osteofit.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan&nbsp;&nbsp;&nbsp; : </strong><strong>Foto pedis </strong><strong>dextra</strong><strong> saat ini dalam batas normal. </strong></p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (308, 'Pelvis', '<p><strong>PELVIS AP/LATERAL</strong></p>\r\n\r\n<p>Besar ,bentuk dan struktur trabecula tulang-tulang pembentuk pelvis normal</p>\r\n\r\n<p>Sela dan permukaan sendi normal</p>\r\n\r\n<p>Caput femoralis dan acetabulum normal</p>\r\n\r\n<p>Tidak tampak lesi litik/sklerotik</p>\r\n\r\n<p>Tidak tampak fraktur</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan</strong><strong>&nbsp; </strong><strong>: </strong><strong>Foto pelvis </strong><strong>saat ini masih dalam batas normal</strong></p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (309, 'Schedel', '<p><strong>SCHEDEL AP / LATERAL</strong></p>\r\n\r\n<p>Besar, bentuk, dan struktur trabekula normal.</p>\r\n\r\n<p>Tabula eksterna-interna dan diploe normal.</p>\r\n\r\n<p>Vascular dan convulational marking normal.</p>\r\n\r\n<p>Sella tursika normal.</p>\r\n\r\n<p>Tidak tampak lesi litik dan sklerotik.</p>\r\n\r\n<p>Tidak tampak garis fraktur.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan :&nbsp; </strong></p>\r\n\r\n<p><strong>Foto Schedel </strong><strong>saat ini </strong><strong>tidak tampak kelainan.</strong></p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (310, 'schuller s + stenver s', '<p><strong>SCHULLER&rsquo;S DAN STENVER&rsquo;S</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Cellulae mastoidea dextra tampak berselubung.</p>\r\n\r\n<p>Angulus Citelli tampak normal.</p>\r\n\r\n<p>Meatus acusticus eksternus tampak normal.</p>\r\n\r\n<p>Tidak tampak lesi lusen dan destruksi.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><u>Kesan :</u></strong><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong></p>\r\n\r\n<p><strong>Menyokong gambaran mastoiditis dextra.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong></p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (311, 'Shoulder', '<p><strong>SHOULDER JOINT</strong><strong> </strong><strong>DEXTRA</strong></p>\r\n\r\n<p>Besar, bentuk, dan struktur trabecula tulang-tulang pembentuk shoulder joint dextra dalam batas normal.</p>\r\n\r\n<p>Sela dan permukaan sendi normal.</p>\r\n\r\n<p>Caput humeri dan fossa glenoid normal.</p>\r\n\r\n<p>Tidak tampak lesi litik/sklerotik.</p>\r\n\r\n<p>Tidak tampak fraktur.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan</strong><strong>&nbsp;&nbsp;&nbsp; </strong><strong>: </strong><strong>Foto Shoulder joint</strong><strong> dextra</strong><strong> saat ini tidak tampak kelainan</strong><strong>.</strong></p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (312, 'Skoliosis', '<p><strong>VERTEBRA WHOLE SPINE</strong></p>\r\n\r\n<p><strong>(Terlampir foto vertebra whole spine 07-04-2020). </strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Curve scoliosis vertebra thorakolumbalis dengan apeks ke kanan pada vertebra Th 9 (cobb&rsquo;s angle 24&deg;) dan apeks ke kiri pada vertebra L2 (cobb&rsquo;s angle 19&deg;)</p>\r\n\r\n<p>Alignment dalam batas normal.</p>\r\n\r\n<p>Besar, bentuk dan struktur trabekula vertebra dalam batas normal.</p>\r\n\r\n<p>Foramina dan disci intervertebralis tidak menyempit.</p>\r\n\r\n<p>Pedicle normal.</p>\r\n\r\n<p>Tidak tampak fraktur.</p>\r\n\r\n<p>Tidak tampak lesi litik dan sklerotik.</p>\r\n\r\n<p>Terpasang brace.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan&nbsp;&nbsp;&nbsp; : </strong></p>\r\n\r\n<p><strong>Scoliosis vertebra thorakolumbalis dengan apeks ke kanan pada vertebra Th 9 (cobb&rsquo;s angle 24&deg;) dan apeks ke kiri pada vertebra L2 (cobb&rsquo;s angle 19&deg;).</strong></p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (313, 'Spondylosis', '<p><strong>VERTEBRA LUMBOSA</strong><strong>K</strong><strong>RAL</strong></p>\r\n\r\n<p>Curve dan alignment dalam batas normal.</p>\r\n\r\n<p>Besar dan struktur trabekula vertebra lumbosakral dalam batas normal.</p>\r\n\r\n<p>Tampak osteofit pada endplate corpora vertebra lumbalis.</p>\r\n\r\n<p>Foramina dan disci intervertebralis tidak menyempit.</p>\r\n\r\n<p>Pedicle normal.</p>\r\n\r\n<p>Tidak tampak fraktur.</p>\r\n\r\n<p>Tidak tampak lesi litik dan sklerotik.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan :&nbsp; </strong></p>\r\n\r\n<p><strong>Spondylosis vertebra lumbalis. </strong></p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (314, 'STL AP+LAT', '<p><strong>SOFT TISSUE LEHER AP/LATERAL</strong></p>\r\n\r\n<p>Kolom udara dalam faring dan laring normal</p>\r\n\r\n<p>Retrofaring &amp; retrolaring&nbsp; space tidak melebar</p>\r\n\r\n<p>Epiglotis dan valekula normal</p>\r\n\r\n<p>Tidak tampak perselubungan.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kesan : &nbsp;&nbsp;&nbsp;&nbsp; Foto Soft Tissue Leher dalam batas normal.</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (315, 'subdural empyema', '<p>Dilakukan CT scan kepala potongan aksial dengan slice interval 5 mm di daerah basis sampai vertex. Potongan dibuat sejajar orbitomeatal. Scanning tanpa memakai kontras media.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tampak defek pada os frontoparietalis kanan (post op?).</p>\r\n\r\n<p>Tampak lesi hipodens inhomogen dengan gambaran udara di dalamnya berbentuk crescent batas tegas tepi relatif regular berukuran lk 8,01 x 3,12 x 8,84 cm (volume lk 114,88 mL) di concavitas frontoparietotemporalis kanan. Tampak kalsifikasi multiple pada tepinya.</p>\r\n\r\n<p>Tampak lesi hipodens inhomogen berbentuk crescent batas tegas tepi ireguler berukuran lk 7,12 x 1,43 x 6,55 cm (volume lk 34,68 mL) di concavitas frontoparietalis kiri.</p>\r\n\r\n<p>Sulci dan gyri corticalis di sekitar lesi, fissura Sylvii bilateral dan fissura interhemisfer tampak menyempit.</p>\r\n\r\n<p>Bentuk dan posisi ventrikel lateralis bilateral asimetris. Ukuran ventrikel lateralis kanan dan kiri, ventrikel 3 dan 4 tampak melebar dan terdorong ke kiri.</p>\r\n\r\n<p>Sisterna ambiens dan basalis tampak melebar.</p>\r\n\r\n<p>Daerah sela tursika dan jukstasella serta daerah &ldquo;cerebello-pontin angle&rdquo; masih dalam batas normal.</p>\r\n\r\n<p>Parenkim cerebri lainnya, cerebellum dan pons tidak menunjukkan densitas patologis.</p>\r\n\r\n<p>Mastoid air cell bilateral yang terscanning tampak normal.</p>\r\n\r\n<p>Sinus paranasalis yang terscanning dalam batas normal.</p>\r\n\r\n<p>Bulbus okuli dan ruang retrobulber bilateral dalam batas normal.</p>\r\n\r\n<p>Tampak pergeseran struktur garis tengah sejauh lk 1,43 cm ke kiri</p>\r\n\r\n<p>Tampak ujung VP shunt berada di cornu posterior ventrikel lateralis kiri.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan :</strong></p>\r\n\r\n<p><strong>Sugestif subdural empyema dengan pneumocephalus dan kalsifikasi pada tepinya berukuran lk 8,01 x 3,12 x 8,84 cm (volume lk 114,88 mL) di concavitas frontoparietotemporalis kanan dan berukuran lk 7,12 x 1,43 x 6,55 cm (volume lk 34,68 mL) di concavitas frontoparietalis kiri dengan midline shift sejauh lk 1,43 cm ke kiri</strong></p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (316, 'Template USG', '<p>PEMERIKSAAN FOTO THORAX PA</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Soft tissue dan skeletal tampak normal.</p>\r\n\r\n<p>Trachea posisi midline.</p>\r\n\r\n<p>Cor tidak membesar.</p>\r\n\r\n<p>Sinuses dan diafragma normal.</p>\r\n\r\n<p>Pulmo :&nbsp;</p>\r\n\r\n<p>Hili normal.</p>\r\n\r\n<p>Corakan bronkhovaskuler normal.</p>\r\n\r\n<p>Tidak tampak infiltrat.</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n\r\n<p>Kesan :</p>\r\n\r\n<p>- Pulmo saat ini tidak tampak kelainan.</p>\r\n\r\n<p>- Tidak tampak kardiomegali.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Terimakasih atas kepercayaan TS.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>PEMERIKSAAN FOTO THORAX PA</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Soft tissue dan skeletal tampak normal.</p>\r\n\r\n<p>Trachea posisi midline.</p>\r\n\r\n<p>Cor membesar ke lateral kiri dengan apeks tertanam pada diafragma, pinggang jantung normal.</p>\r\n\r\n<p>Sinuses dan diafragma normal.</p>\r\n\r\n<p>Pulmo :&nbsp;</p>\r\n\r\n<p>Hili normal.</p>\r\n\r\n<p>Corakan bronkhovaskuler normal.</p>\r\n\r\n<p>Tidak tampak infiltrat.</p>\r\n\r\n<p>Kranialisasi (-).</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n\r\n<p>Kesan :</p>\r\n\r\n<p>- Kardiomegali tanpa bendungan paru.</p>\r\n\r\n<p>- Pulmo saat ini tidak tampak kelainan.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Terimakasih atas kepercayaan TS.</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (317, 'Thoraks kardiomegali', '<p>&nbsp;</p>\r\n\r\n<p><strong>THORAX </strong></p>\r\n\r\n<p>Soft tissue dan skeletal tampak normal.</p>\r\n\r\n<p>Cor membesar ke lateral kiri.</p>\r\n\r\n<p>Sinuses dan diafragma normal.</p>\r\n\r\n<p>Pulmo :</p>\r\n\r\n<p>Hilli normal.</p>\r\n\r\n<p>Corakan bronkhovaskuler normal.</p>\r\n\r\n<p>Tidak tampak infiltrat.</p>\r\n\r\n<p>Kranialisasi (-)</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan : </strong></p>\r\n\r\n<p><strong>Kardiomegali tanpa bendungan paru.</strong></p>\r\n\r\n<p><strong>Pulmo saat ini tidak tampak kelainan. </strong></p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (318, 'Thoraks N PDP', '<p><strong>THORAX </strong></p>\r\n\r\n<p>Soft tissue dan skeletal tampak normal.</p>\r\n\r\n<p>Trachea posisi midline.</p>\r\n\r\n<p>Cor tidak membesar.</p>\r\n\r\n<p>Sinuses dan diafragma normal.</p>\r\n\r\n<p>Pulmo :</p>\r\n\r\n<p>Hilli normal.</p>\r\n\r\n<p>Corakan bronkhovaskuler normal.</p>\r\n\r\n<p>Tidak tampak infiltrat.</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n\r\n<p><strong>Kesan : </strong></p>\r\n\r\n<p><strong>Pulmo saat ini tidak tampak kelainan. </strong></p>\r\n\r\n<p><strong>Tidak tampak gambaran bronchopneumonia/pneumonia. </strong></p>\r\n\r\n<p><strong>Tidak tampak kardiomegali. </strong></p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (319, 'Thoraks normal', '<p><strong>THORAX </strong></p>\r\n\r\n<p>Soft tissue dan skeletal tampak normal.</p>\r\n\r\n<p>Cor tidak membesar.</p>\r\n\r\n<p>Sinuses dan diafragma normal.</p>\r\n\r\n<p>Pulmo :</p>\r\n\r\n<p>Hilli normal.</p>\r\n\r\n<p>Corakan bronkhovaskuler normal.</p>\r\n\r\n<p>Tidak tampak infiltrat</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n\r\n<p><strong>Kesan : </strong></p>\r\n\r\n<p><strong>Pulmo saat ini masih terlihat dalam batas normal</strong></p>\r\n\r\n<p><strong>Tidak tampak kardiomegali. </strong></p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (320, 'Thoraks trauma + BNO tegak', '<p><strong>THORAX </strong></p>\r\n\r\n<p>Cor tidak membesar.</p>\r\n\r\n<p>Sinuses dan diafragma normal.</p>\r\n\r\n<p>Pulmo :</p>\r\n\r\n<p>Hilli normal.</p>\r\n\r\n<p>Corakan bronkhovaskuler normal.</p>\r\n\r\n<p>Tidak tampak infiltrat.</p>\r\n\r\n<p>Skeletal : Tidak tampak fraktur pada os clavicula, scapula dan costae yang terscanning</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan : </strong></p>\r\n\r\n<p><strong>Tidak tampak contusio paru.</strong></p>\r\n\r\n<p><strong>Tidak tampak kardiomegali.</strong></p>\r\n\r\n<p><strong>Tidak tampak </strong><strong>fraktur pada os clavicula, scapula dan costae.</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>BNO TEGAK </strong></p>\r\n\r\n<p>Preperitoneal fat jelas, psoas line kanan tidak jelas, kiri jelas.</p>\r\n\r\n<p>Kontur ginjal bilateral jelas.</p>\r\n\r\n<p>Free air (-), air fluid level (-).</p>\r\n\r\n<p>Distribusi udara usus halus dan colon sulit dinilai pada posisi tegak.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan : Tidak tampak gambaran pneumoperitoneum. </strong></p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (321, 'Thoraks trauma', '<p><strong>THORAX </strong></p>\r\n\r\n<p>Cor tidak membesar.</p>\r\n\r\n<p>Sinuses dan diafragma normal.</p>\r\n\r\n<p>Pulmo :</p>\r\n\r\n<p>Hilli normal.</p>\r\n\r\n<p>Corakan bronkhovaskuler normal.</p>\r\n\r\n<p>Tidak tampak infiltrat.</p>\r\n\r\n<p>Skeletal : Tidak tampak fraktur pada os clavicula, scapula dan costae yang terscanning</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan : </strong></p>\r\n\r\n<p><strong>Tidak tampak contusio paru.</strong></p>\r\n\r\n<p><strong>Tidak tampak kardiomegali.</strong></p>\r\n\r\n<p><strong>Tidak tampak </strong><strong>fraktur pada os clavicula, scapula dan costae.</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (322, 'Thorax (english)', '<p>&nbsp;</p>\r\n\r\n<p><strong>CHEST X-RAY</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Trachea is in midline position.</p>\r\n\r\n<p>Clear both lung fields and costophrenic angles</p>\r\n\r\n<p>Normal cardiomediastinal size and shape.</p>\r\n\r\n<p>Normal outline and position of bilateral diaphragm</p>\r\n\r\n<p>No radiological evidence of enlarged hilar or mediastinal lymph nodes.</p>\r\n\r\n<p>Normal appearance of chest wall and extrathoracic soft tissue.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Impression : </strong></p>\r\n\r\n<ul>\r\n	<li><strong>No evidence of pneumonia. </strong></li>\r\n	<li><strong>Chest x-ray within normal limit.</strong></li>\r\n</ul>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (323, 'Top Lordotik', '<p><strong>TOP LORDOTIK</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Tampak infiltrat di lapang atas paru kanan.</strong></p>\r\n\r\n<p><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong></p>\r\n\r\n<p><strong>Kesan : </strong></p>\r\n\r\n<p><strong>Menyokong gambaran TB Paru Aktif</strong><strong>.</strong></p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (324, 'TTN', '<p><strong>THORAX </strong></p>\r\n\r\n<p>Soft tissue dan skeletal tampak normal.</p>\r\n\r\n<p>Trachea posisi midline</p>\r\n\r\n<p>Cor tidak membesar.</p>\r\n\r\n<p>Sinuses dan diafragma normal.</p>\r\n\r\n<p>Pulmo :</p>\r\n\r\n<p>Hilli normal.</p>\r\n\r\n<p>Corakan bronkhovaskuler bertambah.</p>\r\n\r\n<p>Tampak gambaran retikuloradier dari hilus ke perifer di kedua lapang paru disertai penebalan fissura minor.</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n\r\n<p><strong>Kesan : </strong></p>\r\n\r\n<p><strong>Sugestif transient tachypnea of the newborn. </strong></p>\r\n\r\n<p><strong>Tidak tampak kardiomegali. </strong></p>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (325, 'USG Abdomen + appendix 2', '<p><strong>USG ABDOMEN DAN APPENDIKS</strong></p>\r\n\r\n<p>Hepar&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp;&nbsp; Tidak membesar. Parenkim homogen, tak tampak SOL. Ductus biliaris, vena hepatika, vena porta tak melebar. Tak tampak koleksi cairan di sekitarnya.</p>\r\n\r\n<p>Gallbladder&nbsp;&nbsp; :&nbsp;&nbsp; Besar &amp; bentuk normal. Dinding tidak menebal. Tak tampak batu ataupun SOL.</p>\r\n\r\n<p>Limpa&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp;&nbsp; Besar &amp; bentuk dlm batas normal. Textur parenkhim&nbsp; homogen.Tak tampak SOL. Vena Lienalis tak melebar. Tak tampak koleksi cairan di sekitarnya.</p>\r\n\r\n<p>Pankreas&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp;&nbsp; Besar &amp; bentuk dlm batas normal. Textur homogen</p>\r\n\r\n<p>Paraaorta&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp;&nbsp; Ukuran aorta abdominalis dalam batas normal.Tak tampak nodul hipo/hiperechoic disekitar aorta abdominalis.</p>\r\n\r\n<p>Parailiaka&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp;&nbsp; Tak tampak nodul hipo/hiperechoic disekitar parailiaka bilateral.</p>\r\n\r\n<p>Ginjal&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp;&nbsp; Besar &amp; bentuk normal. Batas textur parenkhim dengan central echocomplex normal. Sistim pelvocalises tidak melebar. Tak tampak batu ataupun SOL</p>\r\n\r\n<p>V. Urinaria&nbsp;&nbsp; :&nbsp;&nbsp; Besar &amp; bentuk&nbsp; normal, dinding tidak menebal. Tak tampak batu ataupun SOL.</p>\r\n\r\n<p>Uterus&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;:&nbsp; Besar normal, parenkim homogen, tidak tampak massa. Tidak tampak koleksi cairan di cavum douglas. Tidak tampak lesi kistik/solid pada adnexa bilateral.</p>\r\n\r\n<p>Prostat&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;:&nbsp; Besar, bentuk &amp; textur dalam batas normal, volume lk 19,6 mL.</p>\r\n\r\n<p>Scan abdomen kanan bawah : Tampak gambaran usus tubuler buntu tanpa peristaltic, non compressible, yang pada potongan transversal membentuk target sign berdiameter lk 1.16 cm, Tampak koleksi cairan bebas minimal di sekitarnya. Tidak tampak massa. Terdapat nyeri tekan probe pada area Mc Burney (tenderness on probe ultrasound).</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan :&nbsp; </strong></p>\r\n\r\n<ul>\r\n	<li><strong>Menyokong gambaran appendicitis dengan koleksi cairan di sekitarnya.</strong></li>\r\n	<li><strong>USG Hepar, gallbladder, limpa, pancreas, kedua ginjal, vesika urinaria &amp; uterus / prostat dalam batas normal. </strong></li>\r\n	<li><strong>Tidak tampak pembesaran KGB paraaorta/parailiaka.</strong></li>\r\n</ul>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (326, 'USG Abdomen + appendix normal', '<p>Hepar&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp;&nbsp; Tidak membesar. Parenkim homogen, tak tampak SOL. Ductus biliaris, vena hepatika, vena porta tak melebar. Tak tampak koleksi cairan di sekitarnya.</p>\r\n\r\n<p>Gallbladder&nbsp;&nbsp; :&nbsp;&nbsp; Besar &amp; bentuk normal. Dinding tidak menebal. Tak tampak batu ataupun SOL.</p>\r\n\r\n<p>Pankreas&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp;&nbsp; Besar &amp; bentuk dlm batas normal. Textur homogen.</p>\r\n\r\n<p>Limpa&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp;&nbsp; Besar &amp; bentuk dlm batas normal. Textur parenkhim&nbsp; homogen.Tak tampak SOL. Vena Lienalis tak melebar. Tak tampak koleksi cairan di sekitarnya.</p>\r\n\r\n<p>Paraaorta&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp;&nbsp; Tak tampak nodul hipo/hiperechoic disekitar aorta abdominalis.</p>\r\n\r\n<p>Parailiaka&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp;&nbsp; Tak tampak nodul hipo/hiperechoic disekitar parailiaka bilateral.</p>\r\n\r\n<p>Ginjal&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp;&nbsp; Besar &amp; bentuk normal. Batas textur parenkhim dengan central echocomplex normal. Sistim pelvocalises tidak melebar. Tak tampak batu ataupun SOL</p>\r\n\r\n<p>V. Urinaria&nbsp;&nbsp; :&nbsp;&nbsp; Besar &amp; bentuk&nbsp; normal, dinding tidak menebal. Tak tampak batu ataupun SOL.</p>\r\n\r\n<p>Uterus&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; Besar normal, parenkim homogen, tidak tampak massa. Tidak tampak koleksi cairan di cavum douglas. Tidak tampak lesi kistik/solid pada adnexa bilateral.</p>\r\n\r\n<p>Prostat&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;:&nbsp; Besar, bentuk &amp; textur dalam batas normal, volume lk 22,3 mL.</p>\r\n\r\n<p>Scan Abdomen Kanan Bawah :</p>\r\n\r\n<p>Tidak tampak gambaran usus tubuler buntu tanpa peristaltic yang pada potongan transversal membentuk target sign, Tidak tampak koleksi cairan bebas. Tidak tampak massa. Tidak terdapat nyeri tekan probe pada area Mc Burney (tenderness on probe ultrasound).</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; </strong></p>\r\n\r\n<ul>\r\n	<li><strong>Tidak jelas tanda-tanda apendisitis. </strong></li>\r\n	<li><strong>USG Hepar, gallbladder, pancreas, limpa, kedua ginjal, vesika urinaria &amp; uterus / prostat dalam batas normal. </strong></li>\r\n	<li><strong>Tidak tampak pembesaran KGB paraaorta/parailiaka. </strong></li>\r\n</ul>\r\n', 'drrosdiana');
INSERT INTO `xray_template` VALUES (327, 'USG Abdomen + appendix', '<p><strong>USG ABDOMEN DAN APPENDIKS</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Hepar&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; Tidak membesar. Textur parenkhim homogen, tak tampak SOL. Ductus biliaris, vena hepatika, vena porta tak melebar.</p>\r\n\r\n<p>Kt Empedu :&nbsp; Besar &amp; bentuk normal. Dinding tidak menebal. Tak tampak batu ataupun SOL.</p>\r\n\r\n<p>Limpa&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; Besar &amp; bentuk dlm batas normal. Textur parenkhim&nbsp; homogen.Tak tampak SOL. Vena Lienalis tak melebar.</p>\r\n\r\n<p>Pankreas&nbsp;&nbsp;&nbsp; :&nbsp; Besar &amp; bentuk dlm batas normal. Textur homogen</p>\r\n\r\n<p>Paraaorta&nbsp;&nbsp;&nbsp; :&nbsp; Sulit dinilai, sebagian besar tertutup reverberasi udara usus.</p>\r\n\r\n<p>Parailiaka&nbsp;&nbsp; :&nbsp; Tampak bayangan noduler hypoechoic multiple disekitar parailiaka bilateral terutama kanan, dengan ukuran terbesar lk 1,15 cm.</p>\r\n\r\n<p>Ginjal&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; Besar &amp; bentuk normal. Batas textur parenkhim dg central echo complex normal. Sistim pelvocalises tidak lebar.Tak tampak batu ataupun SOL</p>\r\n\r\n<p>Kd Kemih&nbsp; :&nbsp; Besar &amp; bentuk&nbsp; normal, dinding menebal. Tak tampak batu ataupun SOL.</p>\r\n\r\n<p>Uterus&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; Ukuran tampak membesar, endometrium menebal (post partum). Tidak tampak koleksi cairan di cavum douglas. Adnexa kanan-kiri tidak tampak lesi kistik/solid.</p>\r\n\r\n<p>Scan abdomen atas-bawah : Terdapat nyeri tekan probe terutama pada abdomen bawah kanan dan kiri (tenderness on probe ultrasound). Tampak gambaran usus-usus melebar dengan peristaltik yang menurun.</p>\r\n\r\n<p>Scan abdomen kanan bawah : Tidak tampak gambaran usus tubuler buntu tanpa peristaltik yang pada potongan transversal membentuk target sign, Tidak tampak koleksi cairan bebas. Tidak tampak massa.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan :&nbsp; </strong></p>\r\n\r\n<ul>\r\n	<li><strong>Gambaran ileus paralitik disertai limfadenopati parailiaka bilateral terutama kanan, perlu dipertimbangkan suatu proses spesifik (peritonitis TB p?)</strong></li>\r\n	<li><strong>Gambaran cystitis.</strong></li>\r\n	<li><strong>Tidak jelas tanda-tanda appendicitis. </strong></li>\r\n	<li><strong>USG Hepatobilier, gallbladder, limpa, pancreas, kedua ginjal, uterus dan adnexa bilateral dalam batas normal. </strong></li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Saran : Foto abdomen 3 posisi. </strong></p>\r\n', 'drrosdiana');
