INSERT INTO `xray_template` VALUES (445, 'Thorax Normal', '<p>Thorax</p>\r\n\r\n<p>Terpasang plate and screw pada os scapula kanan</p>\r\n\r\n<p>Cor membesar ke lateral kiri dengan apeks tertanam pada diafragma, pinggang jantung normal. Kalsifikasi aorta (+).</p>\r\n\r\n<p>Sinuses dan diafragma normal.</p>\r\n\r\n<p>Pulmo : Hilli normal.</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Corakan bronkhovaskuler bertambah . Kranialisasi (-)</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Tampak infiltrat paracardial bilateral &nbsp;</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n\r\n<p><strong>Kesan : </strong><strong>Bronkopneumonia bilateral</strong></p>\r\n\r\n<p><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;</strong><strong>Kardiomegali tanpa bendungan paru. Atherosclerosis aorta </strong></p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n', 'drganjar');
INSERT INTO `xray_template` VALUES (446, 'Thorax Normal1', '<p><strong>Dibandingkan foto tgl 10/08/2015</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Cor sqa</p>\r\n\r\n<p>Pulmo&nbsp;&nbsp;&nbsp;&nbsp; : Tak tampak bercak</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan&nbsp;&nbsp;&nbsp;&nbsp; : Post KP tak tampak aktifitas</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n', 'drganjar');
INSERT INTO `xray_template` VALUES (447, 'Thorax Patologis', '<p><u>Thorax</u></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Cor , sinuses &amp; diafragma normal</p>\r\n\r\n<p>Pulmo&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; : Hilli normal</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp; Corakan bronkhovaskuler normal</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp; Tidak tampak infiltrat</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan : &nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong><strong>Pulmo tidak tampak kelainan</strong></p>\r\n\r\n<p><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Tidak tampak kardiomegali</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n', 'drganjar');
INSERT INTO `xray_template` VALUES (448, 'Thorax perbandingan', '<p>Thorax</p>\r\n\r\n<p>Dibandingkan dengan foto tanggal&nbsp; :</p>\r\n\r\n<p>Cor tidak membesar</p>\r\n\r\n<p>Sinuses dan diafragma normal.</p>\r\n\r\n<p>Pulmo : Tidak tampak lagi infiltrat</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan : TB Paru Aktif perbaikan</strong></p>\r\n\r\n<p><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong></p>\r\n\r\n<p>&nbsp;</p>\r\n', 'drganjar');
INSERT INTO `xray_template` VALUES (449, 'THORAX', '<p>&laquo;Cor&raquo;</p>\r\n\r\n<p>Pulmo&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; &laquo;Hili&raquo; &laquo;Bercak&raquo; &laquo;Perselubungan&raquo; &laquo;Crk_Paru&raquo;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><em>Kesan&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; </em><em>&laquo;Kesan&raquo;</em></p>\r\n', 'drganjar');
INSERT INTO `xray_template` VALUES (450, 'TMJ Normal', '<p>Foto TMJ kiri :</p>\r\n\r\n<p><strong><u>Tutup mulut </u></strong><strong><u>&nbsp;:</u></strong></p>\r\n\r\n<ul>\r\n	<li>Caput mandibula berada pada fossa mandibula</li>\r\n	<li>Posisi simetris</li>\r\n	<li>Sela dan permukaan sendi normal</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><u>Buka mulut</u></strong><strong><u> :</u></strong></p>\r\n\r\n<ul>\r\n	<li>Caput mandibula terletak di bawah tuberkulum artikulare</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><u>Kesan</u></strong><strong>: &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Foto temporo mandibula joint </strong><strong>kiri</strong><strong>l saat ini tidak tampak kelainan</strong></p>\r\n', 'drganjar');
INSERT INTO `xray_template` VALUES (451, 'Top Lordotik', '<p><strong><u>Top Lordotik</u></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tidak tampak infiltrat &nbsp;di apex dan lapang atas kedua paru .</p>\r\n\r\n<p><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong></p>\r\n\r\n<p><strong>Kesan : Tidak tampak </strong><strong>TB Paru Aktif.</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n', 'drganjar');
INSERT INTO `xray_template` VALUES (452, 'USG Abd + App laki (Normal)', '<p>Hepar&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; Tidak membesar. Textur parenkhim homogen, tak tampak SOL. Ductus biliaris, vena hepatika, vena porta tak melebar.</p>\r\n\r\n<p>Kt Empedu: Besar &amp; bentuk normal. Dinding tidak menebal. Tak tampak batu ataupun SOL</p>\r\n\r\n<p>Limpa&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; Besar &amp; bentuk dlm batas normal. Textur parenkhim&nbsp; homogen.Tak tampak SOL. Vena Lienalis tak melebar.</p>\r\n\r\n<p>Pankreas&nbsp;&nbsp;&nbsp; :&nbsp; Besar &amp; bentuk dlm batas normal. Textur homogen</p>\r\n\r\n<p>Paraaorta&nbsp;&nbsp; :&nbsp; Besar &amp; bentuk aorta abdominalis dalam batas normal.Tak tampak bayangan noduler hypo/hyper echoic disekitar aorta abdominalis.</p>\r\n\r\n<p>Ginjal&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; Besar &amp; bentuk normal. Batas textur parenkhim dg central echo complex normal. Sistim pelvocalises tidak lebar.Tak tampak batu ataupun SOL</p>\r\n\r\n<p>Kt Kemih&nbsp; :&nbsp; Besar &amp; bentuk&nbsp; normal, dinding tidak menebal. Tak tampak batu ataupun SOL</p>\r\n\r\n<p>Abdomen kanan bawah : Appendik tidak tervisualisasi. Tidak tampak koleksi cairan</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kesan&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; Appendik tidak tervisualisasi</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp; USG Hepatobilier, gallbladder, limpa, pankreas, kedua ginjal, kt kemih dalam batas normal.</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Tak tampak pembesaran KGB paraaorta.</p>\r\n', 'drganjar');
INSERT INTO `xray_template` VALUES (453, 'USG Abd + App perempuan (Normal)', '<p>Hepar&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; Tidak membesar. Textur parenkhim homogen, tak tampak SOL. Ductus biliaris, vena hepatika, vena porta tak melebar.</p>\r\n\r\n<p>Kt Empedu: Besar &amp; bentuk normal. Dinding tidak menebal. Tak tampak batu ataupun SOL</p>\r\n\r\n<p>Limpa&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; Besar &amp; bentuk dlm batas normal. Textur parenkhim&nbsp; homogen.Tak tampak SOL. Vena Lienalis tak melebar.</p>\r\n\r\n<p>Pankreas&nbsp;&nbsp;&nbsp; :&nbsp; Besar &amp; bentuk dlm batas normal. Textur homogen</p>\r\n\r\n<p>Paraaorta&nbsp;&nbsp; :&nbsp; Besar &amp; bentuk aorta abdominalis dalam batas normal.Tak tampak bayangan noduler hypo/hyper echoic disekitar aorta abdominalis.</p>\r\n\r\n<p>Ginjal&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; Besar &amp; bentuk normal. Batas textur parenkhim dg central echo complex normal. Sistim pelvocalises tidak lebar.Tak tampak batu ataupun SOL</p>\r\n\r\n<p>Kt Kemih&nbsp; :&nbsp; Besar &amp; bentuk&nbsp; normal, dinding tidak menebal. Tak tampak batu ataupun SOL</p>\r\n\r\n<p>Uterus dan Adnexa : Besar &amp; bentuk uterus normal. Tidak tampak kelainan di adnexa</p>\r\n\r\n<p>Abdomen kanan bawah : Appendik tidak tervisualisasi. Tidak tampak koleksi cairan</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kesan&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; Appendik tidak tervisualisasi</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp; USG Hepatobilier, gallbladder, limpa, pankreas, kedua ginjal, kt kemih, uterus dan adnexa dalam batas normal.</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Tak tampak pembesaran KGB paraaorta.</p>\r\n', 'drganjar');
