INSERT INTO `xray_template` VALUES (456, 'USG ABdomen Inne', '<p>Hepar&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; Tidak membesar. Textur parenkhim homogen, tak tampak SOL. Ductus biliaris, vena hepatika, vena porta tak melebar.</p>\r\n\r\n<p>Kt Empedu: Membesar (hydrops) . Dinding tidak menebal. Tampak batu multipel ukuran terbesar 9,1mmm. CBD melebar. Duktus biliaris ektrahepatal melebar</p>\r\n\r\n<p>Limpa&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; Besar &amp; bentuk dlm batas normal. Textur parenkhim&nbsp; homogen.Tak tampak SOL. Vena Lienalis tak melebar.</p>\r\n\r\n<p>Pankreas&nbsp;&nbsp;&nbsp; :&nbsp; Besar &amp; bentuk dlm batas normal. Textur homogen</p>\r\n\r\n<p>Paraaorta&nbsp;&nbsp; :&nbsp; Besar &amp; bentuk aorta abdominalis dalam batas normal.Tak tampak bayangan noduler hypo/hyper echoic disekitar aorta abdominalis.</p>\r\n\r\n<p>Ginjal&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; Besar &amp; bentuk normal. Batas textur parenkhim dg central echo complex normal. Sistim pelvocalises tidak lebar.Tak tampak batu ataupun SOL</p>\r\n\r\n<p>Kt Kemih&nbsp; :&nbsp; Besar &amp; bentuk&nbsp; normal, dinding tidak menebal. Tak tampak batu ataupun SOL</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kesan&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; Hydrops Gallbladder disertai Cholelithiasis Multipel</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp; Pelebaran duktus biliaris ekstrahepatal</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp; USG Hepar, limpa, pankreas, kedua ginjal kt kemih dalam batas normal.</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Tak tampak pembesaran KGB paraaorta.</p>\r\n', 'drganjar');
INSERT INTO `xray_template` VALUES (457, 'USG ABdomen Lengkap 1', '<p>Hepar&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; Tidak membesar. Textur parenkhim homogen, tak tampak SOL. Ductus biliaris, vena hepatika, vena porta tak melebar.</p>\r\n\r\n<p>Kt Empedu: Besar &amp; bentuk normal. Dinding tidak menebal. Tak tampak batu/SOL</p>\r\n\r\n<p>Limpa&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; Besar &amp; bentuk dlm batas normal. Textur parenkhim&nbsp; homogen.Tak tampak SOL. Vena Lienalis tak melebar.</p>\r\n\r\n<p>Pankreas&nbsp;&nbsp;&nbsp; :&nbsp; Besar &amp; bentuk dlm batas normal. Textur homogen</p>\r\n\r\n<p>Paraaorta&nbsp;&nbsp;&nbsp; :&nbsp; Besar &amp; bentuk aorta abdominalis dalam batas normal.Tak tampak bayangan noduler hypo/hyper echoic disekitar aorta abdominalis.</p>\r\n\r\n<p>Ginjal&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; Besar &amp; bentuk normal. Batas textur parenkhim dg central echo complex normal. Sistim pelvocalises kanan tidak melebar, kiri melebar.Tak tampak batu/SOL. Ureter kiri melebar</p>\r\n\r\n<p>Kt Kemih&nbsp;&nbsp; :&nbsp; Besar &amp; bentuk&nbsp; normal, dinding tidak menebal. Tak tampak batu/SOL</p>\r\n\r\n<p>Uterus dan Adnexa :&nbsp;&nbsp;&nbsp; Besar &amp; bentuk uterus dalam batas normal. Tampak lesi hipoechoik batas tegas ukuran 33,1x42,8x39,2mm pada uterus, pada pemeriksaan color doppler tidak tampak vaskularisasi intratumoral. Tidak tampak kelainan di adnexa kanan dan kiri</p>\r\n\r\n<p>Abdomen kanan bawah: Tampak appendik membesar.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kesan&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; Pelvokaliektasi ginjal kiri</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp; Massa Semisolid Uterus</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp; Appendicitis</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp; USG Hepatobilier, gallbladder, limpa, pankreas, ginjal kanan, kt kemih, adnexa dalam batas normal.</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Tak tampak pembesaran KGB paraaorta.</p>\r\n', 'drganjar');
INSERT INTO `xray_template` VALUES (458, 'USG Abdomen', '<p>Ginjal kanan :&nbsp; Besar dan bentuk normal. Batas textur parenkhim dg central echo complex normal. Sistim pelvocalises lebar. Tak tampak batu ataupun SOL. Ureter kanan proksimal lebar, tampak batu ukuran 1,79 cm di ureter proksimal.</p>\r\n\r\n<p>Ginjal kiri&nbsp; :&nbsp; Besar dan bentuk normal. Batas textur parenkhim dg central echo complex normal. Sistim pelvocalises tidak lebar. Tak tampak batu ataupun SOL</p>\r\n\r\n<p>Kt Kemih&nbsp; :&nbsp; Besar, bentuk dan &nbsp;dinding dlm batas normal. Tak tampak batu ataupun SOL</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kesan&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; Diltasi sistim pelvocalises ginjal kanan dan ureter kanan bagian proksimal ec. batu ureter kanan bagian proksimal</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; USG ginjal kiri &amp; kt kemih dalam batas normal.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Saran : BNO-IVP</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n', 'drganjar');
INSERT INTO `xray_template` VALUES (459, 'USG Mammae', '<p><strong><u>Mammae Dextra&nbsp; :</u></strong></p>\r\n\r\n<p>Kutis dan sub kutis&nbsp; normal. Areola normal.Jaringan fibroglandular kurang</p>\r\n\r\n<p>padat, sebagian telah mengalami involusi lemak. Tidak tampak bayangan massa</p>\r\n\r\n<p>iso-hiper-hipo dan anekhoik,</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><u>Mammae Sinistra&nbsp; :</u></strong></p>\r\n\r\n<p>Kutis dan sub kutis&nbsp; normal. Areola normal.Jaringan fibroglandular kurang</p>\r\n\r\n<p>padat, sebagian telah mengalami involusi lemak. Tidak tampak bayangan massa</p>\r\n\r\n<p>iso-hiper-hipo dan anekhoik,</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n\r\n<p><strong><u>Axilla:</u></strong></p>\r\n\r\n<p>Tidak tampak nodul hipo/anekhoik</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kesan:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - USG Mammae Bilateral dalam batas normal</p>\r\n\r\n<p><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - Tidak tampak pembesaran KGB axilla </strong><strong>bilateral</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n', 'drganjar');
INSERT INTO `xray_template` VALUES (460, 'USG Mammae_nona', '<p><strong><u>Mammae Dextra&nbsp; :</u></strong></p>\r\n\r\n<p>Kutis dan sub kutis&nbsp; normal. Areola normal. Jaringan fibroglandular masih</p>\r\n\r\n<p>padat, belum mengalami involusi lemak. Tidak tampak bayangan massa</p>\r\n\r\n<p>iso-hiper-hipo dan anekhoik,</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><u>Mammae Sinistra:</u></strong></p>\r\n\r\n<p>Kutis dan sub kutis&nbsp; normal. Areola normal. Jaringan fibroglandular masih</p>\r\n\r\n<p>padat, belum mengalami involusi lemak. Tidak tampak bayangan massa</p>\r\n\r\n<p>iso-hiper-hipo dan anekhoik,</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><u>Axilla Bilateral :</u></strong></p>\r\n\r\n<p>Tidak tampak nodul hipo/anekhoik</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kesan:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - USG mammae bilateral saat ini masih daam batas normal</p>\r\n\r\n<p><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - Tidak tampak pembesaran KGB axilla kanan-kiri.</strong></p>\r\n', 'drganjar');
INSERT INTO `xray_template` VALUES (461, 'USG testis', '<p><strong><u>Testis Dextra</u></strong><strong><u> </u></strong><strong><u>&nbsp;:</u></strong></p>\r\n\r\n<p>Besar dan bentuk normal, densitas echo parenkim normal. Tidak tampak SOL.</p>\r\n\r\n<p>Tampak koleksi cairan minimal di sekitarnya.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><u>Testis Sinistra :</u></strong></p>\r\n\r\n<p>Besar dan bentuk normal, densitas echo parenkim normal. Tidak tampak SOL.</p>\r\n\r\n<p>Tampak koleksi cairan minimal di sekitarnya.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><u>Epididimi</u></strong><strong><u>s</u></strong><strong><u>:</u></strong></p>\r\n\r\n<p>Besar dan bentuk normal. densitas echo parenkim normal. Tidak tampak SOL.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><u>Pleksus Pampiniformis :</u></strong></p>\r\n\r\n<p>Tidak melebar, pada tes valsava tidak melebar</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><u>Inguinal:</u></strong></p>\r\n\r\n<p>Tidak tampak bayangan usus</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; : &nbsp;&nbsp;&nbsp; USG testis&nbsp; bilateral dan inguinal bilateral saat ini tidak</strong></p>\r\n\r\n<p><strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; tampak kelainan</strong></p>\r\n', 'drganjar');
INSERT INTO `xray_template` VALUES (462, 'USG thorak', '<p><strong><u>Supradiafragma</u></strong><strong><u> kanan </u></strong><strong><u>- kiri</u></strong><strong><u>:</u></strong></p>\r\n\r\n<p>Tidak tampak koleksi cairan di supradiafragma kanan dan kiri.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; : &nbsp;Tidak tampak efusi pleura</strong></p>\r\n', 'drganjar');
INSERT INTO `xray_template` VALUES (463, 'USG thyroid', '<p style=\"text-align:justify\"><strong>Thyroid Dextra :</strong></p>\r\n\r\n<p style=\"text-align:justify\">Tidak membesar, echogenitas normal, tidak tampak lesi fokal</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><strong>Thyroid Sinistra:</strong></p>\r\n\r\n<p style=\"text-align:justify\">Tidak membesar, echogenitas normal, tidak tampak lesi fokal</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><strong>Colli bilateral:</strong></p>\r\n\r\n<p style=\"text-align:justify\">Tidak tampak pembesaran KGB colli bilateral</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><strong>Kesan:</strong></p>\r\n\r\n<p style=\"text-align:justify\"><strong>Thyroid Bilateral tidak tampak kelainan</strong></p>\r\n', 'drganjar');
INSERT INTO `xray_template` VALUES (464, 'USG TU', '<p>Ginjal&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; Besar &amp; bentuk normal. Batas textur parenkhim dg central echo complex normal. Sistim pelvocalises tidak lebar.Tak tampak batu/SOL</p>\r\n\r\n<p>Kt Kemih&nbsp; :&nbsp; Besar &amp; bentuk&nbsp; normal, dinding tidak menebal. Tak tampak batu/SOL</p>\r\n\r\n<p>Prostat&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; Besar, bentuk, &amp; textur dalam batas normal.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kesan&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; USG Kedua ginjal, kt kemih, prostat dalam batas normal.</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\r\n', 'drganjar');
INSERT INTO `xray_template` VALUES (465, 'USG_Abdomen', '<p>Hepar&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; Tepi tajam permukaan rata. Textur parenkhim homogen, tak tampak SOL. Ductus biliaris, vena hepatika, vena porta tak melebar.</p>\r\n\r\n<p>Kt Empedu: Besar bentuk dan dinding normal. Tak tampak batu ataupun SOL</p>\r\n\r\n<p>Limpa&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; Besar bentuk dlm batas normal. Textur parenkhim&nbsp; homogen.Tak tampak SOL. Vena Lienalis tak melebar.</p>\r\n\r\n<p>Pankreas&nbsp;&nbsp;&nbsp; :&nbsp; Besar bentuk dlm batas normal. Textur homogen</p>\r\n\r\n<p>Paraaorta&nbsp;&nbsp; :&nbsp; Besar &amp; bentuk aorta abdominalis dalam batas normal.Tak tampak bayangan noduler hypo ataupun hyper echoic disekitar aorta abdominalis.</p>\r\n\r\n<p>Ginjal&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; Besar bentuk kedua ginjal normal. Batas textur parenkhim dg central echo complex normal. Tak tampak batu ataupun SOL</p>\r\n\r\n<p>Kt Kemih&nbsp; :&nbsp; Besar bentuk dinding dlm batas normal. Tak tampak batu ataupun SOL</p>\r\n\r\n<p>Uterus&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; Besar &amp; bentuk dalam batas normal.</p>\r\n\r\n<p>Ovarium&nbsp;&nbsp;&nbsp; :&nbsp; Besar, bentuk, &amp; textur dalam batas normal.</p>\r\n\r\n<p>Prostat&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; Besar, bentuk, &amp; textur dalam batas normal.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kesan&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; USG Hepatobilier, limpa, pankreas, kedua ginjal, &amp; kt kemih dalam batas normal.</p>\r\n\r\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Tak tampak pembesaran KGB paraaorta.</p>\r\n\r\n<p>&nbsp;</p>\r\n', 'drganjar');
INSERT INTO `xray_template` VALUES (466, 'Waters normal', '<p><strong><u>Water&rsquo;s</u></strong><strong><u> :</u></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<ul>\r\n	<li>Sinus frontalis kanan-kiri, &nbsp;maksilaris kanan-kiri dan sphenoid</li>\r\n</ul>\r\n\r\n<p>kanan-kiri cerah</p>\r\n\r\n<ul>\r\n	<li>Septum nasi di tengah</li>\r\n	<li>Concha nasalis kanan-kiri tidak menebal</li>\r\n	<li>Tulang-tulang sekitar sinus baik</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; :&nbsp; Foto water&rsquo;s saat ini tidak tampak kelainan</strong></p>\r\n', 'drganjar');
INSERT INTO `xray_template` VALUES (467, 'wrist joint', '<p><strong><u>Wrist</u></strong><strong><u> Joint</u></strong><strong><u> </u></strong><strong><u>sinistra</u></strong><strong><u> :</u></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Besar, bentuk dan struktur trabekula normal</p>\r\n\r\n<p>Sela dan permukaaan sendi normal</p>\r\n\r\n<p>Tidak tampak lesi litik/sklerotik</p>\r\n\r\n<p>Tidak tampak osteofit</p>\r\n\r\n<p>Tidak tampak fraktur</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Kesan :</strong><strong> Wrist</strong><strong> joint sinistra</strong><strong> saat ini</strong><strong> tidak tampak kelainan</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n', 'drganjar');
INSERT INTO `xray_template` VALUES (468, 'Thorax MCU', '<strong><u>Thorax </u></strong><br />\r\nSkeletal : Tidak tampak skoliosis<br />\r\nCor, sinuses dan diafragma normal.<br />\r\nPulmo : Hilli normal.<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;Corakan bronkhovaskuler normal.<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;Tidak tampak infiltrate.<br />\r\n&nbsp;<br />\r\n<strong>Kesan : Pulmo saat ini tidak tampak kelainan </strong><br />\r\n<strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp; Cor saat ini tidak tampak kelainan<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Tidak tampak skoliosis</strong><br />\r\n&nbsp;', 'drganjar');
INSERT INTO `xray_template` VALUES (469, 'Thorax normal AA', 'Thorax<br />\r\nCor tidak membesar.&nbsp; Kalsifikasi aorta (+)<br />\r\nSinuses dan diafragma normal.<br />\r\nPulmo : Hilli normal.<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Corakan bronkhovaskuler normal.<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Tidak tampak infiltrat.<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br />\r\n<strong>Kesan :<br />\r\nAtherosklerosis aorta&nbsp;<br />\r\nPulmo saat ini tidak tampak kelainan</strong><br />\r\n<strong>Tidak tampak kardiomegali</strong><br />\r\n<strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong><br />\r\n&nbsp;<br />\r\n<strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </strong><br />\r\n&nbsp;<br />\r\n&nbsp;', 'drganjar');
INSERT INTO `xray_template` VALUES (470, 'CT abdomen Wanita', '<strong>Dilakukan pemeriksaan MSCT</strong><strong>/CT scan </strong><strong>abdomen </strong><strong>sampai pelvis potongan </strong><strong>axial. Dibuat rekonstruksi potongan koronal dan sagittal. Scanning </strong><strong>tanpa&nbsp; memakai kontras.</strong><br />\r\n<br />\r\n<strong><em><u>Scanning daerah :</u></em></strong><br />\r\nHepar&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;: Ukuran tidak membesar, permukaan rata, densitas homogen. Tidak tampak massa. Vena porta&nbsp;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; tidak melebar. Vena hepatica tidak melebar. Vena cava inferior tidak melebar. Duktus biliaris<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; intrahepatal tidak melabar.Tidak tampak koleksi cairan di sekitarnya<br />\r\nKt. Empedu&nbsp; &nbsp;: Ukuran tidak membesar, dinding tidak menebal, regular. Tidak tampak batu/SOL. Duktus biliaris<br />\r\n&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;ekstrahepatal tidak melebar<br />\r\nLimpa&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp; :Ukuran tidak membesar, densitas homogen. Vena lienalis tidak melebar. Tidak tampak koleksi&nbsp;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; cairan di sekitarnya.<br />\r\nPankreas&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp; :Ukuran tidak membesar, densitas homogen, ductus pankreatikus tidak melebar<br />\r\nGinjal kanan &nbsp; :Ukuran tidak membesar, struktur parenkhim homogen, system pelvokalises tidak melebar, tidak<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; tampak batu/massa. Ureter kanan tidak melebar.<br />\r\nGinjal kiri&nbsp; &nbsp; &nbsp;&nbsp;&nbsp;:Ukuran tidak membesar, struktur parenkhim homogen, system pelvokalises tidak melebar,&nbsp;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; tidak tampak batu/massa. Ureter kiri tidak melebar.<br />\r\nVesica urinaria : Besar dan bentuk normal. Tidak tampak batu/SOL.<br />\r\nUterus &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;:&nbsp; Ukuran tidak membesar, densitas homogen. Parametrium bilateral tampak normal<br />\r\nRektum&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp; : Posisi normal, dinding tidak menebal, perirectal fat normal<br />\r\nTidak tampak bayangan hipodens&nbsp; di paraaorta dan parailiaka<em> </em><em>.</em> Tidak tampak kalsifikasi pada aorta abdominalis<br />\r\nStruktur gastrointestinal normal. Tidak dijumpai polip,&nbsp; divertikel atau kolitis. Intraabdomen tidak tampak<br />\r\nmassa yang mencurigakan. Tidak terlihat pengumpulan cairan bebas di rongga abdomen.<br />\r\nPada bagian paru yang tervisualisasi&nbsp; tidak dijumpai kelainan. Tidak dijumpai efusi pleura.<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<br />\r\n<strong>KESAN&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; : </strong>\r\n<ul>\r\n	<li><strong><em>CT-scan Hepar, gallblader,&nbsp;</em></strong><strong><em>lien</em></strong><strong><em>,</em></strong><strong><em> pankreas</em></strong><strong><em>, ginjal kanan-kiri,bladder, uterus dan ovarium&nbsp;</em></strong><strong><em>tidak dijumpai kelainan</em></strong></li>\r\n	<li><strong><em>Struktu</em></strong><strong><em>r</em></strong><strong><em> gastrointestinal tidak tampak kelainan, divertikel, polip atau apolitis</em></strong></li>\r\n	<li><strong><em>Tidak terlihat koleksi cairan bebas intrabdome</em></strong><strong><em>n</em></strong></li>\r\n	<li><strong><em>Tidak tampak pembesaran KGB paraaorta</em></strong></li>\r\n</ul>\r\n<br />\r\n&nbsp;<br />\r\n&nbsp;<br />\r\n&nbsp;<br />\r\n&nbsp;<br />\r\n&nbsp;', 'drganjar');
